@Deprecated
public final class V1BinaryAnnotation implements Comparable<V1BinaryAnnotation> {
  public static final int TYPE_BOOLEAN = 0;
  public static final int TYPE_STRING = 6;

  public static V1BinaryAnnotation createAddress(String address, Endpoint endpoint) {
    if (endpoint == null) throw new NullPointerException("endpoint == null");
    return new V1BinaryAnnotation(address, null, endpoint);
  }

  public static V1BinaryAnnotation createString(String key, String value, Endpoint endpoint) {
    if (value == null) throw new NullPointerException("value == null");
    return new V1BinaryAnnotation(key, value, endpoint);
  }

  public String key() {
    return key;
  }

  public int type() {
    return type;
  }

  @Nullable
  public String stringValue() {
    return stringValue;
  }

  public Endpoint endpoint() {
    return endpoint;
  }

  final String key, stringValue;
  final int type;
  final Endpoint endpoint;

  V1BinaryAnnotation(String key, String stringValue, Endpoint endpoint) {
    if (key == null) throw new NullPointerException("key == null");
    this.key = key;
    this.stringValue = stringValue;
    this.type = stringValue != null ? TYPE_STRING : TYPE_BOOLEAN;
    this.endpoint = endpoint;
  }

  @Override
  public boolean equals(Object o) {
    if (o == this) return true;
    if (!(o instanceof V1BinaryAnnotation)) return false;
    V1BinaryAnnotation that = (V1BinaryAnnotation) o;
    return key.equals(that.key)
        && (stringValue == null ? that.stringValue == null : stringValue.equals(that.stringValue))
        && (endpoint == null ? that.endpoint == null : endpoint.equals(that.endpoint));
  }

  @Override
  public int hashCode() {
    int h = 1;
    h *= 1000003;
    h ^= key.hashCode();
    h *= 1000003;
    h ^= stringValue == null ? 0 : stringValue.hashCode();
    h *= 1000003;
    h ^= endpoint == null ? 0 : endpoint.hashCode();
    return h;
  }

  @Override
  public int compareTo(V1BinaryAnnotation that) {
    if (this == that) return 0;
    return key.compareTo(that.key);
  }
}

public class ComputeMean implements IComputer {
    private ThreadCommSlave comm;
    private LogUtils LOG_UTILS;

    public ComputeMean(ThreadCommSlave comm) {
        this.comm = comm;
        this.LOG_UTILS = new LogUtils(comm, true);
    }

    @Override
    public float[] compute(GBDTCoreData data) throws Mp4jException {
        double[] sumCnt = new double[data.usefulFeatureDim << 1];
        for (int i = 0; i < sumCnt.length; i++) {
            sumCnt[i] = 0;
        }

        for (int i = 0; i < data.sampleNum; i++) {
            for (int fid = 0; fid < data.usefulFeatureDim; fid++) {
                int intval = data.getFeatureVal(i, fid);
                if (intval == Constants.INT_MISSING_VALUE) {
                    continue;
                }
                float val = NumConvertUtils.int2float(intval);
                float wei = data.getSampleWeight(i);
                sumCnt[fid << 1] += val * wei;
                sumCnt[(fid << 1) + 1] += wei;
            }
        }

        if (comm != null) {
            sumCnt = comm.allreduceArray(sumCnt, Operands.DOUBLE_OPERAND(), Operators.Double.SUM, 0, sumCnt.length);
        }

        float[] missVfill = new float[data.usefulFeatureDim];
        for (int fid = 0; fid < data.usefulFeatureDim; fid++) {
            int j = (fid << 1);
            if (sumCnt[j + 1] == 0.0) {
                missVfill[fid] = 0;
                if (comm != null) {
                    LOG_UTILS.importantInfo(String.format("feature(index:%d, name:%s) are all missing values or has no feature values!", fid, data.fIndex2NameMap.get(fid)));
                }
            } else {
                missVfill[fid] = (float) (sumCnt[j] / sumCnt[j + 1]);
            }
        }
        return missVfill;
    }
}

public class MultibinderPlannerNamespace implements SourceNamespace, ModuleNamespace {
    private final Map<String, Provider<Source>> sourceBindings;
    private final Map<String, Provider<Exports>> exportsBindings;

    private String keyFor(List<String> path) {
        return Joiner.on('.').join(path);
    }

    @Inject
    MultibinderPlannerNamespace(Map<String, Provider<Exports>> exportsBindings, Map<String, Provider<Source>> sourceBindings) {
        this.exportsBindings = exportsBindings;
        this.sourceBindings = sourceBindings;
    }

    @Override
    public ModuleType findModule(Location location, ContextPlanner planner, List<String> modulePath) {
        Provider<Exports> moduleProvider = exportsBindings.get(keyFor(modulePath));
        if (moduleProvider == null) {
            return null;
        }
        ExportUnitGenerator adapter = new ExportUnitGenerator(planner.getGambitScope());
        return adapter.apply(modulePath, moduleProvider);
    }

    @Override
    public SourceType findSource(Location location, ContextPlanner planner, List<String> sourcePath) {
        Provider<Source> sourceProvider = sourceBindings.get(keyFor(sourcePath));
        if (sourceProvider == null) {
            return null;
        }
        SourceUnitGenerator adapter = new SourceUnitGenerator(planner.getGambitScope());
        return adapter.apply(sourcePath, sourceProvider);
    }
}

public class ListCommand extends ReplCommand {

	@SuppressWarnings("unchecked")
	public ListCommand() {
		super(false, SqlNameCompleter.class);
	}

	@Override
	public boolean execute(final LineReader reader, final String[] parts, final SqlConfig sqlConfig,
			final Properties props) {
		var writer = reader.getTerminal().writer();
		writer.println("LIST:");
		writer.flush();

		List<String> pathList = null;
		if (parts.length > 1) {
			pathList = sqlConfig.getSqlResourceManager().getSqlPathList().stream().filter(p -> p.contains(parts[1]))
					.collect(Collectors.toList());
		} else {
			pathList = sqlConfig.getSqlResourceManager().getSqlPathList();
		}
		for (String key : pathList) {
			writer.println(key);
		}
		writer.flush();
		return true;
	}

	@Override
	public void showHelp(final Terminal terminal) {
		terminal.writer().println("\t" + this.toString() + "\t: list loaded sql files.");
		terminal.writer().println("\t\tex1) list<Enter> : Show all loaded sql file(s).");
		terminal.writer().println("\t\tex2) list keyword<Enter> : Show loaded sql file(s) filter by keyword.");
	}
}

public final class QRCodeMultiReader extends QRCodeReader implements MultipleBarcodeReader {

  private static final Result[] EMPTY_RESULT_ARRAY = new Result[0];
  private static final ResultPoint[] NO_POINTS = new ResultPoint[0];

  @Override
  public Result[] decodeMultiple(BinaryBitmap image) throws NotFoundException {
    return decodeMultiple(image, null);
  }

  @Override
  public Result[] decodeMultiple(BinaryBitmap image, Map<DecodeHintType,?> hints) throws NotFoundException {
    List<Result> results = new ArrayList<>();
    DetectorResult[] detectorResults = new MultiDetector(image.getBlackMatrix()).detectMulti(hints);
    for (DetectorResult detectorResult : detectorResults) {
      try {
        DecoderResult decoderResult = getDecoder().decode(detectorResult.getBits(), hints);
        ResultPoint[] points = detectorResult.getPoints();
        if (decoderResult.getOther() instanceof QRCodeDecoderMetaData) {
          ((QRCodeDecoderMetaData) decoderResult.getOther()).applyMirroredCorrection(points);
        }
        Result result = new Result(decoderResult.getText(), decoderResult.getRawBytes(), points,
                                   BarcodeFormat.QR_CODE);
        List<byte[]> byteSegments = decoderResult.getByteSegments();
        if (byteSegments != null) {
          result.putMetadata(ResultMetadataType.BYTE_SEGMENTS, byteSegments);
        }
        String ecLevel = decoderResult.getECLevel();
        if (ecLevel != null) {
          result.putMetadata(ResultMetadataType.ERROR_CORRECTION_LEVEL, ecLevel);
        }
        if (decoderResult.hasStructuredAppend()) {
          result.putMetadata(ResultMetadataType.STRUCTURED_APPEND_SEQUENCE,
                             decoderResult.getStructuredAppendSequenceNumber());
          result.putMetadata(ResultMetadataType.STRUCTURED_APPEND_PARITY,
                             decoderResult.getStructuredAppendParity());
        }
        results.add(result);
      } catch (ReaderException re) {
        }
    }
    if (results.isEmpty()) {
      return EMPTY_RESULT_ARRAY;
    } else {
      results = processStructuredAppend(results);
      return results.toArray(new Result[results.size()]);
    }
  }

  private static List<Result> processStructuredAppend(List<Result> results) {
    boolean hasSA = false;

    for (Result result : results) {
      if (result.getResultMetadata().containsKey(ResultMetadataType.STRUCTURED_APPEND_SEQUENCE)) {
        hasSA = true;
        break;
      }
    }
    if (!hasSA) {
      return results;
    }

    List<Result> newResults = new ArrayList<>();
    List<Result> saResults = new ArrayList<>();
    for (Result result : results) {
      newResults.add(result);
      if (result.getResultMetadata().containsKey(ResultMetadataType.STRUCTURED_APPEND_SEQUENCE)) {
        saResults.add(result);
      }
    }
    Collections.sort(saResults, new SAComparator());
    StringBuilder concatedText = new StringBuilder();
    int rawBytesLen = 0;
    int byteSegmentLength = 0;
    for (Result saResult : saResults) {
      concatedText.append(saResult.getText());
      rawBytesLen += saResult.getRawBytes().length;
      if (saResult.getResultMetadata().containsKey(ResultMetadataType.BYTE_SEGMENTS)) {
        @SuppressWarnings("unchecked")
        Iterable<byte[]> byteSegments =
            (Iterable<byte[]>) saResult.getResultMetadata().get(ResultMetadataType.BYTE_SEGMENTS);
        for (byte[] segment : byteSegments) {
          byteSegmentLength += segment.length;
        }
      }
    }
    byte[] newRawBytes = new byte[rawBytesLen];
    byte[] newByteSegment = new byte[byteSegmentLength];
    int newRawBytesIndex = 0;
    int byteSegmentIndex = 0;
    for (Result saResult : saResults) {
      System.arraycopy(saResult.getRawBytes(), 0, newRawBytes, newRawBytesIndex, saResult.getRawBytes().length);
      newRawBytesIndex += saResult.getRawBytes().length;
      if (saResult.getResultMetadata().containsKey(ResultMetadataType.BYTE_SEGMENTS)) {
        @SuppressWarnings("unchecked")
        Iterable<byte[]> byteSegments =
            (Iterable<byte[]>) saResult.getResultMetadata().get(ResultMetadataType.BYTE_SEGMENTS);
        for (byte[] segment : byteSegments) {
          System.arraycopy(segment, 0, newByteSegment, byteSegmentIndex, segment.length);
          byteSegmentIndex += segment.length;
        }
      }
    }
    Result newResult = new Result(concatedText.toString(), newRawBytes, NO_POINTS, BarcodeFormat.QR_CODE);
    if (byteSegmentLength > 0) {
      Collection<byte[]> byteSegmentList = new ArrayList<>();
      byteSegmentList.add(newByteSegment);
      newResult.putMetadata(ResultMetadataType.BYTE_SEGMENTS, byteSegmentList);
    }
    newResults.add(newResult);
    return newResults;
  }

  private static final class SAComparator implements Comparator<Result>, Serializable {
    @Override
    public int compare(Result a, Result b) {
      int aNumber = (int) a.getResultMetadata().get(ResultMetadataType.STRUCTURED_APPEND_SEQUENCE);
      int bNumber = (int) b.getResultMetadata().get(ResultMetadataType.STRUCTURED_APPEND_SEQUENCE);
      if (aNumber < bNumber) {
        return -1;
      }
      if (aNumber > bNumber) {
        return 1;
      }
      return 0;
    }
  }

}

public class DiffusionFilter extends WholeImageFilter {

	private final static int[] diffusionMatrix = {
	 	 0, 0, 0,
	 	 0, 0, 7,
	 	 3, 5, 1,
	};

	private int[] matrix;
	private int sum = 3+5+7+1;
	private boolean serpentine = true;
	private boolean colorDither = true;
	private int levels = 6;

	public DiffusionFilter() {
		setMatrix(diffusionMatrix);
	}

	public void setSerpentine(boolean serpentine) {
		this.serpentine = serpentine;
	}

	public boolean getSerpentine() {
		return serpentine;
	}

	public void setColorDither(boolean colorDither) {
		this.colorDither = colorDither;
	}

	public boolean getColorDither() {
		return colorDither;
	}

	public void setMatrix(int[] matrix) {
		this.matrix = matrix;
		sum = 0;
		for (int i = 0; i < matrix.length; i++)
			sum += matrix[i];
	}

	public int[] getMatrix() {
		return matrix;
	}

	public void setLevels(int levels) {
		this.levels = levels;
	}

	public int getLevels() {
		return levels;
	}

	protected int[] filterPixels( int width, int height, int[] inPixels, Rectangle transformedSpace ) {
		int[] outPixels = new int[width * height];

		int index = 0;
		int[] map = new int[levels];
		for (int i = 0; i < levels; i++) {
			int v = 255 * i / (levels-1);
			map[i] = v;
		}
		int[] div = new int[256];
		for (int i = 0; i < 256; i++)
			div[i] = levels*i / 256;

		for (int y = 0; y < height; y++) {
			boolean reverse = serpentine && (y & 1) == 1;
			int direction;
			if (reverse) {
				index = y*width+width-1;
				direction = -1;
			} else {
				index = y*width;
				direction = 1;
			}
			for (int x = 0; x < width; x++) {
				int rgb1 = inPixels[index];

				int r1 = (rgb1 >> 16) & 0xff;
				int g1 = (rgb1 >> 8) & 0xff;
				int b1 = rgb1 & 0xff;

				if (!colorDither)
					r1 = g1 = b1 = (r1+g1+b1) / 3;

				int r2 = map[div[r1]];
				int g2 = map[div[g1]];
				int b2 = map[div[b1]];

				outPixels[index] = (rgb1 & 0xff000000) | (r2 << 16) | (g2 << 8) | b2;

				int er = r1-r2;
				int eg = g1-g2;
				int eb = b1-b2;

				for (int i = -1; i <= 1; i++) {
					int iy = i+y;
					if (0 <= iy && iy < height) {
						for (int j = -1; j <= 1; j++) {
							int jx = j+x;
							if (0 <= jx && jx < width) {
								int w;
								if (reverse)
									w = matrix[(i+1)*3-j+1];
								else
									w = matrix[(i+1)*3+j+1];
								if (w != 0) {
									int k = reverse ? index - j : index + j;
									rgb1 = inPixels[k];
									r1 = (rgb1 >> 16) & 0xff;
									g1 = (rgb1 >> 8) & 0xff;
									b1 = rgb1 & 0xff;
									r1 += er * w/sum;
									g1 += eg * w/sum;
									b1 += eb * w/sum;
									inPixels[k] = (inPixels[k] & 0xff000000) | (PixelUtils.clamp(r1) << 16) | (PixelUtils.clamp(g1) << 8) | PixelUtils.clamp(b1);
								}
							}
						}
					}
				}
				index += direction;
			}
		}

		return outPixels;
	}

	public String toString() {
		return "Colors/Diffusion Dither...";
	}

}

