public class S3LegalHoldLock extends Lock implements S3Lock {

	private static final Logger logger = LoggerFactory.getLogger(S3LegalHoldLock.class);

	private S3Directory s3Directory;
	private String name;

	@Override
	public void configure(final S3Directory s3Directory, final String name) throws IOException {
		this.s3Directory = s3Directory;
		this.name = name;
	}

	@Override
	public void obtain() throws IOException {
		try {
			if (logger.isDebugEnabled()) {
				logger.info("obtain({})", name);
			}
			putObjectLegalHold(ON);
		} catch (AwsServiceException | SdkClientException e) {
			throw new LockObtainFailedException("Lock object could not be created: ", e);
		}
	}

	@Override
	public void close() throws IOException {
		try {
			if (logger.isDebugEnabled()) {
				logger.info("close({})", name);
			}
putObjectLegalHold(OFF);
} catch (AwsServiceException | SdkClientException e) {
			throw new AlreadyClosedException("Lock was already released: ", e);
		}

}

	@Override
	public void ensureValid() throws IOException {
		try {
			if (logger.isDebugEnabled()) {
				logger.info("ensureValid({})", name);
			}
			if (!isLegalHoldOn()) {
				throw new AlreadyClosedException("Lock instance already released: " + this);
			}
		} catch (AwsServiceException | SdkClientException e) {
			throw new AlreadyClosedException("Lock object not found: " + this);
		}
	}

	private boolean isLegalHoldOn() {
		return s3Directory.getS3().getObjectLegalHold(b ->
				b.bucket(s3Directory.getBucket()).key(name)).legalHold().status().equals(ON);
	}

	private void putObjectLegalHold(ObjectLockLegalHoldStatus status) {
		s3Directory.getS3().putObjectLegalHold(b -> b.bucket(s3Directory.getBucket()).
				key(name).legalHold(l -> l.status(status)));
	}

	@Override
	public String toString() {
		return "S3LegalHoldLock[" + s3Directory.getBucket() + "/" + name + "]";
	}
}

public class TimeChartTouchable extends TimeChart {
    private float lastX1 = 0;
    private float lastX2 = 0;
    private float scaleFactor = 1;
    private double startFactor = 0; public TimeChartTouchable(Context context, AttributeSet attrs) {
        super(context, attrs);
    }

    @Override
    public boolean onTouchEvent(@NonNull MotionEvent event) {
        super.onTouchEvent(event);

        final int action = event.getActionMasked();
        switch (action) {
            case MotionEvent.ACTION_POINTER_DOWN:
                lastX1 = event.getX(0);
                lastX2 = event.getX(1);
                break;
            case MotionEvent.ACTION_UP:
                EventBus.getDefault().post(new ChartFocusEvent.Unfocused());
                break;
            case MotionEvent.ACTION_MOVE:
                if (event.getPointerCount() == 1) {
                    final long millis = (long) plot.getXinverse(0, event.getX());
                    EventBus.getDefault().post(findClosest(millis));
                } else if (event.getPointerCount() == 2) {
                    float x1 = event.getX(0);
                    float x2 = event.getX(1);
                    final float scaleDelta = (x2 - x1) / (lastX2 - lastX1);
                    scaleFactor *= scaleDelta;
                    scaleFactor = Math.max(1f, Math.min(scaleFactor, 10f)); final float b = (lastX2 * x1 - lastX1 * x2) / (lastX2 - lastX1);
                    startFactor -= b / getWidth() / scaleFactor;
                    startFactor = Math.max(0, Math.min(startFactor, 1 - 1 / scaleFactor));
                    lastX1 = x1;
                    lastX2 = x2;
                    this.invalidate();
                }
                break;
        }
        return true; }


    private final MLocation touchLocation = new MLocation();

    @NonNull
    private ChartFocusEvent findClosest(long millis) {
        if (trackData != null && !trackData.data.isEmpty()) {
            touchLocation.millis = millis;
            int closest_index = Collections.binarySearch(trackData.data, touchLocation);
            if (closest_index < 0) closest_index = -closest_index - 1;
            if (closest_index == trackData.data.size()) closest_index--;
            return new ChartFocusEvent.TrackFocused(trackData.data.get(closest_index), trackData.data);
        } else {
            return new ChartFocusEvent.Unfocused();
        }
    }

    @NonNull
    @Override
    public Bounds getBounds(@NonNull Bounds dataBounds, int axis) {
        final double range = dataBounds.x.range();
        dataBounds.x.min = dataBounds.x.min + range * startFactor;
        dataBounds.x.max = dataBounds.x.min + range / scaleFactor;
        return super.getBounds(dataBounds, axis);
    }

}

