public class MapsClient extends GDataServiceClient {
  private static final boolean DEBUG = false;
  public static final boolean LOG_COMMUNICATION = false;

  private static final String MAPS_BASE_FEED_URL =
      "http://maps.google.com/maps/feeds/";
  private static final String MAPS_MAP_FEED_PATH = "maps/default/full";
  private static final String MAPS_FEATURE_FEED_PATH_BEFORE_MAPID = "features/";
  private static final String MAPS_FEATURE_FEED_PATH_AFTER_MAPID = "/full";
  private static final String MAPS_VERSION_FEED_PATH_FORMAT =
      "%smaps/%s/versions/%s/full/%s";

  private static final String MAP_ENTRY_ID_BEFORE_USER_ID = "maps/feeds/maps/";
  private static final String MAP_ENTRY_ID_BETWEEN_USER_ID_AND_MAP_ID = "/";
  private static final String V2_ONLY_PARAM = "?v=2.0";

  public MapsClient(GDataClient dataClient,
      GDataParserFactory dataParserFactory) {
    super(dataClient, dataParserFactory);
  }

  @Override
  public String getServiceName() {
    return MapsConstants.SERVICE_NAME;
  }

  public static String buildMapUrl(String mapId) {
    return MapsConstants.MAPSHOP_BASE_URL + "?msa=0&msid=" + mapId;
  }

  public static String getMapsFeed() {
    if (DEBUG) {
      Log.d("Maps Client", "Requesting map feed:");
    }
    return MAPS_BASE_FEED_URL + MAPS_MAP_FEED_PATH + V2_ONLY_PARAM;
  }

  public static String getFeaturesFeed(String mapid) {
    StringBuilder feed = new StringBuilder();
    feed.append(MAPS_BASE_FEED_URL);
    feed.append(MAPS_FEATURE_FEED_PATH_BEFORE_MAPID);
    feed.append(mapid);
    feed.append(MAPS_FEATURE_FEED_PATH_AFTER_MAPID);
    feed.append(V2_ONLY_PARAM);
    return feed.toString();
  }

  public static String getMapIdFromMapEntryId(String entryId) {
    String userId = null;
    String mapId = null;
    if (DEBUG) {
      Log.d("Maps GData Client", "Getting mapid from entry id: " + entryId);
    }
    int userIdStart =
        entryId.indexOf(MAP_ENTRY_ID_BEFORE_USER_ID)
            + MAP_ENTRY_ID_BEFORE_USER_ID.length();
    int userIdEnd =
        entryId.indexOf(MAP_ENTRY_ID_BETWEEN_USER_ID_AND_MAP_ID, userIdStart);
    if (userIdStart >= 0 && userIdEnd < entryId.length()
        && userIdStart <= userIdEnd) {
      userId = entryId.substring(userIdStart, userIdEnd);
    }
    int mapIdStart =
        entryId.indexOf(MAP_ENTRY_ID_BETWEEN_USER_ID_AND_MAP_ID, userIdEnd)
            + MAP_ENTRY_ID_BETWEEN_USER_ID_AND_MAP_ID.length();
    if (mapIdStart >= 0 && mapIdStart < entryId.length()) {
      mapId = entryId.substring(mapIdStart);
    }
    if (userId == null) {
      userId = "";
    }
    if (mapId == null) {
      mapId = "";
    }
    if (DEBUG) {
      Log.d("Maps GData Client", "Got user id: " + userId);
      Log.d("Maps GData Client", "Got map id: " + mapId);
    }
    return userId + "." + mapId;
  }

  public static String getVersionFeed(String versionUserId,
      String versionClient, String currentVersion) {
    return String.format(Locale.US, MAPS_VERSION_FEED_PATH_FORMAT, MAPS_BASE_FEED_URL,
        versionUserId, versionClient, currentVersion);
  }

}

public class BadgedFourThreeImageView extends FourThreeImageView {

    private Drawable badge;
    private boolean drawBadge;
    private boolean badgeBoundsSet = false;
    private int badgeGravity;
    private int badgePadding;

    public BadgedFourThreeImageView(Context context, AttributeSet attrs) {
        super(context, attrs);
        if (Build.VERSION.SDK_INT>=Build.VERSION_CODES.LOLLIPOP){
            badge = new GifBadge(context);
            TypedArray a = context.obtainStyledAttributes(attrs, R.styleable.BadgedImageView, 0, 0);
            badgeGravity = a.getInt(R.styleable.BadgedImageView_badgeGravity, Gravity.END | Gravity
                    .BOTTOM);
            badgePadding = a.getDimensionPixelSize(R.styleable.BadgedImageView_badgePadding, 0);
            a.recycle();
        }
    }

    public BadgedFourThreeImageView(Context context, AttributeSet attrs, int defStyle){
        super(context,attrs,defStyle);
        if (Build.VERSION.SDK_INT>=Build.VERSION_CODES.LOLLIPOP){
            badge = new GifBadge(context);
            TypedArray a = context.obtainStyledAttributes(attrs, R.styleable.BadgedImageView, 0, 0);
            badgeGravity = a.getInt(R.styleable.BadgedImageView_badgeGravity, Gravity.END | Gravity
                    .BOTTOM);
            badgePadding = a.getDimensionPixelSize(R.styleable.BadgedImageView_badgePadding, 0);
            a.recycle();
        }

    }



    public void showBadge(boolean show) {
        drawBadge = show;
    }

    public void setBadgeColor(@ColorInt int color) {
        badge.setColorFilter(color, PorterDuff.Mode.SRC_IN);
    }

    @Override
    public void draw(Canvas canvas) {
        super.draw(canvas);
        if (Build.VERSION.SDK_INT>=Build.VERSION_CODES.LOLLIPOP){
            if (drawBadge) {
                if (!badgeBoundsSet) {
                    layoutBadge();
                }
                badge.draw(canvas);
            }
        }
    }

    @Override
    protected void onSizeChanged(int w, int h, int oldw, int oldh) {
        super.onSizeChanged(w, h, oldw, oldh);
        if (Build.VERSION.SDK_INT>=Build.VERSION_CODES.LOLLIPOP){
            layoutBadge();

        }
    }

    private void layoutBadge() {
        Rect badgeBounds = badge.getBounds();
        Gravity.apply(badgeGravity,
                badge.getIntrinsicWidth(),
                badge.getIntrinsicHeight(),
                new Rect(0, 0, getWidth(), getHeight()),
                badgePadding,
                badgePadding,
                badgeBounds);
        badge.setBounds(badgeBounds);
        badgeBoundsSet = true;
    }

    private static class GifBadge extends Drawable {

        private static final String GIF = "GIF";
        private static final int TEXT_SIZE = 12;    private static final int PADDING = 4;       private static final int CORNER_RADIUS = 2; private static final int BACKGROUND_COLOR = Color.WHITE;
        private static final String TYPEFACE = "sans-serif-black";
        private static final int TYPEFACE_STYLE = Typeface.NORMAL;
        private static Bitmap bitmap;
        private static int width;
        private static int height;
        private final Paint paint;

        GifBadge(Context context) {
            if (bitmap == null) {
                final DisplayMetrics dm = context.getResources().getDisplayMetrics();
                final float density = dm.density;
                final float scaledDensity = dm.scaledDensity;
                final TextPaint textPaint = new TextPaint(Paint.ANTI_ALIAS_FLAG | Paint
                        .SUBPIXEL_TEXT_FLAG);
                textPaint.setTypeface(Typeface.create(TYPEFACE, TYPEFACE_STYLE));
                textPaint.setTextSize(TEXT_SIZE * scaledDensity);

                final float padding = PADDING * density;
                final float cornerRadius = CORNER_RADIUS * density;
                final Rect textBounds = new Rect();
                textPaint.getTextBounds(GIF, 0, GIF.length(), textBounds);
                height = (int) (padding + textBounds.height() + padding);
                width = (int) (padding + textBounds.width() + padding);
                bitmap = Bitmap.createBitmap(width, height, Bitmap.Config.ARGB_8888);
                bitmap.setHasAlpha(true);
                final Canvas canvas = new Canvas(bitmap);
                final Paint backgroundPaint = new Paint(Paint.ANTI_ALIAS_FLAG);
                backgroundPaint.setColor(BACKGROUND_COLOR);
                if (Build.VERSION.SDK_INT>=Build.VERSION_CODES.LOLLIPOP){
                    canvas.drawRoundRect(0, 0, width, height, cornerRadius, cornerRadius,
                            backgroundPaint);
                }

                textPaint.setXfermode(new PorterDuffXfermode(PorterDuff.Mode.CLEAR));
                canvas.drawText(GIF, padding, height - padding, textPaint);
            }
            paint = new Paint();
        }

        @Override
        public int getIntrinsicWidth() {
            return width;
        }

        @Override
        public int getIntrinsicHeight() {
            return height;
        }

        @Override
        public void draw(Canvas canvas) {
            canvas.drawBitmap(bitmap, getBounds().left, getBounds().top, paint);
        }

        @Override
        public void setAlpha(int alpha) {
            }

        @Override
        public void setColorFilter(ColorFilter cf) {
            paint.setColorFilter(cf);
        }

        @Override
        public int getOpacity() {
            return 0;
        }
    }
}

