@EventBusSubscriber
public class BuilderEntityRenderForwarder extends ABuilderEntityBase{

	public EntityPlayer playerFollowing;
	public int idleTickCounter;
	private long[] lastTickRendered = new long[]{0L, 0L, 0L};
	private float[] lastPartialTickRendered = new float[]{0F, 0F, 0F};
	private boolean doneRenderingShaders;
	private static int framesShadersDetected;
	private static boolean shadersDetected;

	public BuilderEntityRenderForwarder(World world){
		super(world);
		setSize(0.05F, 0.05F);
	}

	public BuilderEntityRenderForwarder(EntityPlayer playerFollowing){
		this(playerFollowing.world);
		this.playerFollowing = playerFollowing;
		this.setPosition(playerFollowing.posX, playerFollowing.posY, playerFollowing.posZ);
	}

    @Override
    public void onEntityUpdate(){
    	super.onEntityUpdate();
    	if(playerFollowing != null && playerFollowing.world == this.world && !playerFollowing.isDead){
    		double playerVelocity = Math.sqrt(playerFollowing.motionX*playerFollowing.motionX + playerFollowing.motionY*playerFollowing.motionY + playerFollowing.motionZ*playerFollowing.motionZ);
    		Vec3d playerEyesVec = playerFollowing.getLookVec().scale(Math.max(1, playerVelocity/2));
    		setPosition(playerFollowing.posX + playerEyesVec.x, playerFollowing.posY + playerFollowing.eyeHeight + playerEyesVec.y, playerFollowing.posZ + playerEyesVec.z);
    	}else if(!world.isRemote){
			setDead();
		}else if(!loadedFromSavedNBT && loadFromSavedNBT){
			playerFollowing = world.getPlayerEntityByUUID(lastLoadedNBT.getUniqueId("playerFollowing"));
			loadedFromSavedNBT = true;
			lastLoadedNBT = null;
		}
    	idleTickCounter = 0;
    }

    @Override
    public boolean shouldRenderInPass(int pass){
        return true;
    }

    public boolean shouldRenderEntity(float partialTicks){
		int renderPass = MinecraftForgeClient.getRenderPass();
		if(renderPass == -1){
			renderPass = 2;
		}

		if(!shadersDetected){
			if(renderPass != 2 && lastTickRendered[renderPass] > lastTickRendered[2] && lastTickRendered[2] > 0){
				if(++framesShadersDetected == 1000){
					shadersDetected = true;
				}
			}else{
				framesShadersDetected = 0;
			}
		}

		if(!shadersDetected || doneRenderingShaders){
			lastTickRendered[renderPass] = world.getTotalWorldTime();
			lastPartialTickRendered[renderPass] = partialTicks;
		}else if(shadersDetected && !doneRenderingShaders){
			if(renderPass == 1){
				doneRenderingShaders = true;
			}
		}

		if(renderPass == 2){
			if(shadersDetected){
				doneRenderingShaders = false;
			}
			return lastTickRendered[0] != lastTickRendered[2] || lastPartialTickRendered[0] != lastPartialTickRendered[2];
		}else{
			return true;
		}
	}

	@Override
	public NBTTagCompound writeToNBT(NBTTagCompound tag){
		super.writeToNBT(tag);
		if(playerFollowing != null){
			tag.setUniqueId("playerFollowing", playerFollowing.getUniqueID());
		}
		return tag;
	}

	@SubscribeEvent
	public static void registerEntities(RegistryEvent.Register<EntityEntry> event){
		event.getRegistry().register(EntityEntryBuilder.create().entity(BuilderEntityRenderForwarder.class).id(new ResourceLocation(MasterLoader.MODID, "mts_entity_renderer"), 1).name("mts_entity_renderer").tracker(32*16, 5, false).build());
	}
}

public class RequestFutureTarget<T, R> implements FutureTarget<R>, Runnable {
    private static final Waiter DEFAULT_WAITER = new Waiter();

    private final Handler mainHandler;
    private final int width;
    private final int height;
    private final boolean assertBackgroundThread;
    private final Waiter waiter;

    private R resource;
    private Request request;
    private boolean isCancelled;
    private Exception exception;
    private boolean resultReceived;
    private boolean exceptionReceived;

    public RequestFutureTarget(Handler mainHandler, int width, int height) {
        this(mainHandler, width, height, true, DEFAULT_WAITER);
    }

    RequestFutureTarget(Handler mainHandler, int width, int height, boolean assertBackgroundThread, Waiter waiter) {
        this.mainHandler = mainHandler;
        this.width = width;
        this.height = height;
        this.assertBackgroundThread = assertBackgroundThread;
        this.waiter = waiter;
    }

    @Override
    public synchronized boolean cancel(boolean mayInterruptIfRunning) {
        if (isCancelled) {
            return true;
        }

        final boolean result = !isDone();
        if (result) {
            isCancelled = true;
            if (mayInterruptIfRunning) {
                clear();
            }
            waiter.notifyAll(this);
        }
        return result;
    }

    @Override
    public synchronized boolean isCancelled() {
        return isCancelled;
    }

    @Override
    public synchronized boolean isDone() {
        return isCancelled || resultReceived;
    }

    @Override
    public R get() throws InterruptedException, ExecutionException {
        try {
            return doGet(null);
        } catch (TimeoutException e) {
            throw new AssertionError(e);
        }
    }

    @Override
    public R get(long time, TimeUnit timeUnit) throws InterruptedException, ExecutionException, TimeoutException {
        return doGet(timeUnit.toMillis(time));
    }

    @Override
    public void getSize(SizeReadyCallback cb) {
        cb.onSizeReady(width, height);
    }

    @Override
    public void setRequest(Request request) {
        this.request = request;
    }

    @Override
    public Request getRequest() {
        return request;
    }

    @Override
    public void onLoadCleared(Drawable placeholder) {
        }

    @Override
    public void onLoadStarted(Drawable placeholder) {
        }

    @Override
    public synchronized void onLoadFailed(Exception e, Drawable errorDrawable) {
         exceptionReceived = true;
        this.exception = e;
        waiter.notifyAll(this);
    }

    @Override
    public synchronized void onResourceReady(R resource, GlideAnimation<? super R> glideAnimation) {
        resultReceived = true;
        this.resource = resource;
        waiter.notifyAll(this);
    }

    private synchronized R doGet(Long timeoutMillis) throws ExecutionException, InterruptedException, TimeoutException {
        if (assertBackgroundThread) {
            Util.assertBackgroundThread();
        }

        if (isCancelled) {
            throw new CancellationException();
        } else if (exceptionReceived) {
            throw new ExecutionException(exception);
        } else if (resultReceived) {
            return resource;
        }

        if (timeoutMillis == null) {
            waiter.waitForTimeout(this, 0);
        } else if (timeoutMillis > 0) {
            waiter.waitForTimeout(this, timeoutMillis);
        }

        if (Thread.interrupted()) {
            throw new InterruptedException();
        } else if (exceptionReceived) {
            throw new ExecutionException(exception);
        } else if (isCancelled) {
            throw new CancellationException();
        } else if (!resultReceived) {
            throw new TimeoutException();
        }

        return resource;
    }

    @Override
    public void run() {
        if (request != null) {
            request.clear();
            cancel(false ;
        }
    }

    @Override
    public void clear() {
        mainHandler.post(this);
    }

    @Override
    public void onStart() {
        }

    @Override
    public void onStop() {
        }

    @Override
    public void onDestroy() {
        }

    static class Waiter {

        public void waitForTimeout(Object toWaitOn, long timeoutMillis) throws InterruptedException {
            toWaitOn.wait(timeoutMillis);
        }

        public void notifyAll(Object toNotify) {
            toNotify.notifyAll();
        }
    }
}

