    @SuppressWarnings("deprecation")
	public CirclePageIndicator(Context context, AttributeSet attrs, int defStyle) {
        super(context, attrs, defStyle);
        if (isInEditMode()) return;

        final Resources res = getResources();
        final int defaultPageColor = res.getColor(R.color.default_circle_indicator_page_color);
        final int defaultFillColor = res.getColor(R.color.default_circle_indicator_fill_color);
        final int defaultOrientation = res.getInteger(R.integer.default_circle_indicator_orientation);
        final int defaultStrokeColor = res.getColor(R.color.default_circle_indicator_stroke_color);
        final float defaultStrokeWidth = res.getDimension(R.dimen.default_circle_indicator_stroke_width);
        final float defaultRadius = res.getDimension(R.dimen.default_circle_indicator_radius);
        final boolean defaultCentered = res.getBoolean(R.bool.default_circle_indicator_centered);
        final boolean defaultSnap = res.getBoolean(R.bool.default_circle_indicator_snap);

        TypedArray a = context.obtainStyledAttributes(attrs, R.styleable.CirclePageIndicator, defStyle, 0);

        mCentered = a.getBoolean(R.styleable.CirclePageIndicator_centered, defaultCentered);
        mOrientation = a.getInt(R.styleable.CirclePageIndicator_android_orientation, defaultOrientation);
        mPaintPageFill.setStyle(Style.FILL);
        mPaintPageFill.setColor(a.getColor(R.styleable.CirclePageIndicator_pageColor, defaultPageColor));
        mPaintStroke.setStyle(Style.STROKE);
        mPaintStroke.setColor(a.getColor(R.styleable.CirclePageIndicator_strokeColor, defaultStrokeColor));
        mPaintStroke.setStrokeWidth(a.getDimension(R.styleable.CirclePageIndicator_strokeWidth, defaultStrokeWidth));
        mPaintFill.setStyle(Style.FILL);
        mPaintFill.setColor(a.getColor(R.styleable.CirclePageIndicator_fillColor, defaultFillColor));
        mRadius = a.getDimension(R.styleable.CirclePageIndicator_radius, defaultRadius);
        mSnap = a.getBoolean(R.styleable.CirclePageIndicator_snap, defaultSnap);

        Drawable background = a.getDrawable(R.styleable.CirclePageIndicator_android_background);
        if (background != null) {
          setBackgroundDrawable(background);
        }

        a.recycle();

        final ViewConfiguration configuration = ViewConfiguration.get(context);
        mTouchSlop = ViewConfigurationCompat.getScaledPagingTouchSlop(configuration);
    }

    private int getItemCount() {
    	if (mViewPager == null || mViewPager.getAdapter() == null) {
    		return 0;
    	}

    	PagerAdapter adapter = mViewPager.getAdapter();
    	int count = adapter.getCount();

    	if (adapter instanceof LoopPagerAdapter) {
    		count = ((LoopPagerAdapter) adapter).getRealCount();
    	}

    	return count;
	}

    public void setCentered(boolean centered) {
        mCentered = centered;
        invalidate();
    }

    public boolean isCentered() {
        return mCentered;
    }

    public void setPageColor(int pageColor) {
        mPaintPageFill.setColor(pageColor);
        invalidate();
    }

    public int getPageColor() {
        return mPaintPageFill.getColor();
    }

    public void setFillColor(int fillColor) {
        mPaintFill.setColor(fillColor);
        invalidate();
    }

    public int getFillColor() {
        return mPaintFill.getColor();
    }

    public void setOrientation(int orientation) {
        switch (orientation) {
            case HORIZONTAL:
            case VERTICAL:
                mOrientation = orientation;
                requestLayout();
                break;

            default:
                throw new IllegalArgumentException("Orientation must be either HORIZONTAL or VERTICAL.");
        }
    }

    public int getOrientation() {
        return mOrientation;
    }

    public void setStrokeColor(int strokeColor) {
        mPaintStroke.setColor(strokeColor);
        invalidate();
    }

    public int getStrokeColor() {
        return mPaintStroke.getColor();
    }

    public void setStrokeWidth(float strokeWidth) {
        mPaintStroke.setStrokeWidth(strokeWidth);
        invalidate();
    }

    public float getStrokeWidth() {
        return mPaintStroke.getStrokeWidth();
    }

    public void setRadius(float radius) {
        mRadius = radius;
        invalidate();
    }

    public float getRadius() {
        return mRadius;
    }

    public void setSnap(boolean snap) {
        mSnap = snap;
        invalidate();
    }

    public boolean isSnap() {
        return mSnap;
    }

    @Override
    protected void onDraw(Canvas canvas) {
        super.onDraw(canvas);

        if (mViewPager == null) {
            return;
        }
        final int count = getItemCount();
        if (count == 0) {
            return;
        }

        if (mCurrentPage >= count) {
            setCurrentItem(count - 1);
            return;
        }

        int longSize;
        int longPaddingBefore;
        int longPaddingAfter;
        int shortPaddingBefore;
        if (mOrientation == HORIZONTAL) {
            longSize = getWidth();
            longPaddingBefore = getPaddingLeft();
            longPaddingAfter = getPaddingRight();
            shortPaddingBefore = getPaddingTop();
        } else {
            longSize = getHeight();
            longPaddingBefore = getPaddingTop();
            longPaddingAfter = getPaddingBottom();
            shortPaddingBefore = getPaddingLeft();
        }

        final float threeRadius = mRadius * 3;
        final float shortOffset = shortPaddingBefore + mRadius;
        float longOffset = longPaddingBefore + mRadius;
        if (mCentered) {
            longOffset += ((longSize - longPaddingBefore - longPaddingAfter) / 2.0f) - ((count * threeRadius) / 2.0f);
        }

        float dX;
        float dY;

        float pageFillRadius = mRadius;
        if (mPaintStroke.getStrokeWidth() > 0) {
            pageFillRadius -= mPaintStroke.getStrokeWidth() / 2.0f;
        }

        for (int iLoop = 0; iLoop < count; iLoop++) {
            float drawLong = longOffset + (iLoop * threeRadius);
            if (mOrientation == HORIZONTAL) {
                dX = drawLong;
                dY = shortOffset;
            } else {
                dX = shortOffset;
                dY = drawLong;
            }
            if (mPaintPageFill.getAlpha() > 0) {
                canvas.drawCircle(dX, dY, pageFillRadius, mPaintPageFill);
            }

            if (pageFillRadius != mRadius) {
                canvas.drawCircle(dX, dY, mRadius, mPaintStroke);
            }
        }

        float cx = (mSnap ? mSnapPage : mCurrentPage) * threeRadius;
        if (!mSnap) {
            cx += mPageOffset * threeRadius;
        }
        if (mOrientation == HORIZONTAL) {
            dX = longOffset + cx;
            dY = shortOffset;
        } else {
            dX = shortOffset;
            dY = longOffset + cx;
        }
        canvas.drawCircle(dX, dY, mRadius, mPaintFill);
    }

    public boolean onTouchEvent(MotionEvent ev) {
        if (super.onTouchEvent(ev)) {
            return true;
        }
        if ((mViewPager == null) || (getItemCount() == 0)) {
            return false;
        }

        final int action = ev.getAction() & MotionEventCompat.ACTION_MASK;
        switch (action) {
            case MotionEvent.ACTION_DOWN:
                mActivePointerId = MotionEventCompat.getPointerId(ev, 0);
                mLastMotionX = ev.getX();
                break;

            case MotionEvent.ACTION_MOVE: {
                final int activePointerIndex = MotionEventCompat.findPointerIndex(ev, mActivePointerId);
                final float x = MotionEventCompat.getX(ev, activePointerIndex);
                final float deltaX = x - mLastMotionX;

                if (!mIsDragging) {
                    if (Math.abs(deltaX) > mTouchSlop) {
                        mIsDragging = true;
                    }
                }

                if (mIsDragging) {
                    mLastMotionX = x;
                    if (mViewPager.isFakeDragging() || mViewPager.beginFakeDrag()) {
                        mViewPager.fakeDragBy(deltaX);
                    }
                }

                break;
            }

            case MotionEvent.ACTION_CANCEL:
            case MotionEvent.ACTION_UP:
                if (!mIsDragging) {
                    final int count = getItemCount();
                    final int width = getWidth();
                    final float halfWidth = width / 2f;
                    final float sixthWidth = width / 6f;

                    if ((mCurrentPage > 0) && (ev.getX() < halfWidth - sixthWidth)) {
                        if (action != MotionEvent.ACTION_CANCEL) {
                            mViewPager.setCurrentItem(mCurrentPage - 1);
                        }
                        return true;
                    } else if ((mCurrentPage < count - 1) && (ev.getX() > halfWidth + sixthWidth)) {
                        if (action != MotionEvent.ACTION_CANCEL) {
                            mViewPager.setCurrentItem(mCurrentPage + 1);
                        }
                        return true;
                    }
                }

                mIsDragging = false;
                mActivePointerId = INVALID_POINTER;
                if (mViewPager.isFakeDragging()) mViewPager.endFakeDrag();
                break;

            case MotionEventCompat.ACTION_POINTER_DOWN: {
                final int index = MotionEventCompat.getActionIndex(ev);
                mLastMotionX = MotionEventCompat.getX(ev, index);
                mActivePointerId = MotionEventCompat.getPointerId(ev, index);
                break;
            }

            case MotionEventCompat.ACTION_POINTER_UP:
                final int pointerIndex = MotionEventCompat.getActionIndex(ev);
                final int pointerId = MotionEventCompat.getPointerId(ev, pointerIndex);
                if (pointerId == mActivePointerId) {
                    final int newPointerIndex = pointerIndex == 0 ? 1 : 0;
                    mActivePointerId = MotionEventCompat.getPointerId(ev, newPointerIndex);
                }
                mLastMotionX = MotionEventCompat.getX(ev, MotionEventCompat.findPointerIndex(ev, mActivePointerId));
                break;
        }

        return true;
    }

    @Override
    public void setViewPager(ViewPager view) {
        if (mViewPager == view) {
            return;
        }
        if (mViewPager != null) {
            mViewPager.setOnPageChangeListener(null);
        }
        if (view.getAdapter() == null) {
            throw new IllegalStateException("ViewPager does not have adapter instance.");
        }
        mViewPager = view;
        mViewPager.setOnPageChangeListener(this);
        invalidate();
    }

    @Override
    public void setViewPager(ViewPager view, int initialPosition) {
        setViewPager(view);
        setCurrentItem(initialPosition);
    }

    @Override
    public void setCurrentItem(int item) {
        if (mViewPager == null) {
            throw new IllegalStateException("ViewPager has not been bound.");
        }
        mViewPager.setCurrentItem(item);
        mCurrentPage = item;
        invalidate();
    }

    @Override
    public void notifyDataSetChanged() {
        invalidate();
    }

    @Override
    public void onPageScrollStateChanged(int state) {
        mScrollState = state;

        if (mListener != null) {
            mListener.onPageScrollStateChanged(state);
        }
    }

    @Override
    public void onPageScrolled(int position, float positionOffset, int positionOffsetPixels) {
        mCurrentPage = position % getItemCount();
        mPageOffset = positionOffset;
        invalidate();

        if (mListener != null) {
            mListener.onPageScrolled(position, positionOffset, positionOffsetPixels);
        }
    }

    @Override
    public void onPageSelected(int position) {
        if (mSnap || mScrollState == ViewPager.SCROLL_STATE_IDLE) {
            mCurrentPage = position % getItemCount();
            mSnapPage = position % getItemCount();
            invalidate();
        }

        if (mListener != null) {
            mListener.onPageSelected(position);
        }
    }

    @Override
    public void setOnPageChangeListener(ViewPager.OnPageChangeListener listener) {
        mListener = listener;
    }

    @Override
    protected void onMeasure(int widthMeasureSpec, int heightMeasureSpec) {
        if (mOrientation == HORIZONTAL) {
            setMeasuredDimension(measureLong(widthMeasureSpec), measureShort(heightMeasureSpec));
        } else {
            setMeasuredDimension(measureShort(widthMeasureSpec), measureLong(heightMeasureSpec));
        }
    }

    private int measureLong(int measureSpec) {
        int result;
        int specMode = MeasureSpec.getMode(measureSpec);
        int specSize = MeasureSpec.getSize(measureSpec);

        if ((specMode == MeasureSpec.EXACTLY) || (mViewPager == null)) {
            result = specSize;
        } else {
            final int count = getItemCount();
            result = (int)(getPaddingLeft() + getPaddingRight()
                    + (count * 2 * mRadius) + (count - 1) * mRadius + 1);
            if (specMode == MeasureSpec.AT_MOST) {
                result = Math.min(result, specSize);
            }
        }
        return result;
    }

    private int measureShort(int measureSpec) {
        int result;
        int specMode = MeasureSpec.getMode(measureSpec);
        int specSize = MeasureSpec.getSize(measureSpec);

        if (specMode == MeasureSpec.EXACTLY) {
            result = specSize;
        } else {
            result = (int)(2 * mRadius + getPaddingTop() + getPaddingBottom() + 1);
            if (specMode == MeasureSpec.AT_MOST) {
                result = Math.min(result, specSize);
            }
        }
        return result;
    }

    @Override
    public void onRestoreInstanceState(Parcelable state) {
        SavedState savedState = (SavedState)state;
        super.onRestoreInstanceState(savedState.getSuperState());
        mCurrentPage = savedState.currentPage;
        mSnapPage = savedState.currentPage;
        requestLayout();
    }

    @Override
    public Parcelable onSaveInstanceState() {
        Parcelable superState = super.onSaveInstanceState();
        SavedState savedState = new SavedState(superState);
        savedState.currentPage = mCurrentPage;
        return savedState;
    }

    static class SavedState extends BaseSavedState {
        int currentPage;

        public SavedState(Parcelable superState) {
            super(superState);
        }

        private SavedState(Parcel in) {
            super(in);
            currentPage = in.readInt();
        }

        @Override
        public void writeToParcel(Parcel dest, int flags) {
            super.writeToParcel(dest, flags);
            dest.writeInt(currentPage);
        }

        public static final Creator<SavedState> CREATOR = new Creator<SavedState>() {
            @Override
            public SavedState createFromParcel(Parcel in) {
                return new SavedState(in);
            }

            @Override
            public SavedState[] newArray(int size) {
                return new SavedState[size];
            }
        };
    }
}

public class SpongeImplLoader {

    private static final Logger LOGGER = LogManagerCompat.getLogger();
    private final List<String> adapterCandidates = new ArrayList<>();
    private String customCandidate;

    private static final String SEARCH_PACKAGE = "com.sk89q.worldedit.sponge.adapter.impl";
    private static final String SEARCH_PACKAGE_DOT = SEARCH_PACKAGE + ".";
    private static final String SEARCH_PATH = SEARCH_PACKAGE.replace(".", "/");
    private static final String CLASS_SUFFIX = ".class";

    private static final String LOAD_ERROR_MESSAGE =
            "\n**********************************************\n" +
                    "** This WorldEdit version does not support your version of Sponge.\n" +
                    "** WorldEdit will not function! \n" +
                    "** \n" +
                    "** Please ensure you are running the latest version\n" +
                    "**********************************************\n";

    public SpongeImplLoader() {
        addDefaults();
    }

    private void addDefaults() {
        String className = System.getProperty("worldedit.sponge.adapter");
        if (className != null) {
            customCandidate = className;
            adapterCandidates.add(className);
            LOGGER.info("-Dworldedit.sponge.adapter used to add " + className + " to the list of available Sponge adapters");
        }
    }

    public void addFromJar(File file) throws IOException {
        Closer closer = Closer.create();
        JarFile jar = closer.register(new JarFile(file));
        try {
            Enumeration<JarEntry> entries = jar.entries();
            while (entries.hasMoreElements()) {
                JarEntry jarEntry = (JarEntry) entries.nextElement();

                String className = jarEntry.getName().replaceAll("[/\\\\]+", ".");

                if (!className.startsWith(SEARCH_PACKAGE_DOT) || jarEntry.isDirectory() || className.contains("$")) continue;

                int beginIndex = 0;
                int endIndex = className.length() - CLASS_SUFFIX.length();
                className = className.substring(beginIndex, endIndex);
                adapterCandidates.add(className);
            }
        } finally {
            closer.close();
        }
    }

    public void addFromPath(ClassLoader classLoader) throws IOException {
        Enumeration<URL> resources = classLoader.getResources(SEARCH_PATH);
        while (resources.hasMoreElements()) {
            File file = new File(resources.nextElement().getFile());
            addFromPath(file);
        }
    }

    private void addFromPath(File file) {
        String resource = SEARCH_PACKAGE_DOT + file.getName();
        if (file.isDirectory()) {
            File[] files = file.listFiles();
            if (files != null) {
                for (File child : files) {
                    addFromPath(child);
                }
            }
        } else if (resource.endsWith(CLASS_SUFFIX)) {
            int beginIndex = 0;
            int endIndex = resource.length() - CLASS_SUFFIX.length();
            String className = resource.substring(beginIndex, endIndex);
            if (!className.contains("$")) {
                adapterCandidates.add(className);
            }
        }
    }

    public SpongeImplAdapter loadAdapter() throws AdapterLoadException {
        List<SpongeImplAdapter> suitableAdapters = Lists.newArrayList();
        for (String className : adapterCandidates) {
            try {
                Class<?> cls = Class.forName(className);
                if (SpongeImplAdapter.class.isAssignableFrom(cls)) {
                    suitableAdapters.add((SpongeImplAdapter) cls.newInstance());
                } else {
                    LOGGER.warn("Failed to load the Sponge adapter class '" + className +
                            "' because it does not implement " + SpongeImplAdapter.class.getCanonicalName());
                }
            } catch (ClassNotFoundException e) {
                LOGGER.warn("Failed to load the Sponge adapter class '" + className +
                        "' that is not supposed to be missing", e);
            } catch (IllegalAccessException e) {
                LOGGER.warn("Failed to load the Sponge adapter class '" + className +
                        "' that is not supposed to be raising this error", e);
            } catch (Throwable e) {
                if (className.equals(customCandidate)) {
                    LOGGER.warn("Failed to load the Sponge adapter class '" + className + "'", e);
                }
            }
        }

        if (suitableAdapters.isEmpty()) {
            throw new AdapterLoadException(LOAD_ERROR_MESSAGE);
        } else {
            if (suitableAdapters.size() == 1) {
                return suitableAdapters.get(0);
            } else {
                return suitableAdapters.stream().sorted((o1, o2) -> {
                    if (o1.isBest() && !o2.isBest()) {
                        return -1;
                    } else if (!o1.isBest() && o2.isBest()) {
                        return 1;
                    }
                    return 0;
                }).findFirst().orElse(suitableAdapters.get(0));
            }
        }
    }
}

public class NonProjectiveStatisticsWriter implements ParseConfigurationProcessor {
  private static final CSVFormatter CSV = new CSVFormatter();

  private final Writer writer;
  private final Writer writer2;
  private final Writer writer3;

  @SuppressWarnings("unused")
  private final String sessionId;
  private int totalCount = 0;
  private int nonProjectiveCount = 0;
  private int nonProjectiveNodeCount = 0;
  private int nonProjectiveEdgeCount = 0;
  private int totalNodeCount = 0;
  private int illNestedCount = 0;
  private int[] gapDegreeCounts = new int[10];
  private int[] edgeDegreeCounts = new int[10];

  public NonProjectiveStatisticsWriter(File outDir, String sessionId) throws IOException {
    this.sessionId = sessionId;
    File csvFile = new File(outDir, TalismaneSession.get(sessionId).getBaseName() + "_nproj.csv");
    csvFile.delete();
    csvFile.createNewFile();
    writer = new BufferedWriter(new OutputStreamWriter(new FileOutputStream(csvFile, false), "UTF8"));

    File csvFile2 = new File(outDir, TalismaneSession.get(sessionId).getBaseName() + "_nprojnodes.csv");
    csvFile2.delete();
    csvFile2.createNewFile();
    writer2 = new BufferedWriter(new OutputStreamWriter(new FileOutputStream(csvFile2, false), "UTF8"));

    File csvFile3 = new File(outDir, TalismaneSession.get(sessionId).getBaseName() + "_nprojedges.csv");
    csvFile3.delete();
    csvFile3.createNewFile();
    writer3 = new BufferedWriter(new OutputStreamWriter(new FileOutputStream(csvFile3, false), "UTF8"));

    writer.write(CSV.format("Sentence"));
    writer.write(CSV.format("Gap degree"));
    writer.write(CSV.format("Max gap node"));
    writer.write(CSV.format("Edge degree"));
    writer.write(CSV.format("Max edge node"));
    writer.write(CSV.format("Well nested?"));
    writer.write(CSV.format("Ill nested nodes"));
    writer.write("\n");
    writer.flush();

    writer2.write(CSV.format("Sentence"));
    writer2.write(CSV.format("Head"));
    writer2.write(CSV.format("Non-proj node"));
    writer2.write(CSV.format("Gap degree"));
    writer2.write(CSV.format("Edge degree"));
    writer2.write(CSV.format("Gap heads"));
    writer2.write("\n");
    writer2.flush();

    writer3.write(CSV.format("Sentence"));
    writer3.write(CSV.format("Non-proj edge"));
    writer3.write("\n");
    writer3.flush();
  }

  @Override
  public void onNextParseConfiguration(ParseConfiguration parseConfiguration) throws IOException {
    ParseTree parseTree = new ParseTree(parseConfiguration, false);
    if (!parseTree.isProjective()) {
      writer.write(CSV.format(parseConfiguration.getSentence().getText().toString()));
      writer.write(CSV.format(parseTree.getGapDegree().getRight()));
      writer.write(CSV.format(parseTree.getGapDegree().getLeft().toString()));
      int gapDegree = parseTree.getGapDegree().getRight();
      if (gapDegree > 9)
        gapDegree = 9;
      gapDegreeCounts[gapDegree]++;
      writer.write(CSV.format(parseTree.getEdgeDegree().getRight()));
      writer.write(CSV.format(parseTree.getEdgeDegree().getLeft().toString()));
      int edgeDegree = parseTree.getEdgeDegree().getRight();
      if (edgeDegree > 9)
        edgeDegree = 9;
      edgeDegreeCounts[edgeDegree]++;
      writer.write(CSV.format(parseTree.isWellNested()));
      for (Pair<ParseTreeNode, ParseTreeNode> illNestedNodes : parseTree.getIllNestedNodes()) {
        writer.write(CSV.format(illNestedNodes.getLeft().toString()));
        writer.write(CSV.format(illNestedNodes.getRight().toString()));
      }
      if (!parseTree.isWellNested())
        illNestedCount++;
      writer.write("\n");
      writer.flush();
      nonProjectiveCount++;

      for (ParseTreeNode nonProjNode : parseTree.getNonProjectiveNodes()) {
        writer2.write(CSV.format(parseConfiguration.getSentence().getText().toString()));
        writer2.write(CSV.format(nonProjNode.getPosTaggedToken().toString()));
        writer2.write(CSV.format(nonProjNode.toString()));
        writer2.write(CSV.format(nonProjNode.getGapCount()));
        writer2.write(CSV.format(nonProjNode.getEdgeCount()));

        for (DependencyArc arc : nonProjNode.getGapHeads()) {
          writer2.write(CSV.format(arc.toString()));
        }

        writer2.write("\n");
        writer2.flush();
        nonProjectiveNodeCount++;
      }

      for (DependencyArc arc : parseTree.getNonProjectiveEdges()) {
        writer3.write(CSV.format(parseConfiguration.getSentence().getText().toString()));
        writer3.write(CSV.format(arc.toString()));
        writer3.write("\n");
        writer3.flush();
        nonProjectiveEdgeCount++;
      }

    } else {
      gapDegreeCounts[0]++;
      edgeDegreeCounts[0]++;
    }
    totalNodeCount += parseConfiguration.getPosTagSequence().size() - 1;
    totalCount++;
  }

  @Override
  public void onCompleteParse() throws IOException {
    writer.write("ALL\n");
    writer.write(CSV.format("total") + CSV.format(totalCount) + CSV.format(100.0) + "\n");
    int projectiveCount = totalCount - nonProjectiveCount;
    writer.write(CSV.format("projective") + CSV.format(projectiveCount) + CSV.format(((double) projectiveCount / (double) totalCount) * 100.0) + "\n");
    writer
        .write(CSV.format("non-projective") + CSV.format(nonProjectiveCount) + CSV.format(((double) nonProjectiveCount / (double) totalCount) * 100.0) + "\n");
    int wellNestedCount = totalCount - illNestedCount;
    writer.write(CSV.format("wellNested") + CSV.format(wellNestedCount) + CSV.format((double) wellNestedCount / (double) totalCount * 100.0) + "\n");
    writer.write(CSV.format("ill-nested") + CSV.format(illNestedCount) + CSV.format((double) illNestedCount / (double) totalCount * 100.0) + "\n");

    for (int i = 0; i < 10; i++) {
      int gapDegree = gapDegreeCounts[i];
      writer.write(CSV.format("gapDegree" + i) + CSV.format(gapDegree) + CSV.format((double) gapDegree / (double) totalCount * 100.0) + "\n");
    }

    for (int i = 0; i < 10; i++) {
      int edgeDegree = edgeDegreeCounts[i];
      writer.write(CSV.format("edgeDegree" + i) + CSV.format(edgeDegree) + CSV.format((double) edgeDegree / (double) totalCount * 100.0) + "\n");
    }

    writer.write("NON PROJECTIVE\n");
    writer.write(CSV.format("total") + CSV.format(nonProjectiveCount) + CSV.format(100.0) + "\n");

    int nprojWellNested = nonProjectiveCount - illNestedCount;
    writer.write(CSV.format("wellNested") + CSV.format(nprojWellNested) + CSV.format((double) nprojWellNested / (double) nonProjectiveCount * 100.0) + "\n");

    writer.write("NODES\n");
    writer.write(CSV.format("total") + CSV.format(totalNodeCount) + CSV.format(100.0) + "\n");
    int projectiveNodeCount = totalNodeCount - nonProjectiveNodeCount;
    writer.write(
        CSV.format("projective") + CSV.format(projectiveNodeCount) + CSV.format(((double) projectiveNodeCount / (double) totalNodeCount) * 100.0) + "\n");
    writer.write(CSV.format("non-projective") + CSV.format(nonProjectiveNodeCount)
        + CSV.format(((double) nonProjectiveNodeCount / (double) totalNodeCount) * 100.0) + "\n");

    int projectiveEdgeCount = totalNodeCount - nonProjectiveEdgeCount;
    writer.write("EDGES\n");
    writer.write(CSV.format("total") + CSV.format(totalNodeCount) + CSV.format(100.0) + "\n");
    writer.write(
        CSV.format("projective") + CSV.format(projectiveEdgeCount) + CSV.format(((double) projectiveEdgeCount / (double) totalNodeCount) * 100.0) + "\n");
    writer.write(CSV.format("non-projective") + CSV.format(nonProjectiveEdgeCount)
        + CSV.format(((double) nonProjectiveEdgeCount / (double) totalNodeCount) * 100.0) + "\n");

    writer.flush();
    writer.close();
  }

  @Override
  public void close() throws IOException {
    this.writer.close();
    this.writer2.close();
  }

}

