public final class TopKPatternsOutputConverter<A extends Comparable<? super A>> implements
    OutputCollector<Integer,FrequentPatternMaxHeap> {

  private final OutputCollector<A,List<Pair<List<A>,Long>>> collector;

  private final Map<Integer,A> reverseMapping;

  public TopKPatternsOutputConverter(OutputCollector<A,List<Pair<List<A>,Long>>> collector,
                                     Map<Integer,A> reverseMapping) {
    this.collector = collector;
    this.reverseMapping = reverseMapping;
  }

  @Override
  public void collect(Integer key, FrequentPatternMaxHeap value) throws IOException {
    List<Pair<List<A>,Long>> perAttributePatterns = Lists.newArrayList();
    PriorityQueue<Pattern> t = value.getHeap();
    while (!t.isEmpty()) {
      Pattern itemSet = t.poll();
      List<A> frequentPattern = Lists.newArrayList();
      for (int j = 0; j < itemSet.length(); j++) {
        frequentPattern.add(reverseMapping.get(itemSet.getPattern()[j]));
      }
      Collections.sort(frequentPattern);

      Pair<List<A>,Long> returnItemSet = new Pair<List<A>,Long>(frequentPattern, itemSet.support());
      perAttributePatterns.add(returnItemSet);
    }
    Collections.reverse(perAttributePatterns);

    collector.collect(reverseMapping.get(key), perAttributePatterns);
  }
}

public class RestEndpointHolderData {

  private String name;

  private String title;

  private List<RestEndpointData> endpoints;

  public RestEndpointHolderData(String name, String title) throws IllegalArgumentException {
    if (!DocData.isValidName(name)) {
      throw new IllegalArgumentException("Name must not be null and must be alphanumeric.");
    }
    if (title == null) {
      throw new IllegalArgumentException("Title must not be null.");
    }
    this.name = name;
    this.title = title;
  }

  public void addEndPoint(RestEndpointData endpoint) {
    if (endpoint != null) {
      if (endpoints == null) {
        endpoints = new Vector<>();
      }
      endpoints.add(endpoint);
      Collections.sort(endpoints);
    }
  }

  @Override
  public String toString() {
    return "HOLD:" + name + ":" + endpoints;
  }

  public RestEndpointHolderData duplicate() {
    return new RestEndpointHolderData(name, title);
  }

  @Override
  protected Object clone() {
    return duplicate();
  }

  public String getName() {
    return name;
  }

  public String getTitle() {
    return title;
  }

  public List<RestEndpointData> getEndpoints() {
    if (endpoints == null) {
      endpoints = new Vector<>(0);
    }
    return endpoints;
  }

}

public class UrlFetchException extends RuntimeException {

  private final HTTPRequest req;
  private final HTTPResponse rsp;

  public UrlFetchException(String message, HTTPRequest req, HTTPResponse rsp) {
    super(message);
    this.req = checkNotNull(req, "req");
    this.rsp = checkNotNull(rsp, "rsp");
  }

  @Override
  public String getMessage() {
    StringBuilder res =
        new StringBuilder(2048 + rsp.getContent().length)
            .append(
                String.format(
                    "%s: %s (HTTP Status %d)\nX-Fetch-URL: %s\nX-Final-URL: %s\n",
                    getClass().getSimpleName(),
                    super.getMessage(),
                    rsp.getResponseCode(),
                    req.getURL().toString(),
                    rsp.getFinalUrl()));
    for (HTTPHeader header : rsp.getHeadersUncombined()) {
      res.append(header.getName());
      res.append(": ");
      res.append(header.getValue());
      res.append('\n');
    }
    res.append(">>>\n");
    res.append(new String(rsp.getContent(), UTF_8));
    res.append("\n<<<");
    return res.toString();
  }
}

@SpringBootApplication
@EnableConfigurationProperties(BomrProperties.class)
public class BomrApplication {

	public static void main(String[] args) {
		ConfigurableApplicationContext context = new SpringApplicationBuilder(BomrApplication.class)
				.properties("logging.level.root=warn", "spring.config.location=file:.bomr/", "spring.config.name=bomr")
				.bannerMode(Mode.OFF).run(args);
		Commands commands = context.getBean(Commands.class);
		if (args.length == 0) {
			displayUsage();
			System.err.println();
			displayCommandList(commands);
			System.exit(-1);
		}
		Command command = commands.get(args[0]);
		if (command == null) {
			System.err.println("bomr: '" + args[0] + "' is not a bomr command");
			System.err.println();
			displayCommandList(commands);
			System.exit(-1);
		}
		else {
			command.invoke(Arrays.copyOfRange(args, 1, args.length));
		}
	}

	private static void displayUsage() {
		System.err.println("Usage: bomr <command> [<args>]");
	}

	private static void displayCommandList(Commands commands) {
		System.err.println("Commands:");
		System.err.println();
		System.err.println(commands.describe());
		System.err.println();
	}

}

