final class GenericMultiValuePropertyDescriptor<V, C extends Collection<V>> extends AbstractMultiValueProperty<V> {


    private final Set<PropertyConstraint<? super C>> listValidators;
    private final ValueParser<V> parser;
    private final Class<V> type;


    GenericMultiValuePropertyDescriptor(String name, String description, float uiOrder,
                                        Collection<V> defaultValue,
                                        Set<PropertyConstraint<? super C>> listValidators,
                                        ValueParser<V> parser,
                                        char delim,
                                        Class<V> type) {
        super(name, description, (List<V>) defaultValue, uiOrder, delim, false);
        this.listValidators = listValidators;
        this.parser = parser;
        this.type = type;

        String dftValueError = errorFor(new ArrayList<>(defaultValue));
        if (dftValueError != null) {
            throw new IllegalArgumentException(dftValueError);
        }
    }


    @SuppressWarnings("unchecked")
    @Override
    public String errorFor(List<V> values) {
        for (PropertyConstraint<? super C> lv : listValidators) {
            String error = lv.validate((C) values);
            if (error != null) {
                return error;
            }
        }
        return null;
    }


    @Override
    protected V createFrom(String toParse) {
        return parser.valueOf(toParse);
    }


    @Override
    public Class<V> type() {
        return type;
    }
}

public class RTScanner {
    private static final Logger LOGGER = LoggerFactory.getLogger(RTScanner.class);
    private static final int DURATION_IN_TEMP_BLACKLIST = 600; private final List<Long> blackListedRepository;
    private final List<Long> whiteListedRepository;
    private final Map<Long,Date> tempBlackList;


    private final InspectBuilds inspectBuilds;
    private final InspectJobs inspectJobs;
    private PipelineRunner pipelineRunner;

    private boolean running;
    private String runId;
    private List<SerializerEngine> engines;
    private BlacklistedSerializer blacklistedSerializer;
    private EndProcessNotifier endProcessNotifier;
    private TimedSummaryNotifier summaryNotifier;

    public RTScanner(String runId) {
        this.blackListedRepository = new ArrayList<>();
        this.whiteListedRepository = new ArrayList<>();
        this.tempBlackList = new HashMap<>();
        this.pipelineRunner = new DockerPipelineRunner(this);
        this.pipelineRunner.initRunner();
        this.inspectBuilds = new InspectBuilds(this);
        this.inspectJobs = new InspectJobs(this);
        this.runId = runId;

        this.blacklistedSerializer = new BlacklistedSerializer(this);
    }

    public RTScanner(String runId, List<SerializerEngine> theengines) {
        this(runId);
        this.engines = theengines;
        this.blacklistedSerializer = new BlacklistedSerializer(this, theengines.toArray(new SerializerEngine[0]));
    }

    public RTScanner(String runId, List<SerializerEngine> engines, PipelineRunner runner) {
        this(runId, engines);
        this.pipelineRunner = runner;
    }

    public void setEndProcessNotifier(EndProcessNotifier endProcessNotifier) {
        this.endProcessNotifier = endProcessNotifier;
    }

    public void setSummaryNotifier(TimedSummaryNotifier summaryNotifier) {
        LOGGER.info("Pipeline configured to send summary emails");
        this.summaryNotifier = summaryNotifier;
    }

    public List<SerializerEngine> getEngines() {
        return engines;
    }

    public String getRunId() {
        return runId;
    }

    public InspectBuilds getInspectBuilds() {
        return inspectBuilds;
    }

    public void initWhiteListedRepository(File whiteListFile) {
        LOGGER.info("Init whitelist repository...");
        try {
            List<String> lines = Files.readAllLines(whiteListFile.toPath());
            for (String repoId : lines) {
                if (!repoId.trim().isEmpty()) {
                    this.whiteListedRepository.add(Long.parseLong(repoId));
                }
            }
        } catch (IOException e) {
            LOGGER.error("Error while initializing whitelist", e);
        }
        LOGGER.info("Whitelist initialized with: "+this.whiteListedRepository.size()+" entries");
    }

    public void initBlackListedRepository(File blackListFile) {
        LOGGER.info("Init blacklist repository...");
        try {
            List<String> lines = Files.readAllLines(blackListFile.toPath());
            for (String repoId : lines) {
                if (!repoId.trim().isEmpty()) {
                    addInBlacklistRepository(RepairnatorConfig.getInstance().getJTravis().repository().fromSlug(repoId).get(), BlacklistedSerializer.Reason.CONFIGURED_AS_BLACKLISTED, "blacklisted from "+blackListFile.getPath());
                }
            }
        } catch (IOException e) {
            LOGGER.error("Error while initializing blacklist", e);
        }
        LOGGER.info("Blacklist initialized with: "+this.blackListedRepository.size()+" entries");
    }

    public void launch() {
        if (!this.running) {
            LOGGER.info("Start running RTScanner...");
            this.pipelineRunner.initRunner();
            new Thread(this.inspectBuilds).start();
            Thread rtScannerThread = Thread.currentThread();
            Thread thread = new Thread(this.inspectJobs);

            thread.setUncaughtExceptionHandler(new Thread.UncaughtExceptionHandler() {
                @Override
                public void uncaughtException(Thread t, Throwable e) {
                    rtScannerThread.interrupt();
                }
            });
            thread.start();
            if(summaryNotifier != null) {
                LOGGER.info("Starting summary notifier");
                new Thread(this.summaryNotifier).start();
            }
            this.running = true;

            if (RepairnatorConfig.getInstance().getDuration() != null) {
                InspectProcessDuration inspectProcessDuration;
                if (this.endProcessNotifier != null) {
                    inspectProcessDuration = new InspectProcessDuration(this.inspectBuilds, this.inspectJobs, this.endProcessNotifier);
                } else {
                    inspectProcessDuration = new InspectProcessDuration(this.inspectBuilds, this.inspectJobs);
                }
                new Thread(inspectProcessDuration).start();
            }
            if(RepairnatorConfig.getInstance().getNumberOfPRs() != 0) {
                RepairnatorConfig conf = RepairnatorConfig.getInstance();
                LOGGER.info("RTScanner configured to stop after " + conf.getNumberOfPRs() + " patched builds.");
                PullRequestCounter PRCounter;
                if(this.endProcessNotifier != null) {
                    PRCounter = new PullRequestCounter(
                            conf.getNumberOfPRs(),
                            conf.getMongodbHost(),
                            conf.getMongodbName(),
                            new GregorianCalendar().getTime(),
                            this.inspectBuilds,
                            this.inspectJobs,
                            this.endProcessNotifier);
                } else {
                    PRCounter = new PullRequestCounter(
                            conf.getNumberOfPRs(),
                            conf.getMongodbHost(),
                            conf.getMongodbName(),
                            new GregorianCalendar().getTime(),
                            this.inspectBuilds,
                            this.inspectJobs);
                }
                new Thread(PRCounter).start();
            }
        }
    }

    private void addInBlacklistRepository(Repository repository, BlacklistedSerializer.Reason reason, String comment) {
        LOGGER.info("Repository "+repository.getSlug()+" is blacklisted, "+reason.name()+" "+comment+"(total bl: "+blackListedRepository.size()+")" );

        this.blacklistedSerializer.addBlackListedRepo(repository, reason, comment);
        this.blackListedRepository.add(repository.getId());
    }

    private void addInWhitelistRepository(Repository repository) {
        LOGGER.info("Repository "+repository.getSlug()+" (id: "+repository.getId()+") is whitelisted. Total ("+whiteListedRepository.size()+")");
        this.whiteListedRepository.add(repository.getId());
    }

    private void addInTempBlackList(Repository repository, String comment) {
        Date expirationDate = new Date(new Date().toInstant().plusSeconds(DURATION_IN_TEMP_BLACKLIST).toEpochMilli());
        LOGGER.info("Repository "+repository.getSlug()+" (id: "+repository.getId()+") is temporary blacklisted (expiration date: "+expirationDate.toString()+"). Reason: "+comment);
        this.tempBlackList.put(repository.getId(), expirationDate);
    }

    public void submitIfBuildIsInteresting(Build build) {
        this.inspectBuilds.submitIfBuildIsInteresting(build);
    }

    public boolean isRepositoryInteresting(long repositoryId) {
        if (this.blackListedRepository.contains(repositoryId)) {
            return false;
        }

        if (this.whiteListedRepository.contains(repositoryId)) {
            return true;
        }

        if (this.tempBlackList.containsKey(repositoryId)) {
            if (this.tempBlackList.get(repositoryId).after(new Date())) {
                return false;
            } else {
                this.tempBlackList.remove(repositoryId);
            }
        }

        JTravis jTravis = RepairnatorConfig.getInstance().getJTravis();
        Optional<Repository> repositoryOptional = jTravis.repository().fromId(repositoryId);
        if (repositoryOptional.isPresent()) {
            Repository repository = repositoryOptional.get();

            Optional<Build> optionalBuild = jTravis.build().lastBuildFromMasterWithState(repository, StateType.PASSED);
            if (!optionalBuild.isPresent()) {
                this.addInTempBlackList(repository, "No successful build found.");
                return false;
            } else {
                Build masterBuild = optionalBuild.get();
                if (masterBuild.getLanguage() == null || !masterBuild.getLanguage().equals("java")) {
                    this.addInBlacklistRepository(repository, BlacklistedSerializer.Reason.OTHER_LANGUAGE, masterBuild.getLanguage());
                    return false;
                }

                Optional<BuildTool> optionalBuildTool = masterBuild.getBuildTool();
                if (!optionalBuildTool.isPresent()) {
                    this.addInBlacklistRepository(repository, BlacklistedSerializer.Reason.UNKNOWN_BUILD_TOOL, "");
                    return false;
                }

                BuildTool buildTool = optionalBuildTool.get();
                if (buildTool == BuildTool.GRADLE) {
                    this.addInBlacklistRepository(repository, BlacklistedSerializer.Reason.USE_GRADLE, "");
                    return false;
                } else if (buildTool == BuildTool.UNKNOWN) {
                    this.addInBlacklistRepository(repository, BlacklistedSerializer.Reason.UNKNOWN_BUILD_TOOL, "");
                    return false;
                }

                if (!masterBuild.getJobs().isEmpty()) {
                    Job firstJob = masterBuild.getJobs().get(0);
                    Optional<Log> optionalLog = firstJob.getLog();
                    if (optionalLog.isPresent()) {
                        Log jobLog = optionalLog.get();
                        if (RepairnatorConfig.getInstance().getLauncherMode() == LauncherMode.CHECKSTYLE ) {
                            if (jobLog.getContent().contains("maven-checkstyle")) {
                                LOGGER.info("Checkstyle has been found in repository "+repository.getSlug()+" (id: "+repositoryId+") build (id: "+masterBuild.getId()+"). The repo is now whitelisted.");
                                this.addInWhitelistRepository(repository);
                                return true;
                            } else {
                                this.addInTempBlackList(repository, "No checkstyle found");
                            }
                        } else {
                            if (jobLog.getTestsInformation() != null && jobLog.getTestsInformation().getRunning() > 0) {
                                LOGGER.info("Tests has been found in repository "+repository.getSlug()+" (id: "+repositoryId+") build (id: "+masterBuild.getId()+"). The repo is now whitelisted.");
                                this.addInWhitelistRepository(repository);
                                return true;
                            } else {
                                this.addInTempBlackList(repository, "No test found");
                            }
                        }
                    } else {
                        LOGGER.error("Error while getting log for job "+firstJob.getId());
                    }
                } else {
                    LOGGER.info("No job found in repository "+repository.getSlug()+" (id: "+repositoryId+") build (id: "+masterBuild.getId()+"). It is not considered right now.");
                }
            }
        } else {
            LOGGER.info("Repository not found with the following id: "+repositoryId+" it will be temporary blacklisted");
            Date expirationDate = new Date(new Date().toInstant().plusSeconds(DURATION_IN_TEMP_BLACKLIST).toEpochMilli());
            this.tempBlackList.put(repositoryId, expirationDate);
        }


        return false;
    }

    public void submitBuildToExecution(Build build) {
        boolean failing = false;
        List<Job> jobs = build.getJobs();
        if (jobs != null) {
            for (Job job : jobs) {
                Optional<Log> optionalLog = job.getLog();
                if (optionalLog.isPresent()) {
                    Log jobLog = optionalLog.get();
                    if (jobLog.getTestsInformation() != null && (jobLog.getTestsInformation().getErrored() >= 0 || jobLog.getTestsInformation().getFailing() >= 0)) {
                        failing = true;
                        break;
                    }
                }
            }
        }

        if (failing) {
            LOGGER.info("Failing or erroring tests has been found in build (id: "+build.getId()+")");
            this.pipelineRunner.submitBuild( new TravisInputBuild(build.getId()));
        } else {
            LOGGER.info("No failing or erroring test has been found in build (id: "+build.getId()+")");
        }
    }

    public void saveInfoToDisk() {
        blacklistedSerializer.serialize();;
    }

    public void stopDockerJobs(){
        if(this.pipelineRunner instanceof DockerPipelineRunner){
            ((DockerPipelineRunner)pipelineRunner).switchOff();
        }
    }

}

@Immutable
final class KafkaMessageTransformer {

    private static final DittoLogger LOGGER = DittoLoggerFactory.getLogger(KafkaMessageTransformer.class);

    private final Source source;
    private final String sourceAddress;
    private final EnforcementFilterFactory<Map<String, String>, Signal<?>> headerEnforcementFilterFactory;
    private final ConnectionMonitor inboundMonitor;

    KafkaMessageTransformer(final Source source, final String sourceAddress,
            final EnforcementFilterFactory<Map<String, String>, Signal<?>> headerEnforcementFilterFactory,
            final ConnectionMonitor inboundMonitor) {
        this.source = source;
        this.sourceAddress = sourceAddress;
        this.headerEnforcementFilterFactory = headerEnforcementFilterFactory;
        this.inboundMonitor = inboundMonitor;
    }

    @Nullable
    public CommittableTransformationResult transform(
            final ConsumerMessage.CommittableMessage<String, String> committableMessage) {

        final TransformationResult result = transform(committableMessage.record());
        if (result == null) {
            return null;
        } else {
            return CommittableTransformationResult.of(result, committableMessage.committableOffset());
        }
    }

    @Nullable
    public TransformationResult transform(final ConsumerRecord<String, String> consumerRecord) {

        LOGGER.trace("Received record from kafka: {}", consumerRecord);

        final Map<String, String> messageHeaders = extractMessageHeaders(consumerRecord);
        final String correlationId = messageHeaders
                .getOrDefault(DittoHeaderDefinition.CORRELATION_ID.getKey(), UUID.randomUUID().toString());

        final StartedTrace trace = DittoTracing.trace(DittoTracing.extractTraceContext(messageHeaders), "kafka.consume")
                .correlationId(correlationId).start();

        try {
            final String key = consumerRecord.key();
            final String value = consumerRecord.value();
            final DittoLogger correlationIdScopedLogger = LOGGER.withCorrelationId(correlationId);
            correlationIdScopedLogger.debug(
                    "Transforming incoming kafka message <{}> with headers <{}> and key <{}>.",
                    value, messageHeaders, key
            );

            final ExternalMessage externalMessage = ExternalMessageFactory.newExternalMessageBuilder(messageHeaders)
                    .withTextAndBytes(value, value == null ? null : value.getBytes())
                    .withAuthorizationContext(source.getAuthorizationContext())
                    .withEnforcement(headerEnforcementFilterFactory.getFilter(messageHeaders))
                    .withHeaderMapping(source.getHeaderMapping())
                    .withSourceAddress(sourceAddress)
                    .withPayloadMapping(source.getPayloadMapping())
                    .build();

            inboundMonitor.success(externalMessage);

            return TransformationResult.successful(externalMessage);
        } catch (final DittoRuntimeException e) {
            if (LOGGER.isDebugEnabled()) {
                LOGGER.withCorrelationId(e).debug(
                        "Got DittoRuntimeException '{}' when command was parsed: {}", e.getErrorCode(),
                        e.getMessage());
            }
            trace.fail(e);
            return TransformationResult.failed(e.setDittoHeaders(DittoHeaders.of(messageHeaders)));
        } catch (final Exception e) {
            inboundMonitor.exception(messageHeaders, e);
            LOGGER.withCorrelationId(correlationId)
                    .error(String.format("Unexpected {%s}: {%s}", e.getClass().getName(), e.getMessage()), e);
            trace.fail(e);
            return null; } finally {
            trace.finish();
        }

    }

    private Map<String, String> extractMessageHeaders(final ConsumerRecord<String, String> consumerRecord) {
        final Map<String, String> messageHeaders = new HashMap<>();
        for (final Header header : consumerRecord.headers()) {
            if (messageHeaders.put(header.key(), new String(header.value())) != null) {
                inboundMonitor.exception("Dropped duplicated headers in record from topic {0} at offset #{1}",
                        consumerRecord.topic(), consumerRecord.offset());
            }
        }
        if (!messageHeaders.containsKey(DittoHeaderDefinition.CORRELATION_ID.getKey())) {
            messageHeaders.put(DittoHeaderDefinition.CORRELATION_ID.getKey(), UUID.randomUUID().toString());
        }

        Arrays.stream(KafkaHeader.values())
                .forEach(kafkaHeader -> kafkaHeader.apply(consumerRecord)
                        .ifPresent(property -> messageHeaders.put(kafkaHeader.getName(), property)));

        return messageHeaders;
    }

}

class PerformanceComparator implements Comparator<TacticReport> {
    public int compare(TacticReport o1, TacticReport o2) {
        int s1 = o1.getAppearance();
        int s2 = o2.getAppearance();

        if (s1 > s2) {
            return -1;
        }

        if (s2 > s1) {
            return 1;
        }

        double n1 = o1.getRating();
        double n2 = o2.getRating();

        if (n1 > n2) {
            return -1;
        }

        if (n2 > n1) {
            return 1;
        }

        return 1;
    }
}

