public final class ShiroAuthProviderFactory implements AuthProviderFactory {

    private final Function<AuthConfig, Ini> iniConfigResolver;

    public ShiroAuthProviderFactory() {
        this(ShiroAuthProviderFactory::fromConfig);
    }

    public ShiroAuthProviderFactory(Function<AuthConfig, Ini> iniConfigResolver) {
        this.iniConfigResolver = requireNonNull(iniConfigResolver, "iniConfigResolver");
    }

    @Override
    public AuthProvider create(AuthProviderParameters parameters) {
        requireNonNull(parameters, "parameters");
        return new ShiroAuthProvider(parameters.authConfig(),
                                     iniConfigResolver.apply(parameters.authConfig()),
                                     parameters.sessionIdGenerator(),
                                     parameters.loginSessionPropagator(),
                                     parameters.logoutSessionPropagator());
    }

    private static Ini fromConfig(AuthConfig cfg) {
        try {
            final String iniPath = cfg.properties(String.class);
            return Ini.fromResourcePath(iniPath);
        } catch (Exception e) {
            throw new IllegalStateException("Failed to create " + Ini.class.getSimpleName(), e);
        }
    }
}

public class GuestPassListCallback extends CallbackMsg {

    private EResult result;

    private int countGuestPassesToGive;

    private int countGuestPassesToRedeem;

    private List<KeyValue> guestPasses;

    public GuestPassListCallback(MsgClientUpdateGuestPassesList msg, InputStream payload) {
        result = msg.getResult();
        countGuestPassesToGive = msg.getCountGuestPassesToGive();
        countGuestPassesToRedeem = msg.getCountGuestPassesToRedeem();

        guestPasses = new ArrayList<>();
        try {
            for (int i = 0; i < countGuestPassesToGive + countGuestPassesToRedeem; i++) {
                KeyValue kv = new KeyValue();
                kv.tryReadAsBinary(payload);
                guestPasses.add(kv);
            }
        } catch (IOException e) {
            throw new IllegalArgumentException("failed to read guest passes", e);
        }
    }

    public EResult getResult() {
        return result;
    }

    public int getCountGuestPassesToGive() {
        return countGuestPassesToGive;
    }

    public int getCountGuestPassesToRedeem() {
        return countGuestPassesToRedeem;
    }

    public List<KeyValue> getGuestPasses() {
        return guestPasses;
    }
}

