                return _alwaysSourceGroupsBuilders;
            default:
                throw new IllegalArgumentException();
        }
    }

    public Collection<IReplicationStaticTargetGroupBuilder> getTargetGroupBuilders(ReplicationNodeMode nodeMode) {
        switch (nodeMode) {
