public class StmtRWSet extends RWSet {

  protected Object field;
  protected PointsToSet base;
  protected boolean callsNative = false;

  @Override
  public String toString() {
    return "[Field: " + field + base + "]\n";
  }

  @Override
  public int size() {
    Set globals = getGlobals();
    Set fields = getFields();
    if (globals == null) {
      return (fields == null) ? 0 : fields.size();
    } else if (fields == null) {
      return globals.size();
    } else {
      return globals.size() + fields.size();
    }
  }

  @Override
  public boolean getCallsNative() {
    return callsNative;
  }

  @Override
  public boolean setCallsNative() {
    boolean ret = !callsNative;
    callsNative = true;
    return ret;
  }

  @Override
  public Set<Object> getGlobals() {
    return (base == null) ? Collections.singleton(field) : Collections.emptySet();
  }

  @Override
  public Set<Object> getFields() {
    return (base != null) ? Collections.singleton(field) : Collections.emptySet();
  }

  @Override
  public PointsToSet getBaseForField(Object f) {
    return field.equals(f) ? base : null;
  }

  @Override
  public boolean hasNonEmptyIntersection(RWSet other) {
    if (field == null) {
      return false;
    }
    if (other instanceof StmtRWSet) {
      StmtRWSet o = (StmtRWSet) other;
      if (!this.field.equals(o.field)) {
        return false;
      } else if (this.base == null) {
        return o.base == null;
      } else {
        return Union.hasNonEmptyIntersection(this.base, o.base);
      }
    } else if (other instanceof MethodRWSet) {
      if (this.base == null) {
        return other.getGlobals().contains(this.field);
      } else {
        return Union.hasNonEmptyIntersection(this.base, other.getBaseForField(this.field));
      }
    } else {
      return other.hasNonEmptyIntersection(this);
    }
  }

  @Override
  public boolean union(RWSet other) {
    throw new RuntimeException("Can't do that");
  }

  @Override
  public boolean addGlobal(SootField global) {
    if (field != null || base != null) {
      throw new RuntimeException("Can't do that");
    }
    field = global;
    return true;
  }

  @Override
  public boolean addFieldRef(PointsToSet otherBase, Object field) {
    if (this.field != null || base != null) {
      throw new RuntimeException("Can't do that");
    }
    this.field = field;
    base = otherBase;
    return true;
  }

  @Override
  public boolean isEquivTo(RWSet other) {
    if (!(other instanceof StmtRWSet)) {
      return false;
    }
    StmtRWSet o = (StmtRWSet) other;
    if (this.callsNative != o.callsNative) {
      return false;
    }
    if (!this.field.equals(o.field)) {
      return false;
    }
    if (this.base instanceof FullObjectSet && o.base instanceof FullObjectSet) {
      return true;
    }
    return this.base == o.base;
  }
}

public class SHA1 {

	private final File file;
	private FileInputStream fis;
	private MessageDigest sha1;

	public SHA1(String filename) {
		this.file = new File(filename);
	}

	public SHA1(File file) {
		this.file = file;
	}

	public String SHA1sum() {
		try {
			sha1 = MessageDigest.getInstance("SHA1");
			fis = new FileInputStream(file);
			byte[] dataBytes = new byte[1024];

			int read = 0;

			while ((read = fis.read(dataBytes)) != -1) {
				sha1.update(dataBytes, 0, read);
			}

			return toString(sha1);

		} catch (IOException ex) {
			throw new RuntimeException("Can't read file to count SHA-1 hash, check your permissions", ex);
		} catch (NoSuchAlgorithmException ex) {
			throw new RuntimeException("Problem with SHA-1 hash", ex);
		}
	}

	public static String toString(MessageDigest sha1) {
		byte[] mdbytes = sha1.digest();

		StringBuilder sb = new StringBuilder();
        for (byte mdbyte : mdbytes) {
            sb.append(Integer.toString((mdbyte & 0xff) + 0x100, 16).substring(1));
        }
		return sb.toString();
	}
}

public class RepositoryServiceImpl extends AbstractWebServicesService implements RepositoryService {

    private final AbstractPortProvider portProvider;

    public RepositoryServiceImpl(BindingSession session, AbstractPortProvider portProvider) {
        setSession(session);
        this.portProvider = portProvider;
    }

    @Override
    public List<RepositoryInfo> getRepositoryInfos(ExtensionsData extension) {
        CmisVersion cmisVersion = CmisBindingsHelper.getForcedCmisVersion(getSession());
        if (cmisVersion == null) {
            cmisVersion = CmisVersion.CMIS_1_1;
        }

        RepositoryServicePort port = portProvider.getRepositoryServicePort(cmisVersion, "getRepositories");

        List<CmisRepositoryEntryType> entries = null;
        try {
            entries = port.getRepositories(convert(extension));
        } catch (CmisException e) {
            throw convertException(e);
        } catch (Exception e) {
            throw new CmisRuntimeException("Error: " + e.getMessage(), e);
        } finally {
            portProvider.endCall(port);
        }

        List<RepositoryInfo> infos = null;
        if (entries != null) {
            port = portProvider.getRepositoryServicePort(cmisVersion, "getRepositoryInfo");

            try {
                infos = new ArrayList<RepositoryInfo>();

                for (CmisRepositoryEntryType entry : entries) {
                    try {
                        CmisRepositoryInfoType info = port.getRepositoryInfo(entry.getRepositoryId(), null);
                        infos.add(convert(info));
                    } catch (CmisBaseException e) {
                        RepositoryInfoImpl info = new RepositoryInfoImpl();
                        info.setId(entry.getRepositoryId());
                        info.setName(entry.getRepositoryName());
                        infos.add(info);
                    }
                }
            } catch (CmisException e) {
                throw convertException(e);
            } catch (Exception e) {
                throw new CmisRuntimeException("Error: " + e.getMessage(), e);
            } finally {
                portProvider.endCall(port);
            }
        }

        return infos;
    }

    @Override
    public RepositoryInfo getRepositoryInfo(String repositoryId, ExtensionsData extension) {
        CmisVersion cmisVersion = CmisBindingsHelper.getForcedCmisVersion(getSession());
        if (cmisVersion == null) {
            cmisVersion = CmisVersion.CMIS_1_1;
        }

        RepositoryServicePort port = portProvider.getRepositoryServicePort(cmisVersion, "getRepositoryInfo");

        try {
            return convert(port.getRepositoryInfo(repositoryId, convert(extension)));
        } catch (CmisException e) {
            throw convertException(e);
        } catch (Exception e) {
            throw new CmisRuntimeException("Error: " + e.getMessage(), e);
        } finally {
            portProvider.endCall(port);
        }
    }

