public abstract class BaseTokenFilter<T extends GenericToken> implements TokenFilter {

    private final TokenManager tokenManager;
    private final LinkedList<T> unprocessedTokens; private final Iterable<T> remainingTokens;
    private boolean discardingSuppressing;
    private T currentToken;

    public BaseTokenFilter(final TokenManager tokenManager) {
        this.tokenManager = tokenManager;
        this.unprocessedTokens = new LinkedList<>();
        this.remainingTokens = new RemainingTokens();
    }

    @Override
    public final T getNextToken() {
        currentToken = null;
        if (!unprocessedTokens.isEmpty()) {
            currentToken = unprocessedTokens.poll();
        } else {
            currentToken = (T) tokenManager.getNextToken();
        }
        while (!shouldStopProcessing(currentToken)) {
            analyzeToken(currentToken);
            analyzeTokens(currentToken, remainingTokens);
            processCPDSuppression(currentToken);

            if (!isDiscarding()) {
                return currentToken;
            }

            if (!unprocessedTokens.isEmpty()) {
                currentToken = unprocessedTokens.poll();
            } else {
                currentToken = (T) tokenManager.getNextToken();
            }
        }

        return null;
    }

    private boolean isDiscarding() {
        return discardingSuppressing || isLanguageSpecificDiscarding();
    }

    private void processCPDSuppression(final T currentToken) {
        GenericToken comment = currentToken.getPreviousComment();
        while (comment != null) {
            if (comment.getImage().contains("CPD-OFF")) {
                discardingSuppressing = true;
                break;
            }
            if (comment.getImage().contains("CPD-ON")) {
                discardingSuppressing = false;
                break;
            }
            comment = comment.getPreviousComment();
        }
    }

    protected void analyzeToken(final T currentToken) {
        }

    protected void analyzeTokens(final T currentToken, final Iterable<T> remainingTokens) {
        }

    protected boolean isLanguageSpecificDiscarding() {
        return false;
    }

    protected abstract boolean shouldStopProcessing(T currentToken);

    private class RemainingTokens implements Iterable<T> {

        @Override
        public Iterator<T> iterator() {
            return new RemainingTokensIterator(currentToken);
        }

        private class RemainingTokensIterator extends AbstractIterator<T> implements Iterator<T> {

            int index = 0; T startToken;

            RemainingTokensIterator(final T startToken) {
                this.startToken = startToken;
            }

            @Override
            protected void computeNext() {
                assert index >= 0;
                if (startToken != currentToken) { throw new ConcurrentModificationException("Using iterator after next token has been requested.");
                }
                if (index < unprocessedTokens.size()) {
                    setNext(unprocessedTokens.get(index++));
                } else {
                    final T nextToken = (T) tokenManager.getNextToken();
                    if (shouldStopProcessing(nextToken)) {
                        done();
                        return;
                    }
                    index++;
                    unprocessedTokens.add(nextToken);
                    setNext(nextToken);
                }
            }

        }
    }

}

public class CacheMXBeanImpl<K,V> implements CacheMXBean<V> {

    private static final Logger _log = LoggerFactory.getLogger(CacheMXBeanImpl.class);

    private final Cache<K,V> _cache;

    public CacheMXBeanImpl(Cache<K,V> cache){
        _cache = cache;
        MBeanServer server = ManagementFactory.getPlatformMBeanServer();
        try {
            String name = String.format("%s:type=Cache,name=%s",
                    _cache.getClass().getPackage().getName(), _cache.getName());
            ObjectName mxBeanName = new ObjectName(name);
            if( !server.isRegistered(mxBeanName)) {
                server.registerMBean(this, new ObjectName(name));
            }
        } catch (MalformedObjectNameException | InstanceAlreadyExistsException
                | MBeanRegistrationException | NotCompliantMBeanException ex) {
            _log.warn("Failed to register JMX bean: {}", ex.getMessage());
        }

    }

    @Override
    public void clear() {
        _cache.clear();
    }

    @Override
    public String[] getEntries() {
        List<CacheElement<V>> entries = _cache.entries();
        String[] asArray = new String[entries.size()];
        int i = 0;
        for(CacheElement<V> v: entries) {
            asArray[i] = v.toString();
            i++;
        }
        return asArray;
    }

    @Override
    public int getSize() {
        return _cache.size();
    }

    @Override
    public long getEntryIdleTime() {
        return _cache.getEntryIdleTime();
    }

    @Override
    public long getEntryLiveTime() {
        return _cache.getEntryLiveTime();
    }

    @Override
    public long getLastClean() {
        return System.currentTimeMillis() - _cache.lastClean();
    }
}

public class Utils {

    private Utils() {
    }

    public static Point getDisplaySize(Context context) {
        WindowManager wm = (WindowManager) context.getSystemService(Context.WINDOW_SERVICE);
        Display display = wm.getDefaultDisplay();
        Point size = new Point();
        display.getSize(size);
        return size;
    }

    public static void showToast(Context context, String msg) {
        Toast.makeText(context, msg, Toast.LENGTH_LONG).show();
    }

    public static void showToast(Context context, int resourceId) {
        Toast.makeText(context, context.getString(resourceId), Toast.LENGTH_LONG).show();
    }

    public static int convertDpToPixel(Context ctx, int dp) {
        float density = ctx.getResources().getDisplayMetrics().density;
        return Math.round((float) dp * density);
    }

    public static String formatMillis(int millis) {
        String result = "";
        int hr = millis / 3600000;
        millis %= 3600000;
        int min = millis / 60000;
        millis %= 60000;
        int sec = millis / 1000;
        if (hr > 0) {
            result += hr + ":";
        }
        if (min >= 0) {
            if (min > 9) {
                result += min + ":";
            } else {
                result += "0" + min + ":";
            }
        }
        if (sec > 9) {
            result += sec;
        } else {
            result += "0" + sec;
        }
        return result;
    }
}

