public class ProgressHelper {
    public static OkHttpClient addProgressResponseListener(OkHttpClient client, final ProgressListener progressListener){
        Interceptor interceptor = new Interceptor() {
            @Override
            public Response intercept(Chain chain) throws IOException {
                Response originalResponse = chain.proceed(chain.request());
                return originalResponse.newBuilder()
                        .body(new ProgressResponseBody(originalResponse.body(), progressListener))
                        .build();
            }
        };
        return client.newBuilder()
                .addInterceptor(interceptor)
                .build();
    }

    public static ProgressRequestBody addProgressRequestListener(RequestBody requestBody, ProgressListener progressRequestListener){
        return new ProgressRequestBody(requestBody,progressRequestListener);
    }
}

public class AnnotationConfigChecker {

    public static void check(Class<?> annotatedClass) {
        String index = DocumentUtils.getIndexName(annotatedClass);
        checkIndex(index);

        String typeName = DocumentUtils.getTypeName(annotatedClass);
        checkType(typeName);

        String alias = DocumentUtils.getAlias(annotatedClass);
        checkAlias(alias);
    }

    public static void check(Set<Class<?>> annotatedClasses) {
        annotatedClasses.forEach(AnnotationConfigChecker::check);
    }

    private static void checkIndex(String index) {
        if(index.length() >= 255) {
            throw new InvalidIndexNameException("index name[" + index + "] length shouldn't exceed 255 characters");
        }
        if(StringUtils.containsUpperCase(index))
            throw new InvalidIndexNameException("index name[" + index + "] should be all lowerCase");
        if(StringUtils.startsWithAny(index, "_", "-", "+"))
            throw new InvalidIndexNameException("index name[" + index + "] shouldn't starts with _,-,+");
    }

    private static void checkType(String type) {
        if(type.length() >= 255) {
            throw new InvalidIndexNameException("type name[" + type + "] length shouldn't exceed 255 characters");
        }
        if(StringUtils.containsUpperCase(type))
            throw new InvalidIndexNameException("type name[" + type + "] should be all lowerCase");
        if(StringUtils.startsWithAny(type, "_", "-", "+"))
            throw new InvalidIndexNameException("type name[" + type + "] shouldn't starts with _,-,+");
    }

    private static void checkAlias(String alias) {
        if(alias.length() >= 255) {
            throw new InvalidIndexNameException("alias name[" + alias + "] length shouldn't exceed 255 characters");
        }
        if(StringUtils.containsUpperCase(alias))
            throw new InvalidIndexNameException("alias name[" + alias + "] should be all lowerCase");
        if(StringUtils.startsWithAny(alias, "_", "-", "+"))
            throw new InvalidIndexNameException("alias name[" + alias + "] shouldn't starts with _,-,+");
    }

}

public class JedisUtils {
    private static final Logger logger = LoggerFactory.getLogger(JedisUtils.class);

    private static final DynamicLongProperty minRetryMs = DynamicPropertyFactory.getInstance()
            .getLongProperty("florida.storage.isAlive.retry.min.ms", 3000L);

    private static final DynamicLongProperty maxRetryMs = DynamicPropertyFactory.getInstance()
            .getLongProperty("florida.storage.isAlive.retry.max.ms", 30000L);

    private static final DynamicIntProperty jedisConnectTimeoutMs = DynamicPropertyFactory.getInstance()
            .getIntProperty("florida.storage.jedis.connect.timeout.ms", 30000);

    public static Jedis connect(final String host, final int port) {
        Jedis jedis;
        try {
            jedis = new Jedis(host, port, jedisConnectTimeoutMs.getValue());
            jedis.connect();
            return jedis;
        } catch (Exception e) {
            logger.warn("Unable to connect to host:" + host + " port: " + port);
        }

        return null;
    }

    public static boolean isWritableWithRetry(final String host, final int port) {
        BoundedExponentialRetryCallable<Boolean> jedisRetryCallable = new BoundedExponentialRetryCallable<Boolean>() {
            Jedis jedis = null;

            @Override
            public Boolean retriableCall() throws Exception {
                jedis = connect(host, port);
                String status = jedis.setex("ignore_dyno", 1, "dynomite");
                if (!status.equalsIgnoreCase("OK")) {
                    jedis.disconnect();
                    return false;
                }
                return true;

            }

            @Override
            public void forEachExecution() {
                jedis.disconnect();
            }
        };

        jedisRetryCallable.setMin(minRetryMs.getValue());
        jedisRetryCallable.setMax(maxRetryMs.getValue());

        try {
            return jedisRetryCallable.call();
        } catch (Exception e) {
            logger.warn(String.format("All retries to SETEX to host:%s port:%s failed.", host, port));
            return false;
        }

    }

    public static boolean isAliveWithRetry(final String host, final int port) {

        BoundedExponentialRetryCallable<Boolean> jedisRetryCallable = new BoundedExponentialRetryCallable<Boolean>() {
            Jedis jedis = null;

            @Override
            public Boolean retriableCall() throws Exception {
                jedis = connect(host, port);
                if (jedis.ping() == null) {
                    jedis.disconnect();
                    return false;
                }
                jedis.disconnect();
                return true;
            }

            @Override
            public void forEachExecution() {
                jedis.disconnect();
            }
        };

        jedisRetryCallable.setMin(minRetryMs.getValue());
        jedisRetryCallable.setMax(maxRetryMs.getValue());

        try {
            return jedisRetryCallable.call();
        } catch (Exception e) {
            logger.warn(String.format("All retries to PING host:%s port:%s failed.", host, port));
            return false;
        }

    }
}

public class CVB0Driver extends AbstractJob {
  private static final Logger log = LoggerFactory.getLogger(CVB0Driver.class);

  public static final String NUM_TOPICS = "num_topics";
  public static final String NUM_TERMS = "num_terms";
  public static final String DOC_TOPIC_SMOOTHING = "doc_topic_smoothing";
  public static final String TERM_TOPIC_SMOOTHING = "term_topic_smoothing";
  public static final String DICTIONARY = "dictionary";
  public static final String DOC_TOPIC_OUTPUT = "doc_topic_output";
  public static final String MODEL_TEMP_DIR = "topic_model_temp_dir";
  public static final String ITERATION_BLOCK_SIZE = "iteration_block_size";
  public static final String RANDOM_SEED = "random_seed";
  public static final String TEST_SET_FRACTION = "test_set_fraction";
  public static final String NUM_TRAIN_THREADS = "num_train_threads";
  public static final String NUM_UPDATE_THREADS = "num_update_threads";
  public static final String MAX_ITERATIONS_PER_DOC = "max_doc_topic_iters";
  public static final String MODEL_WEIGHT = "prev_iter_mult";
  public static final String NUM_REDUCE_TASKS = "num_reduce_tasks";
  public static final String BACKFILL_PERPLEXITY = "backfill_perplexity";
  private static final String MODEL_PATHS = "mahout.lda.cvb.modelPath";

  @Override
  public int run(String[] args) throws Exception {
    addInputOption();
    addOutputOption();
    addOption(DefaultOptionCreator.maxIterationsOption().create());
    addOption(DefaultOptionCreator.CONVERGENCE_DELTA_OPTION, "cd", "The convergence delta value", "0");
    addOption(DefaultOptionCreator.overwriteOption().create());

    addOption(NUM_TOPICS, "k", "Number of topics to learn", true);
    addOption(NUM_TERMS, "nt", "Vocabulary size", false);
    addOption(DOC_TOPIC_SMOOTHING, "a", "Smoothing for document/topic distribution", "0.0001");
    addOption(TERM_TOPIC_SMOOTHING, "e", "Smoothing for topic/term distribution", "0.0001");
    addOption(DICTIONARY, "dict", "Path to term-dictionary file(s) (glob expression supported)",
        false);
    addOption(DOC_TOPIC_OUTPUT, "dt", "Output path for the training doc/topic distribution",
        false);
    addOption(MODEL_TEMP_DIR, "mt", "Path to intermediate model path (useful for restarting)",
        false);
    addOption(ITERATION_BLOCK_SIZE, "block", "Number of iterations per perplexity check", "10");
    addOption(RANDOM_SEED, "seed", "Random seed", false);
    addOption(TEST_SET_FRACTION, "tf", "Fraction of data to hold out for testing", "0");
    addOption(NUM_TRAIN_THREADS, "ntt", "number of threads per mapper to train with", "4");
    addOption(NUM_UPDATE_THREADS, "nut", "number of threads per mapper to update the model with",
        "1");
    addOption(MAX_ITERATIONS_PER_DOC, "mipd",
        "max number of iterations per doc for p(topic|doc) learning", "10");
    addOption(NUM_REDUCE_TASKS, null,
        "number of reducers to use during model estimation", "10");
    addOption(buildOption(BACKFILL_PERPLEXITY, null,
        "enable backfilling of missing perplexity values", false, false, null));

    if(parseArguments(args) == null) {
      return -1;
    }

    int numTopics = Integer.parseInt(getOption(NUM_TOPICS));
    Path inputPath = getInputPath();
    Path topicModelOutputPath = getOutputPath();
    int maxIterations = Integer.parseInt(getOption(DefaultOptionCreator.MAX_ITERATIONS_OPTION));
    int iterationBlockSize = Integer.parseInt(getOption(ITERATION_BLOCK_SIZE));
    double convergenceDelta = Double.parseDouble(getOption(DefaultOptionCreator.CONVERGENCE_DELTA_OPTION));
    double alpha = Double.parseDouble(getOption(DOC_TOPIC_SMOOTHING));
    double eta = Double.parseDouble(getOption(TERM_TOPIC_SMOOTHING));
    int numTrainThreads = Integer.parseInt(getOption(NUM_TRAIN_THREADS));
    int numUpdateThreads = Integer.parseInt(getOption(NUM_UPDATE_THREADS));
    int maxItersPerDoc = Integer.parseInt(getOption(MAX_ITERATIONS_PER_DOC));
    Path dictionaryPath = hasOption(DICTIONARY) ? new Path(getOption(DICTIONARY)) : null;
    int numTerms = hasOption(NUM_TERMS)
                 ? Integer.parseInt(getOption(NUM_TERMS))
                 : getNumTerms(getConf(), dictionaryPath);
    Path docTopicOutputPath = hasOption(DOC_TOPIC_OUTPUT) ? new Path(getOption(DOC_TOPIC_OUTPUT)) : null;
    Path modelTempPath = hasOption(MODEL_TEMP_DIR)
                       ? new Path(getOption(MODEL_TEMP_DIR))
                       : getTempPath("topicModelState");
    long seed = hasOption(RANDOM_SEED)
              ? Long.parseLong(getOption(RANDOM_SEED))
              : System.nanoTime() % 10000;
    float testFraction = hasOption(TEST_SET_FRACTION)
                       ? Float.parseFloat(getOption(TEST_SET_FRACTION))
                       : 0.0f;
    int numReduceTasks = Integer.parseInt(getOption(NUM_REDUCE_TASKS));
    boolean backfillPerplexity = hasOption(BACKFILL_PERPLEXITY);

    return run(getConf(), inputPath, topicModelOutputPath, numTopics, numTerms, alpha, eta,
        maxIterations, iterationBlockSize, convergenceDelta, dictionaryPath, docTopicOutputPath,
        modelTempPath, seed, testFraction, numTrainThreads, numUpdateThreads, maxItersPerDoc,
        numReduceTasks, backfillPerplexity);
  }

  private static int getNumTerms(Configuration conf, Path dictionaryPath) throws IOException {
    FileSystem fs = dictionaryPath.getFileSystem(conf);
    Text key = new Text();
    IntWritable value = new IntWritable();
    int maxTermId = -1;
    for (FileStatus stat : fs.globStatus(dictionaryPath)) {
      SequenceFile.Reader reader = new SequenceFile.Reader(fs, stat.getPath(), conf);
      while (reader.next(key, value)) {
        maxTermId = Math.max(maxTermId, value.get());
      }
    }
    return maxTermId + 1;
  }

  public static int run(Configuration conf,
                        Path inputPath,
                        Path topicModelOutputPath,
                        int numTopics,
                        int numTerms,
                        double alpha,
                        double eta,
                        int maxIterations,
                        int iterationBlockSize,
                        double convergenceDelta,
                        Path dictionaryPath,
                        Path docTopicOutputPath,
                        Path topicModelStateTempPath,
                        long randomSeed,
                        float testFraction,
                        int numTrainThreads,
                        int numUpdateThreads,
                        int maxItersPerDoc,
                        int numReduceTasks,
                        boolean backfillPerplexity)
      throws ClassNotFoundException, IOException, InterruptedException {
    Preconditions.checkArgument(testFraction >= 0.0 && testFraction <= 1.0,
        "Expected 'testFraction' value in range [0, 1] but found value '%s'", testFraction);
    Preconditions.checkArgument(!backfillPerplexity || testFraction > 0.0,
        "Expected 'testFraction' value in range (0, 1] but found value '%s'", testFraction);

    String infoString = "Will run Collapsed Variational Bayes (0th-derivative approximation) " +
      "learning for LDA on {} (numTerms: {}), finding {}-topics, with document/topic prior {}, " +
      "topic/term prior {}.  Maximum iterations to run will be {}, unless the change in " +
      "perplexity is less than {}.  Topic model output (p(term|topic) for each topic) will be " +
      "stored {}.  Random initialization seed is {}, holding out {} of the data for perplexity " +
      "check\n";
    log.info(infoString, new Object[] {inputPath, numTerms, numTopics, alpha, eta, maxIterations,
        convergenceDelta, topicModelOutputPath, randomSeed, testFraction});
    infoString = dictionaryPath == null
               ? "" : "Dictionary to be used located " + dictionaryPath.toString() + '\n';
    infoString += docTopicOutputPath == null
               ? "" : "p(topic|docId) will be stored " + docTopicOutputPath.toString() + '\n';
    log.info(infoString);

    FileSystem fs = FileSystem.get(conf);
    int iterationNumber = getCurrentIterationNumber(conf, topicModelStateTempPath, maxIterations);
    log.info("Current iteration number: {}", iterationNumber);

    conf.set(NUM_TOPICS, String.valueOf(numTopics));
    conf.set(NUM_TERMS, String.valueOf(numTerms));
    conf.set(DOC_TOPIC_SMOOTHING, String.valueOf(alpha));
    conf.set(TERM_TOPIC_SMOOTHING, String.valueOf(eta));
    conf.set(RANDOM_SEED, String.valueOf(randomSeed));
    conf.set(NUM_TRAIN_THREADS, String.valueOf(numTrainThreads));
    conf.set(NUM_UPDATE_THREADS, String.valueOf(numUpdateThreads));
    conf.set(MAX_ITERATIONS_PER_DOC, String.valueOf(maxItersPerDoc));
    conf.set(MODEL_WEIGHT, "1"); conf.set(TEST_SET_FRACTION, String.valueOf(testFraction));

    List<Double> perplexities = Lists.newArrayList();
    for (int i = 1; i <= iterationNumber; i++) {
      Path modelPath = modelPath(topicModelStateTempPath, i);

      double perplexity = readPerplexity(conf, topicModelStateTempPath, i);
      if (Double.isNaN(perplexity)) {
        if (!(backfillPerplexity && i % iterationBlockSize == 0)) {
          continue;
        }
        log.info("Backfilling perplexity at iteration {}", i);
        if (!fs.exists(modelPath)) {
          log.error("Model path '{}' does not exist; Skipping iteration {} perplexity calculation", modelPath.toString(), i);
          continue;
        }
        perplexity = calculatePerplexity(conf, inputPath, modelPath, i);
      }

      perplexities.add(perplexity);
      log.info("Perplexity at iteration {} = {}", i, perplexity);
    }

    long startTime = System.currentTimeMillis();
    while(iterationNumber < maxIterations) {
      if (convergenceDelta > 0.0) {
        double delta = rateOfChange(perplexities);
        if (delta < convergenceDelta) {
          log.info("Convergence achieved at iteration {} with perplexity {} and delta {}",
              new Object[]{iterationNumber, perplexities.get(perplexities.size() - 1), delta});
          break;
        }
      }

      iterationNumber++;
      log.info("About to run iteration {} of {}", iterationNumber, maxIterations);
      Path modelInputPath = modelPath(topicModelStateTempPath, iterationNumber - 1);
      Path modelOutputPath = modelPath(topicModelStateTempPath, iterationNumber);
      runIteration(conf, inputPath, modelInputPath, modelOutputPath, iterationNumber,
          maxIterations, numReduceTasks);

      if(testFraction > 0 && iterationNumber % iterationBlockSize == 0) {
        perplexities.add(calculatePerplexity(conf, inputPath, modelOutputPath, iterationNumber));
        log.info("Current perplexity = {}", perplexities.get(perplexities.size() - 1));
        log.info("(p_{} - p_{}) / p_0 = {}; target = {}", new Object[]{
            iterationNumber , iterationNumber - iterationBlockSize, rateOfChange(perplexities), convergenceDelta
        });
      }
    }
    log.info("Completed {} iterations in {} seconds", iterationNumber,
        (System.currentTimeMillis() - startTime)/1000);
    log.info("Perplexities: ({})", Joiner.on(", ").join(perplexities));

    Path finalIterationData = modelPath(topicModelStateTempPath, iterationNumber);
    Job topicModelOutputJob = topicModelOutputPath != null
        ? writeTopicModel(conf, finalIterationData, topicModelOutputPath)
        : null;
    Job docInferenceJob = docTopicOutputPath != null
        ? writeDocTopicInference(conf, inputPath, finalIterationData, docTopicOutputPath)
        : null;
    if(topicModelOutputJob != null && !topicModelOutputJob.waitForCompletion(true)) {
      return -1;
    }
    if(docInferenceJob != null && !docInferenceJob.waitForCompletion(true)) {
      return -1;
    }
    return 0;
  }

  private static double rateOfChange(List<Double> perplexities) {
    int sz = perplexities.size();
    if(sz < 2) {
      return Double.MAX_VALUE;
    }
    return Math.abs(perplexities.get(sz - 1) - perplexities.get(sz - 2)) / perplexities.get(0);
  }

  private static double calculatePerplexity(Configuration conf, Path corpusPath, Path modelPath, int iteration)
      throws IOException,
      ClassNotFoundException, InterruptedException {
    String jobName = "Calculating perplexity for " + modelPath;
    log.info("About to run: " + jobName);
    Job job = new Job(conf, jobName);
    job.setJarByClass(CachingCVB0PerplexityMapper.class);
    job.setMapperClass(CachingCVB0PerplexityMapper.class);
    job.setCombinerClass(DualDoubleSumReducer.class);
    job.setReducerClass(DualDoubleSumReducer.class);
    job.setNumReduceTasks(1);
    job.setOutputKeyClass(DoubleWritable.class);
    job.setOutputValueClass(DoubleWritable.class);
    job.setInputFormatClass(SequenceFileInputFormat.class);
    job.setOutputFormatClass(SequenceFileOutputFormat.class);
    FileInputFormat.addInputPath(job, corpusPath);
    Path outputPath = perplexityPath(modelPath.getParent(), iteration);
    FileOutputFormat.setOutputPath(job, outputPath);
    setModelPaths(job, modelPath);
    HadoopUtil.delete(conf, outputPath);
    if(!job.waitForCompletion(true)) {
      throw new InterruptedException("Failed to calculate perplexity for: " + modelPath);
    }
    return readPerplexity(conf, modelPath.getParent(), iteration);
  }

  public static class DualDoubleSumReducer extends
    Reducer<DoubleWritable, DoubleWritable, DoubleWritable, DoubleWritable> {
    private final DoubleWritable outKey = new DoubleWritable();
    private final DoubleWritable outValue = new DoubleWritable();

    @Override
    public void run(Context context) throws IOException,
        InterruptedException {
      double keySum = 0.0;
      double valueSum = 0.0;
      while (context.nextKey()) {
        keySum += context.getCurrentKey().get();
        for (DoubleWritable value : context.getValues()) {
          valueSum += value.get();
        }
      }
      outKey.set(keySum);
      outValue.set(valueSum);
      context.write(outKey, outValue);
    }
  }

  public static double readPerplexity(Configuration conf, Path topicModelStateTemp, int iteration)
      throws IOException {
    Path perplexityPath = perplexityPath(topicModelStateTemp, iteration);
    FileSystem fs = FileSystem.get(conf);
    if (!fs.exists(perplexityPath)) {
      log.warn("Perplexity path {} does not exist, returning NaN", perplexityPath);
      return Double.NaN;
    }
    double perplexity = 0;
    double modelWeight = 0;
    long n = 0;
    for (Pair<DoubleWritable, DoubleWritable> pair : new SequenceFileDirIterable<DoubleWritable, DoubleWritable>(
        perplexityPath, PathType.LIST, PathFilters.partFilter(), null, true, conf)) {
      modelWeight += pair.getFirst().get();
      perplexity += pair.getSecond().get();
      n++;
    }
    log.info("Read {} entries with total perplexity {} and model weight {}", new Object[] { n,
            perplexity, modelWeight });
    return perplexity / modelWeight;
  }

  private static Job writeTopicModel(Configuration conf, Path modelInput, Path output) throws IOException,
      InterruptedException, ClassNotFoundException {
    String jobName = String.format("Writing final topic/term distributions from %s to %s", modelInput,
        output);
    log.info("About to run: " + jobName);
    Job job = new Job(conf, jobName);
    job.setJarByClass(CVB0Driver.class);
    job.setInputFormatClass(SequenceFileInputFormat.class);
    job.setMapperClass(CVB0TopicTermVectorNormalizerMapper.class);
    job.setNumReduceTasks(0);
    job.setOutputKeyClass(IntWritable.class);
    job.setOutputValueClass(VectorWritable.class);
    job.setOutputFormatClass(SequenceFileOutputFormat.class);
    FileInputFormat.addInputPath(job, modelInput);
    FileOutputFormat.setOutputPath(job, output);
    job.submit();
    return job;
  }

  private static Job writeDocTopicInference(Configuration conf, Path corpus, Path modelInput, Path output)
      throws IOException, ClassNotFoundException, InterruptedException {
    String jobName = String.format("Writing final document/topic inference from %s to %s", corpus,
        output);
    log.info("About to run: " + jobName);
    Job job = new Job(conf, jobName);
    job.setMapperClass(CVB0DocInferenceMapper.class);
    job.setNumReduceTasks(0);
    job.setInputFormatClass(SequenceFileInputFormat.class);
    job.setOutputFormatClass(SequenceFileOutputFormat.class);
    job.setOutputKeyClass(IntWritable.class);
    job.setOutputValueClass(VectorWritable.class);
    FileSystem fs = FileSystem.get(conf);
    if(modelInput != null && fs.exists(modelInput)) {
      FileStatus[] statuses = FileSystem.get(conf).listStatus(modelInput, PathFilters.partFilter());
      URI[] modelUris = new URI[statuses.length];
      for(int i = 0; i < statuses.length; i++) {
        modelUris[i] = statuses[i].getPath().toUri();
      }
      DistributedCache.setCacheFiles(modelUris, conf);
    }
    FileInputFormat.addInputPath(job, corpus);
    FileOutputFormat.setOutputPath(job, output);
    job.setJarByClass(CVB0Driver.class);
    job.submit();
    return job;
  }

  public static Path modelPath(Path topicModelStateTempPath, int iterationNumber) {
    return new Path(topicModelStateTempPath, "model-" + iterationNumber);
  }

  public static Path stage1OutputPath(Path topicModelStateTempPath, int iterationNumber) {
    return new Path(topicModelStateTempPath, "tmp-" + iterationNumber);
  }

  public static Path perplexityPath(Path topicModelStateTempPath, int iterationNumber) {
    return new Path(topicModelStateTempPath, "perplexity-" + iterationNumber);
  }

  private static int getCurrentIterationNumber(Configuration config, Path modelTempDir, int maxIterations)
      throws IOException {
    FileSystem fs = FileSystem.get(config);
    int iterationNumber = 1;
    Path iterationPath = modelPath(modelTempDir, iterationNumber);
    while(fs.exists(iterationPath) && iterationNumber <= maxIterations) {
      log.info("Found previous state: " + iterationPath);
      iterationNumber++;
      iterationPath = modelPath(modelTempDir, iterationNumber);
    }
    return iterationNumber - 1;
  }

  public static void runIteration(Configuration conf, Path corpusInput, Path modelInput, Path modelOutput,
                                  int iterationNumber, int maxIterations, int numReduceTasks) throws IOException, ClassNotFoundException, InterruptedException {
    String jobName = String.format("Iteration %d of %d, input path: %s",
        iterationNumber, maxIterations, modelInput);
    log.info("About to run: " + jobName);
    Job job = new Job(conf, jobName);
    job.setJarByClass(CVB0Driver.class);
    job.setMapperClass(CachingCVB0Mapper.class);
    job.setCombinerClass(VectorSumReducer.class);
    job.setReducerClass(VectorSumReducer.class);
    job.setNumReduceTasks(numReduceTasks);
    job.setOutputKeyClass(IntWritable.class);
    job.setOutputValueClass(VectorWritable.class);
    job.setInputFormatClass(SequenceFileInputFormat.class);
    job.setOutputFormatClass(SequenceFileOutputFormat.class);
    FileInputFormat.addInputPath(job, corpusInput);
    FileOutputFormat.setOutputPath(job, modelOutput);
    setModelPaths(job, modelInput);
    HadoopUtil.delete(conf, modelOutput);
    if(!job.waitForCompletion(true)) {
      throw new InterruptedException(String.format("Failed to complete iteration %d stage 1",
          iterationNumber));
    }
  }

  private static void setModelPaths(Job job, Path modelPath) throws IOException {
    Configuration conf = job.getConfiguration();
    FileSystem fs = FileSystem.get(conf);
    if (modelPath == null || !fs.exists(modelPath)) {
      return;
    }
    FileStatus[] statuses = FileSystem.get(conf).listStatus(modelPath, PathFilters.partFilter());
    Preconditions.checkState(statuses.length > 0, "No part files found in model path '%s'", modelPath.toString());
    String[] modelPaths = new String[statuses.length];
    for (int i = 0; i < statuses.length; i++) {
      modelPaths[i] = statuses[i].getPath().toUri().toString();
    }
    conf.setStrings(MODEL_PATHS, modelPaths);
  }

  public static Path[] getModelPaths(Configuration conf) {
    String[] modelPathNames = conf.getStrings(MODEL_PATHS);
    if (modelPathNames == null || modelPathNames.length == 0) {
      return null;
    }
    Path[] modelPaths = new Path[modelPathNames.length];
    for (int i = 0; i < modelPathNames.length; i++) {
      modelPaths[i] = new Path(modelPathNames[i]);
    }
    return modelPaths;
  }

  public static void main(String[] args) throws Exception {
    ToolRunner.run(new Configuration(), new CVB0Driver(), args);
  }
}

@MessageConnectorSummary(
    info = "Connects to a file.",
    addressFormat = "file:<filename>"
)
public class FileConnector extends AbstractMessageConnector implements FileConnectorMBean, FileReaderParams {
    static final long NA_TIME = Long.MIN_VALUE;

    private String address;
    private StreamCompression compression;
    private FileFormat format;
    private long startTime = NA_TIME;
    private long stopTime = NA_TIME;
    private long delayTime = NA_TIME;
    private double speed = 1;
    private TimestampsType time;
    private boolean ignoreTime;
    private boolean cycle;
    private MessageType readAs;
    private boolean schemeKnown;
    private MessageType resyncOn;
    private String fieldReplacer;

    private volatile FileReaderHandler handler;

    public FileConnector(ApplicationConnectionFactory factory, String address) {
        super(factory);
        if (address == null)
            throw new NullPointerException();
        QDConfig.setDefaultProperties(this, FileConnectorMBean.class, MessageConnector.class.getName());
        QDConfig.setDefaultProperties(this, FileConnectorMBean.class, FileConnector.class.getName());
        this.address = address;
    }

    @Override
    public synchronized void start() {
        if (handler != null)
            return;
        log.info("Starting FileConnector to " + LogUtil.hideCredentials(getAddress()));
        handler = new FileReaderHandler(this);
        handler.start();
        notifyMessageConnectorListeners();
    }

    @Override
    protected synchronized Joinable stopImpl() {
        FileReaderHandler handler = this.handler;
        this.handler = null; if (handler != null) {
            log.info("Stopping FileConnector");
            handler.close();
        }
        return handler;
    }

    @Override
    protected synchronized void handlerClosed(AbstractConnectionHandler handler) {
        if (handler != this.handler)
            return;
        this.handler = null;
    }


    @Override
    public synchronized String getAddress() {
        return address;
    }

    @Override
    public synchronized void setAddress(String address) {
        if (!address.equals(this.address)) {  log.info("Setting address=" + LogUtil.hideCredentials(address));
            this.address = address;
            reconfigure();
        }
    }

    @Override
    public synchronized StreamCompression getCompression() {
        return compression;
    }

    @Override
    @MessageConnectorProperty("File compression (one of \"none\", \"gzip\", or \"zip\"), " +
        "autodetect by default from file header")
    public void setCompression(StreamCompression compression) {
        if (!compression.equals(this.compression)) {
            log.info("Setting setCompression=" + compression);
            this.compression = compression;
            reconfigure();
        }
    }

    @Override
    public synchronized FileFormat getFormat() {
        return format;
    }

    @Override
    @MessageConnectorProperty("File format (one of \"binary\", \"text\", \"csv\", or \"blob:<record>:<symbol>\"), " +
        "autodetect by default from file header")
    public synchronized void setFormat(FileFormat format) {
        if (!format.equals(this.format)) {
            log.info("Setting format=" + format);
            this.format = format;
            reconfigure();
        }
    }

    @Override
    public boolean isActive() {
        return handler != null;
    }

    @Override
    public MessageConnectorState getState() {
        FileReaderHandler handler = this.handler;
        return handler != null ? handler.getHandlerState() : MessageConnectorState.DISCONNECTED;
    }

    @Override
    public int getConnectionCount() {
        return getState() == MessageConnectorState.CONNECTED ? 1 : 0;
    }

    @Override
    public EndpointStats retrieveCompleteEndpointStats() {
        EndpointStats stats = super.retrieveCompleteEndpointStats();
        FileReaderHandler handler = this.handler; if (handler != null && handler.getHandlerState() == MessageConnectorState.CONNECTED) {
            stats.addActiveConnectionCount(1);
            stats.addConnectionStats(handler.getConnectionStats());
        }
        return stats;
    }

    private void checkTimeAndIgnoreTime(TimestampsType time, boolean ignoreTime) {
        if (ignoreTime && time != null)
            throw new IllegalArgumentException("Cannot set both ignoreTime and time. Use time=none to ignore time");
    }

    private void checkStartAndStop(long startTime, long stopTime) {
        if (startTime != NA_TIME && stopTime != NA_TIME && stopTime <= startTime)
            throw new IllegalArgumentException("Cannot set stop at or before start");
    }

    private void checkStartAndDelay(long startTime, long delayTime) {
        if (startTime != NA_TIME && delayTime != NA_TIME)
            throw new IllegalArgumentException("Cannot set both start and delay");
    }

    private void checkStopAndDelay(long stopTime, long delayTime) {
        if (stopTime != NA_TIME && delayTime != NA_TIME)
            throw new IllegalArgumentException("Cannot set both stop and delay");
    }

    private void checkSpeedAndDelay(double speed, long delayTime) {
        if (speed != 1 && delayTime != NA_TIME)
            throw new IllegalArgumentException("Cannot set both speed and delay");
    }

    private void checkCycleAndDelay(boolean cycle, long delayTime) {
        if (cycle && delayTime != NA_TIME)
            throw new IllegalArgumentException("Cannot set both cycle and delay");
    }

    @Override
    public synchronized long getStartTime() {
        return startTime;
    }

    @Override
    public synchronized Date getStart() {
        return startTime == NA_TIME ? null : new Date(startTime);
    }

    @Override
    @MessageConnectorProperty("Time to start playing from, use [YYYYMMDD-]HHMMSS[.sss][tz] format")
    public synchronized void setStart(Date time) {
        long startTime = time == null ? NA_TIME : time.getTime();
        if (this.startTime != startTime) {
            checkStartAndStop(startTime, stopTime);
            checkStartAndDelay(startTime, delayTime);
            log.info("Setting start=" + TimeFormat.DEFAULT.format(time));
            this.startTime = startTime;
            reconfigure();
        }
    }

    @Override
    public synchronized long getStopTime() {
        return stopTime;
    }

    @Override
    public synchronized Date getStop() {
        return stopTime == NA_TIME ? null : new Date(stopTime);
    }

    @Override
    @MessageConnectorProperty("Time to stop playing, use [YYYYMMDD-]HHMMSS[.sss][tz] format")
    public synchronized void setStop(Date time) {
        long stopTime = time == null ? NA_TIME : time.getTime();
        if (this.stopTime != stopTime) {
            checkStartAndStop(startTime, stopTime);
            checkStopAndDelay(stopTime, delayTime);
            log.info("Setting stop=" + TimeFormat.DEFAULT.format(time));
            this.stopTime = stopTime;
            reconfigure();
        }
    }

    @Override
    public synchronized long getDelayTime() {
        return delayTime;
    }

    @Override
    @Monitored(name = "delayed", description = "Configured delaying time period")
    public synchronized TimePeriod getDelayed() {
        return delayTime == NA_TIME ? null : TimePeriod.valueOf(delayTime);
    }

    @Override
    @MessageConnectorProperty("Delay relatively to current time")
    public synchronized void setDelayed(TimePeriod delay) {
        long delayTime = delay == null ? NA_TIME : delay.getTime();
        if (this.delayTime != delayTime) {
            checkStartAndDelay(startTime, delayTime);
            checkStopAndDelay(stopTime, delayTime);
            checkSpeedAndDelay(speed, delayTime);
            checkCycleAndDelay(cycle, delayTime);
            log.info("Setting delayed=" + delay);
            this.delayTime = delayTime;
            reconfigure();
        }
    }

    @Override
    public double getSpeed() {
        return speed;
    }

    @Override
    @MessageConnectorProperty("Replay speed vs real time, use \"max\" to read file as fast as possible, defaults to 1")
    public void setSpeed(double speed) {
        if (!(speed > 0)) throw new IllegalArgumentException("Invalid speed=" + speed);
        if (this.speed != speed) {
            checkSpeedAndDelay(speed, delayTime);
            log.info("Setting speed=" + (speed == Double.MAX_VALUE ? "max" : String.valueOf(speed)));
            this.speed = speed;
            reconfigure();
        }
    }

    @Override
    public synchronized TimestampsType getTime() {
        return time;
    }

    @Override
    @MessageConnectorProperty("Time format (one of \"none\", \"long\", \"text\", or \"event\"), autodetect by default")
    public synchronized void setTime(TimestampsType time) {
        if (this.time != time) {
            checkTimeAndIgnoreTime(time, ignoreTime);
            log.info("Setting time=" + time);
            this.time = time;
            reconfigure();
        }
    }

    @Override
    public synchronized boolean isIgnoreTime() {
        return ignoreTime;
    }

    @Override
    @MessageConnectorProperty("Ignores \".time\" files even if they present")
    public synchronized void setIgnoreTime(boolean ignore) {
        if (ignoreTime != ignore) {
            checkTimeAndIgnoreTime(time, ignore);
            log.warn("SETTING DEPRECATED ignoreTime=" + ignore + ". Set speed=max to replay as fast as possible.");
            ignoreTime = ignore;
            reconfigure();
        }
    }

    @Override
    public synchronized boolean isCycle() {
        return cycle;
    }

    @Override
    @MessageConnectorProperty("Enables cycle playback")
    public synchronized void setCycle(boolean cycle) {
        if (this.cycle != cycle) {
            checkCycleAndDelay(cycle, delayTime);
            log.info("Setting cycle=" + cycle);
            this.cycle = cycle;
            reconfigure();
        }
    }

    @Override
    public synchronized MessageType getReadAs() {
        return readAs;
    }

    @Override
    @MessageConnectorProperty("Overrides the type of read messages (one of \"ticker_data\", \"stream_data\", \"history_data\", or \"raw_data\", works for binary tape files only)")
    public synchronized void setReadAs(MessageType readAs) {
        if (this.readAs != readAs) {
            log.info("Setting readAs=" + readAs);
            this.readAs = readAs;
            reconfigure();
        }
    }

    @Override
    public synchronized boolean isSchemeKnown() {
        return schemeKnown;
    }

    @Override
    @MessageConnectorProperty("Enables parsing of files without record descriptions")
    public synchronized void setSchemeKnown(boolean schemeKnown) {
        if (this.schemeKnown != schemeKnown) {
            log.info("Setting schemeKnown=" + schemeKnown);
            this.schemeKnown = schemeKnown;
            reconfigure();
        }
    }

    @Override
    public synchronized  MessageType getResyncOn() {
        return resyncOn;
    }

    @Override
    @MessageConnectorProperty("Message type to resync partial binary stream captured with tcpdump")
    public synchronized void setResyncOn(MessageType resyncOn) {
        if (this.resyncOn != resyncOn) {
            log.info("Setting resyncOn=" + resyncOn);
            this.resyncOn = resyncOn;
            reconfigure();
        }
    }

    @Override
    @Monitored(name = "delay_actual", description = "Actual delay in ms or delay time if not connected yet")
    public long getDelayActual() {
        FileReaderHandler handler = this.handler;
        return handler != null ? handler.getDelayActual() : getDelayTime();
    }

    @Override
    public synchronized String getFieldReplacer() {
        return fieldReplacer;
    }

    @Override
    @MessageConnectorProperty("Configuration of field replacers")
    public synchronized void setFieldReplacer(String fieldReplacer) {
        if (!fieldReplacer.equals(this.fieldReplacer)) {
            log.info("Setting fieldReplacers=" + fieldReplacer);
            this.fieldReplacer = fieldReplacer;
            reconfigure();
        }
    }
}

