public class XmlBeanPropertyMeta extends ExtendedBeanPropertyMeta {

	public static final XmlBeanPropertyMeta DEFAULT = new XmlBeanPropertyMeta();

	private Namespace namespace = null;
	private XmlFormat xmlFormat = XmlFormat.DEFAULT;
	private String childName;
	private final XmlMetaProvider xmlMetaProvider;

	public XmlBeanPropertyMeta(BeanPropertyMeta bpm, XmlMetaProvider mp) {
		super(bpm);
		this.xmlMetaProvider = mp;

		for (Xml xml : bpm.getAnnotations(Xml.class))
			findXmlInfo(xml, mp);

		if (namespace == null)
			namespace = mp.getXmlClassMeta(bpm.getBeanMeta().getClassMeta()).getNamespace();
	}

	private XmlBeanPropertyMeta() {
		super(null);
		this.xmlMetaProvider = null;
	}

	public Namespace getNamespace() {
		return namespace;
	}

	public XmlFormat getXmlFormat() {
		return xmlFormat;
	}

	public String getChildName() {
		return childName;
	}

	private void findXmlInfo(Xml xml, MetaProvider mp) {
		if (xml == null)
			return;
		BeanPropertyMeta bpm = getBeanPropertyMeta();
		ClassMeta<?> cmProperty = bpm.getClassMeta();
		ClassMeta<?> cmBean = bpm.getBeanMeta().getClassMeta();
		String name = bpm.getName();

		List<Xml> xmls = bpm.getAllAnnotationsParentFirst(Xml.class);
		List<XmlSchema> schemas = bpm.getAllAnnotationsParentFirst(XmlSchema.class);
		namespace = XmlUtils.findNamespace(xmls, schemas);

		if (xmlFormat == XmlFormat.DEFAULT)
			xmlFormat = xml.format();

		boolean isCollection = cmProperty.isCollectionOrArray();

		String cen = xml.childName();
		if ((! cen.isEmpty()) && (! isCollection))
			throw new BeanRuntimeException(cmProperty.getInnerClass(),
				"Annotation error on property ''{0}''.  @Xml.childName can only be specified on collections and arrays.", name);

		if (xmlFormat == XmlFormat.COLLAPSED) {
			if (isCollection) {
				if (cen.isEmpty() && xmlMetaProvider != null)
					cen = xmlMetaProvider.getXmlClassMeta(cmProperty).getChildName();
				if (cen == null || cen.isEmpty())
					cen = cmProperty.getElementType().getDictionaryName();
				if (cen == null || cen.isEmpty())
					cen = name;
			} else {
				throw new BeanRuntimeException(cmBean.getInnerClass(),
					"Annotation error on property ''{0}''.  @Xml.format=COLLAPSED can only be specified on collections and arrays.", name);
			}
			if (cen.isEmpty() && isCollection)
				cen = cmProperty.getDictionaryName();
		}

		if (! cen.isEmpty())
			childName = cen;
	}
}

public class LongWritable implements WritableComparable {
    private long value;

    public LongWritable() {}

    public LongWritable(@JsonProperty("value") long value) {
        set(value);
    }

    public void set(long value) {
        this.value = value;
    }

    public long get() {
        return value;
    }

    public void readFields(DataInput in) throws IOException {
        value = in.readLong();
    }

    @Override
    public void writeType(DataOutput out) throws IOException {
        out.writeShort(WritableType.Long.typeIdx());
    }

    public void write(DataOutput out) throws IOException {
        out.writeLong(value);
    }

    public boolean fuzzyEquals(Writable o, double tolerance) {
        double other;
        if (o instanceof IntWritable){
            other = ((IntWritable) o).toDouble();
        } else if (o instanceof  LongWritable) {
            other = ((LongWritable) o).toDouble();
        } else if (o instanceof ByteWritable) {
            other = ((ByteWritable) o).toDouble();
        } else if (o instanceof  DoubleWritable) {
            other = ((DoubleWritable) o).toDouble();
        } else if (o instanceof  FloatWritable) {
            other = ((FloatWritable) o).toDouble();
        } else { return false; }
        return DoubleMath.fuzzyEquals(this.value, other, tolerance);
    }

    public boolean equals(Object o) {
        if (o instanceof ByteWritable){
            ByteWritable other = (ByteWritable) o;
            return this.value == other.get();
        }
        if (o instanceof LongWritable){
            LongWritable other = (LongWritable) o;
            return this.value == other.get();
        }
        if (o instanceof IntWritable){
            IntWritable other = (IntWritable) o;
            return this.value == other.get();
        }
        else { return false; }
    }

    public int hashCode() {
        return (int)(value ^ value >>> 32);
    }

    public int compareTo(Object o) {
        long thisValue = this.value;
        long thatValue = ((LongWritable) o).value;
        return (thisValue < thatValue ? -1 : (thisValue == thatValue ? 0 : 1));
    }

    public String toString() {
        return Long.toString(value);
    }

    public static class Comparator extends WritableComparator {
        public Comparator() {
            super(LongWritable.class);
        }

        public int compare(byte[] b1, int s1, int l1, byte[] b2, int s2, int l2) {
            long thisValue = readLong(b1, s1);
            long thatValue = readLong(b2, s2);
            return (thisValue < thatValue ? -1 : (thisValue == thatValue ? 0 : 1));
        }
    }

    public static class DecreasingComparator extends Comparator {
        public int compare(WritableComparable a, WritableComparable b) {
            return -super.compare(a, b);
        }

        public int compare(byte[] b1, int s1, int l1, byte[] b2, int s2, int l2) {
            return -super.compare(b1, s1, l1, b2, s2, l2);
        }
    }

    static { WritableComparator.define(LongWritable.class, new Comparator());
    }

    @Override
    public double toDouble() {
        return value;
    }

    @Override
    public float toFloat() {
        return value;
    }

    @Override
    public int toInt() {
        return (int) value;
    }

    @Override
    public long toLong() {
        return value;
    }

    @Override
    public WritableType getType() {
        return WritableType.Long;
    }

}

@SuppressWarnings("deprecation")
final class LazyConnectExchangeClient implements ExchangeClient {

    private final static Logger logger = LoggerFactory.getLogger(LazyConnectExchangeClient.class);

    private final URL url;
    private final ExchangeHandler requestHandler;
    private volatile ExchangeClient client;
    private final Lock connectLock = new ReentrantLock();
    private final boolean initialState;

    protected final boolean requestWithWarning;

    static final String REQUEST_WITH_WARNING_KEY = "lazyclient_request_with_warning";

    private AtomicLong warningcount = new AtomicLong(0);

    public LazyConnectExchangeClient(URL url, ExchangeHandler requestHandler) {
        this.url = url.addParameter(Constants.SEND_RECONNECT_KEY, Boolean.TRUE.toString());
        this.requestHandler = requestHandler;
        this.initialState = url.getParameter(Constants.LAZY_CONNECT_INITIAL_STATE_KEY, Constants.DEFAULT_LAZY_CONNECT_INITIAL_STATE);
        this.requestWithWarning = url.getParameter(REQUEST_WITH_WARNING_KEY, false);
    }


    private void initClient() throws RemotingException {
        if (client != null)
            return;
        if (logger.isInfoEnabled()) {
            logger.info("Lazy connect to " + url);
        }
        connectLock.lock();
        try {
            if (client != null)
                return;
            this.client = Exchangers.connect(url, requestHandler);
        } finally {
            connectLock.unlock();
        }
    }

    public ResponseFuture request(Object request) throws RemotingException {
        warning(request);
        initClient();
        return client.request(request);
    }

    public URL getUrl() {
        return url;
    }

    public InetSocketAddress getRemoteAddress() {
        if (client == null) {
            return InetSocketAddress.createUnresolved(url.getHost(), url.getPort());
        } else {
            return client.getRemoteAddress();
        }
    }

    public ResponseFuture request(Object request, int timeout) throws RemotingException {
        warning(request);
        initClient();
        return client.request(request, timeout);
    }

    private void warning(Object request) {
        if (requestWithWarning) {
            if (warningcount.get() % 5000 == 0) {
                logger.warn(new IllegalStateException("safe guard client , should not be called ,must have a bug."));
            }
            warningcount.incrementAndGet();
        }
    }

    public ChannelHandler getChannelHandler() {
        checkClient();
        return client.getChannelHandler();
    }

    public boolean isConnected() {
        if (client == null) {
            return initialState;
        } else {
            return client.isConnected();
        }
    }

    public InetSocketAddress getLocalAddress() {
        if (client == null) {
            return InetSocketAddress.createUnresolved(NetUtils.getLocalHost(), 0);
        } else {
            return client.getLocalAddress();
        }
    }

    public ExchangeHandler getExchangeHandler() {
        return requestHandler;
    }

    public void send(Object message) throws RemotingException {
        initClient();
        client.send(message);
    }

    public void send(Object message, boolean sent) throws RemotingException {
        initClient();
        client.send(message, sent);
    }

    public boolean isClosed() {
        if (client != null)
            return client.isClosed();
        else
            return true;
    }

    public void close() {
        if (client != null)
            client.close();
    }

    public void close(int timeout) {
        if (client != null)
            client.close(timeout);
    }

    public void reset(URL url) {
        checkClient();
        client.reset(url);
    }

    @Deprecated
    public void reset(Parameters parameters) {
        reset(getUrl().addParameters(parameters.getParameters()));
    }

    public void reconnect() throws RemotingException {
        checkClient();
        client.reconnect();
    }

    public Object getAttribute(String key) {
        if (client == null) {
            return null;
        } else {
            return client.getAttribute(key);
        }
    }

    public void setAttribute(String key, Object value) {
        checkClient();
        client.setAttribute(key, value);
    }

    public void removeAttribute(String key) {
        checkClient();
        client.removeAttribute(key);
    }

    public boolean hasAttribute(String key) {
        if (client == null) {
            return false;
        } else {
            return client.hasAttribute(key);
        }
    }

    private void checkClient() {
        if (client == null) {
            throw new IllegalStateException(
                    "LazyConnectExchangeClient state error. the client has not be init .url:" + url);
        }
    }
}

public abstract class AbstractReactiveFlushingEventListener {

	private static final Log LOG = LoggerFactory.make( Log.class, MethodHandles.lookup() );

	protected CompletionStage<Void> performExecutions(EventSource session) {
		LOG.trace( "Executing flush" );

		return voidFuture()
				.thenCompose( v -> {
					session.getJdbcCoordinator().flushBeginning();
					session.getPersistenceContext().setFlushing( true );
					actionQueue( session ).prepareActions();
					return actionQueue( session ).executeActions();
				} )
				.whenComplete( (v, x) -> {
					session.getPersistenceContext().setFlushing( false );
					session.getJdbcCoordinator().flushEnding();
				} );
	}

	private ReactiveActionQueue actionQueue(EventSource session) {
		return session.unwrap( ReactiveSession.class ).getReactiveActionQueue();
	}

	protected CompletionStage<Void> flushEverythingToExecutions(FlushEvent event) throws HibernateException {

		LOG.trace( "Flushing session" );

		EventSource session = event.getSession();

		final PersistenceContext persistenceContext = session.getPersistenceContextInternal();
		session.getInterceptor().preFlush( persistenceContext.managedEntitiesIterator() );

		return prepareEntityFlushes(session, persistenceContext)
				.thenAccept( v -> {
					prepareCollectionFlushes( persistenceContext );
					persistenceContext.setFlushing(true);
					try {
						int entityCount = flushEntities(event, persistenceContext);
						int collectionCount = flushCollections(session, persistenceContext);

						event.setNumberOfEntitiesProcessed(entityCount);
						event.setNumberOfCollectionsProcessed(collectionCount);
					}
					finally {
						persistenceContext.setFlushing(false);
					}
				} );

		}

	private CompletionStage<Void> prepareEntityFlushes(EventSource session, PersistenceContext persistenceContext) throws HibernateException {

		LOG.debug( "Processing flush-time cascades" );

		IdentitySet copiedAlready = new IdentitySet( 10 );
		Map.Entry<Object, EntityEntry>[] entries = persistenceContext.reentrantSafeEntityEntries();
		return loop(
				entries,
				index -> flushable( entries[index].getValue() ),
				index -> cascadeOnFlush( session, entries[index].getValue().getPersister(), entries[index].getKey(), copiedAlready ) );
	}

	private static boolean flushable(EntityEntry entry) {
		Status status = entry.getStatus();
		return status == Status.MANAGED
			|| status == Status.SAVING
			|| status == Status.READ_ONLY;
	}

	private void prepareCollectionFlushes(PersistenceContext persistenceContext) throws HibernateException {

		LOG.debug( "Dirty checking collections" );
		persistenceContext.forEachCollectionEntry( (pc,ce) -> ce.preFlush( pc ), true );
	}

	private int flushEntities(final FlushEvent event, final PersistenceContext persistenceContext) throws HibernateException {

		LOG.trace( "Flushing entities and processing referenced collections" );

		final EventSource source = event.getSession();
		final Iterable<FlushEntityEventListener> flushListeners =
				source.getFactory()
						.getFastSessionServices()
						.eventListenerGroup_FLUSH_ENTITY
						.listeners();

		final Map.Entry<Object,EntityEntry>[] entityEntries = persistenceContext.reentrantSafeEntityEntries();
		final int count = entityEntries.length;

		for ( Map.Entry<Object,EntityEntry> me : entityEntries ) {

			EntityEntry entry = me.getValue();
			Status status = entry.getStatus();

			if ( status != Status.LOADING && status != Status.GONE ) {
				final FlushEntityEvent entityEvent = new FlushEntityEvent( source, me.getKey(), entry );
				for ( FlushEntityEventListener listener : flushListeners ) {
					listener.onFlushEntity( entityEvent );
				}
			}
		}

		source.getActionQueue().sortActions();

		return count;
	}

	private int flushCollections(final EventSource session, final PersistenceContext persistenceContext) throws HibernateException {
		LOG.trace( "Processing unreferenced collections" );

		final int count = persistenceContext.getCollectionEntriesSize();

		persistenceContext.forEachCollectionEntry(
				(persistentCollection, collectionEntry) -> {
					if ( !collectionEntry.isReached() && !collectionEntry.isIgnore() ) {
						Collections.processUnreachableCollection( persistentCollection, session );
					}
				}, true );

		LOG.trace( "Scheduling collection removes/(re)creates/updates" );

		final ReactiveActionQueue actionQueue = session.unwrap( ReactiveSession.class).getReactiveActionQueue();
		final Interceptor interceptor = session.getInterceptor();
		persistenceContext.forEachCollectionEntry(
				(coll, ce) -> {
					if ( ce.isDorecreate() ) {
						interceptor.onCollectionRecreate( coll, ce.getCurrentKey() );
						actionQueue.addAction(
								new ReactiveCollectionRecreateAction(
										coll,
										ce.getCurrentPersister(),
										ce.getCurrentKey(),
										session
								)
						);
					}
					if ( ce.isDoremove() ) {
						interceptor.onCollectionRemove( coll, ce.getLoadedKey() );
						actionQueue.addAction(
								new ReactiveCollectionRemoveAction(
										coll,
										ce.getLoadedPersister(),
										ce.getLoadedKey(),
										ce.isSnapshotEmpty( coll ),
										session
								)
						);
					}
					if ( ce.isDoupdate() ) {
						interceptor.onCollectionUpdate( coll, ce.getLoadedKey() );
						actionQueue.addAction(
								new ReactiveCollectionUpdateAction(
										coll,
										ce.getLoadedPersister(),
										ce.getLoadedKey(),
										ce.isSnapshotEmpty( coll ),
										session
								)
						);
					}
					if ( !coll.wasInitialized() && coll.hasQueuedOperations() ) {
						actionQueue.addAction(
								new QueuedOperationCollectionAction(
										coll,
										ce.getLoadedPersister(),
										ce.getLoadedKey(),
										session
								)
						);
					}
				}, true );

		actionQueue.sortCollectionActions();

		return count;
	}

	private CompletionStage<Void> cascadeOnFlush(
			EventSource session,
			EntityPersister persister,
			Object object,
			IdentitySet copiedAlready)
			throws HibernateException {
		return new Cascade<>(
				CascadingActions.PERSIST_ON_FLUSH,
				CascadePoint.BEFORE_FLUSH,
				persister, object, copiedAlready, session
		).cascade();
	}

	protected void postFlush(SessionImplementor session) throws HibernateException {

		LOG.trace( "Post flush" );

		final PersistenceContext persistenceContext = session.getPersistenceContextInternal();
		persistenceContext.clearCollectionsByKey();

		persistenceContext.getBatchFetchQueue().clear();

		persistenceContext.forEachCollectionEntry(
				(persistentCollection, collectionEntry) -> {
					collectionEntry.postFlush( persistentCollection );
					if ( collectionEntry.getLoadedPersister() == null ) {
						persistentCollection.unsetSession( session );
						persistenceContext.removeCollectionEntry( persistentCollection );
					}
					else {
						CollectionKey collectionKey = new CollectionKey(
								collectionEntry.getLoadedPersister(),
								collectionEntry.getLoadedKey()
						);
						persistenceContext.addCollectionByKey( collectionKey, persistentCollection );
					}
				}, true
		);
	}

	protected void postPostFlush(SessionImplementor session) {
		session.getInterceptor().postFlush( session.getPersistenceContextInternal().managedEntitiesIterator() );
	}

}

public final class PackageMatcher {
    private static final String OPT_LETTERS_AT_START = "(?:^\\w*)?";
    private static final String OPT_LETTERS_AT_END = "(?:\\w*$)?";
    private static final String ARBITRARY_PACKAGES = "\\.(?:\\w+\\.)*";
    private static final String TWO_DOTS_REGEX = String.format("(?:%s%s%s)?", OPT_LETTERS_AT_START, ARBITRARY_PACKAGES, OPT_LETTERS_AT_END);

    private static final String TWO_STAR_CAPTURE_LITERAL = "(**)";
    private static final String TWO_STAR_CAPTURE_REGEX = "(\\w+(?:\\.\\w+)*)";
    static final String TWO_STAR_REGEX_MARKER = "#%#%#";

    private static final Set<Character> PACKAGE_CONTROL_SYMBOLS = ImmutableSet.of('*', '(', ')', '.');

    private final String packageIdentifier;
    private final Pattern packagePattern;

    private PackageMatcher(String packageIdentifier) {
        validate(packageIdentifier);

        this.packageIdentifier = packageIdentifier;
        this.packagePattern = Pattern.compile(convertToRegex(packageIdentifier));
    }

    private void validate(String packageIdentifier) {
        if (packageIdentifier.contains("...")) {
            throw new IllegalArgumentException("Package Identifier may not contain more than two '.' in a row");
        }
        if (packageIdentifier.replace("(**)", "").contains("**")) {
            throw new IllegalArgumentException("Package Identifier may not contain more than one '*' in a row");
        }
        if (packageIdentifier.contains("(..)")) {
            throw new IllegalArgumentException("Package Identifier does not support capturing via (..), use (**) instead");
        }
        validateCharacters(packageIdentifier);
    }

    private void validateCharacters(String packageIdentifier) {
        for (int i = 0; i < packageIdentifier.length(); i++) {
            char c = packageIdentifier.charAt(i);
            if (!Character.isJavaIdentifierPart(c) && !PACKAGE_CONTROL_SYMBOLS.contains(c)) {
                throw new IllegalArgumentException(
                        String.format("Package Identifier '%s' may only consist of valid java identifier parts or the symbols '.)(*'",
                                packageIdentifier));
            }
        }
    }

    private String convertToRegex(String packageIdentifier) {
        return packageIdentifier.
                replace(TWO_STAR_CAPTURE_LITERAL, TWO_STAR_REGEX_MARKER).
                replace("*", "\\w+").
                replace(".", "\\.").
                replace(TWO_STAR_REGEX_MARKER, TWO_STAR_CAPTURE_REGEX).
                replace("\\.\\.", TWO_DOTS_REGEX);
    }

    @PublicAPI(usage = ACCESS)
    public static PackageMatcher of(String packageIdentifier) {
        return new PackageMatcher(packageIdentifier);
    }

    @PublicAPI(usage = ACCESS)
    public boolean matches(String aPackage) {
        return packagePattern.matcher(aPackage).matches();
    }

    @PublicAPI(usage = ACCESS)
    public Optional<Result> match(String aPackage) {
        Matcher matcher = packagePattern.matcher(aPackage);
        return matcher.matches() ? Optional.of(new Result(matcher)) : Optional.<Result>empty();
    }

    @Override
    public String toString() {
        return "PackageMatcher{" + packageIdentifier + '}';
    }

    public static final class Result {
        private final Matcher matcher;

        private Result(Matcher matcher) {
            this.matcher = matcher;
        }

        @PublicAPI(usage = ACCESS)
        public int getNumberOfGroups() {
            return matcher.groupCount();
        }

        @PublicAPI(usage = ACCESS)
        public String getGroup(int number) {
            return matcher.group(number);
        }
    }

    @PublicAPI(usage = ACCESS)
    public static final Function<Result, List<String>> TO_GROUPS = new Function<Result, List<String>>() {
        @Override
        public List<String> apply(Result input) {
            List<String> result = new ArrayList<>();
            for (int i = 0; i < input.getNumberOfGroups(); i++) {
                result.add(input.getGroup(i + 1));
            }
            return result;
        }
    };
}

public class GridSqlCreateIndex extends GridSqlStatement {
    private String schemaName;

    private String tblName;

    private boolean ifNotExists;

    private QueryIndex idx;

    public String schemaName() {
        return schemaName;
    }

    public void schemaName(String schemaName) {
        this.schemaName = schemaName;
    }

    public String tableName() {
        return tblName;
    }

    public void tableName(String tblName) {
        this.tblName = tblName;
    }

    public boolean ifNotExists() {
        return ifNotExists;
    }

    public void ifNotExists(boolean ifNotExists) {
        this.ifNotExists = ifNotExists;
    }

    public QueryIndex index() {
        return idx;
    }

    public void index(QueryIndex idx) {
        this.idx = idx;
    }

    @Override public String getSQL() {
        StringBuilder sb = new StringBuilder("CREATE ")
            .append(idx.getIndexType() == QueryIndexType.GEOSPATIAL ? "SPATIAL " : "")
            .append("INDEX ").append(ifNotExists ? "IF NOT EXISTS " : "")
            .append(Parser.quoteIdentifier(schemaName, true)).append('.')
            .append(Parser.quoteIdentifier(idx.getName(), true)).append(" ON ")
            .append(Parser.quoteIdentifier(tblName, true)).append(" (");

        boolean first = true;

        for (Map.Entry<String, Boolean> e : idx.getFields().entrySet()) {
            if (first)
                first = false;
            else
                sb.append(", ");

            sb.append(Parser.quoteIdentifier(e.getKey(), true)).append(e.getValue() ? " ASC" : " DESC");
        }

        sb.append(')');

        return sb.toString();
    }
}

public abstract class TileEntityUpdateable extends TileEntity implements ITileWrapper {

    @Nullable
    private Coord4D cachedCoord;
    private boolean cacheCoord;

    public TileEntityUpdateable(TileEntityType<?> type) {
        super(type);
    }

    protected void cacheCoord() {
        cacheCoord = true;
        updateCoord();
    }

    @Nonnull
    protected World getWorldNN() {
        return Objects.requireNonNull(getLevel(), "getWorldNN called before world set");
    }

    public boolean isRemote() {
        return getWorldNN().isClientSide();
    }

    public void markDirtyComparator() {
    }

    protected void updateBlockState(@Nonnull BlockState newState) {
        this.blockState = newState;
    }

    @Override
    public void setChanged() {
        markDirty(true);
    }

    public void markDirty(boolean recheckBlockState) {
        if (level != null) {
            if (recheckBlockState) {
                updateBlockState(level.getBlockState(worldPosition));
            }
            WorldUtils.markChunkDirty(level, worldPosition);
            if (!isRemote()) {
                markDirtyComparator();
            }
        }
    }

    @Nullable
    @Override
    public SUpdateTileEntityPacket getUpdatePacket() {
        return new SUpdateTileEntityPacket(getBlockPos(), 0, getUpdateTag());
    }

    @Override
    public void handleUpdateTag(BlockState state, @Nonnull CompoundNBT tag) {
        super.load(state, tag);
    }

    @Nonnull
    @Override
    public CompoundNBT getUpdateTag() {
        return getReducedUpdateTag();
    }

    @Nonnull
    public CompoundNBT getReducedUpdateTag() {
        return super.getUpdateTag();
    }

    @Override
    public void onDataPacket(NetworkManager net, SUpdateTileEntityPacket pkt) {
        if (isRemote() && net.getDirection() == PacketDirection.CLIENTBOUND) {
            handleUpdatePacket(pkt.getTag());
        }
    }

    public void handleUpdatePacket(@Nonnull CompoundNBT tag) {
        handleUpdateTag(getBlockState(), tag);
    }

    public void sendUpdatePacket() {
        sendUpdatePacket(this);
    }

    public void sendUpdatePacket(TileEntity tracking) {
        if (isRemote()) {
            Mekanism.logger.warn("Update packet call requested from client side", new IllegalStateException());
        } else if (isRemoved()) {
            Mekanism.logger.warn("Update packet call requested for removed tile", new IllegalStateException());
        } else {
            Mekanism.packetHandler.sendToAllTracking(new PacketUpdateTile(this), tracking);
        }
    }

    @Override
    public World getTileWorld() {
        return level;
    }

    @Override
    public BlockPos getTilePos() {
        return worldPosition;
    }

    @Override
    public double getViewDistance() {
        return MekanismConfig.client.terRange.get();
    }

    @Override
    public void load(@Nonnull BlockState state, @Nonnull CompoundNBT nbt) {
        super.load(state, nbt);
        updateCoord();
    }

    @Override
    public void setLevelAndPosition(@Nonnull World world, @Nonnull BlockPos pos) {
        super.setLevelAndPosition(world, pos);
        updateCoord();
    }

    @Override
    public void setPosition(@Nonnull BlockPos pos) {
        super.setPosition(pos);
        updateCoord();
    }

    private void updateCoord() {
        if (cacheCoord && level != null) {
            cachedCoord = new Coord4D(worldPosition, level);
        }
    }

    @Override
    public Coord4D getTileCoord() {
        return cacheCoord && cachedCoord != null ? cachedCoord : ITileWrapper.super.getTileCoord();
    }
}

@RequiredArgsConstructor
public class ChainedFilter<T> implements Filter<T> {
  @NonNull private final List<Filter<T>> filters;

  public static <T> Builder<T> builder() {
    return new Builder<>();
  }

  public boolean apply(final T object) {
    return filters.stream().allMatch(filter -> filter.apply(object));
  }

  @NoArgsConstructor(access = AccessLevel.PRIVATE)
  public static final class Builder<T> {
    private final List<Filter<T>> filters = new ArrayList<>();

    public Builder<T> addFilter(Filter<T> filter) {
      filters.add(filter);
      return this;
    }

    public Filter<T> build() {
      return new ChainedFilter<>(filters);
    }
  }
}

public class Polygon {
    Point[] vertices;

    public Polygon(Point[] vertices) {
        this.vertices = vertices;
    }

    public int getVerticesNumber() {
        return vertices.length;
    }

    public Point[] getVertices() {
        return vertices;
    }

    public void move(Point movement) {
        for (Point vertex : vertices) {
            vertex.translate(movement.x, movement.y);
        }
    }

    public double getArea() {
        double sum = 0;
        for (int i = 0; i < vertices.length; i++) {
            int next = (i + 1) % vertices.length;
            sum += (vertices[i].x * vertices[next].y) - (vertices[i].y * vertices[next].x);
        }
        return sum / 2.0;
    }

    @Override
    public String toString() {
        return "Polygon{" +
                "vertices=" + Arrays.toString(vertices) +
                '}';
    }

    public static void main(String[] args) {
        Point[] vertices = {new Point(0,0), new Point(10, 0), new Point(5, 10)};
        Polygon p = new Polygon(vertices);
        System.out.println(p.getVerticesNumber());
        System.out.println(p.getArea());
        p.move(new Point(-5, -10));
        System.out.println(p);
    }
}

public class AutoShapeDataKit
{
    public static int getColor(Map<String, Integer> schemeColor, Element solidFillElement)
    {
        String val;
        Element clr;
        int color = -1;
        if(solidFillElement.element("srgbClr") != null)
        {
            clr = solidFillElement.element("srgbClr");
            color = (int)Long.parseLong( clr.attributeValue("val"), 16);
            color = (0xFF << 24) | color;
        }
        else if((clr = solidFillElement.element("scrgbClr")) != null)
        {
            int r = Integer.parseInt(clr.attributeValue("r")) * 255 / 100;
            int g = Integer.parseInt(clr.attributeValue("g")) * 255 / 100;
            int b = Integer.parseInt(clr.attributeValue("b")) * 255 / 100;
            return ColorUtil.rgb(r, g, b);
        }
        else if(solidFillElement.element("schemeClr") != null
            || solidFillElement.element("prstClr") != null)
        {
            if (schemeColor != null && schemeColor.size() > 0)
            {
                clr = solidFillElement.element("schemeClr");
                if (clr == null)
                {
                    clr = solidFillElement.element("prstClr");
                }
                val = clr.attributeValue("val");
                if("black".equals(val))
                {
                    color = Color.BLACK;
                }
                else if ("red".equals(val))
                {
                    color = Color.RED;
                }
                else if ("gray".equals(val))
                {
                    color = Color.GRAY;
                }
                else if ("blue".equals(val))
                {
                    color = Color.BLUE;
                }
                else if ("green".equals(val))
                {
                    color = Color.GREEN;
                }
                if (color == -1)
                {
                    color = schemeColor.get(val);   
                }
                if (clr.element("tint") != null)
                {
                    color = ColorUtil.instance().getColorWithTint(color, 
                        Integer.parseInt(clr.element("tint").attributeValue("val")) / 100000.0);
                }
                else if (clr.element("lumOff") != null)
                {
                    color = ColorUtil.instance().getColorWithTint(color, 
                        Integer.parseInt(clr.element("lumOff").attributeValue("val")) / 100000.0);
                }
                else if (clr.element("lumMod") != null)
                {
                    color = ColorUtil.instance().getColorWithTint(color, 
                        Integer.parseInt(clr.element("lumMod").attributeValue("val")) / 100000.0 - 1);
                }
                else if (clr.element("shade") != null)
                {
                    color = ColorUtil.instance().getColorWithTint(color, 
                        -Integer.parseInt(clr.element("shade").attributeValue("val")) / 200000.0);
                }

                if(clr.element("alpha") != null)
                {
                    val = clr.element("alpha").attributeValue("val");
                    if(val != null)
                    {
                        int alpha = (int)(Integer.parseInt(val) / 100000f * 255);
                        color = (0xFFFFFF & color) | (alpha << 24);
                    }
                }
            }
        }
        else if(solidFillElement.element("sysClr") != null)
        {
            clr = solidFillElement.element("sysClr");
            color = Integer.parseInt( clr.attributeValue("lastClr"), 16);
            color = (0xFF << 24) | color;
        }
        return color;
    }

    public static BackgroundAndFill processBackground(IControl control, ZipPackage zipPackage, PackagePart  drawingPart, 
        Element bgPr, Map<String, Integer> schemeColor)
    {
    	try
    	{
    		if (bgPr != null)
            {
                BackgroundAndFill bgFill = new BackgroundAndFill();
                Element fill = bgPr.element("solidFill");
                if (fill != null)
                {
                    bgFill.setFillType(BackgroundAndFill.FILL_SOLID);
                    bgFill.setForegroundColor(getColor(schemeColor, fill));
                    return bgFill;
                }
                else if ((fill = bgPr.element("blipFill")) != null)
                {
                    Element blip = fill.element("blip");
                    if (blip != null && blip.attribute("embed") != null)
                    {
                        String id = blip.attributeValue("embed");
                        if (id != null)
                        {
                            PackageRelationship imageShip = drawingPart.getRelationship(id);
                            if (imageShip != null)
                            {
                                PackagePart picPart = zipPackage.getPart(imageShip.getTargetURI());
                                if (picPart != null)
                                {  
                                	Element tile = fill.element("tile");
                                	if(tile == null)
                                	{
                                		bgFill.setFillType(BackgroundAndFill.FILL_PICTURE);
                                		Element stretch = fill.element("stretch");
                                		if(stretch != null)
                                		{
                                			Element fillRect = stretch.element("fillRect");
                                			if(fillRect != null)
                                			{
                                				PictureStretchInfo stretchInfo = new PictureStretchInfo();
                                				boolean validate = false;
                                    			String str = fillRect.attributeValue("l");
                                    			if(str != null)
                                    			{
                                    				validate = true;
                                    				stretchInfo.setLeftOffset(Float.parseFloat(str) / 100000);
                                    			}

                                    			str = fillRect.attributeValue("r");
                                    			if(str != null)
                                    			{
                                    				validate = true;
                                    				stretchInfo.setRightOffset(Float.parseFloat(str) / 100000);
                                    			}

                                    			str = fillRect.attributeValue("t");
                                    			if(str != null)
                                    			{
                                    				validate = true;
                                    				stretchInfo.setTopOffset(Float.parseFloat(str) / 100000);
                                    			}

                                    			str = fillRect.attributeValue("b");
                                    			if(str != null)
                                    			{
                                    				validate = true;
                                    				stretchInfo.setBottomOffset(Float.parseFloat(str) / 100000);
                                    			}

                                    			if(validate)
                                    			{
                                    				bgFill.setStretch(stretchInfo);
                                    			}                                			
                                			}
                                		}
                                        bgFill.setPictureIndex(control.getSysKit().getPictureManage().addPicture(picPart));
                                	}
                                	else
                                	{
                                		int index = control.getSysKit().getPictureManage().addPicture(picPart);
                                		bgFill.setFillType(BackgroundAndFill.FILL_SHADE_TILE);
                                		TileShader tileShader = ShaderKit.readTile(control.getSysKit().getPictureManage().getPicture(index), tile);
                                		Element alphaModFix = blip.element("alphaModFix");
                                		if(alphaModFix != null)
                                		{
                                			String amt = alphaModFix.attributeValue("amt");
                                			if(amt != null)
                                			{
                                				tileShader.setAlpha(Math.round(Integer.parseInt(amt) / 100000.f * 255));
                                			}
                                		}

                                        bgFill.setShader(tileShader);
                                	}
                                    return bgFill;
                                }
                            }
                        }
                    }
                }
                else if ((fill = bgPr.element("gradFill")) != null)
                {
                    Element gsLst = fill.element("gsLst");
                    {
                    	bgFill.setFillType(ShaderKit.getGradientType(fill));
                        bgFill.setShader(ShaderKit.readGradient(schemeColor, fill));
                        return bgFill;
                    }
                }
                else if ((fill = bgPr.element("fillRef")) != null)
                {
                    bgFill.setFillType(BackgroundAndFill.FILL_SOLID);
                    bgFill.setForegroundColor(getColor(schemeColor, fill));
                    return bgFill;
                }
                else if ((fill = bgPr.element("pattFill")) != null)
                {
                    Element bgClr = fill.element("bgClr");
                    {
                        bgFill.setFillType(BackgroundAndFill.FILL_SOLID);
                        bgFill.setForegroundColor(getColor(schemeColor, bgClr));
                        return bgFill;
                    }
                }
            }
            return null;
    	}
        catch(Exception e)
        {
        	return null;
        }
    }

    public static AbstractShape getAutoShape(IControl control, ZipPackage zipPackage, PackagePart  drawingPart, 
            Element sp, Rectangle rect, Map<String, Integer> schemeColor, int type) throws Exception
    {
    	return getAutoShape(control, zipPackage,  drawingPart,sp, rect, schemeColor, type, false);
    }

    public static AbstractShape getAutoShape(IControl control, ZipPackage zipPackage, PackagePart  drawingPart, 
        Element sp, Rectangle rect, Map<String, Integer> schemeColor, int type, boolean hasTextbox) throws Exception
    {
        if (rect == null || sp == null)
        {
            return null;
        }

        int shapeType = ShapeTypes.ArbitraryPolygon;
        Element spPr = sp.element("spPr");
        if (spPr != null)
        {
            String val;
            Float[] values = null;
            boolean border = true;    

            String name = ReaderKit.instance().getPlaceholderName(sp);
            String spName = sp.getName();
            if (spName.equals("cxnSp"))
            {
                border = true;
                shapeType = ShapeTypes.Line;
            }
            else if (name != null)
            {
                if (name.contains("Text Box") || name.contains("TextBox"))
                {
                    shapeType = ShapeTypes.Rectangle;
                }
            }

            Element prstGeom = spPr.element("prstGeom");
            if (prstGeom != null)
            {
                if (prstGeom.attribute("prst") != null)
                {
                    val = prstGeom.attributeValue("prst");
                    if (val != null && val.length() > 0)
                    {
                        shapeType = AutoShapeTypes.instance().getAutoShapeType(val);
                    }
                }

                Element avLst = prstGeom.element("avLst");
                if (avLst != null)
                {
                    List<Element> gds = avLst.elements("gd");
                    if (gds.size() > 0)
                    {
                        values = new Float[gds.size()];
                        for (int i = 0; i < gds.size(); i++)
                        {
                            Element gd = gds.get(i);
                            val = gd.attributeValue("fmla");
                            val = val.substring(4);
                            values[i] = Float.parseFloat(val) / 100000;
                        }
                    }
                }
            }
            else if(spPr.element("custGeom") != null)
            {
                shapeType = ShapeTypes.ArbitraryPolygon;                
            }

            BackgroundAndFill fill = null;
            if (fill == null && spPr.element("noFill") == null && !spName.equals("cxnSp"))
            {
                fill = processBackground(control, zipPackage, drawingPart, spPr, schemeColor);
                if (fill == null && shapeType != ShapeTypes.Arc && shapeType != ShapeTypes.BracketPair 
                    && shapeType != ShapeTypes.LeftBracket && shapeType != ShapeTypes.RightBracket
                    && shapeType != ShapeTypes.BracePair && shapeType != ShapeTypes.LeftBrace
                    && shapeType != ShapeTypes.RightBrace && shapeType != ShapeTypes.ArbitraryPolygon)
                {
                    fill = processBackground(control, zipPackage, drawingPart, sp.element("style"), schemeColor);
                }
            }

            Line line = LineKit.createShapeLine(control, zipPackage, drawingPart, sp, schemeColor);

            Element ln = spPr.element("ln");
            Element style = sp.element("style");
            if (ln != null)
            {                
                if (ln.element("noFill") != null)
                {
                    border = false;
                }
            }
            else if (border)
            {
                if (style == null || style.element("lnRef") == null)
                {
                    border = false;
                }
            }

            if(shapeType != ShapeTypes.Line && shapeType != ShapeTypes.StraightConnector1
        			&& rect != null && (rect.width == 0 || rect.height == 0))
        	{
        		return null;
        	}

            if (shapeType == ShapeTypes.Line || shapeType == ShapeTypes.StraightConnector1
                || shapeType == ShapeTypes.BentConnector3 || shapeType == ShapeTypes.CurvedConnector3)
            {
                LineShape lineShape = null;
                if (type == MainConstant.APPLICATION_TYPE_WP)
                {
                    lineShape = new WPAutoShape();
                }
                else
                {
                    lineShape = new LineShape();
                }
                lineShape.setShapeType(shapeType);
                lineShape.setBounds(rect);
                lineShape.setAdjustData(values);
                lineShape.setLine(line);

                if (ln != null)
                {
                    Element temp = ln.element("headEnd");
                    if (temp != null && temp.attribute("type") != null)
                    {
                        byte arrowType = Arrow.getArrowType(temp.attributeValue("type"));
                        if (arrowType != Arrow.Arrow_None)
                        {
                            lineShape.createStartArrow(arrowType, 
                                Arrow.getArrowSize(temp.attributeValue("w")), 
                                Arrow.getArrowSize(temp.attributeValue("len")));
                        }
                    }
                    temp = ln.element("tailEnd");
                    if (temp != null && temp.attribute("type") != null)
                    {
                        byte arrowType = Arrow.getArrowType(temp.attributeValue("type"));
                        if (arrowType != Arrow.Arrow_None)
                        {
                            lineShape.createEndArrow(arrowType, 
                                Arrow.getArrowSize(temp.attributeValue("w")), 
                                Arrow.getArrowSize(temp.attributeValue("len")));
                        }
                    }
                }
                ReaderKit.instance().processRotation(spPr, lineShape);
                return lineShape;
            }
            else if(shapeType == ShapeTypes.ArbitraryPolygon)
            {
                ArbitraryPolygonShape arbitraryPolygonShape = null;
                if (type == MainConstant.APPLICATION_TYPE_WP)
                {
                    arbitraryPolygonShape = new WPAutoShape();
                }
                else
                {
                    arbitraryPolygonShape = new ArbitraryPolygonShape();
                }

                BackgroundAndFill lineFill = null;
                if(line != null)
                {
                	lineFill = line.getBackgroundAndFill();
                }
                ArbitraryPolygonShapePath.processArbitraryPolygonShape(arbitraryPolygonShape, sp, fill, border, lineFill, ln, rect);

                arbitraryPolygonShape.setShapeType(shapeType);
                arbitraryPolygonShape.setLine(line);
                ReaderKit.instance().processRotation(spPr, arbitraryPolygonShape);
                return arbitraryPolygonShape;
            }
            else if (hasTextbox || fill != null || border)
            {
                AutoShape autoShape = null;
                if (type == MainConstant.APPLICATION_TYPE_WP)
                {
                    autoShape = new WPAutoShape();
                    autoShape.setShapeType(shapeType);
                }
                else
                {
                    autoShape = new AutoShape(shapeType);
                }
                autoShape.setBounds(rect);

                if (fill != null)
                {
                    autoShape.setBackgroundAndFill(fill);
                }
                if (line != null)
                {
                	autoShape.setLine(line);
                }
                autoShape.setAdjustData(values);
                ReaderKit.instance().processRotation(spPr, autoShape);

                return autoShape;
            }
        }
        return null;
    }

    public static void processPictureShape(IControl control, ZipPackage zipPackage, PackagePart  drawingPart, 
            Element bgPr, Map<String, Integer> schemeColor, PictureShape shape)
    {
    	if (shape == null)
        {
            return;
        }

        if (bgPr != null)
        {
        	BackgroundAndFill fill = 
        			AutoShapeDataKit.processBackground(control, zipPackage, drawingPart, 
        					bgPr, schemeColor);

        	shape.setBackgroundAndFill(fill);
        	Line line = LineKit.createLine(control, zipPackage, drawingPart, bgPr.element("ln"), schemeColor);
            shape.setLine(line);
         }
    }
}

