public class SignTool {

    public static String sign(String toSign) throws NoSuchAlgorithmException {

        MessageDigest md = MessageDigest.getInstance("MD5");
        md.update(toSign.getBytes());

        StringBuilder key = new StringBuilder();
        byte[] b = md.digest();
        for (byte aB : b) {
            long t = aB < 0 ? 256 + aB : aB;
            key.append(Long.toHexString(t));
        }

        return key.toString();
    }

}

public class PersisterCache implements Persister {

  private static final Logger LOGGER = LoggingUtils.getLogger(PersisterCache.class);

  private final Persister persister;

  private final Lock rlock;

  private final Lock rwlock;

  private MemPersister cache;

  public PersisterCache(Persister persister, SchedulerConfig schedulerConfig) {
    this.persister = persister;
    ReadWriteLock lock = CycleDetectingLockUtils.newLock(schedulerConfig, PersisterCache.class);
    this.rlock = lock.readLock();
    this.rwlock = lock.writeLock();
  }

  @Override
  public byte[] get(String path) throws PersisterException {
    rlock.lock();
    try {
      return getCache().get(path);
    } finally {
      rlock.unlock();
    }
  }

  @Override
  public Collection<String> getChildren(String path) throws PersisterException {
    rlock.lock();
    try {
      return getCache().getChildren(path);
    } finally {
      rlock.unlock();
    }
  }

  @Override
  public void set(String path, byte[] bytes) throws PersisterException {
    rwlock.lock();
    try {
      persister.set(path, bytes);
      getCache().set(path, bytes);
    } finally {
      rwlock.unlock();
    }
  }

  @Override
  public Map<String, byte[]> getMany(Collection<String> paths) throws PersisterException {
    rwlock.lock();
    try {
      return getCache().getMany(paths);
    } finally {
      rwlock.unlock();
    }
  }

  @Override
  public void setMany(Map<String, byte[]> pathBytesMap) throws PersisterException {
    rwlock.lock();
    try {
      persister.setMany(pathBytesMap);
      getCache().setMany(pathBytesMap);
    } finally {
      rwlock.unlock();
    }
  }

  @Override
  public void recursiveCopy(String srcPath, String destPath) throws PersisterException {
    rwlock.lock();
    try {
      persister.recursiveCopy(srcPath, destPath);
      getCache().recursiveCopy(srcPath, destPath);
    } finally {
      rwlock.unlock();
    }
  }

  @Override
  public void recursiveDeleteMany(Collection<String> paths) throws PersisterException {
    rwlock.lock();
    try {
      persister.recursiveDeleteMany(paths);
      getCache().recursiveDeleteMany(paths);
    } finally {
      rwlock.unlock();
    }
  }

  @Override
  public void recursiveDelete(String path) throws PersisterException {
    rwlock.lock();
    try {
      persister.recursiveDelete(path);
      try {
        getCache().recursiveDelete(path);
      } catch (PersisterException e) {
        LOGGER.error(
            "Didn't find value {} in cache to delete, but underlying storage had the value",
            path
        );
      }
    } finally {
      rwlock.unlock();
    }
  }

  @Override
  public void close() {
    rwlock.lock();
    try {
      persister.close();
      if (cache != null) {
        cache.close();
      }
    } finally {
      rwlock.unlock();
    }
  }

  public void refresh() throws PersisterException {
    rwlock.lock();
    try {
      if (cache != null) {
        LOGGER.info("Cache content before refresh:\n{}", cache.getDebugString());
      }
      cache = null;
      getCache();
    } finally {
      rwlock.unlock();
    }
  }

  private MemPersister getCache() throws PersisterException {
    if (cache == null) {
      cache = MemPersister.newBuilder()
          .disableLocking()
          .setData(PersisterUtils.getAllData(persister))
          .build();
      LOGGER.info("Loaded data from persister:\n{}", cache.getDebugString());
    }
    return cache;
  }
}

@JsxClass(isJSObject = false)
public class AbstractList extends SimpleScriptable implements Function, ExternalArrayData {

    protected enum EffectOnCache {
        NONE,
        RESET
    }

    private boolean avoidObjectDetection_;

    private boolean attributeChangeSensitive_;

    private List<DomNode> cachedElements_;

    private boolean listenerRegistered_;

    public AbstractList() {
    }

    public AbstractList(final DomNode domeNode, final boolean attributeChangeSensitive) {
        this(domeNode, attributeChangeSensitive, null);
    }

    protected AbstractList(final DomNode domNode, final List<DomNode> initialElements) {
        this(domNode, true, new ArrayList<>(initialElements));
    }

    private AbstractList(final DomNode domNode, final boolean attributeChangeSensitive,
            final List<DomNode> initialElements) {
        if (domNode != null) {
            setDomNode(domNode, false);
            final ScriptableObject parentScope = domNode.getScriptableObject();
            if (parentScope != null) {
                setParentScope(parentScope);
                setPrototype(getPrototype(getClass()));
            }
        }
        attributeChangeSensitive_ = attributeChangeSensitive;
        cachedElements_ = initialElements;
        if (initialElements != null) {
            registerListener();
        }
        setExternalArrayData(this);
    }

    @Override
    public boolean avoidObjectDetection() {
        return avoidObjectDetection_;
    }

    public void setAvoidObjectDetection(final boolean newValue) {
        avoidObjectDetection_ = newValue;
    }

    @Override
    public Object call(final Context cx, final Scriptable scope, final Scriptable thisObj, final Object[] args) {
        if (args.length == 0) {
            throw Context.reportRuntimeError("Zero arguments; need an index or a key.");
        }
        final Object object = getIt(args[0]);
        if (object == NOT_FOUND) {
            if (getBrowserVersion().hasFeature(HTMLCOLLECTION_NULL_IF_NOT_FOUND)) {
                return null;
            }
            return Undefined.instance;
        }
        return object;
    }

    @Override
    public final Scriptable construct(final Context cx, final Scriptable scope, final Object[] args) {
        return null;
    }

    private Object getIt(final Object o) {
        if (o instanceof Number) {
            final Number n = (Number) o;
            final int i = n.intValue();
            return get(i, this);
        }
        final String key = String.valueOf(o);
        return get(key, this);
    }

    @Override
    protected void setDomNode(final DomNode domNode, final boolean assignScriptObject) {
        final DomNode oldDomNode = getDomNodeOrNull();

        super.setDomNode(domNode, assignScriptObject);

        if (oldDomNode != domNode) {
            listenerRegistered_ = false;
        }
    }

    public List<DomNode> getElements() {
        List<DomNode> cachedElements = cachedElements_;

        if (cachedElements == null) {
            if (getParentScope() == null) {
                cachedElements = new ArrayList<>();
            }
            else {
                cachedElements = computeElements();
            }
            cachedElements_ = cachedElements;
        }
        registerListener();

        return cachedElements;
    }

    private void registerListener() {
        if (!listenerRegistered_) {
            final DomNode domNode = getDomNodeOrNull();
            if (domNode != null) {
                final DomHtmlAttributeChangeListenerImpl listener = new DomHtmlAttributeChangeListenerImpl(this);
                domNode.addDomChangeListener(listener);
                if (attributeChangeSensitive_) {
                    if (domNode instanceof HtmlElement) {
                        ((HtmlElement) domNode).addHtmlAttributeChangeListener(listener);
                    }
                    else if (domNode instanceof HtmlPage) {
                        ((HtmlPage) domNode).addHtmlAttributeChangeListener(listener);
                    }
                }
                listenerRegistered_ = true;
            }
        }
    }

    protected List<DomNode> computeElements() {
        final List<DomNode> response = new ArrayList<>();
        final DomNode domNode = getDomNodeOrNull();
        if (domNode == null) {
            return response;
        }
        for (final DomNode node : getCandidates()) {
            if (node instanceof DomElement && isMatching(node)) {
                response.add(node);
            }
        }
        return response;
    }

    protected Iterable<DomNode> getCandidates() {
        final DomNode domNode = getDomNodeOrNull();
        return domNode.getDescendants();
    }

    protected boolean isMatching(final DomNode node) {
        return false;
    }

    @Override
    protected Object getWithPreemption(final String name) {
        if ("length".equals(name)) {
            return NOT_FOUND;
        }

        final List<DomNode> elements = getElements();

        final List<DomNode> matchingElements = new ArrayList<>();

        for (final DomNode next : elements) {
            if (next instanceof DomElement) {
                final String id = ((DomElement) next).getId();
                if (name.equals(id)) {
                    matchingElements.add(next);
                }
            }
        }

        if (matchingElements.size() == 1) {
            return getScriptableForElement(matchingElements.get(0));
        }
        else if (!matchingElements.isEmpty()) {
            final AbstractList collection = create(getDomNodeOrDie(), matchingElements);
            collection.setAvoidObjectDetection(true);
            return collection;
        }

        return getWithPreemptionByName(name, elements);
    }

    protected AbstractList create(final DomNode parentScope, final List<DomNode> initialElements) {
        return new AbstractList(parentScope, initialElements);
    }

    protected Object getWithPreemptionByName(final String name, final List<DomNode> elements) {
        final List<DomNode> matchingElements = new ArrayList<>();
        for (final DomNode next : elements) {
            if (next instanceof DomElement) {
                final String nodeName = ((DomElement) next).getAttributeDirect("name");
                if (name.equals(nodeName)) {
                    matchingElements.add(next);
                }
            }
        }

        if (matchingElements.isEmpty()) {
            return NOT_FOUND;
        }
        else if (matchingElements.size() == 1) {
            return getScriptableForElement(matchingElements.get(0));
        }

        final DomNode domNode = getDomNodeOrNull();
        final AbstractList collection = create(domNode, matchingElements);
        collection.setAvoidObjectDetection(true);
        return collection;
    }

    @JsxGetter
    public final int getLength() {
        return getElements().size();
    }

    @JsxFunction
    public Object item(final Object index) {
        final Object object = getIt(index);
        if (object == NOT_FOUND) {
            return null;
        }
        return object;
    }

    @Override
    public String toString() {
        return getClass().getSimpleName() + " for " + getDomNodeOrNull();
    }

    @Override
    protected Object equivalentValues(final Object other) {
        if (other == this) {
            return Boolean.TRUE;
        }
        else if (other instanceof AbstractList) {
            final AbstractList otherArray = (AbstractList) other;
            final DomNode domNode = getDomNodeOrNull();
            final DomNode domNodeOther = otherArray.getDomNodeOrNull();
            if (getClass() == other.getClass()
                    && domNode == domNodeOther
                    && getElements().equals(otherArray.getElements())) {
                return Boolean.TRUE;
            }
            return NOT_FOUND;
        }

        return super.equivalentValues(other);
    }

    private static final class DomHtmlAttributeChangeListenerImpl
                                    implements DomChangeListener, HtmlAttributeChangeListener {

        private transient WeakReference<AbstractList> nodeList_;

        DomHtmlAttributeChangeListenerImpl(final AbstractList nodeList) {
            super();

            nodeList_ = new WeakReference<>(nodeList);
        }

        @Override
        public void nodeAdded(final DomChangeEvent event) {
            clearCache();
        }

        @Override
        public void nodeDeleted(final DomChangeEvent event) {
            clearCache();
        }

        @Override
        public void attributeAdded(final HtmlAttributeChangeEvent event) {
            handleChangeOnCache(event);
        }

        @Override
        public void attributeRemoved(final HtmlAttributeChangeEvent event) {
            handleChangeOnCache(event);
        }

        @Override
        public void attributeReplaced(final HtmlAttributeChangeEvent event) {
            final AbstractList nodes = nodeList_.get();
            if (null == nodes) {
                return;
            }
            if (nodes.attributeChangeSensitive_) {
                handleChangeOnCache(event);
            }
        }

        private void handleChangeOnCache(final HtmlAttributeChangeEvent event) {
            final AbstractList nodes = nodeList_.get();
            if (null == nodes) {
                return;
            }

            final EffectOnCache effectOnCache = nodes.getEffectOnCache(event);
            if (EffectOnCache.NONE == effectOnCache) {
                return;
            }
            if (EffectOnCache.RESET == effectOnCache) {
                clearCache();
            }
        }

        private void clearCache() {
            final AbstractList nodes = nodeList_.get();
            if (null != nodes) {
                nodes.cachedElements_ = null;
            }
        }
    }

    protected EffectOnCache getEffectOnCache(final HtmlAttributeChangeEvent event) {
        return EffectOnCache.RESET;
    }

    protected Scriptable getScriptableForElement(final Object object) {
        if (object instanceof Scriptable) {
            return (Scriptable) object;
        }
        return getScriptableFor(object);
    }

    @Override
    public void defineProperty(final String propertyName, final Object delegateTo,
            final Method getter, final Method setter, final int attributes) {
        if ("length".equals(propertyName) && getPrototype() != null) {
            return;
        }

        super.defineProperty(propertyName, delegateTo, getter, setter, attributes);
    }

    @Override
    public Object getArrayElement(final int index) {
        final List<DomNode> elements = getElements();
        if (index >= 0 && index < elements.size()) {
            return getScriptableForElement(elements.get(index));
        }
        return NOT_FOUND;
    }

    @Override
    public void setArrayElement(final int index, final Object value) {
        }

    @Override
    public int getArrayLength() {
        return getElements().size();
    }
}

public final class ShortListImpl extends AbstractList implements ShortList {

    public static final ShortListImpl EMPTY_LIST = new ShortListImpl(new short[0], 0);

    private final short[] fArray;
    private final int fLength;

    public ShortListImpl(short[] array, int length) {
        fArray = array;
        fLength = length;
    }

    @Override
    public int getLength() {
        return fLength;
    }

    @Override
    public boolean contains(short item) {
        for (int i = 0; i < fLength; i++) {
            if (fArray[i] == item) {
                return true;
            }
        }
        return false;
    }

    @Override
    public short item(int index) throws XSException {
        if (index < 0 || index >= fLength) {
            throw new XSException(XSException.INDEX_SIZE_ERR, null);
        }
        return fArray[index];
    }

    @Override
    public boolean equals(Object obj) {
        if (obj == null || !(obj instanceof ShortList)) {
            return false;
        }
        ShortList rhs = (ShortList) obj;

        if (fLength != rhs.getLength()) {
            return false;
        }
        for (int i = 0; i < fLength; ++i) {
            if (fArray[i] != rhs.item(i)) {
                return false;
            }
        }
        return true;
    }

    @Override
    public Object get(int index) {
        if (index >= 0 && index < fLength) {
            return new Short(fArray[index]);
        }
        throw new IndexOutOfBoundsException("Index: " + index);
    }

    @Override
    public int size() {
        return getLength();
    }

}

public class Real extends Number implements Field<Real>, Comparable<Real> {

    public static final Real ZERO = new Real(BigDecimal.ZERO);
    public static final Real ONE = new Real(BigDecimal.ONE);
    private final BigDecimal value;
    private static final long serialVersionUID = 1L;

    public Real(double value) {
        this.value = BigDecimal.valueOf(value);
    }

    public Real(long value) {
        this.value = BigDecimal.valueOf(value);
    }

    public Real(BigDecimal value) {
        this.value = value;
    }

    public Real(BigInteger value) {
        this.value = new BigDecimal(value);
    }

    public Real(String value) {
        this.value = new BigDecimal(value);
    }

    public BigDecimal toBigDecimal() {
        return value;
    }

    @Override
    public int intValue() {
        return value.intValue();
    }

    @Override
    public long longValue() {
        return value.longValue();
    }

    @Override
    public float floatValue() {
        return value.floatValue();
    }

    @Override
    public double doubleValue() {
        return value.doubleValue();
    }

    @Override
    public Real add(Real that) {
        return new Real(this.value.add(that.value));
    }

    @Override
    public Real minus(Real that) {
        return add(that.opposite());
    }

    @Override
    public Real opposite() {
        return new Real(this.value.negate());
    }

    @Override
    public Real multiply(Real that) {
        return new Real(this.value.multiply(that.value));
    }

    @Override
    public Real divide(Real that) {
        return divide(that, Constant.MACH_SCALE);
    }

    public Real divide(Real that, int scale) {
        Real result = new Real(this.value.divide(that.value, scale, BigDecimal.ROUND_HALF_EVEN));
        return result;
    }

    @Override
    public Real inverse() throws InverseNonExistent {
        return ONE().divide(this);
    }

    @Override
    public Real ZERO() {
        return Real.ZERO;
    }

    @Override
    public Real ONE() {
        return Real.ONE;
    }

    @Override
    public int compareTo(Real that) {
        return value.compareTo(that.value);
    }

    @Override
    public String toString() {
        return value.stripTrailingZeros().toString();
    }

    @Override
    public boolean equals(Object obj) {
        if (obj == null) {
            return false;
        }
        if (getClass() != obj.getClass()) {
            return false;
        }
        final Real other = (Real) obj;
        if (this.value != other.value && (this.value == null || this.value.compareTo(other.value) != 0)) {
            return false;
        }
        return true;
    }

    @Override
    public int hashCode() {
        int hash = 7;
        hash = 29 * hash + (this.value != null ? this.value.hashCode() : 0);
        return hash;
    }
}

public class RelationOneToMany<J extends Comparable, S extends StaMainTable<J, ?, S>, T extends StaMainTable<J, ?, T>> implements Relation<J> {

    private final EntityType targetType;
    private final String name;
    private final S source;

    private FieldAccessor<J, S> sourceFieldAccessor;

    private final T target;

    private FieldAccessor<J, T> targetFieldAccessor;

    private final boolean distinctRequired;

    public RelationOneToMany(S source, T target, EntityType targetType) {
        this.source = source;
        this.target = target;
        this.targetType = targetType;
        this.name = targetType.entityName;
        this.distinctRequired = false;
    }

    public RelationOneToMany(S source, T target, EntityType targetType, boolean distinctRequired) {
        this.source = source;
        this.target = target;
        this.targetType = targetType;
        this.name = targetType.entityName;
        this.distinctRequired = distinctRequired;
    }

    public RelationOneToMany<J, S, T> setSourceFieldAccessor(FieldAccessor<J, S> sourceFieldAccessor) {
        this.sourceFieldAccessor = sourceFieldAccessor;
        return this;
    }

    public RelationOneToMany<J, S, T> setTargetFieldAccessor(FieldAccessor<J, T> targetFieldAccessor) {
        this.targetFieldAccessor = targetFieldAccessor;
        return this;
    }

    @Override
    public TableRef<J> join(QueryState<J, ?, ?> queryState, TableRef<J> sourceRef) {
        TableField<Record, J> sourceField = sourceFieldAccessor.getField(source);
        T targetAliased = (T) target.as(queryState.getNextAlias());
        TableField<Record, J> targetField = targetFieldAccessor.getField(targetAliased);
        queryState.setSqlFrom(queryState.getSqlFrom().innerJoin(targetAliased).on(targetField.eq(sourceField)));
        if (distinctRequired) {
            queryState.setDistinctRequired(distinctRequired);
        }
        return QueryBuilder.createJoinedRef(sourceRef, targetType, targetAliased);
    }

    @Override
    public String getName() {
        return name;
    }

}

@Immutable
@JsonParsableCommand(typePrefix = PolicyCommand.TYPE_PREFIX, name = ModifyPolicy.NAME)
public final class ModifyPolicy extends AbstractCommand<ModifyPolicy> implements PolicyModifyCommand<ModifyPolicy> {

    public static final String NAME = "modifyPolicy";

    public static final String TYPE = TYPE_PREFIX + NAME;

    static final JsonFieldDefinition<JsonObject> JSON_POLICY =
            JsonFactory.newJsonObjectFieldDefinition("policy", FieldType.REGULAR, JsonSchemaVersion.V_2);

    private final PolicyId policyId;
    private final Policy policy;

    private ModifyPolicy(final PolicyId policyId, final Policy policy, final DittoHeaders dittoHeaders) {
        super(TYPE, dittoHeaders);
        this.policyId = policyId;
        this.policy = policy;

        final JsonObject policyJsonObject = policy.toJson();

        PolicyCommandSizeValidator.getInstance().ensureValidSize(
                policyJsonObject::getUpperBoundForStringSize,
                () -> policyJsonObject.toString().length(),
                () -> dittoHeaders);
    }

    public static ModifyPolicy of(final PolicyId policyId, final Policy policy, final DittoHeaders dittoHeaders) {
        Objects.requireNonNull(policyId, "The Policy identifier must not be null!");
        Objects.requireNonNull(policy, "The Policy must not be null!");
        return new ModifyPolicy(policyId, policy, dittoHeaders);
    }

    public static ModifyPolicy fromJson(final String jsonString, final DittoHeaders dittoHeaders) {
        return fromJson(JsonFactory.newObject(jsonString), dittoHeaders);
    }

    public static ModifyPolicy fromJson(final JsonObject jsonObject, final DittoHeaders dittoHeaders) {
        return new CommandJsonDeserializer<ModifyPolicy>(TYPE, jsonObject).deserialize(() -> {
            final JsonObject policyJsonObject = jsonObject.getValueOrThrow(JSON_POLICY);
            final Policy policy = PoliciesModelFactory.newPolicy(policyJsonObject);

            final Optional<String> optionalPolicyId =
                    jsonObject.getValue(PolicyCommand.JsonFields.JSON_POLICY_ID);
            final PolicyId policyId = optionalPolicyId.map(PolicyId::of)
                    .orElseGet(() -> policy.getEntityId().orElseThrow(() ->
                            new JsonMissingFieldException(PolicyCommand.JsonFields.JSON_POLICY_ID)
                    ));

            return of(policyId, policy, dittoHeaders);
        });
    }

    public Policy getPolicy() {
        return policy;
    }

    @Override
    public PolicyId getEntityId() {
        return policyId;
    }

    @Override
    public Optional<JsonValue> getEntity(final JsonSchemaVersion schemaVersion) {
        return Optional.of(policy.toJson(schemaVersion, FieldType.regularOrSpecial()));
    }

    @Override
    public JsonPointer getResourcePath() {
        return JsonPointer.empty();
    }

    @Override
    protected void appendPayload(final JsonObjectBuilder jsonObjectBuilder, final JsonSchemaVersion schemaVersion,
            final Predicate<JsonField> thePredicate) {
        final Predicate<JsonField> predicate = schemaVersion.and(thePredicate);
        jsonObjectBuilder.set(PolicyCommand.JsonFields.JSON_POLICY_ID, String.valueOf(policyId), predicate);
        jsonObjectBuilder.set(JSON_POLICY, policy.toJson(schemaVersion, thePredicate), predicate);
    }

    @Override
    public Category getCategory() {
        return Category.MODIFY;
    }

    @Override
    public ModifyPolicy setDittoHeaders(final DittoHeaders dittoHeaders) {
        return of(policyId, policy, dittoHeaders);
    }

    @Override
    protected boolean canEqual(@Nullable final Object other) {
        return (other instanceof ModifyPolicy);
    }

    @SuppressWarnings("squid:MethodCyclomaticComplexity")
    @Override
    public boolean equals(@Nullable final Object obj) {
        if (this == obj) {
            return true;
        }
        if (null == obj || getClass() != obj.getClass()) {
            return false;
        }
        final ModifyPolicy that = (ModifyPolicy) obj;
        return that.canEqual(this) && Objects.equals(policyId, that.policyId)
                && Objects.equals(policy, that.policy) && super.equals(obj);
    }

    @SuppressWarnings("squid:S109")
    @Override
    public int hashCode() {
        return Objects.hash(super.hashCode(), policyId, policy);
    }

    @Override
    public String toString() {
        return getClass().getSimpleName() + " [" + super.toString() + ", policyId=" + policyId + ", policy=" + policy +
                "]";
    }

}

public abstract class JsonRpcIdentifiableMessage extends JsonRpcMessage {
    private final int id;

    public JsonRpcIdentifiableMessage(JsonRpcVersion version, int id) {
        super(version);
        this.id = requireNonNegative(id);
    }

    @JsonInclude(JsonInclude.Include.ALWAYS)
    public int getId() {
        return id;
    }

    private static int requireNonNegative(int id) {
        if (id < 0) {
            throw new IllegalArgumentException(
                    String.format("JSON-RPC message id should be a positive number, but was %s.", id)
            );
        }

        return id;
    }
}

