abstract class CreateOrUpdateRegistrarCommand extends MutatingCommand {

  @Inject CertificateChecker certificateChecker;

  @Parameter(description = "Client identifier of the registrar account", required = true)
  List<String> mainParameters;

  @Parameter(
      names = "--registrar_type",
      description = "Type of the registrar")
  Registrar.Type registrarType;

  @Nullable
  @Parameter(
      names = "--registrar_state",
      description = "Initial state of the registrar")
  Registrar.State registrarState;

  @Parameter(
      names = "--allowed_tlds",
      description = "Comma-delimited list of TLDs which the registrar is allowed to use")
  List<String> allowedTlds = new ArrayList<>();

  @Parameter(
      names = "--add_allowed_tlds",
      description = "Comma-delimited list of TLDs to add to TLDs a registrar is allowed to use")
  List<String> addAllowedTlds = new ArrayList<>();

  @Nullable
  @Parameter(
      names = "--password",
      description = "Password for the registrar account")
  String password;

  @Nullable
  @Parameter(
      names = "--name",
      description = "Name of the registrar")
  String registrarName;

  @Nullable
  @Parameter(names = "--email", description = "Email address of registrar")
  String email;

  @Nullable
  @Parameter(
      names = "--icann_referral_email",
      description = "ICANN referral email, as specified in registrar contract")
  String icannReferralEmail;

  @Nullable
  @Parameter(
      names = "--url",
      description = "URL of registrar's website",
      converter = OptionalStringParameter.class,
      validateWith = OptionalStringParameter.class)
  private Optional<String> url;

  @Nullable
  @Parameter(
      names = "--phone",
      description = "E.164 phone number, e.g. +1.2125650666",
      converter = OptionalPhoneNumberParameter.class,
      validateWith = OptionalPhoneNumberParameter.class)
  Optional<String> phone;

  @Nullable
  @Parameter(
      names = "--fax",
      description = "E.164 fax number, e.g. +1.2125650666",
      converter = OptionalPhoneNumberParameter.class,
      validateWith = OptionalPhoneNumberParameter.class)
  Optional<String> fax;

  @Nullable
  @Parameter(
      names = "--cert_file",
      description = "File containing client certificate (X.509 PEM)",
      validateWith = PathParameter.InputFile.class)
  Path clientCertificateFilename;

  @Nullable
  @Parameter(
      names = "--failover_cert_file",
      description = "File containing failover client certificate (X.509 PEM)",
      validateWith = PathParameter.InputFile.class)
  Path failoverClientCertificateFilename;

  @Parameter(
      names = "--ip_allow_list",
      description = "Comma-delimited list of IP ranges. An empty string clears the allow list.")
  List<String> ipAllowList = new ArrayList<>();

  @Nullable
  @Parameter(
      names = "--iana_id",
      description = "Registrar IANA ID",
      converter = OptionalLongParameter.class,
      validateWith = OptionalLongParameter.class)
  Optional<Long> ianaId;

  @Nullable
  @Parameter(
      names = "--billing_id",
      description = "Registrar Billing ID (i.e. Oracle #)",
      converter = OptionalLongParameter.class,
      validateWith = OptionalLongParameter.class)
  private Optional<Long> billingId;

  @Nullable
  @Parameter(
      names = "--po_number",
      description = "Purchase Order number used for billing invoices",
      converter = OptionalStringParameter.class,
      validateWith = OptionalStringParameter.class)
  private Optional<String> poNumber;

  @Nullable
  @Parameter(
      names = "--billing_account_map",
      description =
          "Registrar Billing Account key-value pairs (formatted as key=value[,key=value...]), "
              + "where key is a currency unit (USD, JPY, etc) and value is the registrar's billing "
              + "account id for that currency. During update, only the pairs that need updating "
              + "need to be provided.",
      converter = CurrencyUnitToStringMap.class,
      validateWith = CurrencyUnitToStringMap.class)
  private Map<CurrencyUnit, String> billingAccountMap;

  @Nullable
  @Parameter(
      names = "--street",
      variableArity = true,
      description = "Street lines of address. Can take up to 3 lines.")
  List<String> street;

  @Nullable
  @Parameter(
      names = "--city",
      description = "City of address")
  String city;

  @Nullable
  @Parameter(
      names = "--state",
      description = "State/Province of address. The value \"null\" clears this field.")
  String state;

  @Nullable
  @Parameter(
      names = "--zip",
      description = "Postal code of address. The value \"null\" clears this field.")
  String zip;

  @Nullable
  @Parameter(
      names = "--cc",
      description = "Country code of address")
  String countryCode;

  @Nullable
  @Parameter(
      names = "--block_premium",
      description = "Whether premium name registration should be blocked on this registrar",
      arity = 1)
  private Boolean blockPremiumNames;

  @Nullable
  @Parameter(
      names = "--sync_groups",
      description = "Whether this registrar's groups should be updated at the next scheduled sync",
      arity = 1)
  private Boolean contactsRequireSyncing;

  @Nullable
  @Parameter(
      names = "--registry_lock_allowed",
      description = "Whether this registrar is allowed to use registry lock",
      arity = 1)
  private Boolean registryLockAllowed;

  @Nullable
  @Parameter(
      names = "--drive_folder_id",
      description = "Id (not full URL) of this registrar's folder in Drive",
      converter = OptionalStringParameter.class,
      validateWith = OptionalStringParameter.class)
  Optional<String> driveFolderId;

  @Nullable
  @Parameter(
      names = "--passcode",
      description = "Telephone support passcode")
  String phonePasscode;

  @Nullable
  @Parameter(
      names = "--whois",
      description = "Hostname of registrar WHOIS server. (Default: whois.nic.google)")
  String whoisServer;

  @Parameter(
      names = "--rdap_servers",
      description =
          "Comma-delimited list of RDAP servers. An empty argument clears the list."
              + " Note that for real registrars this could get overridden periodically by"
              + " ICANN-registered values.")
  List<String> rdapServers = new ArrayList<>();

  @Nullable
  abstract Registrar getOldRegistrar(String clientId);

  abstract void checkModifyAllowedTlds(@Nullable Registrar oldRegistrar);

  protected void initRegistrarCommand() {}

  @Override
  protected final void init() throws Exception {
    initRegistrarCommand();
    DateTime now = DateTime.now(UTC);
    for (String clientId : mainParameters) {
      Registrar oldRegistrar = getOldRegistrar(clientId);
      Registrar.Builder builder =
          (oldRegistrar == null)
              ? new Registrar.Builder().setRegistrarId(clientId)
              : oldRegistrar.asBuilder();

      if (!isNullOrEmpty(password)) {
        builder.setPassword(password);
      }
      if (!isNullOrEmpty(registrarName)) {
        builder.setRegistrarName(registrarName);
      }
      if (!isNullOrEmpty(email)) {
        builder.setEmailAddress(email);
      } else if (!isNullOrEmpty(
          icannReferralEmail)) { builder.setEmailAddress(icannReferralEmail);
      }
      if (url != null) {
        builder.setUrl(url.orElse(null));
      }
      if (phone != null) {
        builder.setPhoneNumber(phone.orElse(null));
      }
      if (fax != null) {
        builder.setFaxNumber(fax.orElse(null));
      }
      if (registrarType != null) {
        builder.setType(registrarType);
      }
      if (registrarState != null) {
        builder.setState(registrarState);
      }
      if (driveFolderId != null) {
        builder.setDriveFolderId(driveFolderId.orElse(null));
      }
      if (!allowedTlds.isEmpty() || !addAllowedTlds.isEmpty()) {
        checkModifyAllowedTlds(oldRegistrar);
      }
      if (!allowedTlds.isEmpty()) {
        checkArgument(
            addAllowedTlds.isEmpty(), "Can't specify both --allowedTlds and --addAllowedTlds");
        ImmutableSet.Builder<String> allowedTldsBuilder = new ImmutableSet.Builder<>();
        for (String allowedTld : allowedTlds) {
          allowedTldsBuilder.add(canonicalizeDomainName(allowedTld));
        }
        builder.setAllowedTlds(allowedTldsBuilder.build());
      }
      if (!addAllowedTlds.isEmpty()) {
        ImmutableSet.Builder<String> allowedTldsBuilder = new ImmutableSet.Builder<>();
        if (oldRegistrar != null) {
          allowedTldsBuilder.addAll(oldRegistrar.getAllowedTlds());
        }
        for (String allowedTld : addAllowedTlds) {
          allowedTldsBuilder.add(canonicalizeDomainName(allowedTld));
        }
        builder.setAllowedTlds(allowedTldsBuilder.build());
      }
      if (!ipAllowList.isEmpty()) {
        ImmutableList.Builder<CidrAddressBlock> ipAllowListBuilder = new ImmutableList.Builder<>();
        if (!(ipAllowList.size() == 1 && ipAllowList.get(0).contains("null"))) {
          for (String ipRange : ipAllowList) {
            if (!ipRange.isEmpty()) {
              ipAllowListBuilder.add(CidrAddressBlock.create(ipRange));
            }
          }
        }
        builder.setIpAddressAllowList(ipAllowListBuilder.build());
      }
      if (clientCertificateFilename != null) {
        String asciiCert = new String(Files.readAllBytes(clientCertificateFilename), US_ASCII);
        if (!asciiCert.equals("")) {
          certificateChecker.validateCertificate(asciiCert);
        }
        builder.setClientCertificate(asciiCert, now);
      }

      if (failoverClientCertificateFilename != null) {
        String asciiCert =
            new String(Files.readAllBytes(failoverClientCertificateFilename), US_ASCII);
        if (!asciiCert.equals("")) {
          certificateChecker.validateCertificate(asciiCert);
        }
        builder.setFailoverClientCertificate(asciiCert, now);
      }
      if (ianaId != null) {
        builder.setIanaIdentifier(ianaId.orElse(null));
      }
      if (billingId != null) {
        builder.setBillingIdentifier(billingId.orElse(null));
      }
      Optional.ofNullable(poNumber).ifPresent(builder::setPoNumber);
      if (billingAccountMap != null) {
        LinkedHashMap<CurrencyUnit, String> newBillingAccountMap = new LinkedHashMap<>();
        if (oldRegistrar != null && oldRegistrar.getBillingAccountMap() != null) {
          newBillingAccountMap.putAll(oldRegistrar.getBillingAccountMap());
        }
        newBillingAccountMap.putAll(billingAccountMap);
        builder.setBillingAccountMap(newBillingAccountMap);
      }
      List<Object> streetAddressFields = Arrays.asList(street, city, state, zip, countryCode);
      checkArgument(
          streetAddressFields.stream().anyMatch(isNull())
              == streetAddressFields.stream().allMatch(isNull()),
          "Must specify all fields of address");
      if (street != null) {
        builder.setLocalizedAddress(new RegistrarAddress.Builder()
            .setStreet(ImmutableList.copyOf(street))
            .setCity(city)
            .setState("null".equals(state) ? null : state)
            .setZip("null".equals(zip) ? null : zip)
            .setCountryCode(countryCode)
            .build());
      }
      Optional.ofNullable(blockPremiumNames).ifPresent(builder::setBlockPremiumNames);
      Optional.ofNullable(contactsRequireSyncing).ifPresent(builder::setContactsRequireSyncing);
      Optional.ofNullable(registryLockAllowed).ifPresent(builder::setRegistryLockAllowed);
      Optional.ofNullable(phonePasscode).ifPresent(builder::setPhonePasscode);
      Optional.ofNullable(icannReferralEmail).ifPresent(builder::setIcannReferralEmail);
      Optional.ofNullable(whoisServer).ifPresent(builder::setWhoisServer);

      if (!rdapServers.isEmpty()) {
        builder.setRdapBaseUrls(
            rdapServers.stream().filter(server -> !server.isEmpty()).collect(toImmutableSet()));
      }

      String oldRegistrarName = (oldRegistrar == null) ? null : oldRegistrar.getRegistrarName();
      if (registrarName != null && !registrarName.equals(oldRegistrarName)) {
        String normalizedName = normalizeRegistrarName(registrarName);
        for (Registrar registrar : Registrar.loadAll()) {
          if (registrar.getRegistrarName() != null) {
            checkArgument(
                !normalizedName.equals(normalizeRegistrarName(registrar.getRegistrarName())),
                "The registrar name %s normalizes identically to existing registrar name %s",
                registrarName,
                registrar.getRegistrarName());
          }
        }
      }

      Registrar newRegistrar = builder.build();

      if (Registrar.Type.REAL.equals(registrarType)) {
        checkArgument(
            newRegistrar.getPhonePasscode() != null, "--passcode is required for REAL registrars.");
        ImmutableSet<CurrencyUnit> tldCurrencies =
            newRegistrar
                .getAllowedTlds()
                .stream()
                .map(tld -> Registry.get(tld).getCurrency())
                .collect(toImmutableSet());
        Set<CurrencyUnit> currenciesWithoutBillingAccountId =
            newRegistrar.getBillingAccountMap() == null
                ? tldCurrencies
                : Sets.difference(tldCurrencies, newRegistrar.getBillingAccountMap().keySet());
        checkArgument(
            currenciesWithoutBillingAccountId.isEmpty(),
            "Need billing account map entries for currencies: %s",
            Joiner.on(' ').join(currenciesWithoutBillingAccountId));
      }

      stageEntityChange(oldRegistrar, newRegistrar);
    }
  }
}

public class ProgramTreeLoader {
	protected static CategoryServiceAsync categorySrv = GWT.create(CategoryService.class);
	protected static Logger logger = Logger.getLogger(ProgramTreeLoader.class.getName());

	public static ProgramTree load(final String userEmail) {
		final ProgramTree tree = new ProgramTree();
		ProgramServiceAsync svc = GWT.create(ProgramService.class);
		svc.load(new AsyncCallback<List<Program>>() {

			@Override
			public void onFailure(Throwable caught) {
				logger.info("Load programs failed");
			}

			@Override
			public void onSuccess(List<Program> result) {
				for (Program m : result) {
					if (!m.getDeprecated()) {
						ProgramLeaf node = new ProgramLeaf(m);
						addProgramLeaf(tree, node, userEmail);
						addContextMenu(tree,node);
					}
				}
			}
		});

		return tree;
	}

	public static void addProgramLeaf(final ProgramTree tree,
			final ProgramLeaf node,
			String userEmail) {
		Module m = node.getModule();
		String category = m.getCategory();
		if ("0A0F402F-670F-4696-9D9C-42F0E0D665A1".equals(category) 
				|| "shared program".equals(category.toLowerCase()) || "".equals(category)) {
			tree.getSharedTree().addItem(node);
		}
		else if ("0A0F402F-670F-4696-9D9C-42F0E0D665A2".equals(category) 
				|| "my program".equals(category.toLowerCase()) || "".equals(category)) {
			if (m.getOwner().equals(userEmail)) {
				tree.getMyTree().addItem(node);
				node.addMenuItem(ProgramDeleteMenu.create(node));
			}
		}else if("0A0F402F-670F-4696-9D9C-42F0E0D665A0".equals(category) 
				|| "system program".equals(category.toLowerCase()) || "".equals(category)){
			tree.getSysTree().addItem(node);
			node.addMenuItem(ProgramDeleteMenu.create(node));
		}else if(category.contains(">")){			
			TreeItem treeItem = tree.findTreeItem(category);
			treeItem.addItem(node);
		}else {
			Category query = new Category();
			query.setId(category);
			query.setType("prog");
			categorySrv.getCategory(query, "", new AsyncCallback<List<Category>>(){

				@Override
				public void onFailure(Throwable caught) {
					Window.alert(caught.getMessage());
				}

				@Override
				public void onSuccess(List<Category> result) {
					if(result != null){
						TreeItem treeItem;
						if(result.get(0).getPath().equals(null)){
							treeItem = tree.findTreeItem(result.get(0).getName());
						}else
							treeItem = tree.findTreeItem(result.get(0).getPath());

						treeItem.addItem(node);
					}
				}

			});
		}
	}

	public static void addContextMenu(ProgramTree programTree,ProgramLeaf node) {

		if(!AppController.email.equals("guest")){
			node.addMenuItem(ProgramEditMenu.create(node,programTree));
			node.addMenuItem(ProgramUpdateMenu.create(node,programTree));
			node.addMenuItem(ProgramDeprecateMenu.create(node));
		}
		node.addMenuItem(ProgramDownloadMenu.create(node));

	}
}

@SuppressWarnings("FinalClass")
public final class Parser {

    private String string;
    private int position;

    public Parser(final String string) {
        this.string = string;
        skipWhitespace();
    }

    public void expect(final String... words) {
        for (final String word : words) {
            expect(word, false);
        }
    }

    public boolean expect(final String word, final boolean optional) {
        final int wordEnd = position + word.length();

        if (wordEnd <= string.length()
                && string.substring(position, wordEnd).equalsIgnoreCase(word)
                && (wordEnd == string.length()
                || Character.isWhitespace(string.charAt(wordEnd))
                || string.charAt(wordEnd) == '('
                || string.charAt(wordEnd) == ')'
                || string.charAt(wordEnd) == ';'
                || string.charAt(wordEnd) == ','
                || string.charAt(wordEnd) == '['
                || "(".equals(word) || ",".equals(word) || "[".equals(word)
                || "]".equals(word))) {
            position = wordEnd;
            skipWhitespace();

            return true;
        }

        if (optional) {
            return false;
        }

        int dumpEndPosition = position + 20;
        if (string.length() - (position + 1) < 20) {
            dumpEndPosition = string.length() - 1;
        }

        throw new ParserException(MessageFormat.format(
                Resources.getString("CannotParseStringExpectedWord"), string,
                word, position + 1, string.substring(position, dumpEndPosition)));
    }

    public boolean expectOptional(final String... words) {
        final int oldPosition = position;
        boolean found = expect(words[0], true);

        if (!found) {
            return false;
        }

        for (int i = 1; i < words.length; i++) {
            skipWhitespace();
            found = expect(words[i], true);
            if (!found) {
                position = oldPosition;
                return false;
            }
        }

        return true;
    }

    public void skipWhitespace() {
        for (; position < string.length(); position++) {
            if (!Character.isWhitespace(string.charAt(position))) {
                break;
            }
        }
    }

    public String parseIdentifier() {
        String identifier = parseIdentifierInternal();

        while (string.charAt(position) == '.') {
            position++;
            identifier += '.' + parseIdentifierInternal();
        }

        skipWhitespace();

        return identifier;
    }

    private String parseIdentifierInternal() {
        final boolean quoted = string.charAt(position) == '"';

        if (quoted) {
            final int endPos = string.indexOf('"', position + 1);
            final String result = string.substring(position, endPos + 1);
            position = endPos + 1;

            return result;
        } else {
            int endPos = position;

            for (; endPos < string.length(); endPos++) {
                final char chr = string.charAt(endPos);

                if (Character.isWhitespace(chr) || chr == ',' || chr == ')'
                        || chr == '(' || chr == ';' || chr == '.') {
                    break;
                }
            }

            final String result =
                    string.substring(position, endPos).toLowerCase(
                    Locale.ENGLISH);

            position = endPos;

            return result;
        }
    }

    public String getRest() {
        final String result;

        if (string.charAt(string.length() - 1) == ';') {
            if (position == string.length() - 1) {
                return null;
            } else {
                result = string.substring(position, string.length() - 1);
            }
        } else {
            result = string.substring(position);
        }

        position = string.length();

        return result;
    }

    public int parseInteger() {
        int endPos = position;

        for (; endPos < string.length(); endPos++) {
            if (!Character.isLetterOrDigit(string.charAt(endPos))) {
                break;
            }
        }

        try {
            final int result =
                    Integer.parseInt(string.substring(position, endPos));

            position = endPos;
            skipWhitespace();

            return result;
        } catch (final NumberFormatException ex) {
            throw new ParserException(MessageFormat.format(
                    Resources.getString("CannotParseStringExpectedInteger"),
                    string, position + 1,
                    string.substring(position, position + 20)), ex);
        }
    }

    public String parseString() {
        final boolean quoted = string.charAt(position) == '\'';

        if (quoted) {
            boolean escape = false;
            int endPos = position + 1;

            for (; endPos < string.length(); endPos++) {
                final char chr = string.charAt(endPos);

                if (chr == '\\') {
                    escape = !escape;
                } else if (!escape && chr == '\'') {
                    if (endPos + 1 < string.length()
                            && string.charAt(endPos + 1) == '\'') {
                        endPos++;
                    } else {
                        break;
                    }
                }
            }

            final String result;

            try {
                if (endPos >= string.length())
                {
                    endPos = string.lastIndexOf('\'');
                }
                result = string.substring(position, endPos + 1);
            } catch (final Throwable ex) {
                throw new RuntimeException("Failed to get substring: " + string
                        + " start pos: " + position + " end pos: "
                        + (endPos + 1), ex);
            }

            position = endPos + 1;
            skipWhitespace();

            return result;
        } else {
            int endPos = position;

            for (; endPos < string.length(); endPos++) {
                final char chr = string.charAt(endPos);

                if (Character.isWhitespace(chr) || chr == ',' || chr == ')'
                        || chr == ';') {
                    break;
                }
            }

            if (position == endPos) {
                throw new ParserException(MessageFormat.format(
                        Resources.getString("CannotParseStringExpectedString"),
                        string, position + 1));
            }

            final String result = string.substring(position, endPos);

            position = endPos;
            skipWhitespace();

            return result;
        }
    }

    public String getExpression() {
        final int endPos = getExpressionEnd();

        if (position == endPos) {
            throw new ParserException(MessageFormat.format(
                    Resources.getString("CannotParseStringExpectedExpression"),
                    string, position + 1,
                    string.substring(position, position + 20)));
        }

        final String result = string.substring(position, endPos).trim();

        position = endPos;

        return result;
    }

    private int getExpressionEnd() {
        int bracesCount = 0;
        boolean singleQuoteOn = false;
        int charPos = position;

        for (; charPos < string.length(); charPos++) {
            final char chr = string.charAt(charPos);

            if (chr == '(' || chr == '[') {
                bracesCount++;
            } else if (chr == ')' || chr == ']') {
                if (bracesCount == 0) {
                    break;
                } else {
                    bracesCount--;
                }
            } else if (chr == '\'') {
                singleQuoteOn = !singleQuoteOn;

                if (charPos > 0 && string.charAt(charPos - 1) == '\\') {
                    singleQuoteOn = !singleQuoteOn;
                }
            } else if ((chr == ',') && !singleQuoteOn && (bracesCount == 0)) {
                break;
            } else if (chr == ';' && bracesCount == 0 && !singleQuoteOn) {
                break;
            }
        }

        return charPos;
    }

    public int getPosition() {
        return position;
    }

    public String getString() {
        return string;
    }

    public void throwUnsupportedCommand() {
        throw new ParserException(MessageFormat.format(
                Resources.getString("CannotParseStringUnsupportedCommand"),
                string, position + 1,
                string.substring(position, string.length() > position + 20
                ? position + 20 : string.length())));
    }

    public String expectOptionalOneOf(final String... words) {
        for (final String word : words) {
            if (expectOptional(word)) {
                return word;
            }
        }

        return null;
    }

    public String getSubString(final int startPos, final int endPos) {
        return string.substring(startPos, endPos);
    }

    public void setPosition(final int position) {
        this.position = position;
    }

    public String parseDataType() {
        int endPos = position;

        while (endPos < string.length()
                && !Character.isWhitespace(string.charAt(endPos))
                && string.charAt(endPos) != '('
                && string.charAt(endPos) != ')'
                && string.charAt(endPos) != ',') {
            endPos++;
        }

        if (endPos == position) {
            throw new ParserException(MessageFormat.format(
                    Resources.getString("CannotParseStringExpectedDataType"),
                    string, position + 1,
                    string.substring(position, position + 20)));
        }

        String dataType = string.substring(position, endPos);

        position = endPos;
        skipWhitespace();

        if ("character".equalsIgnoreCase(dataType)
                && expectOptional("varying")) {
            dataType = "character varying";
        } else if ("double".equalsIgnoreCase(dataType)
                && expectOptional("precision")) {
            dataType = "double precision";
        }

        final boolean timestamp = "timestamp".equalsIgnoreCase(dataType)
                || "time".equalsIgnoreCase(dataType);

        if (string.charAt(position) == '(') {
            dataType += getExpression();
        }

        if (timestamp) {
            if (expectOptional("with", "time", "zone")) {
                dataType += " with time zone";
            } else if (expectOptional("without", "time", "zone")) {
                dataType += " without time zone";
            }
        }

        if (expectOptional("[")) {
            expect("]");
            dataType += "[]";
        }

        return dataType;
    }

    public boolean isConsumed() {
        return position == string.length()
                || position + 1 == string.length()
                && string.charAt(position) == ';';
    }
}

@Singleton
public class ClassLoaderServiceImpl implements ClassLoaderService {

    private static final ArkLogger                       LOGGER                                    = ArkLoggerFactory
                                                                                                       .getDefaultLogger();

    private static final String                          ARK_SPI_PACKAGES                          = "com.alipay.sofa.ark.spi";
    private static final String                          ARK_API_PACKAGES                          = "com.alipay.sofa.ark.api";
    private static final String                          ARK_LOG_PACKAGES                          = "com.alipay.sofa.ark.common.log";
    private static final String                          ARK_EXCEPTION_PACKAGES                    = "com.alipay.sofa.ark.exception";

    private static final List<String>                    SUN_REFLECT_GENERATED_ACCESSOR            = new ArrayList<>();

    private ConcurrentHashMap<String, ClassLoader>       exportClassAndClassLoaderMap              = new ConcurrentHashMap<>();
    private ConcurrentHashMap<String, ClassLoader>       exportNodeAndClassLoaderMap               = new ConcurrentHashMap<>();
    private ConcurrentHashMap<String, ClassLoader>       exportStemAndClassLoaderMap               = new ConcurrentHashMap<>();

    private ConcurrentHashMap<String, List<ClassLoader>> exportResourceAndClassLoaderMap           = new ConcurrentHashMap<>();
    private ConcurrentHashMap<String, List<ClassLoader>> exportPrefixStemResourceAndClassLoaderMap = new ConcurrentHashMap<>();
    private ConcurrentHashMap<String, List<ClassLoader>> exportSuffixStemResourceAndClassLoaderMap = new ConcurrentHashMap<>();

    private ClassLoader                                  jdkClassLoader;
    private ClassLoader                                  arkClassLoader;
    private ClassLoader                                  systemClassLoader;
    private ClassLoader                                  agentClassLoader;

    @Inject
    private PluginManagerService                         pluginManagerService;

    @Inject
    private BizManagerService                            bizManagerService;

    static {
        SUN_REFLECT_GENERATED_ACCESSOR.add("sun.reflect.GeneratedMethodAccessor");
        SUN_REFLECT_GENERATED_ACCESSOR.add("sun.reflect.GeneratedConstructorAccessor");
        SUN_REFLECT_GENERATED_ACCESSOR.add("sun.reflect.GeneratedSerializationConstructorAccessor");
    }

    @Override
    public boolean isSunReflectClass(String className) {
        for (String sunAccessor : SUN_REFLECT_GENERATED_ACCESSOR) {
            if (className.startsWith(sunAccessor)) {
                return true;
            }
        }
        return false;
    }

    @Override
    public boolean isArkSpiClass(String className) {
        return className.startsWith(ARK_SPI_PACKAGES);
    }

    @Override
    public boolean isArkApiClass(String className) {
        return className.startsWith(ARK_API_PACKAGES);
    }

    @Override
    public boolean isArkLogClass(String className) {
        return className.startsWith(ARK_LOG_PACKAGES);
    }

    @Override
    public boolean isArkExceptionClass(String className) {
        return className.startsWith(ARK_EXCEPTION_PACKAGES);
    }

    @Override
    public void prepareExportClassAndResourceCache() {
        for (Plugin plugin : pluginManagerService.getPluginsInOrder()) {
            for (String exportIndex : plugin.getExportPackageNodes()) {
                exportNodeAndClassLoaderMap.putIfAbsent(exportIndex, plugin.getPluginClassLoader());
            }
            for (String exportIndex : plugin.getExportPackageStems()) {
                exportStemAndClassLoaderMap.putIfAbsent(exportIndex, plugin.getPluginClassLoader());
            }
            for (String exportIndex : plugin.getExportClasses()) {
                exportClassAndClassLoaderMap
                    .putIfAbsent(exportIndex, plugin.getPluginClassLoader());
            }
            for (String resource : plugin.getExportResources()) {
                exportResourceAndClassLoaderMap.putIfAbsent(resource, new LinkedList<>());
                exportResourceAndClassLoaderMap.get(resource).add(plugin.getPluginClassLoader());
            }
            for (String resource : plugin.getExportPrefixResourceStems()) {
                exportPrefixStemResourceAndClassLoaderMap.putIfAbsent(resource, new LinkedList<>());
                exportPrefixStemResourceAndClassLoaderMap.get(resource).add(
                    plugin.getPluginClassLoader());
            }
            for (String resource : plugin.getExportSuffixResourceStems()) {
                exportSuffixStemResourceAndClassLoaderMap.putIfAbsent(resource, new LinkedList<>());
                exportSuffixStemResourceAndClassLoaderMap.get(resource).add(
                    plugin.getPluginClassLoader());
            }
        }
    }

    @Override
    public boolean isClassInImport(String pluginName, String className) {
        Plugin plugin = pluginManagerService.getPluginByName(pluginName);
        AssertUtils.assertNotNull(plugin, "plugin: " + pluginName + " is null");

        for (String importName : plugin.getImportClasses()) {
            if (className.equals(importName)) {
                return true;
            }
        }

        String pkg = ClassUtils.getPackageName(className);
        for (String pattern : plugin.getImportPackageNodes()) {
            if (pkg.equals(pattern)) {
                return true;
            }
        }

        for (String pattern : plugin.getImportPackageStems()) {
            if (pkg.startsWith(pattern)) {
                return true;
            }
        }

        return false;
    }

    @Override
    public ClassLoader findExportClassLoader(String className) {
        ClassLoader exportClassLoader = exportClassAndClassLoaderMap.get(className);
        String packageName = ClassUtils.getPackageName(className);
        if (exportClassLoader == null) {
            exportClassLoader = exportNodeAndClassLoaderMap.get(packageName);
        }
        while (!Constants.DEFAULT_PACKAGE.equals(packageName) && exportClassLoader == null) {
            exportClassLoader = exportStemAndClassLoaderMap.get(packageName);
            packageName = ClassUtils.getPackageName(packageName);
        }
        return exportClassLoader;
    }

    @Override
    public boolean isResourceInImport(String pluginName, String resourceName) {
        Plugin plugin = pluginManagerService.getPluginByName(pluginName);
        AssertUtils.assertNotNull(plugin, "plugin: " + pluginName + " is null");

        for (String importResource : plugin.getImportResources()) {
            if (importResource.equals(resourceName)) {
                return true;
            }
        }

        for (String importResource : plugin.getImportPrefixResourceStems()) {
            if (resourceName.startsWith(importResource)) {
                return true;
            }
        }

        for (String importResource : plugin.getImportSuffixResourceStems()) {
            if (resourceName.endsWith(importResource)) {
                return true;
            }
        }

        return false;
    }

    @Override
    public List<ClassLoader> findExportResourceClassLoadersInOrder(String resourceName) {

        if (exportResourceAndClassLoaderMap.containsKey(resourceName)) {
            return exportResourceAndClassLoaderMap.get(resourceName);
        }

        for (String stemResource : exportPrefixStemResourceAndClassLoaderMap.keySet()) {
            if (resourceName.startsWith(stemResource)) {
                return exportPrefixStemResourceAndClassLoaderMap.get(stemResource);
            }
        }

        for (String stemResource : exportSuffixStemResourceAndClassLoaderMap.keySet()) {
            if (resourceName.endsWith(stemResource)) {
                return exportSuffixStemResourceAndClassLoaderMap.get(stemResource);
            }
        }
        return null;
    }

    @Override
    public ClassLoader getJDKClassLoader() {
        return jdkClassLoader;
    }

    @Override
    public ClassLoader getArkClassLoader() {
        return arkClassLoader;
    }

    @Override
    public ClassLoader getSystemClassLoader() {
        return systemClassLoader;
    }

    @Override
    public ClassLoader getAgentClassLoader() {
        return agentClassLoader;
    }

    @Override
    public ClassLoader getBizClassLoader(String bizIdentity) {
        Biz biz = bizManagerService.getBizByIdentity(bizIdentity);
        return biz == null ? null : biz.getBizClassLoader();
    }

    @Override
    public ClassLoader getPluginClassLoader(String pluginName) {
        Plugin plugin = pluginManagerService.getPluginByName(pluginName);
        return plugin == null ? null : plugin.getPluginClassLoader();
    }

    @Override
    public void init() throws ArkRuntimeException {
        arkClassLoader = this.getClass().getClassLoader();
        systemClassLoader = ClassLoader.getSystemClassLoader();
        agentClassLoader = createAgentClassLoader();

        ClassLoader extClassLoader = systemClassLoader;
        while (extClassLoader.getParent() != null) {
            extClassLoader = extClassLoader.getParent();
        }
        List<URL> jdkUrls = new ArrayList<>();
        try {
            String javaHome = System.getProperty("java.home").replace(File.separator + "jre", "");
            URL[] urls = ((URLClassLoader) systemClassLoader).getURLs();
            for (URL url : urls) {
                if (url.getPath().startsWith(javaHome)) {
                    if (LOGGER.isDebugEnabled()) {
                        LOGGER.debug(String.format("Find JDK Url: %s", url));
                    }
                    jdkUrls.add(url);
                }
            }
        } catch (Throwable e) {
            LOGGER.warn("Meet exception when parse JDK urls", e);
        }

        jdkClassLoader = new JDKDelegateClassLoader(jdkUrls.toArray(new URL[0]), extClassLoader);
    }

    @Override
    public void dispose() throws ArkRuntimeException {

    }

    private ClassLoader createAgentClassLoader() throws ArkRuntimeException {
        return new AgentClassLoader(ClassLoaderUtils.getAgentClassPath(), null);
    }

    @Override
    public boolean isDeniedImportClass(String bizIdentity, String className) {
        Biz biz = bizManagerService.getBizByIdentity(bizIdentity);
        if (biz == null) {
            return false;
        }

        for (String pattern : biz.getDenyImportClasses()) {
            if (pattern.equals(className)) {
                return true;
            }
        }

        String pkg = ClassUtils.getPackageName(className);
        for (String pattern : biz.getDenyImportPackageNodes()) {
            if (pkg.equals(pattern)) {
                return true;
            }
        }

        for (String pattern : biz.getDenyImportPackageStems()) {
            if (pkg.startsWith(pattern)) {
                return true;
            }
        }

        return false;
    }

    @Override
    public boolean isDeniedImportResource(String bizIdentity, String resourceName) {
        Biz biz = bizManagerService.getBizByIdentity(bizIdentity);
        if (biz == null) {
            return false;
        }

        for (String resource : biz.getDenyImportResources()) {
            if (resource.equals(resourceName)) {
                return true;
            }
        }

        for (String resource : biz.getDenyPrefixImportResourceStems()) {
            if (resourceName.startsWith(resource)) {
                return true;
            }
        }

        for (String resource : biz.getDenySuffixImportResourceStems()) {
            if (resourceName.endsWith(resource)) {
                return true;
            }
        }

        return false;
    }

    @Override
    public int getPriority() {
        return DEFAULT_PRECEDENCE;
    }
}

