public class TimeToSampleAtom extends LeafAtom {

	public static class TimeToSampleEntry {
		long sampleCount, sampleDuration;

		public TimeToSampleEntry(long count, long duration) {
			this.sampleCount = count;
			this.sampleDuration = duration;
		}

		public TimeToSampleEntry(InputStream in) throws IOException {
			sampleCount = Atom.read32Int(in);
			sampleDuration = Atom.read32Int(in);
		}

		public long getSampleCount() {
			return sampleCount;
		}

		public long getSampleDuration() {
			return sampleDuration;
		}

		@Override
		public String toString() {
			return "[" + sampleCount + ", " + sampleDuration + "]";
		}

		protected void write(OutputStream out) throws IOException {
			Atom.write32Int(out, sampleCount);
			Atom.write32Int(out, sampleDuration);
		}
	}

	public static final String ATOM_TYPE = "stts";

	protected int version = 0;
	protected int flags = 0;
	protected TimeToSampleEntry[] table = new TimeToSampleEntry[0];

	public TimeToSampleAtom(int version, int flags) {
		super(null);
		this.version = version;
		this.flags = flags;
	}

	public TimeToSampleAtom() {
		super(null);
	}

	public TimeToSampleAtom(Atom parent, InputStream in) throws IOException {
		super(parent);
		version = in.read();
		flags = read24Int(in);
		int entryCount = (int) read32Int(in);
		table = new TimeToSampleEntry[entryCount];
		for (int a = 0; a < table.length; a++) {
			table[a] = new TimeToSampleEntry(in);
		}
	}

	@Override
	public String getIdentifier() {
		return ATOM_TYPE;
	}

	@Override
	protected long getSize() {
		return 16 + table.length * 8;
	}

	@Override
	protected void writeContents(GuardedOutputStream out) throws IOException {
		out.write(version);
		write24Int(out, flags);
		write32Int(out, table.length);
		for (int a = 0; a < table.length; a++) {
			table[a].write(out);
		}
	}

	@Override
	public String toString() {
		StringBuffer sb = new StringBuffer();
		sb.append("[ ");
		for (int a = 0; a < table.length; a++) {
			if (a != 0) {
				sb.append(", ");
			}
			sb.append(table[a].toString());
		}
		sb.append(" ]");
		String tableString = sb.toString();

		return "TimeToSampleAtom[ version=" + version + ", " + "flags=" + flags
				+ ", " + "table=" + tableString + "]";
	}

	public void addSampleTime(long duration) {
		addSampleTime(1, duration);
	}

	public void addSampleTime(long sampleCount, long duration) {
		if (table.length == 0
				|| table[table.length - 1].sampleDuration != duration) {
			TimeToSampleEntry[] newTable = new TimeToSampleEntry[table.length
					+ 1];
			System.arraycopy(table, 0, newTable, 0, table.length);
			newTable[newTable.length - 1] = new TimeToSampleEntry(sampleCount,
					duration);
			table = newTable;
		} else {
			table[table.length - 1].sampleCount++;
		}
	}

	public long getDurationOfSample(long sampleIndex) {
		for (int a = 0; a < table.length; a++) {
			if (sampleIndex < table[a].sampleCount) {
				return table[a].sampleDuration;
			}
			sampleIndex = sampleIndex - table[a].sampleCount;
		}
		throw new RuntimeException(
				"Could not find a sample at index " + sampleIndex);
	}

	public int getVersion() {
		return version;
	}

	public int getFlags() {
		return flags;
	}

	public TimeToSampleEntry[] getTable() {
		TimeToSampleEntry[] copy = new TimeToSampleEntry[table.length];
		System.arraycopy(table, 0, copy, 0, table.length);
		return copy;
	}
}

public class AttributedString {

    private List<AttributedElement> elements;

    public static AttributedString plain(String string) {
        return new AttributedString((string != null) ? string : "", simple());
    }

    public static AttributedString plain(String string, List<AttributedType> kinds) {
        return new AttributedString((string != null) ? string : "", kinds);
    }

    public static AttributedString xhtml(String string) {
        return AttributedString.xhtml(string, new XmlConvertRule());
    }

    public static AttributedString xhtml(String string, XmlConvertRule rule) {
        return XmlParseUtil.xhtml(string).toAttributedString(rule);
    }

    public static AttributedString elements(List<AttributedElement> elements) {
        return new AttributedString(elements);
    }

    private AttributedString(List<AttributedElement> elements) {
        this.elements = elements;
    }

    private AttributedString(String text, List<AttributedType> kinds) {
        AttributedItem model = new AttributedItem();
        model.setKind(AttributedKind.PLAIN);
        model.setDisplayText(text);

        Stream<AttributedElement> stream = Stream.of(model);
        for (AttributedType kind : kinds) {
            stream = stream
                    .map(elem -> scanElements(elem, kind))
                    .flatMap(Collection::stream);
        }
        elements = stream.collect(toList());
    }

    public void addEmojiElement(List<Emoji> emojis) {
        if (emojis != null && !emojis.isEmpty()) {
            Stream<AttributedElement> stream = elements.stream();
            for (Emoji emoji : emojis) {
                stream = stream
                        .map(elem -> scanEmojis(elem, emoji))
                        .flatMap(Collection::stream);
            }
            elements = stream.collect(toList());
        }
    }

    public List<AttributedElement> getElements() {
        return this.elements;
    }

    public String getDisplayText() {
        return this.elements.stream()
                .map(AttributedElement::getDisplayText)
                .collect(Collectors.joining());
    }

    private List<AttributedElement> scanElements(
            AttributedElement element,
            AttributedType kind) {

        if (element.getKind() == AttributedKind.PLAIN) {
            String text = element.getDisplayText();
            if (!text.isEmpty()) {

                Pattern p = Pattern.compile(kind.getRegex());
                Matcher m = p.matcher(text);

                if (m.find()) {
                    int i = m.start();
                    String found = m.group();

                    if (i >= 0) {
                        String before = text.substring(0, i);
                        String after = text.substring(i + found.length());
                        List<AttributedElement> results = new ArrayList<>();

                        {
                            AttributedItem model = new AttributedItem();
                            model.setKind(AttributedKind.PLAIN);
                            model.setDisplayText(before);
                            results.add(model);
                        }
                        {
                            AttributedItem model = new AttributedItem();
                            model.setDisplayText(kind.getDisplayedText(m));
                            model.setExpandedText(kind.getExpandedText(m));
                            model.setKind(kind.getKind());
                            results.add(model);
                        }
                        {
                            AttributedItem model = new AttributedItem();
                            model.setKind(AttributedKind.PLAIN);
                            model.setDisplayText(after);

                            results.addAll(scanElements(model, kind));
                        }
                        return results;

                    } else {

                        Logger log = Logger.getLogger(AttributedString.class);
                        log.debug("UnExpected Status");
                        log.debug("Text : " + text);
                        log.debug("Found: " + found);
                        log.debug("Index: " + i);
                    }
                }
            }
        }
        return Collections.singletonList(element);
    }

    private List<AttributedElement> scanEmojis(
            AttributedElement element,
            Emoji emoji) {

        if (element.getKind() == AttributedKind.PLAIN) {
            String text = element.getDisplayText();

            String regex = ":" + emoji.getCode() + ":";
            Pattern p = Pattern.compile(regex);
            Matcher m = p.matcher(text);

            if (m.find()) {
                int i = m.start();
                String found = m.group();

                if (i >= 0) {
                    String before = text.substring(0, i);
                    String after = text.substring(i + found.length());
                    List<AttributedElement> results = new ArrayList<>();

                    {
                        AttributedItem model = new AttributedItem();
                        model.setKind(AttributedKind.PLAIN);
                        model.setDisplayText(before);
                        results.add(model);
                    }
                    {
                        AttributedItem model = new AttributedItem();
                        model.setDisplayText(regex);
                        model.setExpandedText(emoji.getUrl());
                        model.setKind(AttributedKind.EMOJI);
                        results.add(model);
                    }
                    {
                        AttributedItem model = new AttributedItem();
                        model.setKind(AttributedKind.PLAIN);
                        model.setDisplayText(after);

                        results.addAll(scanEmojis(model, emoji));
                    }
                    return results;

                } else {

                    Logger log = Logger.getLogger(AttributedString.class);
                    log.debug("UnExpected Status");
                    log.debug("Text : " + text);
                    log.debug("Found: " + found);
                    log.debug("Index: " + i);
                }
            }
        }

        return Collections.singletonList(element);
    }
}

public class SpaceProjectionResultObjectProvider implements ResultObjectProvider {
    private Object[][] _result;
    private int _currentIndex;

    public SpaceProjectionResultObjectProvider(Object[][] result) {
        _result = result;
        _currentIndex = -1;
    }

    public boolean absolute(int pos) throws Exception {
        if (pos >= 0 && pos < _result.length) {
            _currentIndex = pos;
            return true;
        }
        return false;
    }

    public void close() throws Exception {
        reset();
    }

    public Object getResultObject() throws Exception {
        return _result[_currentIndex];
    }

    public void handleCheckedException(Exception e) {
        throw new RuntimeException(e);
    }

    public boolean next() throws Exception {
        return absolute(_currentIndex + 1);
    }

    public void open() throws Exception {
    }

    public void reset() throws Exception {
        _currentIndex = -1;
    }

    public int size() throws Exception {
        return _result.length;
    }

    public boolean supportsRandomAccess() {
        return true;
    }

}

