public class Utils {

    private static final int INDEX_NOT_FOUND = -1;
    private static final SimpleDateFormat DATE_NO_MINUTES = new SimpleDateFormat("MMM dd, yyyy");
    private static final SimpleDateFormat DATE_WITH_MINUTES = new SimpleDateFormat("MMM dd yyyy | KK:mm a");

    public static float clamp(float min, float max, float value) {
        float minimum = Math.max(min, value);
        return Math.min(minimum, max);
    }

    public static float getViewRawY(View view) {
        int[] location = new int[2];
        location[0] = 0;
        location[1] = (int) view.getY();
        ((View) view.getParent()).getLocationInWindow(location);
        return location[1];
    }

    public static void setTint(Context context, CheckBox box, int color) {
        if (Build.VERSION.SDK_INT >= 21) return;
        ColorStateList sl = new ColorStateList(new int[][]{
                new int[]{-android.R.attr.state_checked},
                new int[]{android.R.attr.state_checked}
        }, new int[]{getColor(context, R.color.grey), color});

        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.LOLLIPOP) {
            box.setButtonTintList(sl);
        } else {
            Drawable drawable = DrawableCompat.wrap(ContextCompat.getDrawable(box.getContext(), R.drawable.abc_btn_check_material));
            DrawableCompat.setTintList(drawable, sl);
            box.setButtonDrawable(drawable);
        }
    }

    public String getDate(File f) {
        return getDate(f.lastModified());
    }

    public static String getDate(long f) {
        return DATE_WITH_MINUTES.format(f);
    }

    public static String getDate(long f, String year) {
        String date = DATE_NO_MINUTES.format(f);
        if (date.substring(date.length() - 4, date.length()).equals(year))
            date = date.substring(0, date.length() - 6);
        return date;
    }

    public static int calculateInSampleSize(BitmapFactory.Options options, int reqWidth, int reqHeight) {
        final int height = options.outHeight;
        final int width = options.outWidth;
        int inSampleSize = 1;

        if (height > reqHeight || width > reqWidth) {

            final int halfHeight = height / 2;
            final int halfWidth = width / 2;

            while ((halfHeight / inSampleSize) > reqHeight && (halfWidth / inSampleSize) > reqWidth) {
                inSampleSize *= 2;
            }
        }

        return inSampleSize;
    }

    public boolean isAtleastKitkat() {
        return Build.VERSION.SDK_INT >= Build.VERSION_CODES.KITKAT;
    }

    public static int getColor(Context c, @ColorRes int color) {
        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.M) {
            return c.getColor(color);
        } else {
            return c.getResources().getColor(color);
        }
    }

    public static int dpToPx(Context c, int dp) {
        DisplayMetrics displayMetrics = c.getResources().getDisplayMetrics();
        return Math.round(dp * (displayMetrics.xdpi / DisplayMetrics.DENSITY_DEFAULT));
    }

    public static String differenceStrings(String str1, String str2) {
        if (str1 == null) {
            return str2;
        }
        if (str2 == null) {
            return str1;
        }
        int at = indexOfDifferenceStrings(str1, str2);
        if (at == INDEX_NOT_FOUND) {
            return "";
        }
        return str2.substring(at);
    }

    private static int indexOfDifferenceStrings(CharSequence cs1, CharSequence cs2) {
        if (cs1 == cs2) {
            return INDEX_NOT_FOUND;
        }
        if (cs1 == null || cs2 == null) {
            return 0;
        }
        int i;
        for (i = 0; i < cs1.length() && i < cs2.length(); ++i) {
            if (cs1.charAt(i) != cs2.charAt(i)) {
                break;
            }
        }
        if (i < cs2.length() || i < cs1.length()) {
            return i;
        }
        return INDEX_NOT_FOUND;
    }

    public static void disableScreenRotation(ExplorerActivity mainActivity) {
        int screenOrientation = mainActivity.getResources().getConfiguration().orientation;

        if (screenOrientation == Configuration.ORIENTATION_LANDSCAPE) {

            mainActivity.setRequestedOrientation(ActivityInfo.SCREEN_ORIENTATION_LANDSCAPE);
        } else if (screenOrientation == Configuration.ORIENTATION_PORTRAIT) {

            mainActivity.setRequestedOrientation(ActivityInfo.SCREEN_ORIENTATION_PORTRAIT);
        }
    }
}

public class ReadDictionaryUtils {

	public static IGenstarDictionary<Attribute<? extends IValue>> 
				readBayesianNetworkAsDictionary(CategoricalBayesianNetwork bn) {

		Collection<Attribute<? extends IValue>> attributes = new LinkedList<>();

		for (NodeCategorical n: bn.getNodes()) {

			final boolean isRange = detectIsRange(n.getDomain());
			final boolean isInteger = !isRange && detectIsInteger(n.getDomain());

			GSEnumDataType dataType = GSEnumDataType.Nominal;
			if (isRange) {
				dataType = GSEnumDataType.Range;
			} else if (isInteger) {
				dataType = GSEnumDataType.Integer;
			}

			Attribute<? extends IValue> att;
			try {
				att = AttributeFactory.getFactory().createAttribute(
						n.getName(), 
						dataType,
						new ArrayList<String>(n.getDomain())
						);
				attributes.add(att);
			} catch (GSIllegalRangedData e) {
				e.printStackTrace();
				throw new RuntimeException(e);
			}

		}

		return new AttributeDictionary(attributes);

	}


	public static boolean detectIsRange(Collection<String> modalities) {

		if (modalities.size()<2)
			return false;

		System.out.println("is this a range? "+modalities);

		 Pattern oneNumber = Pattern.compile("[\\D]*\\d+[\\D]*"); 

		List<String> mods = new ArrayList<>(modalities);

		if (!oneNumber.matcher(mods.get(0)).matches()) {
			System.out.println("no, because the first number is not a number: "+mods.get(0));
			return false;
		}

		if (!oneNumber.matcher(mods.get(mods.size()-1)).matches()) {
			System.out.println("no, because the last number is not a number: "+(mods.size()-1));
			return false;
		}

		Pattern twoNumbers = Pattern.compile("[\\D]*\\d+[\\D]+\\d+[\\D]*");

		for (int i=1; i<mods.size()-2; i++) {
			if (!twoNumbers.matcher(mods.get(i)).matches()) {
				return false;
			}
			System.out.println("this might be a range:"+mods.get(i));
		}

		return true;
	}

	public static boolean detectIsInteger(Collection<String> modalities) {

		Pattern oneNumber = Pattern.compile("[\\D]*\\d+[\\D]*"); 

		for (String s: modalities) {
			if (!oneNumber.matcher(s).matches())
				return false;
			}
		return true;
	}



	private ReadDictionaryUtils() {}


	public static IGenstarDictionary<Attribute<? extends IValue>> readFromGenstarConfig(
			String filename) {

		GenstarJsonUtil sju = new GenstarJsonUtil();

		try {
			return sju.unmarshalFromGenstarJson(
					FileSystems.getDefault().getPath(filename), 
					AttributeDictionary.class);
		} catch (IllegalArgumentException | IOException e) {
			e.printStackTrace();
			throw new RuntimeException("error while reading the config file: "+e.getMessage(), e);
		}

	}

}

