public class StreamEventsIterator extends AbstractEventsIterator<Long, StreamEventsSlice> {

    StreamEventsIterator(long eventNumber, Function<Long, CompletableFuture<StreamEventsSlice>> reader) {
        super(eventNumber, reader);
    }

    @Override
    protected void onBatchReceived(StreamEventsSlice slice) {
        switch (slice.status) {
            case Success:
                super.onBatchReceived(slice);
                break;
            case StreamNotFound:
                throw new StreamNotFoundException(slice.stream);
            case StreamDeleted:
                throw new StreamDeletedException(slice.stream);
            default:
                throw new IllegalStateException(String.format("Unexpected read status: %s", slice.status));
        }
    }

    @Override
    protected Long getNextCursor(StreamEventsSlice slice) {
        return slice.nextEventNumber;
    }

    @Override
    protected List<ResolvedEvent> getEvents(StreamEventsSlice slice) {
        return slice.events;
    }

    @Override
    protected boolean isEndOfStream(StreamEventsSlice slice) {
        return slice.isEndOfStream;
    }

}

public final class ReferenceCountedContext implements AlterableContext {


    private static final ThreadLocal<Map<ReferenceCountedContext, Map<Contextual<?>, Instance<?>>>> ALL_INSTANCES =
        ThreadLocal.withInitial(() -> new HashMap<>());


    ReferenceCountedContext() {
        super();
        ALL_INSTANCES.get().put(this, new HashMap<>());
    }


    public int decrementReferenceCount(final Contextual<?> c) {
        return this.decrementReferenceCount(c, 1);
    }

    public int decrementReferenceCount(final Contextual<?> c, final int amount) {
        final int returnValue;
        if (amount < 0) {
            throw new IllegalArgumentException("amount < 0: " + amount);
        } else if (c == null) {
            returnValue = 0;
        } else {
            final Map<?, ? extends Instance<?>> instances = ALL_INSTANCES.get().get(this);
            if (instances == null) {
                returnValue = 0;
            } else {
                final Instance<?> instance = instances.get(c);
                if (instance == null) {
                    returnValue = 0;
                } else {
                    returnValue = instance.decrementReferenceCount(amount);
                    if (returnValue <= 0) {
                        instances.remove(c);
                    }
                }
            }
        }
        return returnValue;
    }

    public int getReferenceCount(final Contextual<?> c) {
        final int returnValue;
        if (c == null) {
            returnValue = 0;
        } else {
            final Map<?, ? extends Instance<?>> instances = ALL_INSTANCES.get().get(this);
            if (instances == null) {
                returnValue = 0;
            } else {
                final Instance<?> instance = instances.get(c);
                if (instance == null) {
                    returnValue = 0;
                } else {
                    returnValue = Math.max(0, instance.getReferenceCount());
                }
            }
        }
        return returnValue;
    }

    @Override
    public void destroy(final Contextual<?> contextual) {
        this.decrementReferenceCount(contextual);
    }

    @Override
    public <T> T get(final Contextual<T> contextual) {
        return this.get(contextual, null, false);
    }

    @Override
    public <T> T get(final Contextual<T> contextual, final CreationalContext<T> cc) {
        return this.get(contextual, cc, true);
    }

    private <T> T get(final Contextual<T> contextual, final CreationalContext<T> cc, final boolean maybeCreate) {
        final T returnValue;
        if (contextual == null) {
            returnValue = null;
        } else {
            final Map<ReferenceCountedContext, Map<Contextual<?>, Instance<?>>> allInstances = ALL_INSTANCES.get();
            assert allInstances != null;
            Map<Contextual<?>, Instance<?>> instances = allInstances.get(this);
            if (instances == null && maybeCreate) {
                instances = new HashMap<>();
                allInstances.put(this, instances);
            }
            if (instances == null) {
                returnValue = null;
            } else {
                @SuppressWarnings("unchecked")
                final Instance<T> temp = (Instance<T>) instances.get(contextual);
                Instance<T> instance = temp;
                if (instance == null) {
                    if (maybeCreate) {
                        instance = new Instance<T>(contextual, cc);
                        instances.put(contextual, instance);
                        returnValue = instance.get();
                    } else {
                        returnValue = null;
                    }
                } else {
                    returnValue = instance.get();
                }
            }
        }
        return returnValue;
    }

    @Override
    public Class<? extends Annotation> getScope() {
        return ReferenceCounted.class;
    }

    @Override
    public boolean isActive() {
        return true;
    }


    public static ReferenceCountedContext getInstanceFrom(final BeanManager beanManager) {
        Objects.requireNonNull(beanManager);
        return (ReferenceCountedContext) beanManager.getContext(ReferenceCounted.class);
    }


    private static final class Instance<T> {


        private T object;

        private int referenceCount;

        private final CreationalContext<T> creationalContext;

        private final Contextual<T> contextual;


        private Instance(final Contextual<T> contextual, final CreationalContext<T> creationalContext) {
            super();
            this.contextual = Objects.requireNonNull(contextual);
            this.creationalContext = creationalContext;
            this.object = this.contextual.create(creationalContext);
        }


        private T get() {
            if (this.referenceCount < 0) {
                throw new IllegalStateException("this.referenceCount < 0: " + this.referenceCount);
            } else if (this.referenceCount < Integer.MAX_VALUE) {
                ++this.referenceCount;
            }
            return this.object;
        }

        private int getReferenceCount() {
            return this.referenceCount;
        }

        private int decrementReferenceCount(final int amount) {
            if (amount < 0) {
                throw new IllegalArgumentException("amount < 0: " + amount);
            } else if (this.referenceCount > 0) {
                this.referenceCount = Math.max(0, this.referenceCount - amount);
                if (this.referenceCount == 0) {
                    this.contextual.destroy(this.object, this.creationalContext);
                    if (this.creationalContext != null) {
                        this.creationalContext.release();
                    }
                    this.object = null;
                    this.referenceCount = -1;
                }
            } else {
                throw new IllegalStateException("this.referenceCount <= 0: " + this.referenceCount);
            }
            return this.referenceCount;
        }

    }

}

public class EagerHttpRequest extends RawHttpRequest {

    private EagerHttpRequest(RequestLine requestLine,
                             RawHttpHeaders headers,
                             @Nullable EagerBodyReader bodyReader,
                             @Nullable InetAddress senderAddress) {
        super(requestLine, headers, bodyReader, senderAddress);
    }

    public static EagerHttpRequest from(RawHttpRequest request)
            throws IOException {
        if (request instanceof EagerHttpRequest) {
            return (EagerHttpRequest) request;
        }

        @Nullable EagerBodyReader bodyReader = request.getBody().isPresent() ?
                request.getBody().get().eager() :
                null;

        RawHttpHeaders headers;
        if (bodyReader != null) {
            RawHttpHeaders trailingHeaders = bodyReader.asChunkedBodyContents()
                    .map(ChunkedBodyContents::getTrailerHeaders)
                    .orElse(emptyRawHttpHeaders());
            if (trailingHeaders.isEmpty()) {
                headers = request.getHeaders();
            } else {
                headers = RawHttpHeaders.newBuilderSkippingValidation(request.getHeaders())
                        .merge(trailingHeaders)
                        .build();
            }
        } else {
            headers = request.getHeaders();
        }

        return new EagerHttpRequest(request.getStartLine(), headers,
                bodyReader, request.getSenderAddress().orElse(null));
    }

    @Override
    public Optional<EagerBodyReader> getBody() {
        Optional<? extends BodyReader> body = super.getBody();
        return body.map(b -> (EagerBodyReader) b);
    }

    @Override
    public EagerHttpRequest eagerly() {
        return this;
    }

    @Override
    public EagerHttpRequest withHeaders(RawHttpHeaders headers) {
        return withHeaders(headers, true);
    }

    @Override
    public EagerHttpRequest withHeaders(RawHttpHeaders headers, boolean append) {
        try {
            return super.withHeaders(headers, append).eagerly();
        } catch (IOException e) {
            throw new IllegalStateException("unreachable");
        }
    }

    @Override
    public EagerHttpRequest withRequestLine(RequestLine requestLine) {
        try {
            return super.withRequestLine(requestLine).eagerly();
        } catch (IOException e) {
            throw new IllegalStateException("unreachable");
        }
    }

    @Override
    public int hashCode() {
        return Objects.hash(getStartLine(), getHeaders(), getBody());
    }

    @Override
    public boolean equals(Object other) {
        if (this == other) return true;
        if (other == null || getClass() != other.getClass()) return false;

        EagerHttpRequest that = (EagerHttpRequest) other;

        return getStartLine().equals(that.getStartLine())
                && getHeaders().equals(that.getHeaders())
                && getBody().equals(that.getBody());
    }
}

public final class MimeUtility {

    private static final String US_ASCII_CHARSET = "US-ASCII";

    private static final String BASE64_ENCODING_MARKER = "B";

    private static final String QUOTEDPRINTABLE_ENCODING_MARKER = "Q";

    private static final String ENCODED_TOKEN_MARKER = "=?";

    private static final String ENCODED_TOKEN_FINISHER = "?=";

    private static final String LINEAR_WHITESPACE = " \t\r\n";

    private static final Map<String, String> MIME2JAVA = new HashMap<String, String>();

    static {
        MIME2JAVA.put("iso-2022-cn", "ISO2022CN");
        MIME2JAVA.put("iso-2022-kr", "ISO2022KR");
        MIME2JAVA.put("utf-8", "UTF8");
        MIME2JAVA.put("utf8", "UTF8");
        MIME2JAVA.put("ja_jp.iso2022-7", "ISO2022JP");
        MIME2JAVA.put("ja_jp.eucjp", "EUCJIS");
        MIME2JAVA.put("euc-kr", "KSC5601");
        MIME2JAVA.put("euckr", "KSC5601");
        MIME2JAVA.put("us-ascii", "ISO-8859-1");
        MIME2JAVA.put("x-us-ascii", "ISO-8859-1");
    }

    private MimeUtility() {
        }

    public static String decodeText(String text) throws UnsupportedEncodingException {
        if (text.indexOf(ENCODED_TOKEN_MARKER) < 0) {
            return text;
        }

        int offset = 0;
        int endOffset = text.length();

        int startWhiteSpace = -1;
        int endWhiteSpace = -1;

        StringBuilder decodedText = new StringBuilder(text.length());

        boolean previousTokenEncoded = false;

        while (offset < endOffset) {
            char ch = text.charAt(offset);

            if (LINEAR_WHITESPACE.indexOf(ch) != -1) { startWhiteSpace = offset;
                while (offset < endOffset) {
                    ch = text.charAt(offset);
                    if (LINEAR_WHITESPACE.indexOf(ch) != -1) { offset++;
                    } else {
                        endWhiteSpace = offset;
                        break;
                    }
                }
            } else {
                int wordStart = offset;

                while (offset < endOffset) {
                    ch = text.charAt(offset);
                    if (LINEAR_WHITESPACE.indexOf(ch) == -1) { offset++;
                    } else {
                        break;
                    }

                    }
                String word = text.substring(wordStart, offset);
                if (word.startsWith(ENCODED_TOKEN_MARKER)) {
                    try {
                        String decodedWord = decodeWord(word);

                        if (!previousTokenEncoded && startWhiteSpace != -1) {
                            decodedText.append(text.substring(startWhiteSpace, endWhiteSpace));
                            startWhiteSpace = -1;
                        }
                        previousTokenEncoded = true;
                        decodedText.append(decodedWord);
                        continue;

                    } catch (ParseException e) {
                        }
                }
                if (startWhiteSpace != -1) {
                    decodedText.append(text.substring(startWhiteSpace, endWhiteSpace));
                    startWhiteSpace = -1;
                }
                previousTokenEncoded = false;
                decodedText.append(word);
            }
        }

        return decodedText.toString();
    }

    private static String decodeWord(String word) throws ParseException, UnsupportedEncodingException {
        if (!word.startsWith(ENCODED_TOKEN_MARKER)) {
            throw new ParseException("Invalid RFC 2047 encoded-word: " + word);
        }

        int charsetPos = word.indexOf('?', 2);
        if (charsetPos == -1) {
            throw new ParseException("Missing charset in RFC 2047 encoded-word: " + word);
        }

        String charset = word.substring(2, charsetPos).toLowerCase(Locale.ENGLISH);

        int encodingPos = word.indexOf('?', charsetPos + 1);
        if (encodingPos == -1) {
            throw new ParseException("Missing encoding in RFC 2047 encoded-word: " + word);
        }

        String encoding = word.substring(charsetPos + 1, encodingPos);

        int encodedTextPos = word.indexOf(ENCODED_TOKEN_FINISHER, encodingPos + 1);
        if (encodedTextPos == -1) {
            throw new ParseException("Missing encoded text in RFC 2047 encoded-word: " + word);
        }

        String encodedText = word.substring(encodingPos + 1, encodedTextPos);

        if (encodedText.length() == 0) {
            return "";
        }

        try {
            ByteArrayOutputStream out = new ByteArrayOutputStream(encodedText.length());

            byte[] encodedData = encodedText.getBytes(StandardCharsets.US_ASCII);

            if (encoding.equals(BASE64_ENCODING_MARKER)) {
                Base64Decoder.decode(encodedData, out);
            } else if (encoding.equals(QUOTEDPRINTABLE_ENCODING_MARKER)) { QuotedPrintableDecoder.decode(encodedData, out);
            } else {
                throw new UnsupportedEncodingException("Unknown RFC 2047 encoding: " + encoding);
            }
            byte[] decodedData = out.toByteArray();
            return new String(decodedData, javaCharset(charset));
        } catch (IOException e) {
            throw new UnsupportedEncodingException("Invalid RFC 2047 encoding");
        }
    }

    private static String javaCharset(String charset) {
        if (charset == null) {
            return null;
        }

        String mappedCharset = MIME2JAVA.get(charset.toLowerCase(Locale.ENGLISH));
        if (mappedCharset == null) {
            return charset;
        }
        return mappedCharset;
    }

}

