public class AsyncListenerHandler {
	public static final ReportType REPORT_HANDLER_NOT_STARTED = new ReportType(
		"Plugin %s did not start the asynchronous handler %s by calling start() or syncStart().");

	private static final PacketEvent INTERUPT_PACKET = new PacketEvent(new Object());

	private static final PacketEvent WAKEUP_PACKET = new PacketEvent(new Object());

	private static final int TICKS_PER_SECOND = 20;

	private static final AtomicInteger nextID = new AtomicInteger();

	private static final int DEFAULT_CAPACITY = 1024;

	private volatile boolean cancelled;

	private final AtomicInteger started = new AtomicInteger();

	private PacketListener listener;

	private AsyncFilterManager filterManager;
	private NullPacketListener nullPacketListener;

	private ArrayBlockingQueue<PacketEvent> queuedPackets = new ArrayBlockingQueue<PacketEvent>(DEFAULT_CAPACITY);

	private final Set<Integer> stoppedTasks = new HashSet<Integer>();
	private final Object stopLock = new Object();

	private int syncTask = -1;

	private Thread mainThread;

	private int warningTask;

	private TimedListenerManager timedManager = TimedListenerManager.getInstance();

	AsyncListenerHandler(Thread mainThread, AsyncFilterManager filterManager, PacketListener listener) {
		if (filterManager == null)
			throw new IllegalArgumentException("filterManager cannot be NULL");
		if (listener == null)
			throw new IllegalArgumentException("listener cannot be NULL");

		this.mainThread = mainThread;
		this.filterManager = filterManager;
		this.listener = listener;
		startWarningTask();
	}

	private void startWarningTask() {
		warningTask = filterManager.getScheduler().scheduleSyncDelayedTask(getPlugin(), new Runnable() {
			@Override
			public void run() {
				ProtocolLibrary.getErrorReporter().reportWarning(AsyncListenerHandler.this, Report.
						newBuilder(REPORT_HANDLER_NOT_STARTED).
						messageParam(listener.getPlugin(), AsyncListenerHandler.this).
						build()
				);
			}
		}, 2 * TICKS_PER_SECOND);
	}

	private void stopWarningTask() {
		int taskId = warningTask;

		if (warningTask >= 0) {
			filterManager.getScheduler().cancelTask(taskId);
			warningTask = -1;
		}
	}

	public boolean isCancelled() {
		return cancelled;
	}

	public PacketListener getAsyncListener() {
		return listener;
	}

	void setNullPacketListener(NullPacketListener nullPacketListener) {
		this.nullPacketListener = nullPacketListener;
	}

	PacketListener getNullPacketListener() {
		return nullPacketListener;
	}

	public Plugin getPlugin() {
		return listener != null ? listener.getPlugin() : null;
	}

	public void cancel() {
		close();
	}

	public void enqueuePacket(PacketEvent packet) {
		if (packet == null)
			throw new IllegalArgumentException("packet is NULL");

		queuedPackets.add(packet);
	}

	public AsyncRunnable getListenerLoop() {
		return new AsyncRunnable() {

			private final AtomicBoolean firstRun = new AtomicBoolean();
			private final AtomicBoolean finished = new AtomicBoolean();
			private final int id = nextID.incrementAndGet();

			@Override
			public int getID() {
				return id;
			}

			@Override
			public void run() {
				if (firstRun.compareAndSet(false, true)) {
					listenerLoop(id);

					synchronized (stopLock) {
						stoppedTasks.remove(id);
						stopLock.notifyAll();
						finished.set(true);
					}

				} else {
					if (finished.get())
						throw new IllegalStateException(
							"This listener has already been run. Create a new instead.");
					else
						throw new IllegalStateException(
							"This listener loop has already been started. Create a new instead.");
				}
			}

			@Override
			public boolean stop() throws InterruptedException {
				synchronized (stopLock) {
					if (!isRunning())
						return false;

					stoppedTasks.add(id);

					for (int i = 0; i < getWorkers(); i++) {
						queuedPackets.offer(WAKEUP_PACKET);
					}

					finished.set(true);
					waitForStops();
					return true;
				}
			}

			@Override
			public boolean isRunning() {
				return firstRun.get() && !finished.get();
			}

			@Override
			public boolean isFinished() {
				return finished.get();
			}
		};
	}

	public synchronized void start() {
		if (listener.getPlugin() == null)
			throw new IllegalArgumentException("Cannot start task without a valid plugin.");
		if (cancelled)
			throw new IllegalStateException("Cannot start a worker when the listener is closing.");

		final AsyncRunnable listenerLoop = getListenerLoop();

		stopWarningTask();
		scheduleAsync(new Runnable() {
			@Override
			public void run() {
				Thread thread = Thread.currentThread();

				String previousName = thread.getName();
				String workerName = getFriendlyWorkerName(listenerLoop.getID());

				thread.setName(workerName);
				listenerLoop.run();
				thread.setName(previousName);
			}
		});
	}

	public synchronized void start(Function<AsyncRunnable, Void> executor) {
		if (listener.getPlugin() == null)
			throw new IllegalArgumentException("Cannot start task without a valid plugin.");
		if (cancelled)
			throw new IllegalStateException("Cannot start a worker when the listener is closing.");

		final AsyncRunnable listenerLoop = getListenerLoop();
		final Function<AsyncRunnable, Void> delegateCopy = executor;

		scheduleAsync(new Runnable() {
			@Override
			public void run() {
				delegateCopy.apply(listenerLoop);
			}
		});
	}

	private void scheduleAsync(Runnable runnable) {
		listener.getPlugin().getServer().getScheduler().runTaskAsynchronously(listener.getPlugin(), runnable);
	}

	public String getFriendlyWorkerName(int id) {
		return String.format("Protocol Worker #%s - %s - [recv: %s, send: %s]",
				id,
				PacketAdapter.getPluginName(listener),
				fromWhitelist(listener.getReceivingWhitelist()),
				fromWhitelist(listener.getSendingWhitelist())
		);
	}

	private String fromWhitelist(ListeningWhitelist whitelist) {
		if (whitelist == null)
			return "";
		else
			return Joiner.on(", ").join(whitelist.getTypes());
	}

	public synchronized boolean syncStart() {
		return syncStart(500, TimeUnit.MICROSECONDS);
	}

	public synchronized boolean syncStart(final long time, final TimeUnit unit) {
		if (time <= 0)
			throw new IllegalArgumentException("Time must be greater than zero.");
		if (unit == null)
			throw new IllegalArgumentException("TimeUnit cannot be NULL.");

		final long tickDelay = 1;
		final int workerID = nextID.incrementAndGet();

		if (syncTask < 0) {
			stopWarningTask();

			syncTask = filterManager.getScheduler().scheduleSyncRepeatingTask(getPlugin(), new Runnable() {
				@Override
				public void run() {
					long stopTime = System.nanoTime() + unit.convert(time, TimeUnit.NANOSECONDS);

					while (!cancelled) {
						PacketEvent packet = queuedPackets.poll();

						if (packet == INTERUPT_PACKET || packet == WAKEUP_PACKET) {
							queuedPackets.add(packet);

							break;
						} else if (packet != null && packet.getAsyncMarker() != null) {
							processPacket(workerID, packet, "onSyncPacket()");
						} else {
							break;
						}

						if (System.nanoTime() < stopTime)
							break;
					}
				}
			}, tickDelay, tickDelay);

			if (syncTask < 0)
				throw new IllegalStateException("Cannot start synchronous task.");
			else
				return true;
		} else {
			return false;
		}
	}

	public synchronized boolean syncStop() {
		if (syncTask > 0) {
			filterManager.getScheduler().cancelTask(syncTask);

			syncTask = -1;
			return true;
		} else {
			return false;
		}
	}

	public synchronized void start(int count) {
		for (int i = 0; i < count; i++)
			start();
	}

	public synchronized void stop() {
		queuedPackets.add(INTERUPT_PACKET);
	}

	public synchronized void stop(int count) {
		for (int i = 0; i < count; i++)
			stop();
	}

	public synchronized void setWorkers(int count) {
		if (count < 0)
			throw new IllegalArgumentException("Number of workers cannot be less than zero.");
		if (count > DEFAULT_CAPACITY)
			throw new IllegalArgumentException("Cannot initiate more than " + DEFAULT_CAPACITY + " workers");
		if (cancelled && count > 0)
			throw new IllegalArgumentException("Cannot add workers when the listener is closing.");

		long time = System.currentTimeMillis();

		while (started.get() != count) {
			if (started.get() < count)
				start();
			else
				stop();

			if ((System.currentTimeMillis() - time) > 50)
				throw new RuntimeException("Failed to set worker count.");
		}
	}

	public synchronized int getWorkers() {
		return started.get();
	}

	private boolean waitForStops() throws InterruptedException {
		synchronized (stopLock) {
			while (stoppedTasks.size() > 0 && !cancelled) {
				stopLock.wait();
			}
			return cancelled;
		}
	}

	private void listenerLoop(int workerID) {
		if (Thread.currentThread().getId() == mainThread.getId())
			throw new IllegalStateException("Do not call this method from the main thread.");
		if (cancelled)
			throw new IllegalStateException("Listener has been cancelled. Create a new listener instead.");

		try {
			if (waitForStops())
				return;

			started.incrementAndGet();

			while (!cancelled) {
				PacketEvent packet = queuedPackets.take();

				if (packet == WAKEUP_PACKET) {
					synchronized (stopLock) {
						if (stoppedTasks.contains(workerID))
							return;
						if (waitForStops())
							return;
					}
				} else if (packet == INTERUPT_PACKET) {
					return;
				}

				if (packet != null && packet.getAsyncMarker() != null) {
					processPacket(workerID, packet, "onAsyncPacket()");
				}
			}

		} catch (InterruptedException e) {
			} finally {
			started.decrementAndGet();
		}
	}

	private void processPacket(int workerID, PacketEvent packet, String methodName) {
		AsyncMarker marker = packet.getAsyncMarker();

		try {
			synchronized (marker.getProcessingLock()) {
				marker.setListenerHandler(this);
				marker.setWorkerID(workerID);

				if (timedManager.isTiming()) {
					TimedTracker tracker = timedManager.getTracker(listener,
						packet.isServerPacket() ? ListenerType.ASYNC_SERVER_SIDE : ListenerType.ASYNC_CLIENT_SIDE);
					long token = tracker.beginTracking();

					if (packet.isServerPacket())
						listener.onPacketSending(packet);
					else
						listener.onPacketReceiving(packet);

					tracker.endTracking(token, packet.getPacketType());

				} else {
					if (packet.isServerPacket())
						listener.onPacketSending(packet);
					else
						listener.onPacketReceiving(packet);
				}
			}

		} catch (OutOfMemoryError e) {
			throw e;
		} catch (ThreadDeath e) {
			throw e;
		} catch (Throwable e) {
			filterManager.getErrorReporter().reportMinimal(listener.getPlugin(), methodName, e);
		}

		if (!marker.hasExpired()) {
			for (; marker.getListenerTraversal().hasNext(); ) {
				AsyncListenerHandler handler = marker.getListenerTraversal().next().getListener();

				if (!handler.isCancelled()) {
					handler.enqueuePacket(packet);
					return;
				}
			}
		}

		filterManager.signalFreeProcessingSlot(packet);

		filterManager.signalPacketTransmission(packet);
	}

	private synchronized void close() {
		if (!cancelled) {
			filterManager.unregisterAsyncHandlerInternal(this);
			cancelled = true;

			syncStop();

			stopThreads();
		}
	}

	private void stopThreads() {
		queuedPackets.clear();
		stop(started.get());

		synchronized (stopLock) {
			stopLock.notifyAll();
		}
	}
}

public class EmacsRenderer extends AbstractIncrementingRenderer {

    public static final String NAME = "emacs";

    protected static final String EOL = System.getProperty("line.separator", "\n");

    public EmacsRenderer() {
        super(NAME, "GNU Emacs integration.");
    }

    @Override
    public String defaultFileExtension() {
        return "emacs";
    }

    @Override
    public void renderFileViolations(Iterator<RuleViolation> violations) throws IOException {
        StringBuilder buf = new StringBuilder();
        while (violations.hasNext()) {
            RuleViolation rv = violations.next();
            buf.setLength(0);
            buf.append(determineFileName(rv.getFilename()));
            buf.append(':').append(Integer.toString(rv.getBeginLine()));
            buf.append(": ").append(rv.getDescription()).append(EOL);
            writer.write(buf.toString());
        }
    }
}

public abstract class AbstractDXRAMService<T> extends AbstractDXRAMModule<T> {
    protected AbstractDXRAMService() {
        super();
    }

    @Override
    protected boolean moduleInit(final DXRAMEngine p_engine) {
        boolean ret;

        resolveComponentDependencies(p_engine);

        try {
            ret = startService(p_engine.getConfig());
        } catch (final Exception e) {
            LOGGER.error("Starting service failed", e);

            return false;
        }

        return ret;
    }

    @Override
    protected boolean moduleShutdown() {
        return shutdownService();
    }

    protected abstract void resolveComponentDependencies(final DXRAMComponentAccessor p_componentAccessor);

    protected abstract boolean startService(final DXRAMConfig p_config);

    protected abstract boolean shutdownService();
}

@RequiredArgsConstructor
@Slf4j
public class SeasonalBuffer {

    private static final long NOT_YET_INITIALIZED = -1L;

    private int cycleLength;

    private int interval;

    private double missingValuePlaceholder;

    private double[] buffer;

    private int currIndex;

    private long lastTimestamp;

    private long firstTimestamp;

    public SeasonalBuffer(int cycleLength, int interval, double missingValuePlaceholder) {
        isStrictlyPositive(cycleLength, "Required: cycleLength > 0");
        isStrictlyPositive(interval, "Required: interval > 0");
        notNull(interval, "Required: missingValue");
        this.cycleLength = cycleLength;
        this.interval = interval;
        this.missingValuePlaceholder = missingValuePlaceholder;
        initState();
    }

    public double updateWhilePadding(MetricData metricData) {
        notNull(metricData, "metricData can't be null");
        checkValidTimestamp(metricData);
        padMissingDataPoints(metricData);
        double oldValue = getValueForCurrentIndex();
        updateBuffer(metricData);
        return oldValue;
    }

    public boolean isReady() {
        return this.lastTimestamp - (this.firstTimestamp + (cycleLength * interval)) >= 0;
    }

    private void initState() {
        this.firstTimestamp = NOT_YET_INITIALIZED;
        this.lastTimestamp = NOT_YET_INITIALIZED;
        this.buffer = new double[this.cycleLength];
        Arrays.fill(this.buffer, this.missingValuePlaceholder);
        this.currIndex = 0;
    }

    private void padMissingDataPoints(MetricData metricData) {
        if (isFirstDataPoint()) { firstTimestamp = metricData.getTimestamp();
            log.debug("First data point received for Seasonal Buffer. " +
                            "Buffer has cycleLength={}, interval={}, and starts at timestamp {} ({}). " +
                            "First metric details: {}",
                    this.cycleLength, this.interval, metricData.getTimestamp(), dateStr(metricData.getTimestamp()), metricData);
            return;
        }
        int numSkippedDataPoints = countIntervalsSkippedSinceLastTimestamp(metricData);
        insertSkippedDataPoints(numSkippedDataPoints);
    }

    private void updateBuffer(MetricData metricData) {
        setBufferValue(metricData.getValue());
        this.currIndex = (this.currIndex + 1) % this.buffer.length;
        this.lastTimestamp = metricData.getTimestamp();
    }

    private double getValueForCurrentIndex() {
        return this.buffer[currIndex];
    }

    private void insertSkippedDataPoints(int numSkippedDataPoints) {
        IntStream.range(0, numSkippedDataPoints).forEach(__ -> {
            setBufferValue(this.missingValuePlaceholder);
            currIndex = (currIndex + 1) % this.buffer.length;
        });
    }

    private void setBufferValue(double value) {
        String valueStr = value == this.missingValuePlaceholder ? "MISSING PLACEHOLDER value (" + this.missingValuePlaceholder + ")" : ("value " + value);
        log.debug("Updating buffer index " + currIndex + " with " + valueStr);
        this.buffer[currIndex] = value;
    }

    private int countIntervalsSkippedSinceLastTimestamp(MetricData metricData) {
        int timeDifference = new Long(metricData.getTimestamp() - lastTimestamp).intValue();
        int intervalsSkipped = timeDifference / this.interval - 1;
        log.debug("Current metric timestamp " + metricData.getTimestamp() + " (" + dateStr(metricData.getTimestamp()) + ") includes " +
                intervalsSkipped + " skipped data points since last timestamp " + lastTimestamp + " (" + dateStr(lastTimestamp) + ")");
        return intervalsSkipped;
    }

    private void checkValidTimestamp(MetricData metricData) {
        long timestamp = metricData.getTimestamp();
        if (timestamp < lastTimestamp) {
            String error = String.format("Current metric %s has a timestamp (%s) dated before the last data point we observed (which had timestamp %d=%s)",
                    metricData, dateStr(timestamp), lastTimestamp, dateStr(lastTimestamp));
            throw new MetricDeliveryTimeException(error);
        }
        if (timestamp == lastTimestamp) {
            String error = String.format("Current metric %s has the same timestamp (%s) as the last data point observed (%s)", metricData, dateStr(lastTimestamp), dateStr(timestamp));
            throw new MetricDeliveryDuplicateException(error);
        }
    }

    private String dateStr(long timestamp) {
        return DateUtil.epochSecondToString(timestamp);
    }

    private boolean isFirstDataPoint() {
        return lastTimestamp == NOT_YET_INITIALIZED;
    }
}

public class RexMultisetUtil {
  private static final Set<SqlOperator> MULTISET_OPERATORS =
      ImmutableSet.of(
          SqlStdOperatorTable.CARDINALITY,
          SqlStdOperatorTable.CAST,
          SqlStdOperatorTable.ELEMENT,
          SqlStdOperatorTable.ELEMENT_SLICE,
          SqlStdOperatorTable.MULTISET_EXCEPT_ALL,
          SqlStdOperatorTable.MULTISET_EXCEPT,
          SqlStdOperatorTable.MULTISET_INTERSECT_ALL,
          SqlStdOperatorTable.MULTISET_INTERSECT,
          SqlStdOperatorTable.MULTISET_UNION_ALL,
          SqlStdOperatorTable.MULTISET_UNION,
          SqlStdOperatorTable.IS_A_SET,
          SqlStdOperatorTable.MEMBER_OF,
          SqlStdOperatorTable.SUBMULTISET_OF);

  private RexMultisetUtil() {
  }

  public static boolean containsMixing(RexProgram program) {
    RexCallMultisetOperatorCounter counter =
        new RexCallMultisetOperatorCounter();
    for (RexNode expr : program.getExprList()) {
      counter.reset();
      expr.accept(counter);

      if ((counter.totalCount != counter.multisetCount)
          && (0 != counter.multisetCount)) {
        return true;
      }
    }
    return false;
  }

  public static boolean containsMixing(RexNode node) {
    RexCallMultisetOperatorCounter counter =
        new RexCallMultisetOperatorCounter();
    node.accept(counter);
    return (counter.totalCount != counter.multisetCount)
        && (0 != counter.multisetCount);
  }

  public static boolean containsMultiset(final RexNode node, boolean deep) {
    return null != findFirstMultiset(node, deep);
  }

  public static boolean containsMultiset(List<RexNode> nodes, boolean deep) {
    for (RexNode node : nodes) {
      if (containsMultiset(node, deep)) {
        return true;
      }
    }
    return false;
  }

  public static boolean containsMultiset(RexProgram program) {
    return containsMultiset(program.getExprList(), true);
  }

  public static boolean isMultisetCast(RexCall call) {
    if (!call.getOperator().equals(SqlStdOperatorTable.CAST)) {
      return false;
    }
    return call.getType().getSqlTypeName() == SqlTypeName.MULTISET;
  }

  public static RexCall findFirstMultiset(final RexNode node, boolean deep) {
    if (node instanceof RexFieldAccess) {
      return findFirstMultiset(
          ((RexFieldAccess) node).getReferenceExpr(),
          deep);
    }

    if (!(node instanceof RexCall)) {
      return null;
    }
    final RexCall call = (RexCall) node;
    RexCall firstOne = null;
    for (SqlOperator op : MULTISET_OPERATORS) {
      firstOne = RexUtil.findOperatorCall(op, call);
      if (null != firstOne) {
        if (firstOne.getOperator().equals(SqlStdOperatorTable.CAST)
            && !isMultisetCast(firstOne)) {
          firstOne = null;
          continue;
        }
        break;
      }
    }

    if (!deep && (firstOne != call)) {
      return null;
    }
    return firstOne;
  }

  private static class RexCallMultisetOperatorCounter
      extends RexVisitorImpl<Void> {
    int totalCount = 0;
    int multisetCount = 0;

    RexCallMultisetOperatorCounter() {
      super(true);
    }

    void reset() {
      totalCount = 0;
      multisetCount = 0;
    }

    public Void visitCall(RexCall call) {
      ++totalCount;
      if (MULTISET_OPERATORS.contains(call.getOperator())) {
        if (!call.getOperator().equals(SqlStdOperatorTable.CAST)
            || isMultisetCast(call)) {
          ++multisetCount;
        }
      }
      return super.visitCall(call);
    }
  }
}

public class RealtimeMidiParserListener extends TrackTimeManager implements ParserListener
{
	private boolean endDaemon;
    private int bpm = MidiDefaults.DEFAULT_TEMPO_BEATS_PER_MINUTE;
    private long originalClockTimeInMillis;
    private long activeTimeInMillis;
    private Map<Long, List<Command>> millisToScheduledCommands;
    private Map<Long, List<ScheduledEvent>> millisToScheduledEvents;
    private List<RealtimeInterpolator> interpolators;
    private RealtimePlayer realtimePlayer;

    public RealtimeMidiParserListener(RealtimePlayer player) throws MidiUnavailableException {
    	super();
    	this.realtimePlayer = player;
    	this.millisToScheduledCommands = new HashMap<Long, List<Command>>();
    	this.millisToScheduledEvents = new HashMap<Long, List<ScheduledEvent>>();
    	this.interpolators = new ArrayList<RealtimeInterpolator>();
    	this.originalClockTimeInMillis = System.currentTimeMillis(); 
    	startDaemon();
    }

    private long getDeltaClockTimeInMillis() {
    	return System.currentTimeMillis() - this.originalClockTimeInMillis;
    }

    public long getCurrentTime() {
        return getDeltaClockTimeInMillis();
    }

    private void startDaemon() {
    	Runnable daemon = new Runnable() {
    		private long lastMillis = 0L;

    		public void run() {
    			while (!endDaemon) {
    				setAllTrackBeatTime(getDeltaClockTimeInMillis());

    				long deltaMillis = getDeltaClockTimeInMillis() - lastMillis;
    				if (deltaMillis > 0) {
	    				for (long time = lastMillis; time < lastMillis+deltaMillis; time++) {
	    					setActiveTimeInMillis(time);
	    					executeScheduledCommands(time);
	    					executeScheduledEvents(time);
	    					updateInterpolators(time);
	    				}
    				}
    				this.lastMillis = this.lastMillis + deltaMillis;
    			}
    		}
    	};

    	Thread t = new Thread(daemon);
    	t.start();
    }

	private void executeScheduledCommands(long time) {
		if (millisToScheduledCommands.containsKey(time)) {
			List<Command> commands = millisToScheduledCommands.get(time);
			for (Command command : commands) {
				command.execute();
			}
		}
    }

	private void executeScheduledEvents(long time) {
    	if (millisToScheduledEvents.containsKey(time)) {
			List<ScheduledEvent> scheduledEvents = millisToScheduledEvents.get(time);
			for (ScheduledEvent event : scheduledEvents) {
				event.execute(realtimePlayer, time);
			}
    	}
    }

	private void updateInterpolators(long time) {
		for (RealtimeInterpolator interpolator : interpolators) {
			if (!interpolator.isStarted()) {
				interpolator.start(time);
			}
			if (interpolator.isActive()) {
				long elapsedTime = time - interpolator.getStartTime();
				double percentComplete = elapsedTime / interpolator.getDurationInMillis();
				interpolator.update(realtimePlayer, elapsedTime, percentComplete);
				if (elapsedTime == interpolator.getDurationInMillis()) {
					interpolator.end();
				}
			}
		}
    }

    public void finish() {
        this.endDaemon = true;
    }

    public RealtimePlayer getRealtimePlayer() {
    	return this.realtimePlayer;
    }

    private void setActiveTimeInMillis(long timeInMillis) {
    	this.activeTimeInMillis = timeInMillis;
    }

    private long getNextAvailableTimeInMillis(long timeInMillis) {
    	if (timeInMillis <= activeTimeInMillis) {
    		timeInMillis += activeTimeInMillis+1;
    	}
    	return timeInMillis;
    }

    public void scheduleCommand(long timeInMillis, Command command) {
    	timeInMillis = getNextAvailableTimeInMillis(timeInMillis);
    	List<Command> commands = millisToScheduledCommands.get(timeInMillis);
    	if (commands == null) {
    		commands = new ArrayList<Command>();
    		millisToScheduledCommands.put(timeInMillis, commands);
    	}
    	commands.add(command);
    }

    public void scheduleEvent(long timeInMillis, ScheduledEvent event) {
    	timeInMillis = getNextAvailableTimeInMillis(timeInMillis);
    	List<ScheduledEvent> events = millisToScheduledEvents.get(timeInMillis);
    	if (events == null) {
    		events = new ArrayList<ScheduledEvent>();
    		millisToScheduledEvents.put(timeInMillis, events);
    	}
    	events.add(event);
    }

    public void unscheduleEvent(long timeInMillis, ScheduledEvent event) {
    	List<ScheduledEvent> events = millisToScheduledEvents.get(timeInMillis);
    	if (events == null) {
    		return;
    	}
    	events.remove(event);
    }

    @Override
    public void beforeParsingStarts() { }

    @Override
    public void afterParsingFinished() { }

    @Override
    public void onTrackChanged(final byte track) {
        setCurrentTrack(track);
    	scheduleCommand((long)getTrackBeatTime(), new Command() {
    		public void execute() {
    			getRealtimePlayer().changeTrack(track);
    		}
    	});        
    }

    @Override 
    public void onLayerChanged(byte layer) { 
        setCurrentLayer(layer);
    }

    @Override 
    public void onInstrumentParsed(final byte instrument) {
    	scheduleCommand((long)getTrackBeatTime(), new Command() {
    		public void execute() {
    			getRealtimePlayer().changeInstrument(instrument);
    		}
    	});
    }

    @Override
    public void onTempoChanged(int tempoBPM) {
    	this.bpm = tempoBPM;
    }

    @Override
    public void onKeySignatureParsed(byte key, byte scale) { }

    @Override 
    public void onTimeSignatureParsed(byte numerator, byte powerOfTwo) { }

    @Override
    public void onBarLineParsed(long time) { }

    @Override 
    public void onTrackBeatTimeBookmarked(String timeBookmarkID) { }

    @Override 
    public void onTrackBeatTimeBookmarkRequested(String timeBookmarkID) { }

    @Override 
    public void onTrackBeatTimeRequested(double time) { }

    @Override
    public void onPitchWheelParsed(final byte lsb, final byte msb) {
    	scheduleCommand((long)getTrackBeatTime(), new Command() {
    		public void execute() {
    			getRealtimePlayer().setPitchBend(lsb + (msb << 7));
    		}
    	});
    }

    @Override
    public void onChannelPressureParsed(final byte pressure) {
    	scheduleCommand((long)getTrackBeatTime(), new Command() {
    		public void execute() {
    			getRealtimePlayer().changeChannelPressure(pressure);
    		}
    	});
    }

    @Override
    public void onPolyphonicPressureParsed(final byte key, final byte pressure) {
    	scheduleCommand((long)getTrackBeatTime(), new Command() {
    		public void execute() {
    			getRealtimePlayer().changePolyphonicPressure(key, pressure);
    		}
    	});
    }

    @Override 
    public void onSystemExclusiveParsed(byte... bytes) { }

    @Override
    public void onControllerEventParsed(final byte controller, final byte value) {
    	scheduleCommand((long)getTrackBeatTime(), new Command() {
    		public void execute() {
    			getRealtimePlayer().changeController(controller, value);
    		}
    	});
    }

    @Override 
    public void onLyricParsed(String lyric) { } 

    @Override 
    public void onMarkerParsed(String marker) { } 

    @Override 
    public void onFunctionParsed(String id, Object message) { }

    @Override
    public void onNotePressed(Note note) { }

    @Override
    public void onNoteReleased(Note note) { }

    @Override  
    public void onNoteParsed(final Note note) {
    	if (note.getDuration() == 0.0) {
    		note.useDefaultDuration();
    	}

    	if (note.isFirstNote()) {
    		setInitialNoteBeatTimeForHarmonicNotes(getTrackBeatTime()); 
    	}

    	if (note.isHarmonicNote()) {
    		setTrackBeatTime(getInitialNoteBeatTimeForHarmonicNotes());
    	} 

    	if (note.isRest()) {
    		advanceTrackBeatTime(convertBeatsToMillis(note.getDuration()));  
    		return;
    	}

    	if (!note.isEndOfTie()) {
        	scheduleCommand((long)getTrackBeatTime(), new Command() {
        		public void execute() {
        			getRealtimePlayer().startNote(note);
        		}
        	});    		
    	}

    	advanceTrackBeatTime(convertBeatsToMillis(note.getDuration()));  

    	if (!note.isStartOfTie()) {
        	scheduleCommand((long)getTrackBeatTime(), new Command() {
        		public void execute() {
        			getRealtimePlayer().stopNote(note);
        		}
        	});    		
    	}
    }

    @Override 
    public void onChordParsed(Chord chord) {
    	for (Note note : chord.getNotes()) {
    		this.onNoteParsed(note);
    	}
    }


    public void onEventScheduled(long timeInMillis, ScheduledEvent event) {
    	scheduleEvent(timeInMillis, event);
    }

    public void onEventUnscheduled(long timeInMillis, ScheduledEvent event) {
    	unscheduleEvent(timeInMillis, event);
    }

    public void onInterpolatorStarted(RealtimeInterpolator interpolator, long durationInMillis) {
        interpolator.setDurationInMillis(durationInMillis);
    	interpolators.add(interpolator);
    }

    public void onInterpolatorStopping(RealtimeInterpolator interpolator) {
    	interpolators.remove(interpolator);
    }

    private long convertBeatsToMillis(double beats) {
    	return (long)((beats / bpm) * MidiDefaults.MS_PER_MIN * 4); 
    }

    interface Command {
    	public void execute();
    }
}

