    @Override
    public void clear() {
        throw new UnsupportedOperationException(ERROR_MSG);
    }

    @Override
    public UnmodifiableTableImpl<E> clone() {
        return new UnmodifiableTableImpl<E>(inner.clone());
    }

     @Override
