    }

}

abstract class WhitelistEntryProtocol extends WhitelistEntryPart {

    private static final String HTTP = "http";
    private static final String HTTPS = "https";

    static WhitelistEntryProtocol parse(final String candidate) {
        if (StringUtils.isBlank(candidate) || HTTPS.equalsIgnoreCase(candidate)) {
            return Https.INSTANCE;
        }
        if (HTTP.equalsIgnoreCase(candidate)) {
            return Http.INSTANCE;
        }
        return InvalidProtocol.INSTANCE;
    }

    private WhitelistEntryProtocol(final boolean valid, final String effective, final String error) {
        super(valid, effective, error);
    }

    abstract int getDefaultPort();

    @Override
    public boolean matches(final URL url) {
        return effective().equalsIgnoreCase(url.getProtocol());
    }

    private static class Http extends WhitelistEntryProtocol {
        private static final Http INSTANCE = new Http();

        private Http() {
            super(true, HTTP, null);
        }

        @Override
        int getDefaultPort() {
            return 80;
        }
    }

    private static class Https extends WhitelistEntryProtocol {
        private static final Https INSTANCE = new Https();

        private Https() {
            super(true, HTTPS, null);
        }

        @Override
        int getDefaultPort() {
            return 443;
        }
    }

    private static class InvalidProtocol extends WhitelistEntryProtocol {
        private static final InvalidProtocol INSTANCE = new InvalidProtocol();

        private InvalidProtocol() {
            super(false, null, Translator.R("WleInvalidProtocol"));
        }

        @Override
        public boolean matches(final URL url) {
            return false;
        }

        @Override
        int getDefaultPort() {
            return -2;
        }
    }
}

public class FilteredProcessSelection implements Serializable {
	private static final long serialVersionUID = 1L;

	private List<ProcessSelection> selection;
	private TimeRange timeRange;

	public FilteredProcessSelection(CpuActivityList cpuActivityList) {

		Set<String> processNames = cpuActivityList.getAllProcesses();
		selection = new ArrayList<ProcessSelection>(processNames.size());
		for (String process : processNames) {
			selection.add(new ProcessSelection(process));
		}
	}

	public FilteredProcessSelection(FilteredProcessSelection filteredProcessSelection) {

		this.timeRange = filteredProcessSelection.timeRange;
		List<ProcessSelection> fps = filteredProcessSelection.selection;
		ProcessSelection processSelection;
		this.selection = new ArrayList<ProcessSelection>(fps.size());
		for (ProcessSelection pSelection : fps) {
			processSelection = new ProcessSelection(pSelection.getProcessName(), pSelection.isSelected());
			this.selection.add(processSelection);
		}
	}

	public Collection<ProcessSelection> getProcessSelection() {
		return selection;
	}

}

