class EventExpressionEvaluator extends CachedExpressionEvaluator {

	private final Map<ExpressionKey, Expression> conditionCache = new ConcurrentHashMap<>(64);


	public boolean condition(String conditionExpression, ApplicationEvent event, Method targetMethod,
			AnnotatedElementKey methodKey, Object[] args, @Nullable BeanFactory beanFactory) {

		EventExpressionRootObject root = new EventExpressionRootObject(event, args);
		MethodBasedEvaluationContext evaluationContext = new MethodBasedEvaluationContext(
				root, targetMethod, args, getParameterNameDiscoverer());
		if (beanFactory != null) {
			evaluationContext.setBeanResolver(new BeanFactoryResolver(beanFactory));
		}

		return (Boolean.TRUE.equals(getExpression(this.conditionCache, methodKey, conditionExpression).getValue(
				evaluationContext, Boolean.class)));
	}

}

public class TemplateResolver extends Resolver {

    protected TemplateResolver(ProcessLog processLog) {
        super(processLog);
    }

    @Override
    public void resolve(LandscapeDescription landscape) {

        Map<ItemDescription, List<String>> templatesAndTargets = new HashMap<>();
        landscape.getSourceReferences().forEach(ref -> {
            ref.getAssignTemplates().forEach((key, identifiers) -> {
                ItemDescription template = landscape.getTemplates().get(key);
                template.setName(null);
                templatesAndTargets.put(template, identifiers);
            });
        });

        templatesAndTargets.forEach((template, identifiers) -> applyTemplateValues(template, identifiers, landscape));
    }

    private void applyTemplateValues(
            ItemDescription template,
            List<String> templateTargets,
            LandscapeDescription landscape
    ) {
        templateTargets.forEach(term ->
                landscape.getItemDescriptions().query(term)
                        .forEach(item -> ItemDescriptionValues.assignSafeNotNull(item, template))
        );
    }
}

public class RunShellGenerator {
	public String generate(String cmdLine) throws CommandParseException {
		Commander commander = CommandParser.parse(cmdLine);
		StringBuffer sb = new StringBuffer("");
		sb.append("#! /bin/bash\n" +
				"#standalone\n" +
				"echo +++++++++++++log information+++++++++++\n" +
				"source /etc/profile\n" +
				"echo $PATH \n" +
				"exit_code=0\n\n" +
				"echo mk action-dir...\n" +
				"actionId=${3##*/}\n" +
				"mkdir $actionId\n" +
				"echo download libaries ...\n" +
				"hdfs dfs -get $2/* 1>>stdout 2>>stderr\n" +
				"#((exit_code=exit_code|$?))\n" +
				"chmod -R +x *\n" +
				"echo download input hdfs files ...\n");

		int idx = 3;
		for (FileHolder fp : commander.getInFileHolders()) {
			idx++;
			if(DatasetType.DIRECTORY.getDesc().equals(fp.getContentType()))
			{
				sb.append(String.format(
								"hdfs dfs -get ${%d}/${%d##*/} ${%d##*/} 1>>stdout 2>>stderr\n" +
								"fi\n\n", idx + 1, idx + 1, idx + 1));

			}else{
				sb.append(String.format(
						"if [ \"${%d}\" = \"HFile\" ]\n" +
								"then\n" +
								"\t hdfs dfs -getmerge ${%d}/${%d##*/} ${%d##*/} 1>>stdout 2>>stderr\n",
								idx, idx + 1, idx + 1, idx + 1));

				sb.append(String.format(
						"else\n" +
								"\t hdfs dfs -get ${%d}/${%d##*/} ${%d##*/} 1>>stdout 2>>stderr\n" +
								"fi\n\n", idx + 1, idx + 1, idx + 1));
			}
			idx++;
		}

		int outIdx = idx;  for (FileHolder fp : commander.getOutFileHolders()) {
			idx++;
			if(DatasetType.DIRECTORY.getDesc().equals(fp.getContentType()))
			{
				sb.append(String.format("mkdir ${%d##*/}\n", idx));
			}
		}

		sb.append("\necho execute command line\n" +
				"echo $1 1>>stdout 2>>stderr\n" +
				"eval $1 1>>stdout 2>>stderr\n" +
				"((exit_code=exit_code|$?))\n\n");

		idx = outIdx;
		for (FileHolder fp : commander.getOutFileHolders()) {
			idx++;
			sb.append( String.format("mkdir $actionId/${%d##*/}\n", idx));
			sb.append( String.format("mv ${%d##*/} $actionId/${%d##*/} 1>>stdout 2>>stderr\n", idx, idx) );
		}

		sb.append("mv stdout stderr $actionId\n" +
				"hdfs dfs -put $actionId $3\n" +
				"hdfs dfs -chmod -R 777 $3\n\n" +
				"exit $exit_code");

		System.out.print(sb.toString());
		return sb.toString();
	}
}

public class UpdateHelper {

    public static boolean areUpdatesNecessary(Context context) {
        for (Update update : Update.values()) {
            if (update.getIsNecessary().apply(context)) {
                return true;
            }
        }
        return false;
    }

    public static void runUpdatesIfNecessary(Context context) {
        for (Update update : Update.values()) {
            if (update.getIsNecessary().apply(context)) {
                update.getRunUpdate().onCallback(context);
            }
        }
    }

    private enum Update {

        Update1(new Function<Context, Boolean>() {

            @Override
            public Boolean apply(Context context) {
                SharedPreferences sharedPrefs = PreferenceManager.getDefaultSharedPreferences(context);
                String bufferPref = sharedPrefs.getString(
                        context.getString(R.string.pref_buffer), context.getString(R.string.pref_buffer_choice_main));

                return bufferPref.equals("all_combined");
            }
        }, new Callback<Context>() {

            @Override
            public void onCallback(Context context) {
                SharedPreferences sharedPrefs = PreferenceManager.getDefaultSharedPreferences(context);
                String bufferPref = sharedPrefs.getString(
                        context.getString(R.string.pref_buffer), context.getString(R.string.pref_buffer_choice_main));

                if (bufferPref.equals("all_combined")) {
                    Editor editor = sharedPrefs.edit();
                    editor.putString(context.getString(R.string.pref_buffer), "main,events,radio");
                    editor.apply();
                }
            }
        }),

        Update2(new Function<Context, Boolean>() {

            @Override
            public Boolean apply(Context context) {
                return SaveLogHelper.checkIfSdCardExists() && SaveLogHelper.legacySavedLogsDirExists();
            }
        }, new Callback<Context>() {

            @Override
            public void onCallback(Context context) {
                if (SaveLogHelper.checkIfSdCardExists()) {
                    SaveLogHelper.moveLogsFromLegacyDirIfNecessary();
                }
            }
        }),

        Update3(new Function<Context, Boolean>() {

            @Override
            public Boolean apply(Context context) {

                boolean isJellyBean = VersionHelper.getVersionSdkIntCompat() >= VersionHelper.VERSION_JELLYBEAN;

                return isJellyBean && !PreferenceHelper.getJellybeanRootRan(context);
            }
        }, new Callback<Context>() {

            @Override
            public void onCallback(Context context) {
                SuperUserHelper.requestRoot(context);
            }
        }),;

        private Function<Context, Boolean> isNecessary;
        private Callback<Context> runUpdate;

        Update(Function<Context, Boolean> isNecessary, Callback<Context> runUpdate) {
            this.isNecessary = isNecessary;
            this.runUpdate = runUpdate;
        }

        public Function<Context, Boolean> getIsNecessary() {
            return isNecessary;
        }

        public Callback<Context> getRunUpdate() {
            return runUpdate;
        }
    }
}

public class ID3v1Tag extends AbstractID3v1Tag implements TagFieldContainer {
  static final String TYPE_COMMENT = "comment";
  private static final int FIELD_COMMENT_LENGTH = 30;
  private static final int FIELD_COMMENT_POS = 97;
  static final int BYTE_TO_UNSIGNED = 0xff;
  static final int GENRE_UNDEFINED = 0xff;
  private static final JLogger LOG = JLoggers.get(ID3v1Tag.class, EalvaTagLog.MARKER);
  private static final ImmutableMap<FieldKey, ID3v1FieldKey> tagFieldToID3v1Field;
  static final ImmutableMap<FieldKey, ID3v1FieldKey> tagFieldToID3v11Field;
  private static final byte RELEASE = 1;
  private static final byte MAJOR_VERSION = 0;
  private static final byte REVISION = 0;

  static {
    final ImmutableMap.Builder<FieldKey, ID3v1FieldKey> builder = ImmutableMap.builder();
    builder.put(FieldKey.ARTIST, ID3v1FieldKey.ARTIST)
           .put(FieldKey.ALBUM, ID3v1FieldKey.ALBUM)
           .put(FieldKey.TITLE, ID3v1FieldKey.TITLE)
           .put(FieldKey.YEAR, ID3v1FieldKey.YEAR)
           .put(FieldKey.GENRE, ID3v1FieldKey.GENRE)
           .put(FieldKey.COMMENT, ID3v1FieldKey.COMMENT);
    tagFieldToID3v1Field = builder.build();

    tagFieldToID3v11Field = ImmutableMap.<FieldKey, ID3v1FieldKey>builder()
        .putAll(tagFieldToID3v1Field)
        .put(FieldKey.TRACK, ID3v1FieldKey.TRACK)
        .build();

  }

  protected String album = "";
  protected String artist = "";
  protected String comment = "";
  protected String title = "";
  protected String year = "";
  protected byte genre = (byte)-1;

  public ID3v1Tag() {
  }

  public ID3v1Tag(ID3v1Tag copyObject) {
    this.album = copyObject.album;
    this.artist = copyObject.artist;
    this.comment = copyObject.comment;
    this.title = copyObject.title;
    this.year = copyObject.year;
    this.genre = copyObject.genre;
  }

  public ID3v1Tag(BaseID3Tag mp3tag) {
    if (mp3tag != null) {
      ID3v11Tag convertedTag;
      if (mp3tag instanceof ID3v1Tag) {
        throw new UnsupportedOperationException("Copy Constructor not called. Please type cast the argument");
      }
      convertedTag = new ID3v11Tag(mp3tag);
      this.album = convertedTag.album;
      this.artist = convertedTag.artist;
      this.comment = convertedTag.comment;
      this.title = convertedTag.title;
      this.year = convertedTag.year;
      this.genre = convertedTag.genre;
    }
  }

  public ID3v1Tag(FileOperator fileOperator, String loggingFilename) throws TagNotFoundException, IOException {
    FileChannel fileChannel = fileOperator.getFileChannel();
    setLoggingFilename(loggingFilename);
    ByteBuffer byteBuffer;

    fileChannel.position(fileChannel.size() - TAG_LENGTH);
    byteBuffer = ByteBuffer.allocate(TAG_LENGTH);
    fileChannel.read(byteBuffer);
    byteBuffer.flip();
    read(byteBuffer);
  }

  public void read(ByteBuffer byteBuffer) throws TagNotFoundException {
    if (!seek(byteBuffer)) {
      throw new TagNotFoundException(loggingFilename + ":" + "ID3v1 tag not found");
    }
    LOG.log(DEBUG, "%s:Reading v1 tag", loggingFilename);
    byte[] dataBuffer = new byte[TAG_LENGTH];
    byteBuffer.position(0);
    byteBuffer.get(dataBuffer, 0, TAG_LENGTH);
    title = new String(dataBuffer, FIELD_TITLE_POS, FIELD_TITLE_LENGTH, StandardCharsets.ISO_8859_1).trim();
    Matcher m = AbstractID3v1Tag.endofStringPattern.matcher(title);
    if (m.find()) {
      title = title.substring(0, m.start());
    }
    artist = new String(dataBuffer, FIELD_ARTIST_POS, FIELD_ARTIST_LENGTH, StandardCharsets.ISO_8859_1).trim();
    m = AbstractID3v1Tag.endofStringPattern.matcher(artist);
    if (m.find()) {
      artist = artist.substring(0, m.start());
    }
    album = new String(dataBuffer, FIELD_ALBUM_POS, FIELD_ALBUM_LENGTH, StandardCharsets.ISO_8859_1).trim();
    m = AbstractID3v1Tag.endofStringPattern.matcher(album);
    LOG.log(TRACE, "%s:Orig Album is:%s", loggingFilename, comment);
    if (m.find()) {
      album = album.substring(0, m.start());
      LOG.log(TRACE, "%s:Album is:%s", loggingFilename, album);
    }
    year = new String(dataBuffer, FIELD_YEAR_POS, FIELD_YEAR_LENGTH, StandardCharsets.ISO_8859_1).trim();
    m = AbstractID3v1Tag.endofStringPattern.matcher(year);
    if (m.find()) {
      year = year.substring(0, m.start());
    }
    comment = new String(dataBuffer, FIELD_COMMENT_POS, FIELD_COMMENT_LENGTH, StandardCharsets.ISO_8859_1).trim();
    m = AbstractID3v1Tag.endofStringPattern.matcher(comment);
    LOG.log(TRACE, "%s:Orig Comment is:%s", loggingFilename, comment);
    if (m.find()) {
      comment = comment.substring(0, m.start());
      LOG.log(TRACE, "%s:Comment is:%s", loggingFilename, comment);
    }
    genre = dataBuffer[FIELD_GENRE_POS];

  }

  public boolean seek(ByteBuffer byteBuffer) {
    byte[] buffer = new byte[FIELD_TAGID_LENGTH];
    byteBuffer.get(buffer, 0, FIELD_TAGID_LENGTH);
    return (Arrays.equals(buffer, TAG_ID));
  }

  public void write(RandomAccessFile file) throws IOException {
    LOG.log(DEBUG, "Saving ID3v1 tag to file");
    byte[] buffer = new byte[TAG_LENGTH];
    int i;
    String str;
    delete(file);
    file.seek(file.length());
    System.arraycopy(TAG_ID, FIELD_TAGID_POS, buffer, FIELD_TAGID_POS, TAG_ID.length);
    int offset = FIELD_TITLE_POS;
    if (TagOptionSingleton.getInstance().isId3v1SaveTitle()) {
      str = ID3Tags.truncate(title, FIELD_TITLE_LENGTH);
      for (i = 0; i < str.length(); i++) {
        buffer[i + offset] = (byte)str.charAt(i);
      }
    }
    offset = FIELD_ARTIST_POS;
    if (TagOptionSingleton.getInstance().isId3v1SaveArtist()) {
      str = ID3Tags.truncate(artist, FIELD_ARTIST_LENGTH);
      for (i = 0; i < str.length(); i++) {
        buffer[i + offset] = (byte)str.charAt(i);
      }
    }
    offset = FIELD_ALBUM_POS;
    if (TagOptionSingleton.getInstance().isId3v1SaveAlbum()) {
      str = ID3Tags.truncate(album, FIELD_ALBUM_LENGTH);
      for (i = 0; i < str.length(); i++) {
        buffer[i + offset] = (byte)str.charAt(i);
      }
    }
    offset = FIELD_YEAR_POS;
    if (TagOptionSingleton.getInstance().isId3v1SaveYear()) {
      str = ID3Tags.truncate(year, AbstractID3v1Tag.FIELD_YEAR_LENGTH);
      for (i = 0; i < str.length(); i++) {
        buffer[i + offset] = (byte)str.charAt(i);
      }
    }
    offset = FIELD_COMMENT_POS;
    if (TagOptionSingleton.getInstance().isId3v1SaveComment()) {
      str = ID3Tags.truncate(comment, FIELD_COMMENT_LENGTH);
      for (i = 0; i < str.length(); i++) {
        buffer[i + offset] = (byte)str.charAt(i);
      }
    }
    offset = FIELD_GENRE_POS;
    if (TagOptionSingleton.getInstance().isId3v1SaveGenre()) {
      buffer[offset] = genre;
    }
    file.write(buffer);
    LOG.log(DEBUG, "Saved ID3v1 tag to file");
  }

  public boolean equals(Object obj) {
    if (!(obj instanceof ID3v1Tag)) {
      return false;
    }
    ID3v1Tag object = (ID3v1Tag)obj;

    return this.album.equals(object.album) &&
        this.artist.equals(object.artist) &&
        this.comment.equals(object.comment) &&
        this.genre == object.genre &&
        this.title.equals(object.title) &&
        this.year.equals(object.year) &&
        super.equals(obj);
  }

