public class StaticSequence extends ArrayList<Class<? extends Sequenceable>> {

    @NonNull
    public static StaticSequence from (@NonNull Class<? extends Sequenceable>... elements) {
        StaticSequence sequence = new StaticSequence();

        for (Class<? extends Sequenceable> thisElement : elements) {
            sequence.add(thisElement);
        }

        return sequence;
    }

    @Nullable
    public Class<? extends Sequenceable> getPrevious (Class<? extends Sequenceable> from) {

        for (int index = 0; index < size(); index++) {
            if (get(index) == from && index > 0) {
                return get(index - 1);
            }
        }

        return null;
    }

    @Nullable
    public Class<? extends Sequenceable> getNext (Class<? extends Sequenceable> from) {

        for (int index = 0; index < size(); index ++ ) {
            if (get(index) == from && index < (size() - 1)) {
                return get(index + 1);
            }
        }

        return null;
    }
}

public class AutoPublishThread extends Thread {
	private int maxInactive = 1;
	private IServer server;
	private boolean publishBegan;
	private boolean done;
	private long lastUpdated;
	public AutoPublishThread(IServer server, int ms) {
		this.server = server;
		this.maxInactive= ms;
		this.publishBegan = false;
		this.done = false;
		this.lastUpdated = System.currentTimeMillis();
		setDaemon(true);
		setPriority(Thread.MIN_PRIORITY + 1);
	}

	@Override
	public void run() {
		boolean shouldPublish = awaitInactivity();
		if( shouldPublish) {
			publishImpl();
			setDone();
		}
	}

	protected void publishImpl() {
		try {
			server.getServerModel().publish(server, ServerManagementAPIConstants.PUBLISH_INCREMENTAL);
		} catch (CoreException e) {
			ServerPublishStateModel.LOG.error(e.getMessage(), e);
		}
	}

	protected boolean awaitInactivity() {
		if( shouldAbort()) {
			setDone();
			return false;
		}

		while( !getPublishBegan()) {
			long preSleepLastUpdated = getLastUpdated();
			sleepExpectedDuration();
			if( shouldAbort()) {
				setDone();
				return false;
			}
			synchronized ( this ) {
				if( getLastUpdated() != preSleepLastUpdated ) {
					continue;
				}
				setPublishBegan();
			}
		}
		return true;
	}

	protected boolean shouldAbort() {
		ServerState state = getServerState(); 
		int runState = state.getState(); 
		int publishState = state.getPublishState();
		if(  runState != ServerManagementAPIConstants.STATE_STARTED
				|| publishState == ServerManagementAPIConstants.PUBLISH_STATE_NONE) {
			return true;
		}				

		return false;
	}

	protected ServerState getServerState() {
		return server.getDelegate().getServerState();
	}

	protected void sleepExpectedDuration() {
		try {
			long curTime = System.currentTimeMillis();
			long nextSleep = getAwakenTime() - curTime;
			if( nextSleep > 0) {
				sleep(nextSleep);
			}
		} catch(InterruptedException ie) {
			Thread.currentThread().interrupt();
		}
	}

	public synchronized void updateInactivityCounter() {
		this.lastUpdated = System.currentTimeMillis();
	}

	protected synchronized long getLastUpdated() {
		return this.lastUpdated;
	}

	protected long getAwakenTime() {
		return getLastUpdated() + maxInactive;
	}

	protected synchronized void setPublishBegan() {
		this.publishBegan = true;
	}
	protected synchronized boolean getPublishBegan() {
		return this.publishBegan;
	}
	protected synchronized void setDone() {
		this.done = true;
	}
	protected synchronized boolean isDone() {
		return this.done;
	}
}

public class MessageColumnIO extends GroupColumnIO {
  private static final Log logger = Log.getLog(MessageColumnIO.class);

  private static final boolean DEBUG = Log.DEBUG;

  private List<PrimitiveColumnIO> leaves;

  private final boolean validating;

  MessageColumnIO(MessageType messageType, boolean validating) {
    super(messageType, null, 0);
    this.validating = validating;
  }

  public List<String[]> getColumnNames() {
    return super.getColumnNames();
  }

  public <T> RecordReader<T> getRecordReader(PageReadStore columns, RecordConverter<T> recordMaterializer) {
    return new RecordReaderImplementation<T>(this, recordMaterializer, validating, new MemColumnReadStore(columns));
  }

  private class MessageColumnIORecordConsumer extends RecordConsumer {
    private ColumnIO currentColumnIO;
    private int currentLevel = 0;
    private final int[] currentIndex;
    private final int[] r;
    private final ColumnWriter[] columnWriter;

    public MessageColumnIORecordConsumer(ColumnWriteStore columns) {
      int maxDepth = 0;
      this.columnWriter = new ColumnWriter[MessageColumnIO.this.getLeaves().size()];
      for (PrimitiveColumnIO primitiveColumnIO : MessageColumnIO.this.getLeaves()) {
        maxDepth = Math.max(maxDepth, primitiveColumnIO.getFieldPath().length);
        columnWriter[primitiveColumnIO.getId()] = columns.getColumnWriter(primitiveColumnIO.getColumnDescriptor());
      }
      currentIndex = new int[maxDepth];
      r = new int[maxDepth];
    }

    public void printState() {
      log(currentLevel+", "+currentIndex[currentLevel]+": "+Arrays.toString(currentColumnIO.getFieldPath())+" r:"+r[currentLevel]);
      if (r[currentLevel] > currentColumnIO.getRepetitionLevel()) {
        throw new InvalidRecordException(r[currentLevel]+"(r) > "+currentColumnIO.getRepetitionLevel()+" ( schema r)");
      }
    }

    private void log(Object m) {
      String indent = "";
      for (int i = 0; i<currentLevel; ++i) {
        indent += "  ";
      }
      logger.debug(indent + m);
    }

    @Override
    public void startMessage() {
      if (DEBUG) log("< MESSAGE START >");
      currentColumnIO = MessageColumnIO.this;
      r[0] = 0;
      currentIndex[0] = 0;
      if (DEBUG) printState();
    }

    @Override
    public void endMessage() {
      writeNullForMissingFields(((GroupColumnIO)currentColumnIO).getChildrenCount() - 1);
      if (DEBUG) log("< MESSAGE END >");
      if (DEBUG) printState();
    }

    @Override
    public void startField(String field, int index) {
      try {
        if (DEBUG) log("startField("+field+", "+index+")");
        writeNullForMissingFields(index - 1);
        currentColumnIO = ((GroupColumnIO)currentColumnIO).getChild(index);
        currentIndex[currentLevel] = index;
        if (DEBUG) printState();
      } catch (RuntimeException e) {
        throw new ParquetEncodingException("error starting field " + field + " at " + index, e);
      }
    }

    private void writeNullForMissingFields(final int to) {
      final int from = currentIndex[currentLevel];
      for (;currentIndex[currentLevel]<=to; ++currentIndex[currentLevel]) {
        try {
          ColumnIO undefinedField = ((GroupColumnIO)currentColumnIO).getChild(currentIndex[currentLevel]);
          int d = currentColumnIO.getDefinitionLevel();
          if (DEBUG) log(Arrays.toString(undefinedField.getFieldPath())+".writeNull("+r[currentLevel]+","+d+")");
          writeNull(undefinedField, r[currentLevel], d);
        } catch (RuntimeException e) {
          throw new ParquetEncodingException("error while writing nulls from " + from + " to " + to + ". current index: "+currentIndex[currentLevel], e);
        }
      }
    }

    private void writeNull(ColumnIO undefinedField, int r, int d) {
      if (undefinedField.getType().isPrimitive()) {
        columnWriter[((PrimitiveColumnIO)undefinedField).getId()].writeNull(r, d);
      } else {
        GroupColumnIO groupColumnIO = (GroupColumnIO)undefinedField;
        int childrenCount = groupColumnIO.getChildrenCount();
        for (int i = 0; i < childrenCount; i++) {
          writeNull(groupColumnIO.getChild(i), r, d);
        }
      }
    }

    private void setRepetitionLevel() {
      r[currentLevel] = currentColumnIO.getRepetitionLevel();
      if (DEBUG) log("r: "+r[currentLevel]);
    }

    @Override
    public void endField(String field, int index) {
      if (DEBUG) log("endField("+field+", "+index+")");
      currentColumnIO = currentColumnIO.getParent();

      currentIndex[currentLevel] = index + 1;

      r[currentLevel] = currentLevel == 0 ? 0 : r[currentLevel - 1];

      if (DEBUG) printState();
    }

    @Override
    public void startGroup() {
      if (DEBUG) log("startGroup()");

      ++ currentLevel;
      r[currentLevel] = r[currentLevel - 1];

      currentIndex[currentLevel] = 0;
      if (DEBUG) printState();
    }

    @Override
    public void endGroup() {
      if (DEBUG) log("endGroup()");
      int lastIndex = ((GroupColumnIO)currentColumnIO).getChildrenCount() - 1;
      writeNullForMissingFields(lastIndex);

      -- currentLevel;

      setRepetitionLevel();
      if (DEBUG) printState();
    }

    @Override
    public void addInteger(int value) {
      if (DEBUG) log("addInt("+value+")");

      getColumnWriter().write(value, r[currentLevel], currentColumnIO.getDefinitionLevel());

      setRepetitionLevel();
      if (DEBUG) printState();
    }

    private ColumnWriter getColumnWriter() {
      return columnWriter[((PrimitiveColumnIO)currentColumnIO).getId()];
    }

    @Override
    public void addLong(long value) {
      if (DEBUG) log("addLong("+value+")");

      getColumnWriter().write(value, r[currentLevel], currentColumnIO.getDefinitionLevel());

      setRepetitionLevel();
      if (DEBUG) printState();
    }

    @Override
    public void addBoolean(boolean value) {
      if (DEBUG) log("addBoolean("+value+")");
      getColumnWriter().write(value, r[currentLevel], currentColumnIO.getDefinitionLevel());

      setRepetitionLevel();
      if (DEBUG) printState();
    }

    @Override
    public void addBinary(Binary value) {
      if (DEBUG) log("addBinary("+value.length()+" bytes)");
      getColumnWriter().write(value, r[currentLevel], currentColumnIO.getDefinitionLevel());

      setRepetitionLevel();
      if (DEBUG) printState();
    }

    @Override
    public void addFloat(float value) {
      if (DEBUG) log("addFloat("+value+")");
      getColumnWriter().write(value, r[currentLevel], currentColumnIO.getDefinitionLevel());

      setRepetitionLevel();
      if (DEBUG) printState();
    }

    @Override
    public void addDouble(double value) {
      if (DEBUG) log("addDouble("+value+")");
      getColumnWriter().write(value, r[currentLevel], currentColumnIO.getDefinitionLevel());

      setRepetitionLevel();
      if (DEBUG) printState();
    }

  }

  public RecordConsumer getRecordWriter(ColumnWriteStore columns) {
    RecordConsumer recordWriter = new MessageColumnIORecordConsumer(columns);
    if (DEBUG) recordWriter = new RecordConsumerLoggingWrapper(recordWriter);
    return validating ? new ValidatingRecordConsumer(recordWriter, getType()) : recordWriter;
  }

  void setLevels() {
    setLevels(0, 0, new String[0], new int[0], Arrays.<ColumnIO>asList(this), Arrays.<ColumnIO>asList(this));
  }

  void setLeaves(List<PrimitiveColumnIO> leaves) {
    this.leaves = leaves;
  }

  public List<PrimitiveColumnIO> getLeaves() {
    return this.leaves;
  }

  @Override
  public MessageType getType() {
    return (MessageType)super.getType();
  }
}

public class TabuSearch extends AOptimizationAlgorithm<GosplPopulation> {

	private ITabuList tabuList;
	private int maxIterations;

	public static Level LOG_LEVEL = Level.DEBUG;

	public TabuSearch(ITabuList tabulist, double fitnessThreshold, int maxIterations) {
		this(new PopulationAttributeNeighborSearch(), tabulist, fitnessThreshold, maxIterations);
	}

	public TabuSearch(IPopulationNeighborSearch<GosplPopulation,?> neighborSearch,
			ITabuList tabuList, double fitnessThreshold, int maxIterations) {
		super(neighborSearch, fitnessThreshold);
		this.tabuList = tabuList;
		this.maxIterations = maxIterations;
	}

	@Override
	public ISyntheticPopulationSolution<GosplPopulation> run(ISyntheticPopulationSolution<GosplPopulation> initialSolution) {
		ISyntheticPopulationSolution<GosplPopulation> bestSolution = initialSolution;
		ISyntheticPopulationSolution<GosplPopulation> currentSolution = initialSolution;
		this.getNeighborSearchAlgorithm().updatePredicates(initialSolution.getSolution());

		double bestFitness = initialSolution.getFitness(this.getObjectives());
		double stempFitness = bestFitness;

		GSPerformanceUtil gspu = new GSPerformanceUtil(
				"Start Tabu Search algorithm"
						+ "\nPopulation size = "+initialSolution.getSolution().size()
						+ "\nSample size = "+super.getSample().size()
						+ "\nMax iteration = "+this.maxIterations
						+ "\nNeighbor search = "+super.getNeighborSearchAlgorithm().getClass().getSimpleName()
						+ "\nSolution = "+initialSolution.getClass().getSimpleName(), 
						LOG_LEVEL);
		gspu.setObjectif(this.maxIterations);

		gspu.sysoStempPerformance(0d, this);
		gspu.sysoStempMessage("Random start solution fitness is "+bestFitness);

		int currentIteration = 0;
		int stuckIdx = 0;
		while (currentIteration++ < this.maxIterations &&
				bestFitness > this.getFitnessThreshold()) {

			if(currentIteration % (this.maxIterations / 10d) == 0
					|| stempFitness - bestFitness > stempFitness / 10) {
				gspu.sysoStempPerformance(currentIteration / gspu.getObjectif(), this);
				gspu.sysoStempMessage("Current fitness is "+bestFitness);
				gspu.sysoStempMessage("Tabu size "+tabuList.getSize()+" "
						+ "| number of stucked iteration "+stuckIdx
						+ " | Current buffer "+super.computeBuffer(bestFitness * stuckIdx, currentSolution));
				stempFitness = bestFitness;
			}

			Collection<ISyntheticPopulationSolution<GosplPopulation>> neighbors = currentSolution.getNeighbors(
					super.getNeighborSearchAlgorithm());

			Map<ISyntheticPopulationSolution<GosplPopulation>, Double> neighborsFitness = neighbors.stream()
					.filter(candidate -> !this.tabuList.contains(candidate))
					.collect(Collectors.toMap( 
							Function.identity(),
							candidate -> candidate.getFitness(this.getObjectives())));

			double candidateFitness = Collections.min(neighborsFitness.values());
			if(candidateFitness < bestFitness) {
				bestSolution = neighborsFitness.entrySet().stream()
						.filter(entry -> entry.getValue() == candidateFitness)
						.map(Entry::getKey).findFirst().get();
				bestFitness = candidateFitness;
				stuckIdx = 0;
			}

			double var = Math.log1p(stuckIdx++) / Math.log(tabuList.maxSize());

			if(GenstarRandom.getInstance().nextDouble() < var) {

				currentSolution = currentSolution.getRandomNeighbor(super.getNeighborSearchAlgorithm(), 
						super.computeBuffer(bestFitness * stuckIdx, currentSolution));
				stuckIdx = 0;

			}	

			tabuList.add(currentSolution);


		}

		return bestSolution;
	}

}

public final class PlainCstInsn
        extends CstInsn {
    public PlainCstInsn(Rop opcode, SourcePosition position,
                        RegisterSpec result, RegisterSpecList sources,
                        Constant cst) {
        super(opcode, position, result, sources, cst);

        if (opcode.getBranchingness() != Rop.BRANCH_NONE) {
            throw new IllegalArgumentException("bogus branchingness");
        }
    }

    @Override
    public TypeList getCatches() {
        return StdTypeList.EMPTY;
    }

    @Override
    public void accept(Visitor visitor) {
        visitor.visitPlainCstInsn(this);
    }

    @Override
    public Insn withAddedCatch(Type type) {
        throw new UnsupportedOperationException("unsupported");
    }

    @Override
    public Insn withRegisterOffset(int delta) {
        return new PlainCstInsn(getOpcode(), getPosition(),
                                getResult().withOffset(delta),
                                getSources().withOffset(delta),
                                getConstant());
    }

    @Override
    public Insn withNewRegisters(RegisterSpec result,
            RegisterSpecList sources) {

        return new PlainCstInsn(getOpcode(), getPosition(),
                                result,
                                sources,
                                getConstant());

    }
}

