public class DefaultLockHelper implements ILockHelper {

  private static final String FOLDER_NAME_LOCK_TOKENS = ".lockTokens"; private static final char LOCK_OWNER_INFO_SEPARATOR = ':';

  private static final String LOCK_OWNER_INFO_SEPARATOR_REGEX = "\\" + LOCK_OWNER_INFO_SEPARATOR; private static final List<Character> RESERVED_CHARS = Arrays.asList( new Character[] { LOCK_OWNER_INFO_SEPARATOR } );

  private static final Log logger = LogFactory.getLog( DefaultLockHelper.class );

  private static final int POSITION_LOCK_OWNER = 0;

  private static final int POSITION_LOCK_DATE = 1;

  private static final int POSITION_LOCK_MESSAGE = 2;

  ITenantedPrincipleNameResolver userNameUtils;

  public DefaultLockHelper( ITenantedPrincipleNameResolver userNameUtils ) {
    super();
    this.userNameUtils = userNameUtils;
  }

  protected void addLockToken( final Session session, final PentahoJcrConstants pentahoJcrConstants, final Lock lock )
    throws RepositoryException {
    Node lockTokensNode = getOrCreateLockTokensNode( session, pentahoJcrConstants, lock );
    Node newLockTokenNode =
        lockTokensNode.addNode( lock.getNode().getIdentifier(), pentahoJcrConstants.getPHO_NT_LOCKTOKENSTORAGE() );
    newLockTokenNode.setProperty( pentahoJcrConstants.getPHO_LOCKEDNODEREF(), lock.getNode() );
    newLockTokenNode.setProperty( pentahoJcrConstants.getPHO_LOCKTOKEN(), lock.getLockToken() );
    session.save();
  }

  protected String getLockToken( final Session session, final PentahoJcrConstants pentahoJcrConstants, final Lock lock )
    throws RepositoryException {
    Node lockTokensNode = getOrCreateLockTokensNode( session, pentahoJcrConstants, lock );
    NodeIterator nodes = lockTokensNode.getNodes( lock.getNode().getIdentifier() );
    Assert.isTrue( nodes.hasNext() );
    return nodes.nextNode().getProperty( pentahoJcrConstants.getPHO_LOCKTOKEN() ).getString();
  }

  public void removeLockToken( final Session session, final PentahoJcrConstants pentahoJcrConstants, final Lock lock )
    throws RepositoryException {
    Node lockTokensNode = getOrCreateLockTokensNode( session, pentahoJcrConstants, lock );
    NodeIterator nodes = lockTokensNode.getNodes( lock.getNode().getIdentifier() );
    if ( nodes.hasNext() ) {
      nodes.nextNode().remove();
    }
    session.save();
  }

  protected Node getOrCreateLockTokensNode( final Session session, final PentahoJcrConstants pentahoJcrConstants,
      final Lock lock ) throws RepositoryException {
    String absPath =
        ServerRepositoryPaths.getUserHomeFolderPath( userNameUtils.getTenant( getLockOwner( session,
            pentahoJcrConstants, lock ) ), userNameUtils.getPrincipleName( getLockOwner( session, pentahoJcrConstants,
            lock ) ) );
    Node userHomeFolderNode = (Node) session.getItem( absPath );
    if ( userHomeFolderNode.hasNode( FOLDER_NAME_LOCK_TOKENS ) ) {
      return userHomeFolderNode.getNode( FOLDER_NAME_LOCK_TOKENS );
    } else {
      Node lockTokensNode =
          userHomeFolderNode.addNode( FOLDER_NAME_LOCK_TOKENS, pentahoJcrConstants.getPHO_NT_INTERNALFOLDER() );
      session.save();
      return lockTokensNode;
    }
  }

  public boolean canUnlock( final Session session, final PentahoJcrConstants pentahoJcrConstants, final Lock lock )
    throws RepositoryException {
    String absPath =
        ServerRepositoryPaths.getUserHomeFolderPath( userNameUtils.getTenant( getLockOwner( session,
            pentahoJcrConstants, lock ) ), userNameUtils.getPrincipleName( getLockOwner( session, pentahoJcrConstants,
            lock ) ) );
    AccessControlManager acMgr = session.getAccessControlManager();
    return acMgr.hasPrivileges( absPath, new Privilege[] {
      acMgr.privilegeFromName( "jcr:read" ), acMgr.privilegeFromName( "jcr:write" ), acMgr.privilegeFromName( "jcr:lockManagement" ) } ); }

  public void addLockTokenToSessionIfNecessary( final Session session, final PentahoJcrConstants pentahoJcrConstants,
      final Serializable fileId ) throws RepositoryException {
    Node fileNode = session.getNodeByIdentifier( fileId.toString() );
    if ( fileNode.isLocked() ) {
      LockManager lockManager = session.getWorkspace().getLockManager();
      Lock lock = lockManager.getLock( fileNode.getPath() );
      String lockToken = getLockToken( session, pentahoJcrConstants, lock );
      lockManager.addLockToken( lockToken );
    }
  }

  public void removeLockTokenFromSessionIfNecessary( final Session session,
      final PentahoJcrConstants pentahoJcrConstants, final Serializable fileId ) throws RepositoryException {
    Node fileNode = session.getNodeByIdentifier( fileId.toString() );
    if ( fileNode.isLocked() ) {
      LockManager lockManager = session.getWorkspace().getLockManager();
      Lock lock = lockManager.getLock( fileNode.getPath() );
      String lockToken = getLockToken( session, pentahoJcrConstants, lock );
      lockManager.removeLockToken( lockToken );
    }
  }

  public void unlockFile( final Session session, final PentahoJcrConstants pentahoJcrConstants,
      final Serializable fileId ) throws RepositoryException {
    Node fileNode = session.getNodeByIdentifier( fileId.toString() );
    LockManager lockManager = session.getWorkspace().getLockManager();
    Lock lock = lockManager.getLock( fileNode.getPath() );
    String lockToken = getLockToken( session, pentahoJcrConstants, lock );
    lockManager.addLockToken( lockToken );
    lock = lockManager.getLock( fileNode.getPath() );
    removeLockToken( session, pentahoJcrConstants, lock );
    lockManager.unlock( fileNode.getPath() );
  }

  public void lockFile( final Session session, final PentahoJcrConstants pentahoJcrConstants,
      final Serializable fileId, final String message ) throws RepositoryException {
    LockManager lockManager = session.getWorkspace().getLockManager();
    final boolean isDeep = true;
    final boolean isSessionScoped = false;
    final long timeoutHint = Long.MAX_VALUE;
    final String ownerInfo =
        makeOwnerInfo( JcrTenantUtils.getTenantedUser( PentahoSessionHolder.getSession().getName() ), Calendar
            .getInstance().getTime(), message );
    Node fileNode = session.getNodeByIdentifier( fileId.toString() );
    Assert.isTrue( fileNode.isNodeType( pentahoJcrConstants.getMIX_LOCKABLE() ) );
    Lock lock = lockManager.lock( fileNode.getPath(), isDeep, isSessionScoped, timeoutHint, ownerInfo );
    addLockToken( session, pentahoJcrConstants, lock );
  }

  private String makeOwnerInfo( final String lockOwner, final Date lockDate, final String lockMessage ) {
    return escape( lockOwner ) + LOCK_OWNER_INFO_SEPARATOR + lockDate.getTime() + LOCK_OWNER_INFO_SEPARATOR
        + escape( lockMessage );
  }

  @Override
  public Date getLockDate( final Session session, final PentahoJcrConstants pentahoJcrConstants, final Lock lock )
    throws RepositoryException {
    String[] tokens = tokenize( lock.getLockOwner() );
    if ( tokens != null ) {
      long date;
      try {
        date = Long.parseLong( tokens[POSITION_LOCK_DATE] );
        return new Date( date );
      } catch ( NumberFormatException e ) {
        logger.debug( "could not parse lock date; returning null", e ); }
    }
    return null;
  }

  @Override
  public String getLockMessage( final Session session, final PentahoJcrConstants pentahoJcrConstants, final Lock lock )
    throws RepositoryException {
    String[] tokens = tokenize( lock.getLockOwner() );
    if ( tokens != null ) {
      return unescape( tokens[POSITION_LOCK_MESSAGE] );
    }
    return null;
  }

  @Override
  public String getLockOwner( final Session session, final PentahoJcrConstants pentahoJcrConstants, final Lock lock )
    throws RepositoryException {
    String[] tokens = tokenize( lock.getLockOwner() );
    if ( tokens != null ) {
      return unescape( tokens[POSITION_LOCK_OWNER] );
    }
    return lock.getLockOwner();
  }

  private String[] tokenize( final String ownerInfo ) {
    if ( ownerInfo != null ) {
      String[] tokens = ownerInfo.split( LOCK_OWNER_INFO_SEPARATOR_REGEX );
      if ( tokens.length == 3 ) {
        return tokens;
      }
    }
    return null;
  }

  private static String escape( final String in ) {
    if ( in == null || in.trim().equals( "" ) ) { return ""; }
    return RepositoryFilenameUtils.escape( in, RESERVED_CHARS );
  }

  private static String unescape( final String in ) {
    if ( in == null || in.trim().equals( "" ) ) { return ""; }
    return RepositoryFilenameUtils.unescape( in );
  }

public class Setup {

  private Intersection _lastBuiltSettlement;
  private int _currentTurn;
  private List<Integer> _setupOrder;

  public Setup(List<Integer> setupOrder) {
    _lastBuiltSettlement = null;
    _currentTurn = 0;
    _setupOrder = setupOrder;
  }

  public List<Integer> getSetupOrder() {
    return Collections.unmodifiableList(_setupOrder);
  }

  public void setLastBuiltSettlement(Intersection i) {
    _lastBuiltSettlement = i;
  }

  public void nextTurn() {
    _currentTurn++;
  }

  public Intersection getLastBuiltSettlement() {
    return _lastBuiltSettlement;
  }

  public int getCurrentPlayerID() {
    if (_currentTurn < _setupOrder.size()) {
      return _setupOrder.get(_currentTurn);
    }
    throw new UnsupportedOperationException("It is not the setup anymore");
  }

}

public class SerialDateChooserPanel extends JPanel implements ActionListener {

    public static final Color DEFAULT_DATE_BUTTON_COLOR = Color.red;

    public static final Color DEFAULT_MONTH_BUTTON_COLOR = Color.lightGray;

    private SerialDate date;

    private Color dateButtonColor;

    private Color monthButtonColor;

    private Color chosenOtherButtonColor = Color.darkGray;

    private int firstDayOfWeek = Calendar.SUNDAY;

    private int yearSelectionRange = 20;

    private Font dateFont = new Font("SansSerif", Font.PLAIN, 10);

    private JComboBox monthSelector = null;

    private JComboBox yearSelector = null;

    private JButton todayButton = null;

    private JButton[] buttons = null;

    private boolean refreshing = false;

    public SerialDateChooserPanel() {

        this(SerialDate.createInstance(new Date()), false,
             DEFAULT_DATE_BUTTON_COLOR,
             DEFAULT_MONTH_BUTTON_COLOR);

    }

    public SerialDateChooserPanel(final SerialDate date, final boolean controlPanel) {

        this(date, controlPanel,
             DEFAULT_DATE_BUTTON_COLOR,
             DEFAULT_MONTH_BUTTON_COLOR);

    }

    public SerialDateChooserPanel(final SerialDate date, final boolean controlPanel,
                                  final Color dateButtonColor, final Color monthButtonColor) {

        super(new BorderLayout());

        this.date = date;
        this.dateButtonColor = dateButtonColor;
        this.monthButtonColor = monthButtonColor;

        add(constructSelectionPanel(), BorderLayout.NORTH);
        add(getCalendarPanel(), BorderLayout.CENTER);
        if (controlPanel) {
            add(constructControlPanel(), BorderLayout.SOUTH);
        }

    }

    public void setDate(final SerialDate date) {

        this.date = date;
        this.monthSelector.setSelectedIndex(date.getMonth() - 1);
        refreshYearSelector();
        refreshButtons();

    }

    public SerialDate getDate() {
        return this.date;
    }

    public void actionPerformed(final ActionEvent e) {

        if (e.getActionCommand().equals("monthSelectionChanged")) {
            final JComboBox c = (JComboBox) e.getSource();
            this.date = SerialDate.createInstance(
                this.date.getDayOfMonth(), c.getSelectedIndex() + 1, this.date.getYYYY()
            );
            refreshButtons();
        }
        else if (e.getActionCommand().equals("yearSelectionChanged")) {
            if (!this.refreshing) {
                final JComboBox c = (JComboBox) e.getSource();
                final Integer y = (Integer) c.getSelectedItem();
                this.date = SerialDate.createInstance(
                    this.date.getDayOfMonth(), this.date.getMonth(), y.intValue()
                );
                refreshYearSelector();
                refreshButtons();
            }
        }
        else if (e.getActionCommand().equals("todayButtonClicked")) {
            setDate(SerialDate.createInstance(new Date()));
        }
        else if (e.getActionCommand().equals("dateButtonClicked")) {
            final JButton b = (JButton) e.getSource();
            final int i = Integer.parseInt(b.getName());
            final SerialDate first = getFirstVisibleDate();
            final SerialDate selected = SerialDate.addDays(i, first);
            setDate(selected);
        }

    }

    private JPanel getCalendarPanel() {

        final JPanel panel = new JPanel(new GridLayout(7, 7));
        panel.add(new JLabel("Sun", SwingConstants.CENTER));
        panel.add(new JLabel("Mon", SwingConstants.CENTER));
        panel.add(new JLabel("Tue", SwingConstants.CENTER));
        panel.add(new JLabel("Wed", SwingConstants.CENTER));
        panel.add(new JLabel("Thu", SwingConstants.CENTER));
        panel.add(new JLabel("Fri", SwingConstants.CENTER));
        panel.add(new JLabel("Sat", SwingConstants.CENTER));

        this.buttons = new JButton[42];
        for (int i = 0; i < 42; i++) {
            final JButton button = new JButton("");
            button.setMargin(new Insets(1, 1, 1, 1));
            button.setName(Integer.toString(i));
            button.setFont(this.dateFont);
            button.setFocusPainted(false);
            button.setActionCommand("dateButtonClicked");
            button.addActionListener(this);
            this.buttons[i] = button;
            panel.add(button);
        }
        return panel;

    }

    protected Color getButtonColor(final SerialDate targetDate) {

        if (this.date.equals(this.date)) {
            return this.dateButtonColor;
        }
        else if (targetDate.getMonth() == this.date.getMonth()) {
            return this.monthButtonColor;
        }
        else {
            return this.chosenOtherButtonColor;
        }

    }

    protected SerialDate getFirstVisibleDate() {

        SerialDate result = SerialDate.createInstance(1, this.date.getMonth(), this.date.getYYYY());
        result = SerialDate.addDays(-1, result);
        while (result.getDayOfWeek() != getFirstDayOfWeek()) {
            result = SerialDate.addDays(-1, result);
        }
        return result;

    }

    private int getFirstDayOfWeek() {
        return this.firstDayOfWeek;
    }

    protected void refreshButtons() {

        SerialDate current = getFirstVisibleDate();
        for (int i = 0; i < 42; i++) {
            final JButton button = this.buttons[i];
            button.setText(String.valueOf(current.getDayOfWeek()));
            button.setBackground(getButtonColor(current));
            current = SerialDate.addDays(1, current);
        }

    }

    private void refreshYearSelector() {
        if (!this.refreshing) {
            this.refreshing = true;
            this.yearSelector.removeAllItems();
            final Vector v = getYears(this.date.getYYYY());
            for (Enumeration e = v.elements(); e.hasMoreElements();) {
                this.yearSelector.addItem(e.nextElement());
            }
            this.yearSelector.setSelectedItem(new Integer(this.date.getYYYY()));
            this.refreshing = false;
        }
    }

    private Vector getYears(final int chosenYear) {
        final Vector v = new Vector();
        for (int i = chosenYear - this.yearSelectionRange; 
            i <= chosenYear + this.yearSelectionRange; i++) {
            v.addElement(new Integer(i));
        }
        return v;
    }

    private JPanel constructSelectionPanel() {
        final JPanel p = new JPanel();
        this.monthSelector = new JComboBox(SerialDate.getMonths());
        this.monthSelector.addActionListener(this);
        this.monthSelector.setActionCommand("monthSelectionChanged");
        p.add(this.monthSelector);

        this.yearSelector = new JComboBox(getYears(0));
        this.yearSelector.addActionListener(this);
        this.yearSelector.setActionCommand("yearSelectionChanged");
        p.add(this.yearSelector);

        return p;
    }

    private JPanel constructControlPanel() {

        final JPanel p = new JPanel();
        p.setBorder(BorderFactory.createEmptyBorder(2, 5, 2, 5));
        this.todayButton = new JButton("Today");
        this.todayButton.addActionListener(this);
        this.todayButton.setActionCommand("todayButtonClicked");
        p.add(this.todayButton);
        return p;

    }

}

public class InternalErrorPage implements Page {

    private final String template;
    private final String errorMsg;
    private final Throwable error;

    private final VersionChecker versionChecker;

    public InternalErrorPage(
            String template, String errorMsg, Throwable error,
            VersionChecker versionChecker
    ) {
        this.template = template;
        this.errorMsg = errorMsg;
        this.error = error;
        this.versionChecker = versionChecker;
    }

    @Override
    public String toHtml() {

        PlaceholderReplacer placeholders = new PlaceholderReplacer();
        placeholders.put("title", Icon.called("bug") + " 500 Internal Error occurred");
        placeholders.put("titleText", "500 Internal Error occurred");
        placeholders.put("paragraph", createContent());
        placeholders.put("version", versionChecker.getUpdateButton().orElse(versionChecker.getCurrentVersionButton()));
        placeholders.put("updateModal", versionChecker.getUpdateModal());
        placeholders.put("contributors", Contributors.generateContributorHtml());
        return placeholders.apply(template);
    }

    private String createContent() {
        StringBuilder paragraph = new StringBuilder();
        paragraph.append("Please report this issue here: ");
        paragraph.append(Html.LINK.create("https://github.com/plan-player-analytics/Plan/issues", "Issues"));
        paragraph.append("<br><br><pre>");
        paragraph.append(error).append(" | ").append(errorMsg);

        for (StackTraceElement element : error.getStackTrace()) {
            paragraph.append("<br>");
            paragraph.append("    ").append(element);
        }
        if (error.getCause() != null) {
            appendCause(error.getCause(), paragraph);
        }

        paragraph.append("</pre>");

        return paragraph.toString();
    }

    private void appendCause(Throwable cause, StringBuilder paragraph) {
        paragraph.append("<br>Caused by: ").append(cause);
        for (StackTraceElement element : cause.getStackTrace()) {
            paragraph.append("<br>");
            paragraph.append("    ").append(element);
        }
        if (cause.getCause() != null) {
            appendCause(cause.getCause(), paragraph);
        }
    }
}

public class QueryEntityVectorContext extends EntityContext {
    float[] centroid; ArrayList<String> words;final int N;
    final protected WordVectors unigramV;
    final protected WordVectors entityV;
    final protected AbstractEntityHash hash;
    boolean DEBUG = false;


    final protected ArrayList<String> sw = new ArrayList<String>() {
        private static final long serialVersionUID = 1L;

        {
            add( "of" );
            add( "the" );
            add( "in" );
            add( "at" );
            add( "on" );
            add( "for" );
        }
    };


    public QueryEntityVectorContext( WordVectors unigramV, WordVectors entityV, AbstractEntityHash hash ) {
        this.unigramV = unigramV;
        this.entityV = entityV;
        this.hash = hash;
        this.N = unigramV.getVectorLength();
    }

    @Override
    public void setContextWords( ArrayList<String> words ) {
        words.removeAll( sw );
        centroid = WordVectorsUtils.centroid( words, N, unigramV );
        this.words = words;
    }

    @Override
    public double getEntityContextScore( Entity e ) {
        CharSequence name = hash.getEntityName( e.id );
        float[] vector = entityV.getVectorOf( name.toString() );
        if( vector != null ) {
            double distance = WordVectorsUtils.sim( vector, centroid, N );
            if( DEBUG ) System.out.println( " Sim of <" + name + "> to " + words + " is = " + distance );
            return distance;
        } else {
            if( DEBUG ) System.out.println( " <" + name + "> doesn't have a vector" );
        }
        return 0;
    }

    @Override
    public String toString() {
        return "QueryEntityVectorContext";
    }

}

@FluentSetters(returns="FluentRequestQueryParamAssertion<R>")
public class FluentRequestQueryParamAssertion<R> extends FluentStringAssertion<R> {

	private final RequestQueryParam value;

	public FluentRequestQueryParamAssertion(RequestQueryParam value, R returns) {
		this(null, value, returns);
	}

	public FluentRequestQueryParamAssertion(Assertion creator, RequestQueryParam value, R returns) {
		super(creator, value.asString().orElse(null), returns);
		this.value = value;
		throwable(BadRequest.class);
	}

	public FluentBooleanAssertion<R> asBoolean() {
		return new FluentBooleanAssertion<>(this, value.asBoolean().orElse(null), returns());
	}

	public FluentIntegerAssertion<R> asInteger() {
		return new FluentIntegerAssertion<>(this, value.asInteger().orElse(null), returns());
	}

	public FluentLongAssertion<R> asLong() {
		return new FluentLongAssertion<>(this, value.asLong().orElse(null), returns());
	}

	public FluentZonedDateTimeAssertion<R> asZonedDateTime() {
		return new FluentZonedDateTimeAssertion<>(this, value.asDatePart().asZonedDateTime().orElse(null), returns());
	}

	public <V> FluentAnyAssertion<V,R> asType(Class<V> type) {
		return new FluentAnyAssertion<>(value.asType(type).orElse(null), returns());
	}

	public FluentAnyAssertion<Object,R> asType(Type type, Type...args) {
		return new FluentAnyAssertion<>(value.asType(type, args).orElse(null), returns());
	}

	@Override public FluentRequestQueryParamAssertion<R> msg(String msg, Object...args) {
		super.msg(msg, args);
		return this;
	}

	@Override public FluentRequestQueryParamAssertion<R> out(PrintStream value) {
		super.out(value);
		return this;
	}

	@Override public FluentRequestQueryParamAssertion<R> silent() {
		super.silent();
		return this;
	}

	@Override public FluentRequestQueryParamAssertion<R> stdout() {
		super.stdout();
		return this;
	}

	@Override public FluentRequestQueryParamAssertion<R> throwable(Class<? extends java.lang.RuntimeException> value) {
		super.throwable(value);
		return this;
	}

	@Override public FluentRequestQueryParamAssertion<R> javaStrings() {
		super.javaStrings();
		return this;
	}

public class KafkaCommonUtils {
  public static final String KAFKA_CREDENTIALS = "kafka";
  public static final String SSL_CREDENTIALS = "ssl";
  public static final String USERNAME = "username";
  public static final String PASSWORD = "password";
  public static final String BUCKET = "bucket";
  private static final Logger LOG = LoggerFactory.getLogger(KafkaCommonUtils.class);

  public static Map<String, Map<String, String>> getKafkaCredentialsFromVault(
      String secretStoreUrl, String token) {
    Map<String, Map<String, String>> credentialMap = new HashMap<>();

    JsonObject credentials = null;
    try {
      HttpClient client = HttpClientBuilder.create().build();
      HttpGet request = new HttpGet(secretStoreUrl);
      request.addHeader("X-Vault-Token", token);
      HttpResponse response = client.execute(request);
      String json = EntityUtils.toString(response.getEntity(), "UTF-8");

      credentials =
          JsonParser.parseString(json)
              .getAsJsonObject()
              .get("data")
              .getAsJsonObject()
              .getAsJsonObject("data");
    } catch (IOException e) {
      LOG.error("Failed to retrieve credentials from Vault.", e);
    }

    if (credentials != null) {
      credentialMap.put(KAFKA_CREDENTIALS, new HashMap<>());

      if (credentials.has(USERNAME) && credentials.has(PASSWORD)) {
        credentialMap.get(KAFKA_CREDENTIALS).put(USERNAME, credentials.get(USERNAME).getAsString());
        credentialMap.get(KAFKA_CREDENTIALS).put(PASSWORD, credentials.get(PASSWORD).getAsString());
      } else {
        LOG.warn(
            "There are no username and/or password for Kafka in Vault."
                + "Trying to initiate an unauthorized connection.");
      }

      try {
        Map<String, String> sslCredentials = new HashMap<>();
        String[] configNames = {
          BUCKET,
          SslConfigs.SSL_TRUSTSTORE_LOCATION_CONFIG,
          SslConfigs.SSL_KEYSTORE_LOCATION_CONFIG,
          SslConfigs.SSL_TRUSTSTORE_PASSWORD_CONFIG,
          SslConfigs.SSL_KEYSTORE_PASSWORD_CONFIG,
          SslConfigs.SSL_KEY_PASSWORD_CONFIG
        };
        for (String configName : configNames) {
          sslCredentials.put(configName, credentials.get(configName).getAsString());
        }
        credentialMap.put(SSL_CREDENTIALS, sslCredentials);
      } catch (NullPointerException e) {
        LOG.warn(
            "There is no enough information to configure SSL."
                + "Trying to initiate an unsecure connection.",
            e);
      }
    }

    return credentialMap;
  }

  public static Map<String, Object> configureKafka(Map<String, String> props) {
    Map<String, Object> config = new HashMap<>();
    if (props != null && props.containsKey(USERNAME) && props.containsKey(PASSWORD)) {
      config.put(SaslConfigs.SASL_MECHANISM, ScramMechanism.SCRAM_SHA_512.mechanismName());
      config.put(
          SaslConfigs.SASL_JAAS_CONFIG,
          String.format(
              "org.apache.kafka.common.security.scram.ScramLoginModule required "
                  + "username=\"%s\" password=\"%s\";",
              props.get(USERNAME), props.get(PASSWORD)));
    }
    return config;
  }
}

