public class SensorService extends Service {

    private static final String TAG = "SensorService";

    private static final int DATA_RATE_LOW_US = 20000;
    private static final int DATA_RATE_HIGH_US = 11250;

    class LocalBinder extends Binder {
        SensorService getService() {
            return SensorService.this;
        }
    }

    public interface OrientationListener {
        void onOrientation(double[] quaternion);
    }

    public interface CalibrationListener {
        void onCalibrationComplete(boolean success);
    }

    private final IBinder binder = new LocalBinder();

    private final SensorEventListener sensorEventListener =
            new SensorEventListener() {
                @Override
                public void onSensorChanged(SensorEvent event) {
                    if (!registered) {
                        return;
                    }

                    switch (event.sensor.getType()) {
                        case Sensor.TYPE_GYROSCOPE:
                        case Sensor.TYPE_GYROSCOPE_UNCALIBRATED:
                            if (calibrating && calibrationData.add(event.values)) {
                                if (calibrationListener != null) {
                                    calibrationListener.onCalibrationComplete(true);
                                }
                                stopInput();
                            }
                            break;
                        default: }
                }

                @Override
                public void onAccuracyChanged(Sensor sensor, int accuracy) {}
            };

    private SensorManager sensorManager;
    private OrientationFusion orientation;

    private boolean calibrating;
    private boolean registered;
    private CalibrationData calibrationData;
    @Nullable private CalibrationListener calibrationListener;

    @Override
    public IBinder onBind(Intent intent) {
        return binder;
    }

    @Override
    public void onCreate() {
        super.onCreate();

        registered = false;
        calibrating = false;
        calibrationData = new CalibrationData(this);
        sensorManager = (SensorManager) getSystemService(Context.SENSOR_SERVICE);

        orientation = new OrientationFusion();
    }

    @Override
    public void onDestroy() {
        stopInput();
        super.onDestroy();
    }

    public void startCalibration(CalibrationListener listener) {
        stopInput();

        calibrationListener = listener;

        Sensor sensorGyroscope = sensorManager.getDefaultSensor(Sensor.TYPE_GYROSCOPE_UNCALIBRATED);
        if (sensorGyroscope == null) {
            sensorGyroscope = sensorManager.getDefaultSensor(Sensor.TYPE_GYROSCOPE);
        }
        if (sensorGyroscope == null
                || !sensorManager.registerListener(sensorEventListener, sensorGyroscope, 10000)) {
            Log.e(TAG, "Failed to register listener for Gyroscope: " + sensorGyroscope);
            if (listener != null) {
                listener.onCalibrationComplete(false);
            }
            return;
        }

        calibrationData.reset();
        calibrating = true;
        registered = true;
    }

    public void startInput(OrientationListener listener, boolean reducedRate) {
        stopInput();
        int samplingPeriodUs = reducedRate ? DATA_RATE_LOW_US : DATA_RATE_HIGH_US;
        orientation.start(listener, samplingPeriodUs, calibrationData.getMedian());
    }

    public void stopInput() {
        if (registered) {
            registered = false;
            calibrating = false;
            sensorManager.unregisterListener(sensorEventListener);
        }

        orientation.stop();

        calibrationListener = null;
    }
}

@Immutable
public final class ClusterRoleStatus implements Jsonifiable<JsonObject> {

    public static final JsonFieldDefinition<JsonArray> JSON_KEY_REACHABLE =
            JsonFactory.newJsonArrayFieldDefinition("reachable");

    public static final JsonFieldDefinition<JsonArray> JSON_KEY_UNREACHABLE =
            JsonFactory.newJsonArrayFieldDefinition("unreachable");

    public static final JsonFieldDefinition<String> JSON_KEY_LEADER = JsonFactory.newStringFieldDefinition("leader");

    private final String role;
    private final Set<String> reachable;
    private final Set<String> unreachable;
    private final String leader;

    private ClusterRoleStatus(final String role,
            final Set<String> reachable,
            final Set<String> unreachable,
            final String leader) {

        this.role = role;
        this.reachable = Collections.unmodifiableSet(reachable);
        this.unreachable = Collections.unmodifiableSet(unreachable);
        this.leader = leader;
    }

    public static ClusterRoleStatus of(final String role, final Set<String> reachable, final Set<String> unreachable,
            final String leader) {
        return new ClusterRoleStatus(role, reachable, unreachable, leader);
    }

    public String getRole() { return role; }

    public Set<String> getReachable() {
        return reachable;
    }

    public Set<String> getUnreachable() {
        return unreachable;
    }

    public Optional<String> getLeader() {
        return Optional.ofNullable(leader);
    }

    @Override
    public JsonObject toJson() {
        final JsonObjectBuilder jsonObjectBuilder = JsonFactory.newObjectBuilder();

        jsonObjectBuilder.set(JSON_KEY_REACHABLE,
                reachable.stream().map(JsonValue::of).collect(JsonCollectors.valuesToArray()));
        jsonObjectBuilder.set(JSON_KEY_UNREACHABLE,
                unreachable.stream().map(JsonValue::of).collect(JsonCollectors.valuesToArray()));
        jsonObjectBuilder.set(JSON_KEY_LEADER, getLeader().orElse("<unknown>"));

        return jsonObjectBuilder.build();
    }

    @Override
    public boolean equals(@Nullable Object o) {
        if (this == o) {
            return true;
        }
        if (!(o instanceof ClusterRoleStatus)) {
            return false;
        }
        final ClusterRoleStatus that = (ClusterRoleStatus) o;
        return Objects.equals(reachable, that.reachable) && Objects.equals(unreachable, that.unreachable) && Objects
                .equals(role, that.role) && Objects.equals(leader, that.leader);
    }

    @Override
    public int hashCode() {
        return Objects.hash(role, reachable, unreachable, leader);
    }

    @Override
    public String toString() {
        return getClass().getSimpleName() + "[" + "role=" + role + ", reachable=" + reachable + ", unreachable="
                + unreachable + ", leader='" + leader + '\'' + ']';
    }

}

public class PLSettings extends KNXMediumSettings
{
	private static final byte[] broadcastDomain = new byte[2];

	private byte[] doa;
	private final boolean pl132;

	public PLSettings(final IndividualAddress device, final byte[] domain, final boolean mediumPL132)
	{
		super(device);
		pl132 = mediumPL132;
		setDomainAddress(domain);
	}

	public PLSettings(final boolean mediumPL132)
	{
		super(null);
		pl132 = mediumPL132;
		doa = broadcastDomain;
	}

	public final synchronized void setDomainAddress(final byte[] domain)
	{
		if (domain == null)
			doa = broadcastDomain;
		else if (domain.length != 2)
			throw new KNXIllegalArgumentException("invalid length of domain address");
		else
			doa = (byte[]) domain.clone();
	}

	public final synchronized byte[] getDomainAddress()
	{
		return (byte[]) doa.clone();
	}

	public int getMedium()
	{
		return pl132 ? MEDIUM_PL132 : MEDIUM_PL110;
	}

	public final boolean isPL132()
	{
		return pl132;
	}

	public String toString()
	{
		return super.toString() + " domain " + ((doa[0] & 0xff) << 8 | doa[1] & 0xff);
	}
}

public class FlexibleTable implements MatrixTable {

    private int count = 0;
    private ArrayList<Object> rowLabels = new ArrayList<Object>();
    private ArrayList<Object> colLabels;
    private ArrayList<ArrayList<Double>> table = new ArrayList<ArrayList<Double>>();public FlexibleTable(Object[] rowLabels, Object[] colLabels, double[][] cells) {
        this.colLabels = new ArrayList<Object>(colLabels.length);
        for (int i = 0; i < colLabels.length; ++i) {
            this.colLabels.add(colLabels[i]);
        }

        for (int i = 0; i < rowLabels.length; ++i) {
            addRowAt(i + 1, rowLabels[i]);
            for (int j = 0; j < colLabels.length; ++j) {
                set(i + 1, j + 1, cells[i][j]);
            }
        }
    }

    public FlexibleTable(Object[] rowLabels, Object[] colLabels) {
        this(rowLabels, colLabels, new double[rowLabels.length][colLabels.length]);
    }

    public FlexibleTable(int nRows, int nCols) {
        this(new Object[nRows], new Object[nCols]);

        for (int i = 0; i < nRows; ++i) {
            renameRow(i + 1, getAutoRowLabel());
        }

        for (int i = 0; i < nCols; ++i) {
            renameCol(i + 1, getAutoColLabel());
        }
    }

    public FlexibleTable(FlexibleTable that) {
        this(that.rowLabels.toArray(new Object[0]),
             that.colLabels.toArray(new Object[0]),
             MatrixUtils.to2DArray(that.toMatrix()));
    }

    @Override
    public int nRows() {
        return rowLabels.size();
    }

    @Override
    public int nCols() {
        return colLabels.size();
    }

    @Override
    public void set(int i, int j, double value) {
        table.get(i - 1).set(j - 1, value);
    }

    @Override
    public double get(int i, int j) {
        return table.get(i - 1).get(j - 1);
    }

    public Vector getRow(int row) throws MatrixAccessException {
        throw new UnsupportedOperationException("Not supported yet.");
    }

    public Vector getColumn(int col) throws MatrixAccessException {
        throw new UnsupportedOperationException("Not supported yet.");
    }

    public int getIndexFromRowLabel(Object label) {
        for (int i = 0; i < rowLabels.size(); ++i) {
            if (rowLabels.get(i).equals(label)) {
                return i + 1;
            }
        }

        throw new RuntimeException(String.format("unrecognized name: %s", label.toString()));
    }

    public int getIndexFromColLabel(Object label) {
        for (int i = 0; i < colLabels.size(); ++i) {
            if (colLabels.get(i).equals(label)) {
                return i + 1;
            }
        }

        throw new RuntimeException(String.format("unrecognized name: %s", label.toString()));
    }

    public Object getRowLabel(int i) {
        return rowLabels.get(i - 1);
    }

    public Object getColLabel(int i) {
        return colLabels.get(i - 1);
    }

    public void renameRow(int i, Object label) {
        rowLabels.set(i - 1, label);
    }

    public void renameCol(int i, Object label) {
        colLabels.set(i - 1, label);
    }

    public void deleteRow(int i) {
        table.remove(i - 1);
        rowLabels.remove(i - 1);
    }

    public void deleteCol(int i) {
        int nRows = nRows();
        for (int j = 0; j < nRows; ++j) {
            Double deleted = table.get(j).remove(i - 1);
        }

        colLabels.remove(i - 1);
    }

    public void addRowAt(int i, Object label) {
        rowLabels.add(i - 1, label);

        int nCols = nCols();
        table.add(i - 1, new ArrayList<Double>(nCols));
        ArrayList<Double> row = table.get(i - 1);
        for (int j = 0; j < nCols; ++j) {
            row.add(new Double(0));
        }
    }

    public void addRowAt(int i) {
        addRowAt(i, getAutoRowLabel());
    }

    public void addColAt(int i, Object label) {
        colLabels.add(i - 1, label);

        int nRows = nRows();
        for (int j = 0; j < nRows; ++j) {
            table.get(j).add(i - 1, 0.);
        }
    }

    public void addColAt(int i) {
        addColAt(i, getAutoColLabel());
    }

    public DenseMatrix toMatrix() {
        int nRows = nRows();
        int nCols = nCols();

        DenseMatrix A = new DenseMatrix(nRows, nCols);

        for (int i = 1; i <= nRows; ++i) {
            for (int j = 1; j <= nCols; ++j) {
                A.set(i, j, get(i, j));
            }
        }

        return A;
    }

    private Object getAutoRowLabel() {
        return String.format("row#%d", ++count);
    }

    private Object getAutoColLabel() {
        return String.format("col#%d", ++count);
    }

    @Override
    public String toString() {
        StringBuilder str = new StringBuilder();

        int nRows = nRows();
        int nCols = nCols();

        str.append(String.format("%dx%d\n", nRows, nCols));

        str.append(String.format("%-32s", ""));
        for (int i = 0; i < nCols; ++i) {
            str.append(String.format("%-32s", colLabels.get(i)));
}
        str.append("\n");

        for (int i = 0; i < nRows; ++i) {
            str.append(String.format("%-32s", rowLabels.get(i)));
            for (int j = 0; j < nCols; ++j) {
                str.append(String.format("%-32f", get(i + 1, j + 1)));
}
            str.append("\n");
        }

        return str.toString();
    }

    @Override
    public boolean equals(Object obj) {
        if (obj == null) {
            return false;
        }
        if (getClass() != obj.getClass()) {
            return false;
        }
        final FlexibleTable other = (FlexibleTable) obj;
        if (this.table != other.table && (this.table == null || !this.table.equals(other.table))) {
            return false;
        }
        if (this.colLabels != other.colLabels && (this.colLabels == null || !this.colLabels.equals(other.colLabels))) {
            return false;
        }
        if (this.rowLabels != other.rowLabels && (this.rowLabels == null || !this.rowLabels.equals(other.rowLabels))) {
            return false;
        }
        return true;
    }

    @Override
    public int hashCode() {
        int hash = 7;
        hash = 41 * hash + (this.table != null ? this.table.hashCode() : 0);
        hash = 41 * hash + (this.colLabels != null ? this.colLabels.hashCode() : 0);
        hash = 41 * hash + (this.rowLabels != null ? this.rowLabels.hashCode() : 0);
        return hash;
    }
}

