            public ModOverridesEntry(GuiConfig owningScreen, GuiConfigEntries owningEntryList, IConfigElement prop) {
                super(owningScreen, owningEntryList, prop);
            }

            @Override
            protected GuiScreen buildChildScreen() {
                List<IConfigElement> list = new ArrayList<IConfigElement>();

                list.add(new DummyCategoryElement("addForgeChunkLoadingModCfg", "forge.configgui.ctgy.forgeChunkLoadingAddModConfig",
                        AddModOverrideEntry.class));
                for (ConfigCategory cc : ForgeChunkManager.getModCategories())
                    list.add(new ConfigElement(cc));

                return new GuiConfig(this.owningScreen, list, this.owningScreen.modID,
                        this.configElement.requiresWorldRestart() || this.owningScreen.allRequireWorldRestart,
                        this.configElement.requiresMcRestart() || this.owningScreen.allRequireMcRestart, this.owningScreen.title,
                        I18n.format("forge.configgui.ctgy.forgeChunkLoadingModConfig"));
            }

            @Override
            public boolean enabled() {
                for (IConfigEntry entry : this.owningEntryList.listEntries) {
                    if (entry.getName().equals("enabled") && entry instanceof BooleanEntry) {
                        return Boolean.valueOf(entry.getCurrentValue().toString());
                    }
                }

                return true;
            }

            @Override
            public boolean isChanged() {
                if (childScreen instanceof GuiConfig) {
                    GuiConfig child = (GuiConfig) childScreen;
                    return child.entryList.listEntries.size() != child.initEntries.size() || child.entryList.hasChangedEntry(true);
                }
                return false;
            }

            @Override
            public void undoChanges() {
                if (childScreen instanceof GuiConfig) {
                    GuiConfig child = (GuiConfig) childScreen;
                    for (IConfigEntry ice : child.entryList.listEntries)
                        if (!child.initEntries.contains(ice) && ForgeChunkManager.getConfig().hasCategory(ice.getName()))
                            ForgeChunkManager.getConfig().removeCategory(ForgeChunkManager.getConfig().getCategory(ice.getName()));

                    child.entryList.listEntries = new ArrayList<IConfigEntry>(child.initEntries);
                }
            }
        }

        public static class AddModOverrideEntry extends CategoryEntry {
            public AddModOverrideEntry(GuiConfig owningScreen, GuiConfigEntries owningEntryList, IConfigElement prop) {
                super(owningScreen, owningEntryList, prop);
            }

            @Override
            protected GuiScreen buildChildScreen() {
                List<IConfigElement> list = new ArrayList<IConfigElement>();

                list.add(new DummyConfigElement("modID", "", ConfigGuiType.STRING, "forge.configgui.modID").setCustomListEntryClass(ModIDEntry.class));
                list.add(new ConfigElement(new Property("maximumTicketCount", "200", Property.Type.INTEGER, "forge.configgui.maximumTicketCount")));
                list.add(new ConfigElement(new Property("maximumChunksPerTicket", "25", Property.Type.INTEGER, "forge.configgui.maximumChunksPerTicket")));

                return new GuiConfig(this.owningScreen, list, this.owningScreen.modID,
                        this.configElement.requiresWorldRestart() || this.owningScreen.allRequireWorldRestart,
                        this.configElement.requiresMcRestart() || this.owningScreen.allRequireMcRestart, this.owningScreen.title,
                        I18n.format("forge.configgui.ctgy.forgeChunkLoadingAddModConfig"));
            }

            @Override
            public boolean isChanged() {
                return false;
            }
        }

        public static class ModIDEntry extends SelectValueEntry {
            public ModIDEntry(GuiConfig owningScreen, GuiConfigEntries owningEntryList, IConfigElement prop) {
                super(owningScreen, owningEntryList, prop, getSelectableValues());
                if (this.selectableValues.size() == 0)
                    this.btnValue.enabled = false;
            }

            private static Map<Object, String> getSelectableValues() {
                Map<Object, String> selectableValues = new TreeMap<Object, String>();

                for (ModContainer mod : Loader.instance().getActiveModList())
                    if (!mod.isImmutable() && mod.getMod() != null)
                        selectableValues.put(mod.getModId(), mod.getName());

                return selectableValues;
            }

            @Override
            public void onGuiClosed() {
                Object modObject = Loader.instance().getModObjectList().get(Loader.instance().getIndexedModList().get(currentValue));
                int maxTickets = 200;
                int maxChunks = 25;
                if (modObject != null) {
                    this.owningEntryList.saveConfigElements();
                    for (IConfigElement ice : this.owningScreen.configElements)
                        if ("maximumTicketCount".equals(ice.getName()))
                            maxTickets = Integer.valueOf(ice.get().toString());
                        else if ("maximumChunksPerTicket".equals(ice.getName()))
                            maxChunks = Integer.valueOf(ice.get().toString());

                    ForgeChunkManager.addConfigProperty(modObject, "maximumTicketCount", String.valueOf(maxTickets), Property.Type.INTEGER);
                    ForgeChunkManager.addConfigProperty(modObject, "maximumChunksPerTicket", String.valueOf(maxChunks), Property.Type.INTEGER);

                    if (this.owningScreen.parentScreen instanceof GuiConfig) {
                        GuiConfig superParent = (GuiConfig) this.owningScreen.parentScreen;
                        ConfigCategory modCtgy = ForgeChunkManager.getConfigFor(modObject);
                        modCtgy.setPropertyOrder(ForgeChunkManager.MOD_PROP_ORDER);
                        ConfigElement modConfig = new ConfigElement(modCtgy);

                        boolean found = false;
                        for (IConfigElement ice : superParent.configElements)
                            if (ice.getName().equals(currentValue))
                                found = true;

                        if (!found)
                            superParent.configElements.add(modConfig);

                        superParent.needsRefresh = true;
                        superParent.initGui();
                    }
                }
            }
        }
    }
}

public final class DomainSerialiser {
    private static final Function<String, InputValueType> INPUT_VALUE_TYPE_FUNCTION = driverValue -> {
        if (driverValue.startsWith(GroupDriver.VG_PREFIX)) {
            return InputValueType.VALUE_GROUP;
        } else if (driverValue.startsWith(DateRangeDriver.DR_PREFIX)) {
            return InputValueType.DATE_RANGE;
        } else if (driverValue.startsWith(IntegerRangeDriver.IR_PREFIX)) {
            return InputValueType.INTEGER_RANGE;
        } else if (driverValue.startsWith(RegexDriver.REGEX_PREFIX) ||
                driverValue.contains(".?") || driverValue.contains(".*")) {
            return InputValueType.REGEX;
        }
        return InputValueType.STRING;
    };
    private static final Integer MIN_INTEGER = new Integer(Integer.MIN_VALUE);
    private static final Integer MAX_INTEGER = new Integer(Integer.MAX_VALUE);

    private DomainSerialiser() {
    }

    public static List<String> convertDrivers(final InputDriver[] drivers) {
        return Arrays.stream(drivers).map(InputDriver::toString).collect(Collectors.toList());
    }

    public static List<String> convertDriversWithSubGroups(final List<InputDriver> drivers) {
        final List<String> driverList = new ArrayList<>(drivers.size());
        GroupDriver.convertDriversIntoDriversAndGroups(drivers, driverList, driverList);
        return driverList;
    }

    public static List<String> convertOutputs(final Map<String, String> outputMap) {
        return outputMap.entrySet().stream().map(
                entry -> entry.getKey() + ":" + entry.getValue()).collect(Collectors.toList());
    }

    public static Map<String, String> convertOutputs(final List<String> outputs) {
        return outputs.stream().filter(item -> item.contains(":"))
                .map(item -> item.split(":", 2)).collect(Collectors.toMap(item -> item[0], item -> item[1],
                        (key, value) -> key));
    }

    public static Supplier<InputDriver> createInputDriver(final String currentDriver, final DriverCache cache) {
        return () -> {
            final InputValueType type = INPUT_VALUE_TYPE_FUNCTION.apply(currentDriver);
            final InputDriver inputDriver;
            if (InputValueType.VALUE_GROUP.equals(type)) {
                inputDriver = getValueGroupDriver(currentDriver, cache, type);
            } else {
                inputDriver = getSingleDriver(currentDriver, cache, type);
            }
            return inputDriver;
        };
    }

    private static InputDriver getSingleDriver(final String currentDriver,
                                               final DriverCache cache,
                                               final InputValueType type) {
        InputDriver inputDriver = cache.get(currentDriver, type);

        if (inputDriver == null) {
            switch (type) {
                case STRING:
                    inputDriver = new StringDriver(currentDriver);
                    break;
                case REGEX:
                    inputDriver = new RegexDriver(currentDriver.replace(RegexDriver.REGEX_PREFIX + ":", ""));
                    break;
                case DATE_RANGE:
                    final StringTokenizer tokenizer = new StringTokenizer(
                            currentDriver.replace(DateRangeDriver.DR_PREFIX + ":",""), "|", false);
                    EhSupport.ensure(tokenizer.countTokens() == 2, "DateRange driver incorrectly formatted");
                    inputDriver = new DateRangeDriver(currentDriver, new Range<>(Instant.parse(tokenizer.nextToken()),
                            Instant.parse(tokenizer.nextToken())));
                    break;
                case INTEGER_RANGE:
                    final StringTokenizer intTokenizer = new StringTokenizer(
                            currentDriver.replace(IntegerRangeDriver.IR_PREFIX + ":", ""), "|", true);
                    EhSupport.ensure(intTokenizer.countTokens() == 2 || intTokenizer.countTokens() == 3,
                            "Integer Range driver incorrectly formatted");
                    if (intTokenizer.countTokens() == 3) {
                        final String minValue = intTokenizer.nextToken();
                        intTokenizer.nextToken();
                        final String maxValue = intTokenizer.nextToken();
                        inputDriver = new IntegerRangeDriver(currentDriver, new Range<>(Integer.parseInt(minValue),
                                Integer.parseInt(maxValue)));
                    } else {
                        final String firstToken = intTokenizer.nextToken();
                        if ("|".equals(firstToken)) {
                            inputDriver = new IntegerRangeDriver(currentDriver, new Range<>(MIN_INTEGER,
                                    Integer.parseInt(intTokenizer.nextToken())));
                        } else {
                            inputDriver = new IntegerRangeDriver(currentDriver,
                                    new Range<>(Integer.parseInt(firstToken),
                                    MAX_INTEGER));
                        }
                    }
                    break;
                default:
                    inputDriver = null;
                    break;
            }
            if (inputDriver != null) {
                cache.put(inputDriver);
            }
        }
        return inputDriver;
    }

    private static InputDriver getValueGroupDriver(final String currentDriver,
                                                   final DriverCache cache,
                                                   final InputValueType type) {
        final String[] tokensGroup = currentDriver.split(GroupDriver.VG_PREFIX);
        final String value = tokensGroup[1].split(":")[0];

        InputDriver inputDriver = cache.get(value, type);
        if (inputDriver == null) {
            final List<InputDriver> topLevelDriver = new ArrayList<>(16);
            for (int i = 2; i < tokensGroup.length; i++) {
                if (Strings.isEmpty(tokensGroup[i])) {
                    continue;
                }
                final String name = tokensGroup[i].split(":")[0];

                InputDriver subGroup = cache.get(name, type);
                if (subGroup == null) {
                    final List<InputDriver> subDrivers = getGroupDrivers(tokensGroup[i], cache);
                    subGroup = new GroupDriver(name, subDrivers);
                    topLevelDriver.add(subGroup);
                    cache.put(subGroup);
                }
            }

            topLevelDriver.addAll(getGroupDrivers(tokensGroup[1], cache));
            inputDriver = new GroupDriver(value, topLevelDriver);
            cache.put(inputDriver);
        }
        return inputDriver;
    }

    private static List<InputDriver> getGroupDrivers(final String currentToken,
                                                     final DriverCache cache) {
        final List<InputDriver> drivers = new ArrayList<>();

        if (Strings.isEmpty(currentToken)) {
            return Collections.emptyList();
        }

        final String[] tokens = currentToken.split(":", 0);

        for (int i = 1; i < tokens.length; i++) {
            final String tokenValue = tokens[i];
            final InputValueType tokenType = INPUT_VALUE_TYPE_FUNCTION.apply(tokenValue);
            final InputDriver driver = getSingleDriver(tokenValue, cache, tokenType);
            drivers.add(driver);
        }
        return drivers;
    }
}

public class TableIdGenerator implements IdGenerator {

	private String name;

	private String table;

	private String pkColumnName = "";

	private String valueColumnName = "";

	private String pkColumnValue = "";

	private Integer initialValue = 0;

	private Integer allocationSize = 50;

	public TableIdGenerator() {
		super();
	}

	public TableIdGenerator(String name, String table, String pkColumnName, String valueColumnName,
			String pkColumnValue, Integer initialValue, Integer allocationSize) {
		this.name = name;
		this.table = table;
		this.pkColumnName = pkColumnName;
		this.valueColumnName = valueColumnName;
		this.pkColumnValue = pkColumnValue;
		this.initialValue = initialValue;
		this.allocationSize = allocationSize;
	}

	@Override
	public IdGenerator newCopy() {
		return new TableIdGenerator(name, table, pkColumnName, valueColumnName, pkColumnValue, initialValue,
				allocationSize);
	}

	@Override
	public GenerationType getGenerationType() {
		return GenerationType.TIMESTAMP;
	}

	@Override
	public String getIdGenName() {
		return name;
	}

	@Override
	public Boolean dependOnAutoIdGenerator() {
		return false;
	}

	@Override
	public Object getNextID(NormalJdbcTool jdbc, Dialect dialect, Type dataType) {
		int countOfRec = ((Number) jdbc .jdbcQueryForObject("select count(*) from " + table + " where " + pkColumnName + "=?", pkColumnValue))
						.intValue();
		if (countOfRec == 0) {
			jdbc.jdbcUpdate("insert into " + table + "( " + pkColumnName + "," + valueColumnName + " )  values(?,?)",
					pkColumnValue, initialValue);
			return initialValue;
		} else {
			jdbc.jdbcUpdate("update " + table + " set " + valueColumnName + "=" + valueColumnName + "+" + allocationSize
					+ "  where " + pkColumnName + " =?", pkColumnValue);

			int last = ((Number) jdbc.jdbcQueryForObject( "select " + valueColumnName + " from " + table + " where " + pkColumnName + "=?", pkColumnValue))
							.intValue();
			return last;
		}
	}

	public String getName() {
		return name;
	}

	public void setName(String name) {
		this.name = name;
	}

	public String getTable() {
		return table;
	}

	public void setTable(String table) {
		this.table = table;
	}

	public String getPkColumnName() {
		return pkColumnName;
	}

	public void setPkColumnName(String pkColumnName) {
		this.pkColumnName = pkColumnName;
	}

	public String getValueColumnName() {
		return valueColumnName;
	}

	public void setValueColumnName(String valueColumnName) {
		this.valueColumnName = valueColumnName;
	}

	public String getPkColumnValue() {
		return pkColumnValue;
	}

	public void setPkColumnValue(String pkColumnValue) {
		this.pkColumnValue = pkColumnValue;
	}

	public Integer getInitialValue() {
		return initialValue;
	}

	public void setInitialValue(Integer initialValue) {
		this.initialValue = initialValue;
	}

	public Integer getAllocationSize() {
		return allocationSize;
	}

	public void setAllocationSize(Integer allocationSize) {
		this.allocationSize = allocationSize;
	}
}

