                .format(format)
                .readOnly(readOnly)
                .writeOnly(writeOnly)
                .comment(comment)
                .examples(examples)

                .contentEncoding(contentEncoding)
                .contentMediaType(contentMediaType);
        properties.forEach(builder::putProperty);
        patternProperties.forEach(builder::putPatternProperty);
        extensions.forEach(builder::putExtension);
        return builder;
    }

