public class SourcePart {

	private int startLine;
	private int endLine;
	private boolean compiledCode;
	private List<String> src;

	public SourcePart(int start, int end, List<String> src) {
		startLine = start;
		endLine = end;
		this.src = new ArrayList<>(src);
	}

	public int getStartLine() {
		return startLine;
	}

	public int getEndLine() {
		return endLine;
	}

	public List<String> getSourcePart() {
		return src.subList(startLine, endLine);
	}

	public int calculateCodeSize(CompilerConfig config, ConstantsContainer ccon, LabelsContainer lcon) {
		int size = 0;
		Machine machine = new Machine();

		for (int i = startLine; i < endLine; i++) {
			size += Assembler.compileSingleLine(machine, ccon, lcon, config, src.get(i));
		}
		return size;
	}

	public boolean isCompiledCode() {
		return compiledCode;
	}

	public void setCompiledCode(boolean compiledCode) {
		this.compiledCode = compiledCode;
	}

}

class CfEnvAwareResource implements Resource {
	private final Resource resource;

	private final boolean hasCfEnv;

	static CfEnvAwareResource of(Resource resource) {
		return new CfEnvAwareResource(resource);
	}
	private CfEnvAwareResource(Resource resource) {
		this.resource = resource;
		this.hasCfEnv = CfEnvResolver.hasCfEnv(this);
	}

	@Override
	public boolean exists() {
		return resource.exists();
	}

	@Override
	public URL getURL() throws IOException {
		return resource.getURL();
	}

	@Override
	public URI getURI() throws IOException {
		return resource.getURI();
	}

	@Override
	public File getFile() throws IOException {
		return resource.getFile();
	}

	@Override
	public long contentLength() throws IOException {
		return resource.contentLength();
	}

	@Override
	public long lastModified() throws IOException {
		return resource.lastModified();
	}

	@Override
	public Resource createRelative(String s) throws IOException {
		return resource.createRelative(s);
	}

	@Override
	public String getFilename() {
		return resource.getFilename();
	}

	@Override
	public String getDescription() {
		return resource.getDescription();
	}

	@Override
	public InputStream getInputStream() throws IOException {
		return resource.getInputStream();
	}

	boolean hasCfEnv() {
		return this.hasCfEnv;
	}

	static class CfEnvResolver {

		private static Log logger = LogFactory.getLog(CfEnvResolver.class);

		private static final String CF_ENV = "io.pivotal.cfenv.core.CfEnv";

		static boolean hasCfEnv(CfEnvAwareResource app
		) {
			try {
				String scheme = app.getURI().getScheme().toLowerCase();
				if (scheme.equals("docker")) {
					return false;
				}
			}
			catch (IOException e) {
				throw new IllegalArgumentException(e.getMessage(), e);
			}

			try {
				JarFileArchive archive = new JarFileArchive(app.getFile());
				List<URL> urls = new ArrayList<>();
				archive.getNestedArchives(entry -> entry.getName().endsWith(".jar"), null).forEachRemaining(a -> {
					try {
						urls.add(a.getUrl());
					}
					catch (MalformedURLException e) {
						logger.error("Unable to process nested archive " +  e.getMessage());
					}
				});
				URLClassLoader classLoader = new URLClassLoader(urls.toArray(new URL[urls.size()]), null);
				try {
					Class.forName(CF_ENV, false, classLoader);
					return true;
				}
				catch (ClassNotFoundException e) {
					logger.debug(app.getFilename() + " does not contain " + CF_ENV);
					return false;
				}
			}
			catch (Exception e) {
				logger.warn("Unable to determine dependencies for " + app.getFilename());
			}
			return false;
		}
	}
}

public class SpectrumExecutionMonitor extends ExecutionMonitor.Adapter
{
    public static final int DEFAULT_WIDTH = 100;
    private static final int PROGRESS_WIDTH = 5;
    private static final char[] WEIGHTS = new char[] {' ', 'K', 'M', 'B', 'T'};

    private final PrintStream out;
    private final int width;
    private long lastProgress;

    public SpectrumExecutionMonitor(long interval, TimeUnit unit, PrintStream out, int width )
    {
        super( interval, unit );
        this.out = out;
        this.width = width;
    }

    @Override
    public void start( StageExecution execution )
    {
        out.println( execution.name() + ", started " + Format.date() );
        lastProgress = 0;
    }

    @Override
    public void end( StageExecution execution, long totalTimeMillis )
    {
        check( execution );
        out.println();
        out.println( "Done in " + Format.duration( totalTimeMillis ) );
    }

    @Override
    public void done( long totalTimeMillis, String additionalInformation )
    {
        out.println();
        out.println( "IMPORT DONE in " + Format.duration( totalTimeMillis ) + ". " + additionalInformation );
    }

    @Override
    public void check( StageExecution execution )
    {
        StringBuilder builder = new StringBuilder();
        printSpectrum( builder, execution, width, DetailLevel.IMPORTANT );

        long progress = last( execution.steps() ).stats().stat( Keys.done_batches ).asLong() * execution.getConfig().batchSize();
        long currentDelta = progress - lastProgress;
        builder.append( " " + fitInProgress( currentDelta ) );

        lastProgress = progress;

        out.print( "\r" + builder );
    }

    public static void printSpectrum( StringBuilder builder, StageExecution execution, int width, DetailLevel additionalStatsLevel )
    {
        long[] values = values( execution );
        long total = total( values );

        width -= 2+ PROGRESS_WIDTH

        Pair<Step<?>,Float> bottleNeck = execution.stepsOrderedBy( Keys.avg_processing_time, false ).iterator().next();
        QuantizedProjection projection = new QuantizedProjection( total, width );
        long lastDoneBatches = 0;
        int stepIndex = 0;
        boolean hasProgressed = false;
        builder.append( '[' );
        for ( Step<?> step : execution.steps() )
        {
            StepStats stats = step.stats();
            if ( !projection.next( values[stepIndex] ) )
            {
                break; }
            long stepWidth = total == 0 && stepIndex == 0 ? width : projection.step();
            if ( stepWidth > 0 )
            {
                if ( hasProgressed )
                {
                    stepWidth--;
                    builder.append( '|' );
                }
                boolean isBottleNeck = bottleNeck.first() == step;
                String name =
                        (isBottleNeck ? "*" : "") +
                        stats.toString( additionalStatsLevel ) + (step.processors( 0 ) > 1
                        ? "(" + step.processors( 0 ) + ")"
                        : "");
                int charIndex = 0; char backgroundChar = step.processors( 0 ) > 1 ? '=' : '-';
                for ( int i = 0; i < stepWidth; i++, charIndex++ )
                {
                    char ch = backgroundChar;
                    if ( charIndex >= 0 && charIndex < name.length() && charIndex < stepWidth )
                    {
                        ch = name.charAt( charIndex );
                    }
                    builder.append( ch );
                }
                hasProgressed = true;
            }
            lastDoneBatches = stats.stat( Keys.done_batches ).asLong();
            stepIndex++;
        }

        long progress = lastDoneBatches * execution.getConfig().batchSize();
        builder.append( "]" ).append( fitInProgress( progress ) );
    }

    private static String fitInProgress( long value )
    {
        int weight = weight( value );

        String progress;
        if ( weight == 0 )
        {
            progress = String.valueOf( value );
        }
        else
        {
            double floatValue = value / pow( 1000, weight );
            progress = String.valueOf( floatValue );
            if ( progress.length() > PROGRESS_WIDTH - 1 )
            {
                progress = progress.substring( 0, PROGRESS_WIDTH - 1 );
            }
            if ( progress.endsWith( "." ) )
            {
                progress = progress.substring( 0, progress.length() - 1 );
            }
            progress += WEIGHTS[weight];
        }

        return pad( progress, PROGRESS_WIDTH, ' ' );
    }

    private static String pad( String result, int length, char padChar )
    {
        while ( result.length() < length )
        {
            result = padChar + result;
        }
        return result;
    }

    private static int weight( long value )
    {
        int weight = 0;
        while ( value >= 1000 )
        {
            value /= 1000;
            weight++;
        }
        return weight;
    }

    private static long[] values( StageExecution execution )
    {
        long[] values = new long[execution.size()];
        int i = 0;
        for ( Step<?> step : execution.steps() )
        {
            values[i++] = avg( step.stats() );
        }
        return values;
    }

    private static long total( long[] values )
    {
        long total = 0;
        for ( long value : values )
        {
            total += value;
        }
        return total;
    }

    private static long avg( StatsProvider step )
    {
        return step.stat( Keys.avg_processing_time ).asLong();
    }
}

public class SecureRandomStrengthener {
    private static final String DEFAULT_PSEUDO_RANDOM_NUMBER_GENERATOR = "SHA1PRNG";

    private static final EntropySource mTimeEntropySource = new EntropySource() {

        final ByteBuffer timeBuffer = ByteBuffer.allocate(Long.SIZE / Byte.SIZE
                * 2);

        @Override
        public ByteBuffer provideEntropy() {
            this.timeBuffer.clear();
            this.timeBuffer.putLong(System.currentTimeMillis());
            this.timeBuffer.putLong(System.nanoTime());
            this.timeBuffer.flip();
            return this.timeBuffer;
        }
    };

    private final String algorithm;
    private final List<EntropySource> entropySources = new LinkedList<EntropySource>();
    private final MessageDigest digest;
    private final ByteBuffer seedBuffer;

    public static SecureRandomStrengthener getInstance() {
        return new SecureRandomStrengthener(
                DEFAULT_PSEUDO_RANDOM_NUMBER_GENERATOR);
    }

    public static SecureRandomStrengthener getInstance(final String algorithm) {
        return new SecureRandomStrengthener(algorithm);
    }

    private SecureRandomStrengthener(final String algorithm) {
        if (algorithm == null || algorithm.length() == 0) {
            throw new IllegalArgumentException(
                    "Please provide a PRNG algorithm string such as SHA1PRNG");
        }

        this.algorithm = algorithm;
        try {
            this.digest = MessageDigest.getInstance("SHA1");
        } catch (final NoSuchAlgorithmException e) {
            throw new IllegalStateException(
                    "MessageDigest to create seed not available", e);
        }
        this.seedBuffer = ByteBuffer.allocate(this.digest.getDigestLength());
    }

    public void addEntropySource(final EntropySource source) {
        if (source == null) {
            throw new IllegalArgumentException(
                    "EntropySource should not be null");
        }
        this.entropySources.add(source);
    }

    public SecureRandom generateAndSeedRandomNumberGenerator() {
        final SecureRandom secureRandom;
        try {
            secureRandom = SecureRandom.getInstance(this.algorithm);
        } catch (final NoSuchAlgorithmException e) {
            throw new IllegalStateException("PRNG is not available", e);
        }

        reseed(secureRandom);
        return secureRandom;
    }

    public void reseed(final SecureRandom secureRandom) {
        this.seedBuffer.clear();
        secureRandom.nextBytes(this.seedBuffer.array());

        for (final EntropySource source : this.entropySources) {
            final ByteBuffer entropy = source.provideEntropy();
            if (entropy == null) {
                continue;
            }

            final ByteBuffer wipeBuffer = entropy.duplicate();
            this.digest.update(entropy);
            wipe(wipeBuffer);
        }

        this.digest.update(mTimeEntropySource.provideEntropy());
        this.digest.update(this.seedBuffer);
        this.seedBuffer.clear();
        try {
            this.digest.digest(this.seedBuffer.array(), 0,
                    this.seedBuffer.capacity());
        } catch (final DigestException e) {
            throw new IllegalStateException(
                    "DigestException should not be thrown", e);
        }
        secureRandom.setSeed(this.seedBuffer.array());

        wipe(this.seedBuffer);
    }

    private void wipe(final ByteBuffer buf) {
        while (buf.hasRemaining()) {
            buf.put((byte) 0);
        }
    }
}

public abstract class ServletInputStream extends InputStream {

    protected ServletInputStream() {
    }

    public int readLine(byte[] b, int off, int len) throws IOException {

        if (len <= 0) {
            return 0;
        }
        int count = 0, c;

        while ((c = read()) != -1) {
            b[off++] = (byte) c;
            count++;
            if (c == '\n' || count == len) {
                break;
            }
        }
        return count > 0 ? count : -1;
    }

    public abstract boolean isFinished();

    public abstract boolean isReady();

    public abstract void setReadListener(ReadListener readListener);
}

public class LocalPlayback implements Playback, AudioManager.OnAudioFocusChangeListener,
        OnCompletionListener, OnErrorListener, OnPreparedListener, OnSeekCompleteListener {

    public static final float VOLUME_DUCK = 0.2f;
    public static final float VOLUME_NORMAL = 1.0f;

    private static final int AUDIO_NO_FOCUS_NO_DUCK = 0;
    private static final int AUDIO_NO_FOCUS_CAN_DUCK = 1;
    private static final int AUDIO_FOCUSED  = 2;

    private final Context mContext;
    private final WifiManager.WifiLock mWifiLock;
    private int mState;
    private boolean mPlayOnFocusGain;
    private Callback mCallback;
    private final MusicProvider mMusicProvider;
    private volatile boolean mAudioNoisyReceiverRegistered;
    private volatile int mCurrentPosition;
    private volatile String mCurrentMediaId;

    private int mAudioFocus = AUDIO_NO_FOCUS_NO_DUCK;
    private final AudioManager mAudioManager;
    private MediaPlayer mMediaPlayerA;
    private MediaPlayer mMediaPlayerB;
    private MediaPlayer mMediaPlayer;
    private boolean mMediaPlayersSwapping;
    private volatile String mNextMediaId;

    @Override
    public boolean supportsGapless() {
        return true;
    }

    private MediaPlayer nextMediaPlayer() {
        if (mMediaPlayer == mMediaPlayerA) return mMediaPlayerB;
        return mMediaPlayerA;
    }

    private final IntentFilter mAudioNoisyIntentFilter =
            new IntentFilter(AudioManager.ACTION_AUDIO_BECOMING_NOISY);

    private final BroadcastReceiver mAudioNoisyReceiver = new BroadcastReceiver() {
        @Override
        public void onReceive(Context context, Intent intent) {
            if (AudioManager.ACTION_AUDIO_BECOMING_NOISY.equals(intent.getAction())) {
                Timber.d("Headphones disconnected.");
                if (isPlaying()) {
                    Intent i = new Intent(context, MusicService.class);
                    i.setAction(MusicService.ACTION_CMD);
                    i.putExtra(MusicService.CMD_NAME, MusicService.CMD_PAUSE);
                    mContext.startService(i);
                }
            }
        }
    };

    public LocalPlayback(Context context, MusicProvider musicProvider) {
        this.mContext = context;
        this.mMusicProvider = musicProvider;
        this.mAudioManager = (AudioManager) context.getSystemService(Context.AUDIO_SERVICE);
        this.mWifiLock = ((WifiManager) context.getApplicationContext()
                .getSystemService(Context.WIFI_SERVICE))
                .createWifiLock(WifiManager.WIFI_MODE_FULL, "uAmp_lock");
        this.mState = PlaybackStateCompat.STATE_NONE;
    }

    @Override
    public void start() {
    }

    @Override
    public void stop(boolean notifyListeners) {
        mState = PlaybackStateCompat.STATE_STOPPED;
        if (notifyListeners && mCallback != null) {
            mCallback.onPlaybackStatusChanged(mState);
        }
        mCurrentPosition = getCurrentStreamPosition();
        giveUpAudioFocus();
        unregisterAudioNoisyReceiver();
        relaxResources(true);
    }

    @Override
    public void setState(int state) {
        this.mState = state;
    }

    @Override
    public int getState() {
        return mState;
    }

    @Override
    public boolean isConnected() {
        return true;
    }

    @Override
    public boolean isPlaying() {
        return mPlayOnFocusGain || (mMediaPlayer != null && mMediaPlayer.isPlaying());
    }

    @Override
    public int getCurrentStreamPosition() {
        return mMediaPlayer != null ?
                mMediaPlayer.getCurrentPosition() : mCurrentPosition;
    }

    @Override
    public void updateLastKnownStreamPosition() {
        if (mMediaPlayer != null) {
            mCurrentPosition = mMediaPlayer.getCurrentPosition();
        }
    }

    public boolean playNext(QueueItem item) {
        MediaPlayer nextPlayer;
        if (mMediaPlayer == mMediaPlayerA) {
            nextPlayer = mMediaPlayerB;
        }
        else nextPlayer = mMediaPlayerA;

        String mediaId = item.getDescription().getMediaId();
        boolean mediaHasChanged = !TextUtils.equals(mediaId, mCurrentMediaId);
        if (mediaHasChanged) {
            mNextMediaId = mediaId;
        }

        MediaMetadataCompat track = mMusicProvider.getMusic(
                MediaIDHelper.extractMusicIDFromMediaID(item.getDescription().getMediaId()));

        String source = track.getString(MusicProviderSource.CUSTOM_METADATA_TRACK_SOURCE);

        nextPlayer.setAudioStreamType(AudioManager.STREAM_MUSIC);

        try {
            nextPlayer.setDataSource(source);
        } catch (IOException ex) {
            Timber.e(ex, "Exception playing song");
            if (mCallback != null) {
                mCallback.onError(ex.getMessage());
            }
        }

        nextPlayer.prepareAsync();

        mMediaPlayersSwapping = true;
        return true;
    }


    @Override
    public void play(QueueItem item) {

        if (mMediaPlayersSwapping) {
            mMediaPlayersSwapping = false;
        }

        mPlayOnFocusGain = true;
        tryToGetAudioFocus();
        registerAudioNoisyReceiver();
        String mediaId = item.getDescription().getMediaId();
        boolean mediaHasChanged = !TextUtils.equals(mediaId, mCurrentMediaId);
        if (mediaHasChanged) {
            mCurrentPosition = 0;
            mCurrentMediaId = mediaId;
        }

        if (mState == PlaybackStateCompat.STATE_PAUSED && !mediaHasChanged && mMediaPlayer != null) {
            configMediaPlayerState();
        } else {
            mState = PlaybackStateCompat.STATE_STOPPED;
            relaxResources(false); MediaMetadataCompat track = mMusicProvider.getMusic(
                    MediaIDHelper.extractMusicIDFromMediaID(item.getDescription().getMediaId()));

            String source = track.getString(MusicProviderSource.CUSTOM_METADATA_TRACK_SOURCE);

            try {
                createMediaPlayerIfNeeded();

                mState = PlaybackStateCompat.STATE_BUFFERING;

                mMediaPlayer.setAudioStreamType(AudioManager.STREAM_MUSIC);
                mMediaPlayer.setDataSource(source);

                mMediaPlayer.prepareAsync();

                mWifiLock.acquire();

                if (mCallback != null) {
                    mCallback.onPlaybackStatusChanged(mState);
                }

            } catch (IOException ex) {
                Timber.e(ex, "Exception playing song");
                if (mCallback != null) {
                    mCallback.onError(ex.getMessage());
                }
            }
        }
    }

    @Override
    public void pause() {
        if (mState == PlaybackStateCompat.STATE_PLAYING) {
            if (mMediaPlayer != null && mMediaPlayer.isPlaying()) {
                mMediaPlayer.pause();
                mCurrentPosition = mMediaPlayer.getCurrentPosition();
            }
            relaxResources(false);
            giveUpAudioFocus();
        }
        mState = PlaybackStateCompat.STATE_PAUSED;
        if (mCallback != null) {
            mCallback.onPlaybackStatusChanged(mState);
        }
        unregisterAudioNoisyReceiver();
    }

    @Override
    public void seekTo(int position) {
        Timber.d("seekTo called with %s", position);

        if (mMediaPlayer == null) {
            mCurrentPosition = position;
        } else {
            if (mMediaPlayer.isPlaying()) {
                mState = PlaybackStateCompat.STATE_BUFFERING;
            }
            mMediaPlayer.seekTo(position);
            if (mCallback != null) {
                mCallback.onPlaybackStatusChanged(mState);
            }
        }
    }

    @Override
    public void setCallback(Callback callback) {
        this.mCallback = callback;
    }

    @Override
    public void setCurrentStreamPosition(int pos) {
        this.mCurrentPosition = pos;
    }

    @Override
    public void setCurrentMediaId(String mediaId) {
        this.mCurrentMediaId = mediaId;
    }

    @Override
    public String getCurrentMediaId() {
        return mCurrentMediaId;
    }

    private void tryToGetAudioFocus() {
        Timber.d("tryToGetAudioFocus");
        if (mAudioFocus != AUDIO_FOCUSED) {
            int result = mAudioManager.requestAudioFocus(this, AudioManager.STREAM_MUSIC,
                    AudioManager.AUDIOFOCUS_GAIN);
            if (result == AudioManager.AUDIOFOCUS_REQUEST_GRANTED) {
                mAudioFocus = AUDIO_FOCUSED;
            }
        }
    }

    private void giveUpAudioFocus() {
        Timber.d("giveUpAudioFocus");
        if (mAudioFocus == AUDIO_FOCUSED) {
            if (mAudioManager.abandonAudioFocus(this) == AudioManager.AUDIOFOCUS_REQUEST_GRANTED) {
                mAudioFocus = AUDIO_NO_FOCUS_NO_DUCK;
            }
        }
    }

    private void configMediaPlayerState() {
        Timber.d("configMediaPlayerState. mAudioFocus=%s", mAudioFocus);
        if (mAudioFocus == AUDIO_NO_FOCUS_NO_DUCK) {
            if (mState == PlaybackStateCompat.STATE_PLAYING) {
                pause();
            }
        } else {  if (mAudioFocus == AUDIO_NO_FOCUS_CAN_DUCK) {
                mMediaPlayer.setVolume(VOLUME_DUCK, VOLUME_DUCK); } else {
                if (mMediaPlayer != null) {
                    mMediaPlayer.setVolume(VOLUME_NORMAL, VOLUME_NORMAL); } }
            if (mPlayOnFocusGain) {
                if (mMediaPlayer != null && !mMediaPlayer.isPlaying()) {
                    Timber.d("configMediaPlayerState startMediaPlayer. seeking to %s ",
                        mCurrentPosition);
                    if (mCurrentPosition == mMediaPlayer.getCurrentPosition()) {
                        mMediaPlayer.start();
                        mState = PlaybackStateCompat.STATE_PLAYING;
                    } else {
                        mMediaPlayer.seekTo(mCurrentPosition);
                        mState = PlaybackStateCompat.STATE_BUFFERING;
                    }
                }
                mPlayOnFocusGain = false;
            }
        }
        if (mCallback != null) {
            mCallback.onPlaybackStatusChanged(mState);
        }
    }

    @Override
    public void onAudioFocusChange(int focusChange) {
        Timber.d("onAudioFocusChange. focusChange=%s", focusChange);
        if (focusChange == AudioManager.AUDIOFOCUS_GAIN) {
            mAudioFocus = AUDIO_FOCUSED;

        } else if (focusChange == AudioManager.AUDIOFOCUS_LOSS ||
                focusChange == AudioManager.AUDIOFOCUS_LOSS_TRANSIENT ||
                focusChange == AudioManager.AUDIOFOCUS_LOSS_TRANSIENT_CAN_DUCK) {
            boolean canDuck = focusChange == AudioManager.AUDIOFOCUS_LOSS_TRANSIENT_CAN_DUCK;
            mAudioFocus = canDuck ? AUDIO_NO_FOCUS_CAN_DUCK : AUDIO_NO_FOCUS_NO_DUCK;

            if (mState == PlaybackStateCompat.STATE_PLAYING && !canDuck) {
                mPlayOnFocusGain = true;
            }
        } else {
            Timber.e("onAudioFocusChange: Ignoring unsupported focusChange: %s", focusChange);
        }
        configMediaPlayerState();
    }

    @Override
    public void onSeekComplete(MediaPlayer mp) {
        Timber.d("onSeekComplete from MediaPlayer: %s", mp.getCurrentPosition());
        mCurrentPosition = mp.getCurrentPosition();
        if (mState == PlaybackStateCompat.STATE_BUFFERING) {
            mMediaPlayer.start();
            mState = PlaybackStateCompat.STATE_PLAYING;
        }
        if (mCallback != null) {
            mCallback.onPlaybackStatusChanged(mState);
        }
    }

    @Override
    public void onCompletion(MediaPlayer player) {
        Timber.d("onCompletion from MediaPlayer");
        if (mMediaPlayersSwapping) {
            mCurrentPosition = 0;
            mCurrentMediaId = mNextMediaId;
            MediaPlayer old = mMediaPlayer;
            mMediaPlayer = nextMediaPlayer();  mMediaPlayersSwapping = false;
            old.reset();  mCallback.onPlaybackStatusChanged(mState);
        }

        if (mCallback != null) {
            mCallback.onCompletion();
        }

    }

    @Override
    public void onPrepared(MediaPlayer player) {
        Timber.d("onPrepared from MediaPlayer");

        if (mMediaPlayersSwapping) {
            mMediaPlayer.setNextMediaPlayer(nextMediaPlayer());
            return;
        }

        configMediaPlayerState();
    }

    @Override
    public boolean onError(MediaPlayer mp, int what, int extra) {
        Timber.e("Media player error: what=%s extra=%s", what, extra);
        if (mCallback != null) {
            mCallback.onError("MediaPlayer error " + what + " (" + extra + ")");
        }
        return true; }

    private void createMediaPlayerIfNeeded() {
        mMediaPlayerA = createMediaPlayer(mMediaPlayerA);
        mMediaPlayerB = createMediaPlayer(mMediaPlayerB);
        if (mMediaPlayer == null) mMediaPlayer = mMediaPlayerA;
    }
    private MediaPlayer createMediaPlayer(MediaPlayer player) {
        Timber.d("createMediaPlayerIfNeeded. needed? %s", (player==null));
        if (player == null) {
            player = new MediaPlayer();

            player.setWakeMode(mContext.getApplicationContext(),
                    PowerManager.PARTIAL_WAKE_LOCK);

            player.setOnPreparedListener(this);
            player.setOnCompletionListener(this);
            player.setOnErrorListener(this);
            player.setOnSeekCompleteListener(this);
        } else {
            player.reset();
        }

        return  player;
    }

    private void relaxResources(boolean releaseMediaPlayer) {
        Timber.d("relaxResources. releaseMediaPlayer=%s", releaseMediaPlayer);

        if (releaseMediaPlayer && mMediaPlayer != null) {
            mMediaPlayer.reset();
            mMediaPlayer.release();
            mMediaPlayer = null;
        }

        if (mWifiLock.isHeld()) {
            mWifiLock.release();
        }
    }

    private void registerAudioNoisyReceiver() {
        if (!mAudioNoisyReceiverRegistered) {
            mContext.registerReceiver(mAudioNoisyReceiver, mAudioNoisyIntentFilter);
            mAudioNoisyReceiverRegistered = true;
        }
    }

    private void unregisterAudioNoisyReceiver() {
        if (mAudioNoisyReceiverRegistered) {
            mContext.unregisterReceiver(mAudioNoisyReceiver);
            mAudioNoisyReceiverRegistered = false;
        }
    }
}

@FieldAreNonnullByDefault
public final class Service implements Parcelable {
    @Json(name = "service_id") public final String id;
    @Json(name = "service_name") public final String name;
    @Json(name = "service_short_name") public final String shortName;

    @Json(name = "is_primary") public final boolean isPrimary;

    @Json(name = "monochrome_icon_url") public final String monochromeIconUrl;
    @HexColor @Json(name = "brand_color") public final int brandColor;
    public final String url;

    public Service(String id, String name, String shortName, boolean isPrimary, String monochromeIconUrl,
            int brandColor, String url) {
        this.id = id;
        this.name = name;
        this.shortName = shortName;
        this.isPrimary = isPrimary;
        this.monochromeIconUrl = monochromeIconUrl;
        this.brandColor = brandColor;
        this.url = url;
    }

    protected Service(Parcel in) {
        id = in.readString();
        name = in.readString();
        shortName = in.readString();
        isPrimary = in.readByte() != 0;
        monochromeIconUrl = in.readString();
        brandColor = in.readInt();
        url = in.readString();
    }

    public static final Creator<Service> CREATOR = new Creator<Service>() {
        @Override
        public Service createFromParcel(Parcel in) {
            return new Service(in);
        }

        @Override
        public Service[] newArray(int size) {
            return new Service[size];
        }
    };

    @Override
    public int describeContents() {
        return 0;
    }

    @Override
    public void writeToParcel(Parcel parcel, int i) {
        parcel.writeString(id);
        parcel.writeString(name);
        parcel.writeString(shortName);
        parcel.writeByte((byte) (isPrimary ? 1 : 0));
        parcel.writeString(monochromeIconUrl);
        parcel.writeInt(brandColor);
        parcel.writeString(url);
    }
}

abstract class CharsetRecog_sbcs extends CharsetRecognizer {

    @Override
    abstract String getName();

    static class NGramParser
    {
private static final int N_GRAM_MASK = 0xFFFFFF;

        protected int byteIndex = 0;
        private int ngram = 0;

        private int[] ngramList;
        protected byte[] byteMap;

        private int ngramCount;
        private int hitCount;

        protected byte spaceChar;

        public NGramParser(int[] theNgramList, byte[] theByteMap)
        {
            ngramList = theNgramList;
            byteMap   = theByteMap;

            ngram = 0;

            ngramCount = hitCount = 0;
        }

        private static int search(int[] table, int value)
        {
            int index = 0;

            if (table[index + 32] <= value) {
                index += 32;
            }

            if (table[index + 16] <= value) {
                index += 16;
            }

            if (table[index + 8] <= value) {
                index += 8;
            }

            if (table[index + 4] <= value) {
                index += 4;
            }

            if (table[index + 2] <= value) {
                index += 2;
            }

            if (table[index + 1] <= value) {
                index += 1;
            }

            if (table[index] > value) {
                index -= 1;
            }

            if (index < 0 || table[index] != value) {
                return -1;
            }

            return index;
        }

        private void lookup(int thisNgram)
        {
            ngramCount += 1;

            if (search(ngramList, thisNgram) >= 0) {
                hitCount += 1;
            }

        }

        protected void addByte(int b)
        {
            ngram = ((ngram << 8) + (b & 0xFF)) & N_GRAM_MASK;
            lookup(ngram);
        }

        private int nextByte(CharsetDetector det)
        {
            if (byteIndex >= det.fInputLen) {
                return -1;
            }

            return det.fInputBytes[byteIndex++] & 0xFF;
        }

        protected void parseCharacters(CharsetDetector det)
        {
            int b;
            boolean ignoreSpace = false;

            while ((b = nextByte(det)) >= 0) {
                byte mb = byteMap[b];

                if (mb != 0) {
                    if (!(mb == spaceChar && ignoreSpace)) {
                        addByte(mb);
                    }

                    ignoreSpace = (mb == spaceChar);
                }
            }

        }

        public int parse(CharsetDetector det)
        {
            return parse (det, (byte)0x20);
        }
        public int parse(CharsetDetector det, byte spaceCh)
        {

            this.spaceChar = spaceCh;

            parseCharacters(det);

            addByte(spaceChar);

            double rawPercent = (double) hitCount / (double) ngramCount;

if (rawPercent > 0.33) {
                return 98;
            }

            return (int) (rawPercent * 300.0);
        }
    }

    static class NGramParser_IBM420 extends NGramParser
    {
        private byte alef = 0x00;

