public class Attributes {

  public class Inventory {
    public Map<String,Set<String>> read;
    public Map<String,Set<String>> write;
    public Set<String> exampleValues;

    public Inventory() {
      this.read = new TreeMap<String,Set<String>>();
      this.write = new TreeMap<String,Set<String>>();
      this.exampleValues = new TreeSet<String>();
    }

    public void read(String module, String state) {
      Set<String> states = this.read.computeIfAbsent(module, f -> new TreeSet<String>());
      if (state != null) {
        states.add(state);        
      }
    }

    public void write(String module, String state, String example) {
      Set<String> states = this.write.computeIfAbsent(module, f -> new TreeSet<String>());
      if (state != null) {
        states.add(state);
      }
      if (example != null) {
        exampleValues.add(example);
      }
    }
  }

  public static void main(String[] args) throws Exception {
    System.out.println("Performing an inventory of attributes into `output/attributes.json`...");

    Map<String,Inventory> output = getAttributeInventory();

    String outFilePath = new File("./output/attributes.json").toPath().toString();
    Writer writer = new FileWriter(outFilePath);
    Gson gson = new GsonBuilder()
        .setFieldNamingPolicy(FieldNamingPolicy.LOWER_CASE_WITH_UNDERSCORES)
        .setPrettyPrinting().create();
    gson.toJson(output, writer);
    writer.flush();
    writer.close();

    graph(output, "attributes_all", false);
    graph(output, "attributes_readwrite", true);

    System.out.println("Catalogued " + output.size() + " attributes.");
    System.out.println("Done.");
  }

  public static Map<String,Inventory> getAttributeInventory() throws Exception {
    Map<String,Inventory> attributes = new TreeMap<String,Inventory>();

    Utilities.walkAllModules((basePath, modulePath) -> {
      try (JsonReader reader = new JsonReader(new FileReader(modulePath.toString()))) {
        JsonObject module = JsonParser.parseReader(reader).getAsJsonObject();
        inventoryModule(attributes, module);
      } catch (IOException e) {
        throw new RuntimeException("Unable to read modules", e);
      }
    });

    CardiovascularDiseaseModule.inventoryAttributes(attributes);
    DeathModule.inventoryAttributes(attributes);
    EncounterModule.inventoryAttributes(attributes);
    HealthInsuranceModule.inventoryAttributes(attributes);
    Immunizations.inventoryAttributes(attributes);
    LifecycleModule.inventoryAttributes(attributes);
    QualityOfLifeModule.inventoryAttributes(attributes);
    C19ImmunizationModule.inventoryAttributes(attributes);

    return attributes;
  }

  private static void inventoryModule(Map<String,Inventory> attributes, JsonObject module) {
    String moduleName = module.get("name").getAsString();
    JsonObject states = module.get("states").getAsJsonObject();
    Set<String> stateNames = new HashSet<String>();
    for (Entry<String, JsonElement> entry : states.entrySet()) {
      stateNames.add(entry.getKey());
    }
    for (Entry<String, JsonElement> entry : states.entrySet()) {
      String stateName = entry.getKey();
      JsonObject state = entry.getValue().getAsJsonObject();
      inventoryState(attributes, moduleName, stateName, state, stateNames);
    }
  }

  private static void inventoryState(Map<String,Inventory> attributes, String moduleName,
      String stateName, JsonObject state, Set<String> stateNames) {

    String type = state.get("type").getAsString();

    if (state.has("reason")) {
      String reason = state.get("reason").getAsString();
      if (!reason.isEmpty()) {
        if (stateNames.contains(reason)) {
          } else {
          Inventory data = attributes.computeIfAbsent(reason,
              f -> new Attributes().new Inventory());
          data.read(moduleName, stateName);    
        }        
      }
    }

    if (state.has("assign_to_attribute")) {
      String attribute = state.get("assign_to_attribute").getAsString();
      if (!attribute.isEmpty()) {
        Inventory data = attributes.computeIfAbsent(attribute,
            f -> new Attributes().new Inventory());
        data.write(moduleName, stateName, type);        
      }
    }

    if (state.has("referenced_by_attribute")) {
      String attribute = state.get("referenced_by_attribute").getAsString();
      if (!attribute.isEmpty()) {
        Inventory data = attributes.computeIfAbsent(attribute,
            f -> new Attributes().new Inventory());
        data.read(moduleName, stateName);        
      }
    }

    if (state.has("attribute")) {
      String attribute = state.get("attribute").getAsString();
      if (!attribute.isEmpty()) {
        Inventory data = attributes.computeIfAbsent(attribute,
            f -> new Attributes().new Inventory());
        if (type.equalsIgnoreCase("SetAttribute")) {
          String value = null;
          try {
            value = state.get("value").getAsJsonPrimitive().toString();
          } catch (Exception e) {
            }
          data.write(moduleName, stateName, value);
        } else if (type.equalsIgnoreCase("Counter")) {
          data.write(moduleName, stateName, "Integer");
        } else if (type.equalsIgnoreCase("Observation")) {
          data.read(moduleName, stateName);
        } else {
          System.out.println("Unhandled State: " + type);
        }        
      }
    }

    inventoryTransition(attributes, moduleName, stateName, state);
  }

  private static void inventoryTransition(Map<String,Inventory> attributes, String moduleName,
      String stateName, JsonObject state) {

    if (state.has("distributed_transition")) {
      } else if (state.has("conditional_transition")) {
      JsonArray transitions = state.get("conditional_transition").getAsJsonArray();
      for (JsonElement element : transitions) {
        JsonObject transition = (JsonObject) element;
        if (transition.has("condition")) {
          JsonObject condition = transition.get("condition").getAsJsonObject();
          inventoryLogic(attributes, moduleName, stateName, condition);
        }
      }
    } else if (state.has("complex_transition")) {
      JsonArray transitions = state.get("complex_transition").getAsJsonArray();
      for (JsonElement element : transitions) {
        JsonObject transition = (JsonObject) element;
        if (transition.has("condition")) {
          JsonObject condition = transition.get("condition").getAsJsonObject();
          inventoryLogic(attributes, moduleName, stateName, condition);
        }
      }
    }
  }

  private static void inventoryLogic(Map<String,Inventory> attributes, String moduleName,
      String stateName, JsonObject logic) {

    String type = logic.get("condition_type").getAsString();

    if (type.equalsIgnoreCase("Attribute")) {
      String attribute = logic.get("attribute").getAsString();
      if (!attribute.isEmpty()) {
        Inventory data = attributes.computeIfAbsent(attribute,
            f -> new Attributes().new Inventory());
        data.read(moduleName, stateName);
      }
    } else if (type.equalsIgnoreCase("Age")) {
      Inventory data = attributes.computeIfAbsent(Person.BIRTHDATE,
          f -> new Attributes().new Inventory());
      data.read(moduleName, stateName);
    } else if (type.equalsIgnoreCase("Gender")) {
      Inventory data = attributes.computeIfAbsent(Person.GENDER,
          f -> new Attributes().new Inventory());
      data.read(moduleName, stateName);
    } else if (type.equalsIgnoreCase("Socioeconomic Status")) {
      Inventory data = attributes.computeIfAbsent(Person.SOCIOECONOMIC_CATEGORY,
          f -> new Attributes().new Inventory());
      data.read(moduleName, stateName);
    } else if (type.equalsIgnoreCase("Race")) {
      Inventory data = attributes.computeIfAbsent(Person.RACE,
          f -> new Attributes().new Inventory());
      data.read(moduleName, stateName);
    } else if (type.equalsIgnoreCase("Symptom")) {
      String attribute = logic.get("symptom").getAsString();
      if (!attribute.isEmpty()) {
        Inventory data = attributes.computeIfAbsent(attribute,
            f -> new Attributes().new Inventory());
        data.read(moduleName, stateName);
      }
    } else if (type.equalsIgnoreCase("Vital Sign")) {
      String attribute = logic.get("vital_sign").getAsString();
      if (!attribute.isEmpty()) {
        Inventory data = attributes.computeIfAbsent(attribute,
            f -> new Attributes().new Inventory());
        data.read(moduleName, stateName);
      }
    } else if (type.equalsIgnoreCase("Observation")
        || type.equalsIgnoreCase("Active Condition")
        || type.equalsIgnoreCase("Active Medication")
        || type.equalsIgnoreCase("Active CarePlan")) {
      if (logic.has("referenced_by_attribute")) {
        String attribute = logic.get("referenced_by_attribute").getAsString();
        if (!attribute.isEmpty()) {
          Inventory data = attributes.computeIfAbsent(attribute,
              f -> new Attributes().new Inventory());
          data.read(moduleName, stateName);
        }
      }
    } else if (type.equalsIgnoreCase("And")
        || type.equalsIgnoreCase("Or")
        || type.equalsIgnoreCase("Not")
        || type.equalsIgnoreCase("AtLeast")
        || type.equalsIgnoreCase("At Most")) {
      if (logic.has("conditions")) {
        JsonArray conditions = logic.get("conditions").getAsJsonArray();
        for (JsonElement element : conditions) {
          JsonObject condition = (JsonObject) element;
          inventoryLogic(attributes, moduleName, stateName, condition);
        }
      }
    }
  }

  public static void inventory(Map<String,Inventory> attributes,
      String module, String attribute, boolean read, boolean write, String example) {
    Inventory data = attributes.computeIfAbsent(attribute, f -> new Attributes().new Inventory());
    if (read) {
      data.read(module, null);
    }
    if (write) {
      data.write(module, null, example);
    }
  }

  public static void graph(Map<String,Inventory> output, String filename, boolean readAndWrite) {
    Map<String,Node> modules = new TreeMap<String,Node>();
    Map<String,List<Link>> writeLinks = new TreeMap<String,List<Link>>();

    Graph graph = Factory.graph().directed();

    for (String attribute : output.keySet()) {
      Inventory inventory = output.get(attribute);
      if (readAndWrite && (inventory.read.isEmpty() || inventory.write.isEmpty())) {
        continue;
      }
      Node attributeNode = Factory.node(attribute).with(Shape.RECTANGLE);

      for (String module : inventory.read.keySet()) {
        String key = module + "\nModule";
        Node moduleNode = null;
        if (modules.containsKey(key)) {
          moduleNode = modules.get(key);
        } else {
          moduleNode = Factory.node(key);
          modules.put(key, moduleNode);
        }
        attributeNode = attributeNode.link(Factory.to(moduleNode).with(Style.DASHED));
      }
      for (String module : inventory.write.keySet()) {
        String key = module + "\nModule";
        Node moduleNode = null;
        if (modules.containsKey(key)) {
          moduleNode = modules.get(key);
        } else {
          moduleNode = Factory.node(key);
          modules.put(key, moduleNode);
        }

        List<Link> moduleWriteLinks = null;
        if (writeLinks.containsKey(key)) {
          moduleWriteLinks = writeLinks.get(key);
        } else {
          moduleWriteLinks = new ArrayList<Link>();
          writeLinks.put(key, moduleWriteLinks);
        }
        Link link = Factory.to(attributeNode);
        moduleWriteLinks.add(link);
      }
      graph = graph.with(attributeNode);
    }

    for (String key : modules.keySet()) {
      Node node = modules.get(key);
      List<Link> moduleWriteLinks = writeLinks.get(key);
      if (moduleWriteLinks != null && !moduleWriteLinks.isEmpty()) {
        graph = graph.with(node.link(moduleWriteLinks.toArray(new Link[0])));        
      } else {
        graph = graph.with(node);
      }
    }

    File graphFile = new File("./output/" + filename + ".png");
    try {
      Graphviz.fromGraph(graph).rasterizer(Rasterizer.BATIK).render(Format.PNG).toFile(graphFile);
    } catch (IOException e) {
      e.printStackTrace();
    }
  }
}

public abstract class ArticledDeclension extends AbstractLanguageDeclension {
    public ArticledDeclension(HumanLanguage language) {
		super(language);
	}

	@Override
    public final boolean hasArticle() {
        return true;
    }
    public abstract static class LegacyArticledNoun extends Noun {
        private static final long serialVersionUID = 1L;

		protected LegacyArticledNoun(ArticledDeclension declension, String name, String pluralAlias, NounType type, String entityName, LanguageStartsWith startsWith, LanguageGender gender, String access, boolean isStandardField, boolean isCopiedFromDefault) {
            super(declension, name, pluralAlias, type, entityName, startsWith, gender, access, isStandardField, isCopiedFromDefault);
        }

        @Override
        public final String getString(NounForm form) {
            if (form instanceof LegacyArticledNounForm) {
                LegacyArticledNounForm lanf = (LegacyArticledNounForm)form;
                if (getNounType() == NounType.ENTITY) {
                    String baseString = getExactString(lanf.getBaseNounForm());
                    ArticleForm articleForm = getDeclension().getApproximateArticleForm(getStartsWith(), getGender(), form.getNumber(), form.getCase());
                    String articleString = ((ArticledDeclension)getDeclension()).getDefaultArticleString(articleForm, lanf.getArticle());
                    return appendArticleToBase(baseString, articleString, form);
                } else {
                    return getExactString(lanf.getBaseNounForm());
                }
            } else {
                return getExactString(form);
            }
        }

        protected String appendArticleToBase(String base, String article, NounForm form) {
            if (article == null) {
                return base;
            }
            return article + getDeclension().formLowercaseNounForm(base, form);
        }

        public abstract String getExactString(NounForm form);
    }

    @Override
    public abstract Article createArticle(String name, LanguageArticle articleType);

    @Override
    public Set<LanguageArticle> getAllowedArticleTypes() {
        return EnumSet.of(LanguageArticle.ZERO, LanguageArticle.INDEFINITE, LanguageArticle.DEFINITE);
    }

    @Override
    public abstract List<? extends ArticleForm> getArticleForms();

    protected abstract String getDefaultArticleString(ArticleForm form, LanguageArticle articleType);

    public static class SimpleArticle extends Article {
        private static final long serialVersionUID = 1L;
		private static final Logger logger = Logger.getLogger(SimpleArticle.class.getName());
        private String value;
        public SimpleArticle(ArticledDeclension declension, String name, LanguageArticle articleType) {
            super(declension, name, articleType);
        }
        @Override
        public Map<SimpleModifierForm, String> getAllValues() {
            return Collections.singletonMap(SimpleModifierForm.SINGULAR, value);
        }
        @Override
        public String getString(ArticleForm form) {
            return value;
        }
        @Override
        protected void setString(ArticleForm form, String value) {
            this.value = intern(value);
        }
        @Override
        public boolean validate(String name) {
            if (this.value == null) {
                logger.info("###\tError: The article " + name + " has no form");
                return false;
            }
            return true;
        }
    }

    public static class SimpleArticledPluralNoun extends LegacyArticledNoun {
        private static final long serialVersionUID = 1L;
		private static final Logger logger = Logger.getLogger(SimpleArticledPluralNoun.class.getName());
        private String singular;
        private String plural;

        public SimpleArticledPluralNoun(ArticledDeclension declension, String name, String pluralAlias, NounType type, String entityName, LanguageStartsWith startsWith, LanguageGender gender, String access, boolean isStandardField, boolean isCopiedFromDefault) {
            super(declension, name, pluralAlias, type, entityName, startsWith, gender, access, isStandardField, isCopiedFromDefault);
        }

        @Override
        public Map<? extends NounForm, String> getAllDefinedValues() {
            return enumMapFilterNulls(PluralNounForm.SINGULAR, singular, PluralNounForm.PLURAL, plural);
        }

        @Override
        public String getDefaultString(boolean isPlural) {
            return isPlural && plural != null ? plural : singular;
        }

        @Override
        public String getExactString(NounForm form) {
            assert form instanceof PluralNounForm : "Why are you asking for some random noun form.  Really? " + form.getClass();
            if (getDeclension().getLanguage().getLocale().getLanguage().equals(LanguageConstants.ENGLISH)) {
                return getDefaultString(form.getNumber() == LanguageNumber.PLURAL);
            }
            return form.getNumber() == LanguageNumber.PLURAL ? plural : singular;
        }

        @Override
        protected void setString(String value, NounForm form) {
            value = intern(value);
            if (form.getNumber().isPlural()) {
                this.plural = value;
                if (value != null && value.equals(this.singular))
                 {
                    this.singular = value; }
            } else {
                this.singular = value;
                if (value != null && value.equals(this.plural))
                 {
                    this.plural = value; }
            }
        }

        @Override
        protected boolean validateValues(String name, LanguageCase _case) {
            if (this.singular == null) {
                logger.info("###\tError: The noun " + name + " has no singular form");
                return false;
            }
            return true;
        }

        @Override
        public Noun clone() {
            SimpleArticledPluralNoun noun = (SimpleArticledPluralNoun) super.clone();
            return noun;
        }

        @Override
        public void makeSkinny() {
        }
    }
}

public class SocketFactoryImpl extends SocketFactory {

    public static final boolean SO_KEEPALIVE = false;

    public static final boolean OOBINLINE = false;

    public static final boolean SO_REUSEADDR = false;

    public static final boolean TCP_NODELAY = true;

    public static final int SO_RCVBUF = 8192;

    public static final int SO_SNDBUF = 1024;

    public static final int SO_TIMEOUT = 12000;

    public static final int SO_CONNECT_TIMEOUT = 5000;

    public static final boolean SO_LINGER = true;

    public static final int LINGER = 0;


    @Override
    public Socket createSocket() throws IOException {
        Socket s = new Socket();
        return applySettings( s );
    }


    protected Socket applySettings( Socket s ) {
        try {
            s.setKeepAlive( SO_KEEPALIVE );
            s.setOOBInline( OOBINLINE );
            s.setReuseAddress( SO_REUSEADDR );
            s.setTcpNoDelay( TCP_NODELAY );
            s.setOOBInline( OOBINLINE );

            s.setReceiveBufferSize( SO_RCVBUF );
            s.setSendBufferSize( SO_SNDBUF );
            s.setSoTimeout( SO_TIMEOUT );
            s.setSoLinger( SO_LINGER, LINGER );
        } catch ( SocketException e ) {
            throw new RuntimeException( e );
        }
        return s;
    }


    @Override
    public Socket createSocket( String host, int port ) throws IOException {
        Socket s = createSocket();
        s.connect( new InetSocketAddress( host, port ), SO_CONNECT_TIMEOUT );
        return s;
    }


    @Override
    public Socket createSocket( InetAddress host, int port ) throws IOException {
        Socket s = createSocket();
        s.connect( new InetSocketAddress( host, port ), SO_CONNECT_TIMEOUT );
        return s;
    }


    @Override
    public Socket createSocket( String host, int port, InetAddress local, int localPort ) throws IOException {
        Socket s = createSocket();
        s.bind( new InetSocketAddress( local, localPort ) );
        s.connect( new InetSocketAddress( host, port ), SO_CONNECT_TIMEOUT );
        return s;
    }


    @Override
    public Socket createSocket( InetAddress host, int port, InetAddress local, int localPort ) throws IOException {
        Socket s = createSocket();
        s.bind( new InetSocketAddress( local, localPort ) );
        s.connect( new InetSocketAddress( host, port ), SO_CONNECT_TIMEOUT );
        return s;
    }


    public static SocketFactory getDefault() {
        return new SocketFactoryImpl();
    }
}

public final class JdbcConnection implements Closeable {
  private static final Logger log = LoggerFactory.getLogger(JdbcConnection.class);

  private final JdbcOperations operations;
  private final ConnectionSource connectionSource;

  private Connection root;

  @Nullable
  private Integer result = null;

  private int[] batchResult = null;

  @Nullable
  private List<Object> keys;

  private boolean canGetKeys;

  final boolean autoClose;

  @Nullable
  private Boolean originalAutoCommit;
  private boolean rollbackOnClose = true;
  private boolean rollbackOnException = true;

  private final HashSet<Statement> statements = new HashSet<>();

  public JdbcConnection(JdbcOperations operations, boolean autoClose) {
    this(operations, operations.getConnectionSource(), autoClose);
  }

  public JdbcConnection(JdbcOperations operations, ConnectionSource connectionSource, boolean autoClose) {
    this.autoClose = autoClose;
    this.operations = operations;
    this.connectionSource = connectionSource;
    createConnection();
  }

  public JdbcConnection(JdbcOperations operations, Connection connection, boolean autoClose) {
    this.root = connection;
    this.autoClose = autoClose;
    this.operations = operations;
    this.connectionSource = ConnectionSource.join(connection);
  }

  protected void onException() {
    if (isRollbackOnException()) {
      rollback(autoClose);
    }
  }

  public Query createQuery(String queryText) {
    boolean returnGeneratedKeys = operations.isGeneratedKeys();
    return createQuery(queryText, returnGeneratedKeys);
  }

  public Query createQuery(String queryText, boolean returnGeneratedKeys) {
    createConnectionIfNecessary();
    return new Query(this, queryText, returnGeneratedKeys);
  }

  public Query createQuery(String queryText, String... columnNames) {
    createConnectionIfNecessary();
    return new Query(this, queryText, columnNames);
  }

  private void createConnectionIfNecessary() {
    try {
      if (root.isClosed()) {
        createConnection();
      }
    }
    catch (SQLException e) {
      throw new PersistenceException("Database access error occurs", e);
    }
  }

  public Query createQueryWithParams(String queryText, Object... paramValues) {
    return createQuery(queryText)
            .withParams(paramValues);
  }

  public JdbcOperations rollback() {
    rollback(true);
    return operations;
  }

  public JdbcConnection rollback(boolean closeConnection) {
    try {
      root.rollback();
    }
    catch (SQLException e) {
      log.warn("Could not roll back transaction. message: {}", e);
    }
    finally {
      if (closeConnection) {
        closeConnection();
      }
    }
    return this;
  }

  public JdbcOperations commit() {
    commit(true);
    return operations;
  }

  public JdbcConnection commit(boolean closeConnection) {
    try {
      root.commit();
    }
    catch (SQLException e) {
      throw new PersistenceException("Commit error", e);
    }
    finally {
      if (closeConnection) {
        closeConnection();
      }
    }
    return this;
  }

  public int getResult() {
    if (result == null) {
      throw new PersistenceException(
              "It is required to call executeUpdate() method before calling getResult().");
    }
    return result;
  }

  void setResult(int result) {
    this.result = result;
  }

  public int[] getBatchResult() {
    if (batchResult == null) {
      throw new PersistenceException(
              "It is required to call executeBatch() method before calling getBatchResult().");
    }
    return batchResult;
  }

  protected void setBatchResult(int[] value) {
    this.batchResult = value;
  }

  protected void setKeys(@Nullable ResultSet rs) {
    if (rs == null) {
      this.keys = null;
    }
    else {
      try {
        final ArrayList<Object> keys = new ArrayList<>();
        while (rs.next()) {
          keys.add(rs.getObject(1));
        }
        this.keys = keys;
      }
      catch (SQLException e) {
        throw new GeneratedKeysException("Cannot get generated keys", e);
      }
    }

  }

  @Nullable
  public Object getKey() {
    assertCanGetKeys();
    List<Object> keys = this.keys;
    if (CollectionUtils.isNotEmpty(keys)) {
      return keys.get(0);
    }
    return null;
  }

  public <V> V getKey(final Class<V> returnType) {
    return getKey(returnType, operations.getConversionService());
  }

  public <V> V getKey(final Class<V> returnType, final ConversionService conversionService) {
    Assert.notNull(conversionService, "conversionService must not be null");
    final Object key = getKey();
    try {
      return conversionService.convert(key, returnType);
    }
    catch (ConversionException e) {
      throw new GeneratedKeysConversionException(
              "Exception occurred while converting value from database to type " + returnType.toString(), e);
    }
  }

  public Object[] getKeys() {
    assertCanGetKeys();
    final List<Object> keys = this.keys;
    if (keys != null) {
      return keys.toArray();
    }
    return null;
  }

  @Nullable
  public <V> List<V> getKeys(Class<V> returnType) {
    return getKeys(returnType, operations.getConversionService());
  }

  @Nullable
  public <V> List<V> getKeys(Class<V> returnType, ConversionService conversionService) {
    assertCanGetKeys();
    if (keys != null) {
      Assert.notNull(conversionService, "conversionService must not be null");
      try {
        final ArrayList<V> convertedKeys = new ArrayList<>(keys.size());
        for (final Object key : keys) {
          convertedKeys.add(conversionService.convert(key, returnType));
        }
        return convertedKeys;
      }
      catch (ConversionException e) {
        throw new GeneratedKeysConversionException(
                "Exception occurred while converting value from database to type " + returnType, e);
      }
    }
    return null;
  }

  private void assertCanGetKeys() {
    if (!canGetKeys) {
      throw new GeneratedKeysException(
              "Keys where not fetched from database." +
                      " Please set the returnGeneratedKeys parameter " +
                      "in the createQuery() method to enable fetching of generated keys.");
    }

  }

  void setCanGetKeys(boolean canGetKeys) {
    this.canGetKeys = canGetKeys;
  }

  void registerStatement(Statement statement) {
    statements.add(statement);
  }

  void removeStatement(Statement statement) {
    statements.remove(statement);
  }

  @Override
  public void close() {
    boolean connectionIsClosed;
    try {
      connectionIsClosed = root.isClosed();
    }
    catch (SQLException e) {
      throw new PersistenceException(
              "encountered a problem while trying to determine whether the connection is closed.", e);
    }

    if (!connectionIsClosed) {
      for (Statement statement : statements) {
        try {
          JdbcUtils.close(statement);
        }
        catch (SQLException e) {
          log.warn("Could not close statement.", e);
        }
      }
      statements.clear();

      boolean rollback = rollbackOnClose;
      if (rollback) {
        try {
          rollback = !root.getAutoCommit();
        }
        catch (SQLException e) {
          log.warn("Could not determine connection auto commit mode.", e);
        }
      }

      if (rollback) {
        rollback(true);
      }
      else {
        closeConnection();
      }
    }
  }

  private void createConnection() {
    try {
      this.root = connectionSource.getConnection();
      this.originalAutoCommit = root.getAutoCommit();
    }
    catch (SQLException ex) {
      throw new CannotGetJdbcConnectionException(
              "Could not acquire a connection from connection-source: " + connectionSource, ex);
    }
  }

  private void closeConnection() {
    if (originalAutoCommit != null) {
      try {
        this.root.setAutoCommit(originalAutoCommit);
      }
      catch (SQLException e) {
        log.warn("Could not reset autocommit state for connection to {}.", originalAutoCommit, e);
      }
    }

    JdbcUtils.closeQuietly(root);
  }

  public boolean isRollbackOnException() {
    return rollbackOnException;
  }

  public void setRollbackOnException(boolean rollbackOnException) {
    this.rollbackOnException = rollbackOnException;
  }

  public boolean isRollbackOnClose() {
    return rollbackOnClose;
  }

  public void setRollbackOnClose(boolean rollbackOnClose) {
    this.rollbackOnClose = rollbackOnClose;
  }

  public Connection getJdbcConnection() {
    return root;
  }

  public JdbcOperations getOperations() {
    return operations;
  }

}

@Deprecated
public abstract class BasicProjectMemoizer<T extends QualifiableNode, O extends QualifiableNode>
    implements ProjectMemoizer<T, O> {

    private Map<QualifiedName, MetricMemoizer<T>> classes = new WeakHashMap<>();
    private Map<QualifiedName, MetricMemoizer<O>> operations = new WeakHashMap<>();

    private final Object classesSynchronizer = new Object();
    private final Object operationsSynchronizer = new Object();

    public void reset() {
        classes.clear();
        operations.clear();
    }


    @Override
    public MetricMemoizer<O> getOperationMemoizer(QualifiedName qname) {
        synchronized (operationsSynchronizer) {
            if (!operations.containsKey(qname)) {
                operations.put(qname, new BasicMetricMemoizer<O>());
            }
        }

        return operations.get(qname);
    }


    @Override
    public MetricMemoizer<T> getClassMemoizer(QualifiedName qname) {
        synchronized (classesSynchronizer) {
            if (!classes.containsKey(qname)) {
                classes.put(qname, new BasicMetricMemoizer<T>());
            }
        }

        return classes.get(qname);
    }
}

@NotThreadSafe
public class DataFrame {
    static final int MIN_ENTRY_LENGTH_NEEDED = EntryHeader.HEADER_SIZE + 1;
    private static final CompositeByteArraySegment.BufferLayout BUFFER_LAYOUT = new CompositeByteArraySegment.BufferLayout(17); private static final byte CURRENT_VERSION = 0;
    private final CompositeArrayView data;
    private WriteFrameHeader header;
    private CompositeArrayView contents;

    @Getter
    @Setter
    private LogAddress address;

    private int writeEntryStartIndex;
    private WriteEntryHeader writeEntryHeader;
    private int writePosition;
    private boolean sealed;

    private DataFrame(CompositeArrayView source) {
        this.data = source;
        this.writeEntryStartIndex = -1;
        this.sealed = false;
        this.writePosition = this.sealed ? -1 : 0;

        int sourceLength = this.data.getLength();
        Exceptions.checkArgument(sourceLength > FrameHeader.SERIALIZATION_LENGTH, "data",
                "Insufficient array length. Byte array must have a length of at least %d.", FrameHeader.SERIALIZATION_LENGTH + 1);

        this.header = new WriteFrameHeader(CURRENT_VERSION, this.data.slice(0, FrameHeader.SERIALIZATION_LENGTH));
        this.contents = this.data.slice(FrameHeader.SERIALIZATION_LENGTH, sourceLength - FrameHeader.SERIALIZATION_LENGTH);
    }

    static DataFrame ofSize(int maxSize) {
        return new DataFrame(new CompositeByteArraySegment(maxSize, BUFFER_LAYOUT));
    }

    public int getLength() {
        return this.header.getSerializationLength() + this.header.getContentLength();
    }

    CompositeArrayView getData() {
        return this.data.slice(0, getLength());
    }

    boolean isEmpty() {
        return this.header.getContentLength() == 0;
    }

    boolean isSealed() {
        return this.sealed;
    }

    boolean startNewEntry(boolean firstRecordEntry) {
        Preconditions.checkState(!this.sealed, "DataFrame is sealed and cannot accept any more entries.");
        endEntry(true);

        if (getAvailableLength() < MIN_ENTRY_LENGTH_NEEDED) {
            return false;
        }

        this.writeEntryStartIndex = this.writePosition;
        this.writeEntryHeader = new WriteEntryHeader(this.contents.slice(this.writePosition, WriteEntryHeader.HEADER_SIZE));
        this.writeEntryHeader.setFirstRecordEntry(firstRecordEntry);
        this.writePosition += WriteEntryHeader.HEADER_SIZE;
        return true;
    }

    void discardEntry() {
        if (this.writeEntryStartIndex < 0) {
            return;
        }

        this.writePosition = this.writeEntryStartIndex;
        this.writeEntryStartIndex = -1;
        this.writeEntryHeader = null;
    }

    boolean endEntry(boolean endOfRecord) {
        if (this.writeEntryStartIndex >= 0) {
            int entryLength = this.writePosition - this.writeEntryStartIndex - WriteEntryHeader.HEADER_SIZE;
            assert entryLength >= 0 : "entryLength is negative.";

            this.writeEntryHeader.setEntryLength(entryLength);
            this.writeEntryHeader.setLastRecordEntry(endOfRecord);
            this.writeEntryHeader.serialize();
            this.writeEntryHeader = null;
            this.writeEntryStartIndex = -1;
        }

        return getAvailableLength() >= MIN_ENTRY_LENGTH_NEEDED;
    }

    int append(byte b) {
        ensureAppendConditions();
        if (getAvailableLength() >= 1) {
            this.contents.set(this.writePosition, b);
            this.writePosition++;
            return 1;
        } else {
            return 0;
        }
    }

    int append(short shortValue) {
        ensureAppendConditions();
        if (getAvailableLength() >= Short.BYTES) {
            this.contents.setShort(this.writePosition, shortValue);
            this.writePosition += Short.BYTES;
            return Short.BYTES;
        } else {
            return 0;
        }
    }

    int append(int intValue) {
        ensureAppendConditions();
        if (getAvailableLength() >= Integer.BYTES) {
            this.contents.setInt(this.writePosition, intValue);
            this.writePosition += Integer.BYTES;
            return Integer.BYTES;
        } else {
            return 0;
        }
    }

    int append(long longValue) {
        ensureAppendConditions();
        if (getAvailableLength() >= Long.BYTES) {
            this.contents.setLong(this.writePosition, longValue);
            this.writePosition += Long.BYTES;
            return Long.BYTES;
        } else {
            return 0;
        }
    }

    int append(BufferView.Reader data) {
        ensureAppendConditions();

        int actualLength = Math.min(data.available(), getAvailableLength());
        if (actualLength > 0) {
            this.contents.copyFrom(data, writePosition, actualLength);
            writePosition += actualLength;
        }

        return actualLength;
    }

    void seal() {
        if (!this.sealed) {
            Preconditions.checkState(writeEntryStartIndex < 0, "An open entry exists. Any open entries must be closed prior to sealing.");

            this.header.setContentLength(writePosition);
            this.header.commit();
            this.sealed = true;
        }
    }

    private int getAvailableLength() {
        return this.contents.getLength() - writePosition;
    }

    private void ensureAppendConditions() {
        Preconditions.checkState(!this.sealed, "DataFrame is sealed.");
        Preconditions.checkState(this.writeEntryStartIndex >= 0, "No entry started.");
    }

    public static DataFrameEntryIterator read(InputStream source, int length, LogAddress address) throws IOException {
        ReadFrameHeader header = new ReadFrameHeader(source);
        if (length < ReadFrameHeader.SERIALIZATION_LENGTH + header.getContentLength()) {
            throw new SerializationException(String.format("Given buffer has insufficient number of bytes for this DataFrame. Expected %d, actual %d.",
                    ReadFrameHeader.SERIALIZATION_LENGTH + header.getContentLength(), length));
        }

        BoundedInputStream contents = new BoundedInputStream(source, header.getContentLength());
        return new DataFrameEntryIterator(contents, address, ReadFrameHeader.SERIALIZATION_LENGTH);
    }

    private static abstract class EntryHeader {
        static final int HEADER_SIZE = Integer.BYTES + Byte.BYTES; static final int FLAGS_OFFSET = Integer.BYTES;
        static final byte FIRST_ENTRY_MASK = 1;
        static final byte LAST_ENTRY_MASK = 2;

        @Getter
        @Setter
        private int entryLength;

        @Getter
        @Setter
        private boolean firstRecordEntry;

        @Getter
        @Setter
        private boolean lastRecordEntry;

        @Override
        public String toString() {
            return String.format("Length = %d, FirstEntry = %s, LastEntry = %s", getEntryLength(), isFirstRecordEntry(), isLastRecordEntry());
        }
    }

    private static class WriteEntryHeader extends EntryHeader {
        private CompositeArrayView data;

        WriteEntryHeader(CompositeArrayView headerContents) {
            Exceptions.checkArgument(headerContents.getLength() == HEADER_SIZE, "headerContents",
                    "Invalid headerContents size. Expected %d, given %d.", HEADER_SIZE, headerContents.getLength());
            this.data = headerContents;
        }

        void serialize() {
            Preconditions.checkState(this.data != null, "Cannot serialize a read-only EntryHeader.");

            this.data.setInt(0, getEntryLength());

            byte flags = isFirstRecordEntry() ? FIRST_ENTRY_MASK : 0;
            flags |= isLastRecordEntry() ? LAST_ENTRY_MASK : 0;
            this.data.set(FLAGS_OFFSET, flags);
        }
    }

    private static class ReadEntryHeader extends EntryHeader {
        ReadEntryHeader(InputStream inputStream) throws IOException {
            setEntryLength(BitConverter.readInt(inputStream));
            byte flags = (byte) inputStream.read();
            if (flags < 0) {
                throw new EOFException();
            }
            setFirstRecordEntry((flags & FIRST_ENTRY_MASK) == FIRST_ENTRY_MASK);
            setLastRecordEntry((flags & LAST_ENTRY_MASK) == LAST_ENTRY_MASK);
        }
    }

    private static abstract class FrameHeader {
        static final int SERIALIZATION_LENGTH = Byte.BYTES + Integer.BYTES + Byte.BYTES;
        @Getter
        @Setter
        private byte version;

        @Getter
        @Setter
        private int contentLength;

        byte encodeFlags() {
            return 0;
        }

        void decodeFlags(byte flags, byte version) {
            }

        @Override
        public String toString() {
            return String.format("Version = %d, ContentLength = %d", getVersion(), getContentLength());
        }
    }

    private static class WriteFrameHeader extends FrameHeader {
        @Getter
        private final int serializationLength;
        private CompositeArrayView buffer;

        WriteFrameHeader(byte version, CompositeArrayView target) {
            Exceptions.checkArgument(target.getLength() == SERIALIZATION_LENGTH, "target",
                    "Unexpected length for target buffer. Expected %d, given %d.", SERIALIZATION_LENGTH, target.getLength());
            setVersion(version);
            this.serializationLength = SERIALIZATION_LENGTH;
            this.buffer = target;
        }

        void commit() {
            Preconditions.checkState(this.buffer != null, "Cannot commit a read-only FrameHeader");
            assert this.buffer.getLength() == SERIALIZATION_LENGTH;

            int bufferOffset = 0;
            this.buffer.set(bufferOffset, getVersion());
            bufferOffset += Byte.BYTES;
            this.buffer.setInt(bufferOffset, getContentLength());
            bufferOffset += Integer.BYTES;
            this.buffer.set(bufferOffset, encodeFlags());
        }
    }

    private static final class ReadFrameHeader extends FrameHeader {
        ReadFrameHeader(InputStream source) throws IOException {
            Preconditions.checkNotNull(source, "source");
            byte version = (byte) source.read();
            setVersion(version);
            if (version < 0) {
                throw new EOFException();
            }

            setContentLength(BitConverter.readInt(source));
            byte flags = (byte) source.read();
            if (flags < 0) {
                throw new EOFException();
            }
            decodeFlags(flags, version);
        }
    }

    public static class DataFrameEntry {
        @Getter
        private final boolean firstRecordEntry;

        @Getter
        private final boolean lastRecordEntry;

        @Getter
        private final boolean lastEntryInDataFrame;

        @Getter
        private final LogAddress frameAddress;

        @Getter
        private final InputStream data;

        @Getter
        private final int length;

        @Getter
        private final int frameOffset;

        private DataFrameEntry(EntryHeader header, BoundedInputStream data, LogAddress frameAddress, boolean lastEntryInDataFrame, int frameOffset) {
            this.firstRecordEntry = header.isFirstRecordEntry();
            this.lastRecordEntry = header.isLastRecordEntry();
            this.lastEntryInDataFrame = lastEntryInDataFrame;
            this.frameAddress = frameAddress;
            this.data = data;
            this.length = data.getBound();
            this.frameOffset = frameOffset;
        }

        @Override
        public String toString() {
            return String.format("Address = %s, Size = %d, First = %s, Last = %s, LastInDataFrame = %s", this.frameAddress,
                    getLength(), isFirstRecordEntry(), isLastRecordEntry(), isLastEntryInDataFrame());
        }
    }

    @RequiredArgsConstructor
    static class DataFrameEntryIterator implements CloseableIterator<DataFrameEntry, IOException> {
        private final BoundedInputStream contents;
        @Getter
        private final LogAddress frameAddress;
        private final int bufferOffset;
        private BoundedInputStream lastEntryContents;


        @Override
        @SneakyThrows(IOException.class)
        public void close() {
            closeLast();
            this.contents.close();
        }

        private void closeLast() throws IOException {
            BoundedInputStream last = this.lastEntryContents;
            if (last != null) {
                last.close();
                this.lastEntryContents = null;
            }
        }

        @VisibleForTesting
        int getLength() {
            return this.contents.getBound();
        }

        @Override
        public DataFrameEntry getNext() throws IOException {
            closeLast();
            if (reachedEnd()) {
                return null;
            }

            if (this.contents.getRemaining() < EntryHeader.HEADER_SIZE) {
                throw new SerializationException(String.format("Data Frame is corrupt. InputStream has insufficient bytes for a new Entry Header (%d).",
                        this.contents.getRemaining()));
            }

            ReadEntryHeader header = new ReadEntryHeader(this.contents);

            if (this.contents.getRemaining() < header.getEntryLength()) {
                throw new SerializationException(String.format("Data Frame is corrupt. Found Entry Length %d which cannot fit in the Frame's remaining length of %d.",
                        header.getEntryLength(), this.contents.getRemaining()));
            }

            int frameOffset = this.bufferOffset + this.contents.getBound() - this.contents.getRemaining();
            BoundedInputStream resultContents = this.contents.subStream(header.getEntryLength());
            this.lastEntryContents = resultContents;
            return new DataFrameEntry(header, resultContents, this.frameAddress, reachedEnd(), frameOffset);
        }

        private boolean reachedEnd() {
            return this.contents.getRemaining() <= 0;
        }
    }

@com.gigaspaces.api.InternalApi
public class PendingFifoSearch {
    static int INITIAL_SCAN_ACTIVE = 0;  static int INITIAL_SCAN_NOTACTIVE = 1;

    private int _status;

    private TreeMap<IEntryHolder, FifoBackgroundRequest> _rejectedFifoEntries = null;

    private boolean _needNotifyTermination;
    private final boolean _memorySpace;
    final private static FifoEntriesComparator m_Comparator =
            new FifoEntriesComparator();


    public PendingFifoSearch(boolean memorySpace) {
        _status = INITIAL_SCAN_ACTIVE;
        _memorySpace = memorySpace;
    }


    public synchronized void addRejectedEntry(IEntryHolder eh, FifoBackgroundRequest red) {

        if (_rejectedFifoEntries == null)
            _rejectedFifoEntries = new TreeMap<IEntryHolder, FifoBackgroundRequest>(m_Comparator);

        _rejectedFifoEntries.put(eh, red);

    }

    public synchronized boolean anyRejectedEntries() {
        return _rejectedFifoEntries != null && !_rejectedFifoEntries.isEmpty();
    }


    public synchronized ArrayList<FifoBackgroundRequest> getRejectedEntries() {
        if (_rejectedFifoEntries == null)
            return null;
        ArrayList<FifoBackgroundRequest> res = new ArrayList<FifoBackgroundRequest>();

        Iterator<Map.Entry<IEntryHolder, FifoBackgroundRequest>> titer = _rejectedFifoEntries.entrySet().iterator();
        while (titer.hasNext()) {
            Map.Entry<IEntryHolder, FifoBackgroundRequest> me = titer.next();
            IEntryHolder eh = me.getKey();
            if (_memorySpace && eh.isDeleted())
                continue;
            res.add(me.getValue());
        }


        return res;
    }

    public synchronized void notifyNonActiveIfNeedTo() {
        _status = INITIAL_SCAN_NOTACTIVE;
        if (_needNotifyTermination) {
            this.notify();
        }
    }

    public synchronized void waitForNonActiveStatus() {
        try {
            if (_status == INITIAL_SCAN_NOTACTIVE)
                return;
            _needNotifyTermination = true;
            this.wait(5000);
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
        }
    }


}

