public class JPGameGrabTask extends AsyncTask<String, Integer, Integer> {
    private static final int TYPE_SUCCESS = 0;
    private static final int TYPE_FAILED = 1;

    private final Context mContext;
    private final SQLiteDatabase mDatabase;

    public JPGameGrabTask(Context context) {
        this.mContext = context;
        this.mDatabase = new GameBaseHelper(mContext).getWritableDatabase();
    }

    @Override
    protected Integer doInBackground(String... params) {
        OkHttpClient client = new OkHttpClient();

        Request request = new Request.Builder()
                .url("https://www.nintendo.co.jp/data/software/xml/switch.xml")
                .build();
        try (Response response = client.newCall(request).execute()) {
            String responseData = response.body().string();
            parseXMLWithPullAndAddToDb(responseData);
        } catch (Exception e) {
            e.printStackTrace();
            return TYPE_FAILED;
        }
        return TYPE_SUCCESS;
    }

    @Override
    protected void onPostExecute(Integer integer) {
        switch (integer) {
            case TYPE_SUCCESS:
                break;
            case TYPE_FAILED:
                break;
            default:
        }

        mDatabase.close();
    }

    private void parseXMLWithPullAndAddToDb(String xmlData) {
        try {
            XmlPullParserFactory factory = XmlPullParserFactory.newInstance();
            XmlPullParser xmlPullParser = factory.newPullParser();
            xmlPullParser.setInput(new StringReader(xmlData));
            int eventType = xmlPullParser.getEventType();

            JPGame jpGame = null;

            while (eventType != XmlPullParser.END_DOCUMENT) {
                String nodeName = xmlPullParser.getName();
                switch (eventType) {
                    case XmlPullParser.START_TAG: {
                        switch (nodeName) {
                            case "InitialCode":
                                jpGame = new JPGame();
                                jpGame.setInitialCode(parseGameCode(xmlPullParser.nextText()));
                                break;
                            case "TitleName":
                                jpGame.setTitleName(xmlPullParser.nextText());
                                break;
                            case "MakerName":
                                jpGame.setMakerName(xmlPullParser.nextText());
                                break;
                            case "MakerKana":
                                jpGame.setMakerKana(xmlPullParser.nextText());
                                break;
                            case "Price":
                                jpGame.setPrice(xmlPullParser.nextText());
                                break;
                            case "SalesDate":
                                jpGame.setSalesDate(xmlPullParser.nextText());
                                break;
                            case "SoftType":
                                jpGame.setSoftType(xmlPullParser.nextText());
                                break;
                            case "PlatformID":
                                jpGame.setPlatformID(xmlPullParser.nextText());
                                break;
                            case "DlIconFlg":
                                jpGame.setDlIconFlg(xmlPullParser.nextText());
                                break;
                            case "LinkURL":
                                jpGame.setLinkURL("https://ec.nintendo.com/JP/ja" + xmlPullParser.nextText());
                                break;
                            case "ScreenshotImgFlg":
                                jpGame.setScreenshotImgFlg(xmlPullParser.nextText());
                                break;
                            case "ScreenshotImgURL":
                                jpGame.setScreenshotImgURL(xmlPullParser.nextText());
                                jpGame.setNsUid(parseNsUid(jpGame.getLinkURL()));
                                addJPGame(jpGame);
                                break;
                        }
                        break;
                    }
                    case XmlPullParser.END_TAG: {
                        break;
                    }
                    default:
                        break;
                }
                eventType = xmlPullParser.next();
            }
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    private void addJPGame(JPGame jpGame) {
        ContentValues values = getContentValues(jpGame);

        JPGameCursorWrapper cursor = queryJPGames("title_name = ?", new String[]{jpGame.getTitleName()});

        if (cursor.moveToFirst()) {
            mDatabase.update(JPGameTable.NAME, values, "title_name = ?", new String[]{jpGame.getTitleName()});
        } else{
            mDatabase.insert(JPGameTable.NAME, null, values);
        }
        cursor.close();
    }

    private static ContentValues getContentValues(JPGame jpGame) {
        ContentValues values = new ContentValues();
        values.put(JPGameTable.Cols.INITIAL_CODE, jpGame.getInitialCode());
        values.put(JPGameTable.Cols.NSUID, jpGame.getNsUid());
        values.put(JPGameTable.Cols.TITLE_NAME, jpGame.getTitleName());
        values.put(JPGameTable.Cols.MAKER_NAME, jpGame.getMakerName());
        values.put(JPGameTable.Cols.MAKER_KANA, jpGame.getMakerKana());
        values.put(JPGameTable.Cols.PRICE, jpGame.getPrice());
        values.put(JPGameTable.Cols.SALES_DATE, jpGame.getSalesDate());
        values.put(JPGameTable.Cols.SOFT_TYPE, jpGame.getSoftType());
        values.put(JPGameTable.Cols.PLATFORM_ID, jpGame.getPlatformID());
        values.put(JPGameTable.Cols.DL_ICON_FLG, jpGame.getDlIconFlg());
        values.put(JPGameTable.Cols.LINK_URL, jpGame.getLinkURL());
        values.put(JPGameTable.Cols.SCREENSHOT_IMG_FLG, jpGame.getScreenshotImgFlg());
        values.put(JPGameTable.Cols.SCREENSHOT_IMG_URL, jpGame.getScreenshotImgURL());

        return values;
    }

    private JPGameCursorWrapper queryJPGames(String whereClause, String[] whereArgs) {
        Cursor cursor = mDatabase.query(
                JPGameTable.NAME,
                null,
                whereClause,
                whereArgs,
                null,
                null,
                null
        );
        return new JPGameCursorWrapper(cursor);
    }

    private String parseGameCode(String gameCode) {
        return gameCode.length() == 8? gameCode.substring(3, 7): null;
    }

    private String parseNsUid(String linkUrl) {
        return linkUrl.split("https://ec.nintendo.com/JP/ja/titles/")[1];
    }
}

public abstract class BottomSheetDependentBehavior<V extends View>
    extends CoordinatorLayout.Behavior<V> {
  public BottomSheetDependentBehavior(Context context, AttributeSet attrs) {
    super(context, attrs);
  }

  protected abstract void onBottomSheetChanged(
      CoordinatorLayout parent, V child, BottomSheetMetrics metrics);

  @Override
  public boolean layoutDependsOn(CoordinatorLayout parent, V child, View dependency) {
    return dependency.getId() == R.id.bottom_sheet_layout;
  }

  @Override
  public boolean onDependentViewChanged(CoordinatorLayout parent, V child, View bottomSheet) {
    onBottomSheetChanged(parent, child, new BottomSheetMetrics(bottomSheet));
    return false;
  }

  @Override
  public boolean onLayoutChild(CoordinatorLayout parent, V child, int layoutDirection) {
    View bottomSheet = parent.findViewById(R.id.bottom_sheet_layout);
    onBottomSheetChanged(parent, child, new BottomSheetMetrics(bottomSheet));
    return false;
  }
}

public class EntityEventContext {

	public static final String INSERT_OPTION = "insertOption";
	public static final String UPDATE_OPTION = "updateOption";
	public static final String DELETE_OPTION = "deleteOption";
	public static final String VALIDATE_PROPERTIES = "validateProperties";
	public static final String BEFORE_UPDATE_ENTITY = "beforeUpdateEntity";

	private HashMap<String, Object> contextValues;

	public EntityEventContext() {
	}

	public void setAttribute(String key, Object value) {
		if (contextValues == null) {
			contextValues = new HashMap<String, Object>();
		}
		contextValues.put(key, value);
	}

	public Object getAttribute(String key) {
		if (contextValues == null) {
			return null;
		}
		return contextValues.get(key);
	}

}

public abstract class DLNAHeader<T> extends UpnpHeader<T> {

    final private static Logger log = Logger.getLogger(DLNAHeader.class.getName());

    public static enum Type {

        TimeSeekRange("TimeSeekRange.dlna.org", TimeSeekRangeHeader.class),
        XSeekRange("X-Seek-Range", TimeSeekRangeHeader.class),
        PlaySpeed("PlaySpeed.dlna.org", PlaySpeedHeader.class),
        AvailableSeekRange("availableSeekRange.dlna.org", AvailableSeekRangeHeader.class),
        GetAvailableSeekRange("getAvailableSeekRange.dlna.org", GetAvailableSeekRangeHeader.class),
        GetContentFeatures("getcontentFeatures.dlna.org", GetContentFeaturesHeader.class),
        ContentFeatures("contentFeatures.dlna.org", ContentFeaturesHeader.class),
        TransferMode("transferMode.dlna.org", TransferModeHeader.class),
        FriendlyName("friendlyName.dlna.org", FriendlyNameHeader.class),
        PeerManager("peerManager.dlna.org", PeerManagerHeader.class),
        AvailableRange("Available-Range.dlna.org", AvailableRangeHeader.class),
        SCID("scid.dlna.org", SCIDHeader.class),
        RealTimeInfo("realTimeInfo.dlna.org", RealTimeInfoHeader.class),
        ScmsFlag("scmsFlag.dlna.org", ScmsFlagHeader.class),
        WCT("WCT.dlna.org", WCTHeader.class),
        MaxPrate("Max-Prate.dlna.org", MaxPrateHeader.class),
        EventType("Event-Type.dlna.org", EventTypeHeader.class),
        Supported("Supported", SupportedHeader.class),
        BufferInfo("Buffer-Info.dlna.org", BufferInfoHeader.class),
        RTPH264DeInterleaving("rtp-h264-deint-buf-cap.dlna.org", BufferBytesHeader.class),
        RTPAACDeInterleaving("rtp-aac-deint-buf-cap.dlna.org", BufferBytesHeader.class),
        RTPAMRDeInterleaving("rtp-amr-deint-buf-cap.dlna.org", BufferBytesHeader.class),
        RTPAMRWBPlusDeInterleaving("rtp-amrwbplus-deint-buf-cap.dlna.org", BufferBytesHeader.class),
        PRAGMA("PRAGMA", PragmaHeader.class);

        private static Map<String, Type> byName = new HashMap<String, Type>() {{
            for (Type t : Type.values()) {
                put(t.getHttpName(), t);
            }
        }};

        private String httpName;
        private Class<? extends DLNAHeader>[] headerTypes;

        private Type(String httpName, Class<? extends DLNAHeader>... headerClass) {
            this.httpName = httpName;
            this.headerTypes = headerClass;
        }

        public String getHttpName() {
            return httpName;
        }

        public Class<? extends DLNAHeader>[] getHeaderTypes() {
            return headerTypes;
        }

        public boolean isValidHeaderType(Class<? extends DLNAHeader> clazz) {
            for (Class<? extends DLNAHeader> permissibleType : getHeaderTypes()) {
                if (permissibleType.isAssignableFrom(clazz)) {
                    return true;
                }
            }
            return false;
        }

        public static Type getByHttpName(String httpName) {
            if (httpName == null) return null;
        	return byName.get(httpName);
        }
    }



    public static DLNAHeader newInstance(DLNAHeader.Type type, String headerValue) {

        DLNAHeader upnpHeader = null;
        for (int i = 0; i < type.getHeaderTypes().length && upnpHeader == null; i++) {
            Class<? extends DLNAHeader> headerClass = type.getHeaderTypes()[i];
            try {
                log.finest("Trying to parse '" + type + "' with class: " + headerClass.getSimpleName());
                upnpHeader = headerClass.newInstance();
                if (headerValue != null) {
                    upnpHeader.setString(headerValue);
                }
            } catch (InvalidHeaderException ex) {
                log.finest("Invalid header value for tested type: " + headerClass.getSimpleName() + " - " + ex.getMessage());
                upnpHeader = null;
            } catch (Exception ex) {
                log.severe("Error instantiating header of type '" + type + "' with value: " + headerValue);
                log.log(Level.SEVERE, "Exception root cause: ", Exceptions.unwrap(ex));
            }

        }
        return upnpHeader;
    }
}

public class MakeTransliterator {
    static int forceSeparateIfShorter = 4; private static final String CHECK_BASE = null; private static final String CHECK_BUILT = null; private static final String TEST_STRING = "territories";
    private static final boolean SHOW_OVERRIDES = true;

    private static final int MINIMUM_FREQUENCY = 9999;

    static boolean isIPA = true;
    static boolean onlyToTarget = true;

    static NumberFormat nf = NumberFormat.getInstance(ULocale.ENGLISH);

    static Collator col = Collator.getInstance(ULocale.ROOT);

    static String cldrDataDir = "C:\\cvsdata\\unicode\\cldr\\tools\\java\\org\\unicode\\cldr\\util\\data\\transforms\\";

    public static void main(String[] args) throws IOException {
        setTranslitDebug(true);

        Locale fil = new Locale("fil");
        System.out.println(fil);
        fil = new Locale("fil", "US");
        System.out.println(fil);

        String sourceFile = cldrDataDir + "internal_raw_IPA.txt";
        String targetFile = cldrDataDir + "en-IPA.txt";
        String targetCountFile = cldrDataDir + "en-IPA_count.txt";
        String skippedLinesFile = "C:\\DATA\\GEN\\SkippedIPA.txt";

        PrintWriter skippedOut = FileUtilities.openUTF8Writer("", skippedLinesFile);

        String fixBadIpaRules = createFromFile(cldrDataDir + "internal_fixBadIpa.txt", null, null);
        fixBadIpa = Transliterator.createFromRules("foo", fixBadIpaRules, Transliterator.FORWARD);

        Map<String, String> overrides = getOverrides();

        String coreForeRules = createFromFile(cldrDataDir + "internal_baseEnglishToIpa.txt", null, null);
        coreBase = Transliterator.createFromRules("foo", coreForeRules, Transliterator.FORWARD);
        if (CHECK_BASE != null) {
            setTranslitDebug(true);
            System.out.println(coreBase.transliterate(CHECK_BASE));
            return;
        }

        if (CHECK_BUILT != null) {
            String foo = createFromFile(cldrDataDir + "en-IPA.txt", null, null);
            Transliterator fooTrans = Transliterator.createFromRules("foo", foo, Transliterator.FORWARD);

            setTranslitDebug(true);
            System.out.println(fooTrans.transliterate(CHECK_BUILT));
            return;
        }

        String coreBackRules = createFromFile(cldrDataDir + "internal_English-IPA-backwards.txt", null, null);
        checkCoreReversibility(skippedOut, coreForeRules, coreBackRules);
        String coreRules = coreForeRules + coreBackRules;
        System.out.println(coreRules);

        Relation<String, Pair<String, Long>> store = Relation.of(new TreeMap<String, Set<Pair<String, Long>>>(MyComparator),
            TreeSet.class);

        targetCharacters = new UnicodeSet();
        sourceCharacters = new UnicodeSet();
        allowedSourceCharacters = new UnicodeSet(
            "[[:Letter:]\u2019]").freeze();
        allowedTargetCharacters = new UnicodeSet(
            "[\u00E6 \u0251 b d\u00F0 e \u0259 \u025B f-i \u026A j-n \u014B o p r s \u0283 t u \u028A v w z \u0292 \u03B8]")
                .freeze();
        countSkipped = 0;
        totalFrequency = 0;
        skippedFrequency = 0;
        int targetField = isIPA ? 2 : 1;

        BufferedReader in = FileUtilities.openUTF8Reader("", sourceFile);
        while (true) {
            String line = in.readLine();
            if (line == null)
                break;
            if (line.startsWith("\uFEFF")) {
                line = line.substring(1);
            }
            String originalLine = line;
            int commentCharPosition = line.indexOf('#');
            if (commentCharPosition >= 0) {
                line = line.substring(0, commentCharPosition);
            }
            line = line.trim();
            frequency = -1;
            String[] pieces = line.split(" *[\\t,] *");
            if (pieces.length <= targetField) {
                continue; }
            String source = pieces[0];
            if (TEST_STRING != null && source.equals(TEST_STRING)) {
                System.out.println(line); }

            source = source.replace("'", "");
            source = UCharacter.toLowerCase(ULocale.ENGLISH, source);
            if (source.endsWith(".")) {
                source = source.substring(0, source.length() - 1);
            }
            if (source.contains(" ") || source.contains("-")) {
                skippedOut.println(originalLine + "\tspace or hyphen");
                countSkipped++;
                skippedFrequency += frequency;
                continue;
            }

            String override = overrides.get(source);
            String spelling = spellout.transliterate(source);

            for (int i = 1; i < pieces.length; ++i) {
                String target = pieces[i];
                if (target.startsWith("%")) {
                    frequency = Long.parseLong(target.substring(1));
                    continue;
                }

                if (override != null) {
                    if (SHOW_OVERRIDES)
                        System.out.println("Overriding\t" + source + "  ! " + target + "  " + override);
                    if (override.length() != 0) {
                        if (TEST_STRING != null && source.equals(TEST_STRING)) {
                            setTranslitDebug(true);
                        }
                        target = fixBadIpa.transliterate(override);
                        setTranslitDebug(false);
                        addSourceTarget(skippedOut, source, target, originalLine, store);
                    }
                    break;
                }

                if (frequency < MINIMUM_FREQUENCY) {
                    countSkipped++;
                    continue;
                }

                target = UCharacter.toLowerCase(ULocale.ENGLISH, target);
                target = target.replace(" ", ""); if (target.startsWith("-") || target.endsWith("-")) {
                    continue;
                }

                String oldTarget = target;
                target = fixBadIpa.transliterate(target);

                if (target.equals(spelling)) {
                    skippedOut.println(originalLine
                        + "\tspellout");
                    countSkipped++;
                    continue;
                }

                if (!target.equals(oldTarget)) {
                    skippedOut.println("\t### fixed IPA:\t" + source + "\t" + target
                        + "\twas: " + oldTarget);
                }

                addSourceTarget(skippedOut, source, target, originalLine, store);
            }
        }

        for (String word : overrides.keySet()) {
            if (!store.containsKey(word)) {
                String target = overrides.get(word);
                if (target.length() != 0) {
                    if (SHOW_OVERRIDES) System.out.println("New overrides:\t" + word + "  " + target);
                    addSourceTarget(skippedOut, word, target, "overrides", store);
                }
            }
        }
        in.close();
        System.out.println("total count: " + nf.format(store.size()));
        System.out.println("skipped count: " + nf.format(countSkipped));

        System.out.println("total frequency-weighted: " + nf.format(totalFrequency));
        System.out.println("skipped frequency-weighted: " + nf.format(skippedFrequency));

        if (false) {
            System.out.println(CldrUtility.LINE_SEPARATOR + "Source Characters ");
            showSet(sourceCharacters);
            System.out.println(CldrUtility.LINE_SEPARATOR + "Target Characters ");
            showSet(targetCharacters);
        }

        int countAdded = 0;
        int countTotal = 0;
        long frequencyAdded = 0;
        long frequencySkipped = 0;

        Transliterator base = Transliterator.createFromRules("foo", coreRules, Transliterator.FORWARD);
        List<String> newRules = new ArrayList<>();
        StringBuilder buffer = new StringBuilder();

        int lastSourceLength = 1;

        Relation<Long, String> count_failures = Relation.of(new TreeMap<Long, Set<String>>(), TreeSet.class);

        sourceLoop: for (String source : store.keySet()) {
            if (TEST_STRING != null && source.equals(TEST_STRING)) {
                System.out.println(source + "\t" + store.getAll(source));
            }
            countTotal++;
            if (source.length() != lastSourceLength && source.length() >= forceSeparateIfShorter) {
                System.out.println("Building transliterator for length " + lastSourceLength + " : " + newRules.size());
                System.out.flush();
                skippedOut.flush();
                String rules = buildRules(coreRules, newRules, buffer);
                base = Transliterator.createFromRules("foo", rules, Transliterator.FORWARD);

                lastSourceLength = source.length();
            }
            Set<Pair<String, Long>> targetSet = store.getAll(source);
            String targetUsingCore = base.transliterate(source);

            String bestTarget = null;
            int bestDistance = 999;
            long frequency = 0;
            for (Pair<String, Long> targetPair : targetSet) {
                String target = targetPair.getFirst();
                if (target.length() == 0) {
                    throw new IllegalArgumentException(source + "  " + target);
                }
                frequency = targetPair.getSecond();

                if (targetUsingCore.equals(target)) {
                    skippedOut.println("# skipping " + source + "  " + target + " ;");
                    frequencySkipped += frequency;
                    continue sourceLoop;
                }
                if (mostlyEqual(source, target, targetUsingCore)) {
                    skippedOut.println("# skipping " + source + "  " + target + " ; # close enough to "
                        + targetUsingCore);
                    frequencySkipped += frequency;
                    continue sourceLoop;
                }
                int distance = distance(source, target, targetUsingCore);
                if (bestDistance > distance) {
                    bestTarget = target;
                    bestDistance = distance;
                }
            }
            if (bestTarget != null) {
                boolean forceSeparate = false;
                if (source.length() < forceSeparateIfShorter || bestTarget.length() * 2 > source.length() * 3) {
                    forceSeparate = true;
                } else {
                    String spelling = spellout.transliterate(source);
                    if (bestTarget.equals(spelling)) {
                        forceSeparate = true;
                    } else {
                        if (source.endsWith("e")) {
                            forceSeparate = true;
                        }
                    }
                }
                String targetUsingBaseCore = coreBase.transliterate(source);

                if (forceSeparate) {
                    source = "$x{" + source + "}$x";
                } else {
                    source = "$x{" + source;
                }
                String hackSource = source.startsWith("use") ? "'" + source + "'" : source;
                newRules.add(hackSource + "  " + bestTarget + " ; # " + targetUsingCore
                    + (targetUsingBaseCore.equals(targetUsingCore) ? "" : "\t\t" + targetUsingBaseCore)
                    + CldrUtility.LINE_SEPARATOR);
                skippedOut.println("# couldn't replace  " + source + "  " + bestTarget + " ; # " + targetUsingCore);
                count_failures.put(-frequency, source + "  " + bestTarget + " ; # " + targetUsingCore);
                countAdded++;
                frequencyAdded += frequency;
            }
        }

        String rules = buildRules(coreRules, newRules, buffer);
        base = Transliterator.createFromRules("foo", rules, Transliterator.FORWARD); PrintWriter out = FileUtilities.openUTF8Writer("", targetFile);
        out.println(rules);
        out.close();

        out = FileUtilities.openUTF8Writer("", targetCountFile);
        for (long count : count_failures.keySet()) {
            for (String line : count_failures.getAll(count)) {
                out.println(count + "\t" + line);
            }
        }
        out.close();

        skippedOut.close();
        System.out.println("countTotal: " + nf.format(countTotal));
        System.out.println("countAdded: " + nf.format(countAdded));
        System.out.println("countSkipped: " + nf.format(countTotal - countAdded));
        System.out.println("frequencyTotal: " + nf.format(frequencyAdded + frequencySkipped));
        System.out.println("frequencyAdded: " + nf.format(frequencyAdded));
        System.out.println("frequencySkipped: " + nf.format(frequencySkipped));
    }

    private static void setTranslitDebug(boolean newSetting) {
        try {
            Field debug = Transliterator.class.getField("DEBUG");
            debug.setBoolean(Transliterator.class, newSetting);
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    private static void addSourceTarget(PrintWriter skippedOut, String source, String target, String originalLine,
        Relation<String, Pair<String, Long>> store) {
        if (source.equals("teh")) {
            System.out.println("debug");
        }
        if (!allowedSourceCharacters.containsAll(source)) {
            skippedOut.println(originalLine
                + "\t# Strange source values:\t"
                + source
                + "\t"
                + new UnicodeSet().addAll(source)
                    .removeAll(allowedSourceCharacters).toPattern(false));
            countSkipped++;
            skippedFrequency += frequency;
            return;
        }
        if (!allowedTargetCharacters.containsAll(target)) {
            System.out.println(originalLine
                + "\t# Strange target values:\t"
                + target
                + "\t"
                + new UnicodeSet().addAll(target)
                    .removeAll(allowedTargetCharacters).toPattern(false));
            countSkipped++;
            skippedFrequency += frequency;
            return;
        }

        sourceCharacters.addAll(source);
        targetCharacters.addAll(target);
        store.put(source, new Pair<>(target, frequency));
        totalFrequency += frequency;

    }

    private static void checkCoreReversibility(PrintWriter skippedOut, String coreRules, String coreBackRules) {
        Transliterator base = Transliterator.createFromRules("foo", coreRules, Transliterator.FORWARD);
        Transliterator back = Transliterator.createFromRules("foo2", coreBackRules, Transliterator.REVERSE);
        String[] tests = "bat bait bet beet bit bite bot boat but bute bout boot book boy pat bat vat fat mat tat dat thew father nat sat zoo ash asia gate cat late rate hate yet rang chat jet"
            .split("\\s");
        for (String test : tests) {
            String test2 = base.transliterate(test);
            String test3 = back.transliterate(test2);
            skippedOut.println(test + "\t " + test2 + "\t " + test3);
        }
        skippedOut.flush();
    }

    private static String buildRules(String coreRules, List<String> newRules, StringBuilder buffer) {
        buffer.setLength(0);
        buffer.append(
            "# Author: M Davis" + CldrUtility.LINE_SEPARATOR +
                "# Email: mark.davis@icu-project.org" + CldrUtility.LINE_SEPARATOR +
                "# Description: English to IPA" + CldrUtility.LINE_SEPARATOR +
                ":: lower(); " + CldrUtility.LINE_SEPARATOR +
                "$x = [:^letter:] ;" + CldrUtility.LINE_SEPARATOR);
        for (int i = newRules.size() - 1; i >= 0; --i) {
            buffer.append(newRules.get(i));
        }
        buffer.append(coreRules);
        String result = buffer.toString();
        return result;
    }

